var textForPages =["1\u000d\u000a\u4e2d\u83ef\u6c11\u570b\u4e00\u4e00\u4e00\u5e74\u6b72\u6b21\u8f9b\u4e11\u570b \u66c6 \u5143 \u6708 \u4e00 \u65e5\u000d\u000a\u8fb2\u66c6\u5341\u4e00\u6708\u5eff\u4e5d\u65e5\u4ed9\u4f5b\u6148\u8a13\u000d\u000a\u4e94\u6fc1\u60e1\u4e16\u842c\u72c0\u5305\u7f85 \u8ff7\u60d1\u4eba\u5fc3\u5e7b\u666f\u64b2\u6714\u000d\u000a\u89ba\u8fd4\u8ff7\u6d25\u958b\u793a\u609f\u5165 \u91cd\u632f\u9053\u5fc3\u613f\u5b89\u7406\u5f97\u000d\u000a\u543e\u4e43\u000d\u000a \u5357\u5c4f\u6fdf\u516c \u5949\u000d\u000a\u547d \u4f86\u5230\u4f5b\u5bae \u96b1\u8eab\u65e9\u53c3\u000d\u000a\u7687 \u99d5 \u518d\u554f\u5f92\u5152\u5011\u5426\u5b89\u5e73","2\u000d\u000a\u4eba\u5fc3\u591a\u6d6e\u52d5 \u4e94\u97f3\u4ee4\u8033\u807e\u000d\u000a\u8ff7\u60d1\u5883\u4e2d\u4e0d\u77e5\u6625\u79cb\u904e\u4e86\u591a\u5c11\u51ac\u000d\u000a\u5e38\u5b58\u50e5\u5016\u5165\u7f8e\u5922\u000d\u000a\u4e0d\u89e3\u7121\u5e38\u8af8\u6cd5\u696d\u6210\u719f\u000d\u000a\u842c\u822c\u56e0\u7de3\u7a7a\u000d\u000a\u6642\u81f3\u672b\u5f8c \u767e\u6b65\u4e5d\u6298\u5f4e\u5761\u3010\u8a3b\u4e09\u3011\u000d\u000a\u56db\u842c\u516b\u5343\u5f15\u8a98 \u5e7e\u500b\u5f92\u5152\u660e\u61c2\u000d\u000a\u6025\u5982\u65cb\u98a8 \u5982\u9677\u4e7e\u8349\u9ede\u706b\u000d\u000a\u55df\u5606\u9b54\u5f37\u800c\u6cd5\u5f31 \u5e2b\u5fc3\u6182\u6101\u91cd\u91cd \uff08\u5176\u4e00\uff09","3\u000d\u000a\u4fee\u9053\u4f55\u70ba\u9996 \u660e\u7406\u65b9\u5bf8\u5b88\u000d\u000a\u7149\u6027\u6db5\u990a\u83ab\u8b93\u7fd2\u6c23\u505a\u4e86\u9738\u738b\u982d\u000d\u000a\u8b58\u5f97\u7576\u524d\u771f\u4e3b\u7fc1\u000d\u000a\u842c\u90e8\u4e39\u7d93\u4e00\u7b46\u7686\u92b7\u52fe\u000d\u000a\u7693\u6708\u7576\u7a7a\u60a0\u000d\u000a\u767e\u5c3a\u7aff\u982d \u884c\u529f\u826f\u6a5f\u628a\u63e1\u000d\u000a\u5982\u5922\u521d\u9192\u8cab\u901a \u52ff\u4ee4\u6b72\u6708\u8e49\u8dce\u000d\u000a\u7a4d\u6975\u63a8\u52d5 \u6148\u60b2\u6fdf\u4e16\u6e21\u773e\u000d\u000a\u842c\u8056\u547d\u8108\u50b3\u85aa\u706b \u5f92\u5152\u5fc3\u624b\u540c\u821f \uff08\u5176\u4e8c\uff09","4\u000d\u000a\u8abf\u5bc4\uff1a\u554f\u000d\u000a\u5802\u524d\u773e\u5f92\u7d30\u601d\u8003\u672b\u904b\u5047\u771f\u7be9\u6aa2\u6dd8\u000d\u000a\u4e2d\u9014\u800c\u5ee2\u4e9f\u5931\u6a5f\u4e94\u860a\u672a\u6ecc\u5b70\u79c9\u64cd\u3010\u8a3b\u4e00\u3011\u000d\u000a\u5750\u81e5\u79aa\u8005\u8a9e\u9ed8\u5b89\u83ab\u7d50\u51a4\u6106\u9192\u660e\u8981\u3010\u8a3b\u4e8c\u3011\u000d\u000a\u5ff5\u82e5\u4e0d\u4fee\u751f\u6d41\u5f0a\u7701\u5bdf\u5f7c\u904e\u5fb7\u91cf\u5c11\u000d\u000a\u6e05\u6de8\u7965\u5b9a\u7406\u5ff5\u5fb9\u6f5b\u79fb\u5584\u5316\u9edc\u72c2\u9a55\u000d\u000a\u660e\u667a\u7acb\u7a69\u5f8b\u5df1\u56b4\u8cac\u5099\u52d9\u5167\u4e09\u6bd2\u6383\u000d\u000a\u6b77\u8003\u64ab\u5e73\u613f\u6fdf\u6025\u82e6\u96e8\u6dd2\u98a8\u8f49\u62c2\u66c9\u000d\u000a\u4e45\u898b\u4eba\u5fc3\u5426\u884c\u6b63\u6cd5\u672b\u8870\u9839\u8aa0\u656c\u79b1\u000d\u000a\u613f\u5b8f\u5b88\u9053\u822c\u82e5\u529b\u591c\u5bd0\u5919\u8208\u7387\u61ff\u8868","5\u000d\u000a\u5f4c\u88dc\u6687\u65e5\u52e4\u78e8\u792a\u7b49\u9592\u8457\u5916\u6c23\u67af\u69c1\u000d\u000a\u8a13\u4e2d\u8a13\uff1a\u3010\u771f\u3011\u000d\u000a\u9053\u5fc3\u5e73\u7a69\u5b9a \u4fee\u8005\u7acb\u613f\u884c\u000d\u000a\u6b63\u6cd5\u672b\u8870\u6fdf \u5df1\u5ff5\u6d41\u5f0a\u7701\u000d\u000a\u5bdf\u5f7c\u56b4\u8cac\u5099 \u52d9\u5916\u8208\u9839\u98a8\u000d\u000a\u5167\u5316\u904e\u6106\u6ecc \u8aa0\u61ff\u5fb7\u79c9\u660e\u000d\u000a \u8abf\u5bc4\uff1a\u862d\u82b1\u8349","6\u000d\u000a\u9053\u5fc3\u8981\u771f \u81f3\u771f\u9022\u8003\u4e0d\u96e2\u9053\u000d\u000a\u5fb7\u884c\u8981\u771f \u81f3\u771f\u53d7\u8fb1\u4e0d\u5931\u5fb7\u000d\u000a\u8aa0\u4fe1\u8981\u771f \u81f3\u771f\u9006\u5883\u4e0d\u6096\u8aa0\u000d\u000a\u613f\u529b\u8981\u771f \u81f3\u771f\u9047\u9b54\u4e0d\u9055\u613f\u000d\u000a\u606d\u656c\u8981\u771f \u81f3\u771f\u805e\u6bc0\u4e0d\u6e1b\u606d\u000d\u000a\u4fee\u6301\u8981\u771f \u81f3\u771f\u56f0\u5384\u4e0d\u6028\u4fee","7\u000d\u000a\u3010\u5357\u5c4f\u6fdf\u516c \u6148\u60b2\u3011\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5927\u6bbf\u5916\u591a\u4f4d\u5764\u8fa6\u4e8b\u4eba\u54e1\u6148\u60b2\uff1a\u000d\u000a\u5f92\u5152\u5011\u6709\u6c92\u6709\u8a8d\u771f\u5b78\u7fd2\uff1f\u9053\u7406\u6709\u6c92\u6709\u807d\u9032\u5fc3\u88e1\u9762\u53bb\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\u9053\u9ede\u50b3\u5e2b\u5011\u6148\u60b2\uff1a\u4f60\u5011\u597d\u3002\u000d\u000a\u5404\u4f4d\u5f92\u5152\u5011\u597d\u554a\uff01\uff08\u8001\u5e2b\u597d\uff09\u6709\u6c92\u6709\u60f3\u5ff5\u70ba\u5e2b\u4e86\u5462\uff1f\uff08\u6709\uff09\u70ba\u5e2b\u65e5\u000d\u000a\u65e5\u60f3\u5ff5\u8457\u5f92\u5152\u5011\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u25ce\u8001\u5e2b\u6d41\u6dda\u54fd\u54bd\uff0c\u8a9e\u91cd\u5fc3\u9577\u7684\u6148\u60b2\uff1a\u000d\u000a\u592a\u591a\u4eba\u53d7\u707d\u53d7\u82e6\u4e86\uff0c\u70ba\u5e2b\u770b\u4e86\u90fd\u4e0d\u5fcd\u5fc3\u4e86\uff0c\u591a\u60f3\u8981\u5e6b\u5e6b\u5f92\u5152\u5011\u000d\u000a\u554a\uff01\u4f46\u662f\u73fe\u5728\u9019\u500b\u4e16\u754c\u300c\u6b63\u6c23\u7684\u529b\u91cf\u300d\u8db3\u5920\u55ce\uff1f\u5f92\u5152\u5011\u770b\u770b\uff0c\u73fe\u5728\u9084\u000d\u000a\u6709\u54ea\u4e00\u500b\u5730\u65b9\u662f\u5b89\u5168\u7684\u5462\uff1f\uff08\u6c92\u6709\uff09\u2500\u2500\u000d\u000a\u5982\u679c\u9023\u5f92\u5152\u5011\u81ea\u5df1\u4fee\u9053\u7684\u5fc3\u90fd\u4e0d\u5b89\u5168\uff0c\u9084\u6709\u54ea\u5152\u662f\u6de8\u571f\u5462\uff1f\u000d\u000a\u8af8\u5929\u4ed9\u4f5b\u65e5\u65e5\u90fd\u5728\u6182\u5fc3\uff0c","8\u000d\u000a\u6182\u5fc3\u4eba\u9593\u7684\u8ff7\u5b50\uff0c\u9084\u8981\u518d\u7e7c\u7e8c\u8ff7\u6627\u4e0b\u53bb\u55ce\uff1f\uff08\u4e0d\u8981\uff09\u000d\u000a\u4eba\u5011\u7684\u751f\u547d\u53c8\u6709\u591a\u9577\u5462\uff1f\u2500\u2500\u722d\u722d\u596a\u596a\uff0c\u5fc3\u6a5f\u5de7\u8a08\uff0c\u6709\u9700\u8981\u5982\u6b64\u000d\u000a\u5728\u4eba\u9593\u9019\u6a23\u751f\u6d3b\u55ce\uff1f\u000d\u000a\u5f92\u5152\u5011\u7686\u8981\u61c2\u5f97\u4fee\u9053\u4e86\u613f\uff0c\u000d\u000a\u77e5\u9053\u6709\u4e00\u689d\u300c\u4fee\u9053\u300d\u7684\u8def\u53ef\u4ee5\u8d70\uff0c\u77e5\u9053\u6709\u4e00\u689d\u300c\u6b78\u9109\u300d\u7684\u8def\uff0c\u000d\u000a\u7d42\u9ede\u6709\u723e\u5011\u7684\u524d\u4eba\u5728\u7b49\u8457\u4f60\u5011\uff0c\u8981\u8b93\u723e\u5011\u7684\u524d\u4eba\u7a7a\u7b49\u55ce\uff1f\uff08\u4e0d\u8981\uff09\u000d\u000a\u4e00\u65e5\u5fa9\u4e00\u65e5\uff0c\u4e00\u5e74\u53c8\u4e00\u5e74\uff0c\u5f92\u5152\u5011\u89ba\u5f97\u81ea\u5df1\u6539\u8b8a\u4e86\u591a\u5c11\uff1f\u8981\u8b93\u70ba\u000d\u000a\u5e2b\u6182\u5fc3\u591a\u4e45\u5462\uff1f\u4e16\u9593\u52d5\u76ea\u4e0d\u5b89\uff0c\u4fee\u9053\u4eba\u7684\u9053\u5834\u4e5f\u662f\u52d5\u76ea\u4e0d\u5b89\u3002\u9019\u9053\u4e5f\u000d\u000a\u9053\u4e0d\u5b8c\u7684\u8f9b\u9178\uff0c\u5c31\u5225\u8aaa\u4e86\uff0c\u4e5f\u7f77\uff01\u000d\u000a\u5f92\u5152\u5011\u80fd\u5920\u7b54\u61c9\u70ba\u5e2b\u55ce\uff1f\u000d\u000a\u5df2\u7d93\u8d70\u5230\u4e86\u9019\u4e00\u4e16\uff0c\u8981\u7528\u76e1\u7562\u751f\u7684\u5fc3\u529b\uff0c\u000d\u000a\u597d\u597d\u4fee\u5b8c\u9019\u6700\u5f8c\u7684\u4e00\u7a0b\u3002\u000d\u000a\u6046\u6301\u5230\u6700\u5f8c\u4e26\u4e0d\u56f0\u96e3\uff0c\u96e3\uff0c\u5168\u90e8\u90fd\u96e3\u5728\u2500\u2500","9\u000d\u000a\u4eba\u5011\u81ea\u5df1\u7684\u5167\u5fc3\u592a\u591a\u7d1b\u64fe\u3002\u000d\u000a\u80fd\u5920\u7b54\u61c9\u70ba\u5e2b\u55ce\uff1f\u000d\u000a\u591a\u591a\u300c\u6210\u5168\u300d\u8eab\u65c1\u7684\u4eba\uff0c\u000d\u000a\u5982\u679c\u8eab\u65c1\u7684\u4eba\u5f92\u5152\u90fd\u7167\u9867\u4e0d\u4e86\uff0c\u000d\u000a\u90a3\u4e16\u9593\u9019\u9ebc\u591a\u7684\u773e\u751f\uff0c\u70ba\u5e2b\u53c8\u600e\u9ebc\u6a23\u6551\u6e21\u5f97\u5b8c\u5462\uff1f\u000d\u000a\u80fd\u5920\u7b54\u61c9\u70ba\u5e2b\u55ce\uff1f\u000d\u000a\u57f9\u80b2\u597d\u81ea\u5df1\u7684\u300c\u9053\u5fc3\u300d\uff0c\u000d\u000a\u5728\u5f88\u591a\u65e5\u5b50\u88e1\uff0c\u9053\u5fc3\u90fd\u6642\u5e38\u98c4\u98c4\u6e3a\u6e3a\uff0c\u000d\u000a\u73fe\u5728\u9084\u5728\uff0c\u5f85\u6703\u5152\u53c8\u8dd1\u8d70\u4e86\u3002\u000d\u000a\u80fd\u5920\u7b54\u61c9\u70ba\u5e2b\u55ce\uff1f\u000d\u000a\u9019\u300c\u5929\u547d\u771f\u50b3\u300d\uff0c\u4e00\u5b9a\u8981\u6c38\u9060\u5b88\u7262\u3002","10\u000d\u000a\u4ee5\u5f8c\u7684\u8b8a\u5316\uff0c\u53ea\u6703\u6108\u4f86\u6108\u591a\uff0c\u4e0d\u6703\u6108\u4f86\u6108\u5c11\u3002\u000d\u000a\u5982\u679c\u5f92\u5152\u5011\u7684\u9053\u5fc3\uff0c\u4e0d\u518d\u6bd4\u73fe\u5728\u66f4\u5805\u5f37\uff1b\u000d\u000a\u90a3\u8981\u600e\u9ebc\u8b93\u70ba\u5e2b\u653e\u5fc3\u5462\uff1f\u000d\u000a\u5927\u5bb6\u9589\u4e0a\u773c\uff0c\u6c89\u975c\u3001\u6c89\u975c\u4e00\u4e0b\u3002\u000d\u000a\u5e0c\u671b\u5f92\u5152\u5011\u4eca\u65e5\u7b54\u61c9\u70ba\u5e2b\u7684\uff0c\u7686\u662f\u300c\u5fc3\u60f3\u800c\u6210\u613f\u300d\u3002\u9019\u4e00\u500b\u613f\u5462\uff1f\u000d\u000a\u5c31\u4e0d\u8981\u8b93\u5b83\u518d\u8b8a\u4e86\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u70ba\u5e2b\u770b\u4e86\u592a\u591a\u592a\u591a\uff0c\u672b\u4e16\u4ee5\u53ca\u5373\u5c07\u8981\u767c\u751f\u7684\u73fe\u8c61\uff0c\u96e3\u4ee5\u63a9\u85cf\u6182\u5fc3\uff0c\u000d\u000a\u6240\u4ee5\uff0c\u5c07\u4e00\u4e9b\u771f\u5fc3\u7684\u8a71\u8a34\u8af8\u5f92\u5152\u3002\uff08\u9ede\u50b3\u5e2b\uff1a\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u5e0c\u671b\u5f92\u5152\u6709\u5fd7\u5411\uff0c\u80fd\u5920\u5ef6\u7e8c\u300c\u9053\u4e4b\u85aa\u706b\u300d\u3002\u000d\u000a\u4e26\u4e0d\u662f\u8981\u4ec0\u9ebc\u540d\uff1f\u4ec0\u9ebc\u4f4d\uff1f\u624d\u53eb\u505a\u5ef6\u7e8c\u3002\u000d\u000a\u800c\u662f\uff0c\u300c\u9053\u300d\u80fd\u5728\u5f92\u5152\u81ea\u5df1\u7684\u5fc3\u4e2d\u6642\u6642\u9ede\u4eae\u8457\u5fc3\u71c8\uff0c\u000d\u000a\u6642\u6642\u4ee5\u5149\u660e\u7684\u5fc3\u5883\uff0c\u4f86\u9762\u5c0d\u4f60\u5468\u906d\u6240\u767c\u751f\u7684\u4e00\u5207\u3002\u000d\u000a\u96e3\u95dc\u4e5f\u597d\uff0c\u9ad8\u8208\u7684\u4e8b\u4e5f\u597d\uff0c\u96e3\u904e\u7684\u4e8b\u4e5f\u597d\uff1b","11\u000d\u000a\u53ea\u8981\u5f92\u5152\u5011\uff0c\u9019\u500b\u300c\u9053\u5fd7\u300d\u5805\u5f37\u3002\u000d\u000a\u70ba\u5e2b\u76f8\u4fe1\uff0c\u5f92\u5152\u5011\u90fd\u80fd\u5920\u2500\u2500\u000d\u000a\u6bd4\u539f\u672c\u7684\u81ea\u5df1\u66f4\u8301\u58ef\uff1b\u6bd4\u539f\u672c\u7684\u81ea\u5df1\u66f4\u9032\u6b65\uff1b\u000d\u000a\u6bd4\u539f\u672c\u7684\u81ea\u5df1\u66f4\u5805\u5f37\u3002\u000d\u000a\u5f92\u5152\u5011\u90fd\u5404\u81ea\u6709\u8457\u5bb6\u5ead\u3001\u6709\u8457\u7236\u6bcd\u3001\u6709\u8457\u5144\u5f1f\u59d0\u59b9\uff0c\u770b\u770b\u73fe\u5728\u80fd\u000d\u000a\u5920\u651c\u624b\u5171\u4f86\u4f5b\u5802\u7684\u6709\u591a\u5c11\u5462\uff1f\u4e0d\u5c11\u7684\u5f92\u5152\u90fd\u662f\u80fd\u5920\u8ddf\u8457\u5bb6\u4eba\u4e00\u8d77\u4f86\uff0c\u000d\u000a\u662f\u4e00\u4ef6\u5f88\u6709\u798f\u5206\u7684\u4e8b\u60c5\u3002\u000d\u000a\u4f46\u662f\u751f\u6d3b\u88e1\u9762\uff0c\u5f88\u5e38\u6703\u6709\u592a\u591a\u7684\u5e72\u64fe\uff0c\u570d\u7e5e\u5728\u6bcf\u4e00\u4f4d\u5f92\u5152\u7684\u8eab\u65c1\uff0c\u000d\u000a\u5f92\u5152\u5011\u771f\u662f\u8f9b\u82e6\u4e86\u5440\uff01\u505a\u4eba\u8f9b\u4e0d\u8f9b\u82e6\uff1f\uff08\u8f9b\u82e6\uff09\u662f\u5440\uff01\u90a3\u9084\u8981\u505a\u55ce\uff1f\u000d\u000a\u5982\u679c\u4e0d\u505a\u4eba\u3001\u4e0d\u4fee\u9053\uff0c\u6703\u4e0d\u6703\u66f4\u8f9b\u82e6\u554a\uff1f\uff08\u6703\uff09\u000d\u000a\u6240\u4ee5\uff0c\u6709\u4e00\u4e9b\u8f9b\u82e6\u662f\u300c\u503c\u5f97\u300d\u7684\uff0c\u000d\u000a\u6709\u4e00\u4e9b\u8f9b\u82e6\u662f\u300c\u81ea\u627e\u300d\u7684\u3002","12\u000d\u000a\u5f92\u5152\u5011\u73fe\u5728\u56de\u60f3\u4e00\u4e0b\uff1a\u81ea\u5df1\u5728\u53d7\u7684\u8f9b\u82e6\uff0c\u662f\u503c\u5f97\u7684\u5462\uff1f\u9084\u662f\u81ea\u627e\u000d\u000a\u7684\u5462\uff1f\u8ddf\u5bb6\u4eba\u6709\u6c92\u6709\u5e38\u5e38\u5435\u67b6\u5440\uff1f\u6709\u6642\u5019\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u81ea\u5df1\u6700\u771f\u5be6\u7684\u6a23\u000d\u000a\u5b50\uff0c\u5c31\u6703\u6d41\u9732\u7d66\u6700\u89aa\u8fd1\u7684\u4eba\u770b\u898b\u3002\u9019\u6c92\u95dc\u4fc2\uff0c\u6bcf\u4e00\u4f4d\u5f92\u5152\u90fd\u662f\uff01\u4f46\u662f\u000d\u000a\u6709\u6c92\u6709\u5728\u9019\u500b\u6642\u5019\u53cd\u7701\u4e00\u4e0b\u81ea\u5df1\u5462\uff1f\u000d\u000a\u4fee\u9053\u6700\u597d\u7684\u74b0\u5883\u5728\u54ea\u88e1\u5440\uff1f\uff08\u5bb6\u88e1\uff09\u000d\u000a\u53ef\u662f\uff0c\u5f88\u5947\u602a\u2500\u2500\u4fee\u9053\u4eba\u5728\u5bb6\u88e1\u6700\u4e0d\u559c\u6b61\u4fee\u9053\uff01\u000d\u000a\u4f86\u5230\u4f5b\u5802\u4fee\u9053\u5f88\u597d\uff0c\u5c0d\u4e0d\u5c0d\uff1f\u4e92\u76f8\u90fd\u505a\u8fa6\u4e8b\u4eba\u54e1\uff0c\u4e92\u76f8\u90fd\u66c9\u5f97\u5e6b\u000d\u000a\u5fd9\uff0c\u54ea\u88e1\u6709\u4e8b\u60c5\u8981\u505a\uff0c\u53ea\u8981\u6709\u4e00\u500b\u4eba\u8d70\u904e\u53bb\u4e86\uff0c\u5f8c\u9762\u4e00\u5b9a\u8ddf\u8457\u5169\u4e09\u500b\uff0c\u000d\u000a\u9019\u6a23\u505a\u8d77\u4e8b\u4f86\u8f15\u9b06\u8a31\u591a\u3002\u000d\u000a\u53ef\u662f\u5728\u5bb6\u88e1\u5462\uff1f\u2500\u2500\u8981\u53bb\u6d17\u7897\u4e86\uff0c\u6709\u6642\u5019\u90a3\u4e00\u500b\u4f4d\u5b50\u90fd\u662f\u7a7a\u7684\uff1b\u000d\u000a\u8981\u53bb\u6383\u5730\u3001\u62d6\u5730\u4e86\uff0c\u53ef\u80fd\u597d\u5e7e\u500b\u79ae\u62dc\uff0c\u6383\u5e1a\u90fd\u9084\u6c92\u51fa\u73fe\u904e\u5462\uff01\u000d\u000a\u5f92\u5152\u77e5\u9053\u70ba\u4ec0\u9ebc\u5728\u5bb6\u88e1\u4e0d\u5bb9\u6613\u4fee\u9053\u55ce\uff1f\u975c\u4e0d\u4e0b\u4f86\u55ce\uff1f\u4e5f\u662f\u3002\u56e0\u70ba\u000d\u000a\u5bb6\u88e1\u7684\u74b0\u5883\u662f\u81ea\u5df1\u6700\u719f\u6089\u7684\u74b0\u5883\u3002\u4eba\u5728\u719f\u6089\u7684\u74b0\u5883\u88e1\u982d\uff0c\u662f\u4e0d\u662f\u5c31\u4e0d\u000d\u000a\u6703\u9867\u616e\u9019\u9ebc\u591a\u4e86\uff1f\u4eba\u5728\u8ddf\u719f\u6089\u7684\u4eba\u8b1b\u8a71\u7684\u6642\u5019\uff0c\u9084\u6703\u60f3\u9019\u9ebc\u591a\u55ce\uff1f\u4f86","13\u000d\u000a\u4f5b\u5802\uff0c\u8ddf\u524d\u8ce2\u3001\u9053\u89aa\u8aaa\u8a71\u90fd\u5f88\u6709\u79ae\u8c8c\uff0c\u5c0d\u4e0d\u5c0d\uff1f\u53ef\u662f\u56de\u5230\u5bb6\u88e1\uff0c\u592b\u59bb\u000d\u000a\u540c\u4fee\u4e4b\u9593\u3001\u5144\u5f1f\u59d0\u59b9\u4e4b\u9593\uff0c\u8b1b\u8a71\u7684\u97f3\u8abf\u548c\u5167\u5bb9\u597d\u50cf\u5c31\u6709\u4e00\u9ede\u9ede\u6539\u8b8a\u4e86\u3002\u000d\u000a\u5f92\u5152\u5011\u77e5\u9053\u4f5b\u4ed9\u300c\u807d\u7684\u300d\u662f\u54ea\u88e1\u55ce\uff1f\u000d\u000a\u807d\u7684\u662f\u4f60\u5011\u5728\u300c\u5bb6\u88e1\u9762\u300d\u7684\u5c0d\u8a71\u5462\uff01\u000d\u000a\u5927\u5bb6\u90fd\u300c\u6e05\u53e3\u300d\u2500\u2500\u9019\u662f\u6b8a\u52dd\u7684\u613f\u529b\uff0c\u000d\u000a\u4f46\u4e5f\u662f\u4fee\u9053\u4eba\u57fa\u672c\u7684\u6148\u60b2\u3002\u000d\u000a\u73fe\u5728\u8981\u4e0d\u8981\u66f4\u9032\u4e00\u6b65\u4e86\uff1f\u000d\u000a\u73fe\u5728\u9019\u9ebc\u6df7\u4e82\u7684\u6642\u4ee3\uff0c\u5982\u679c\u4e0d\u8b93\u81ea\u5df1\u66f4\u9032\u4e00\u6b65\u3001\u66f4\u9032\u5169\u6b65\uff0c\u000d\u000a\u90a3\u4f60\u6703\u4e0d\u6703\u88ab\u9019\u300c\u6642\u4ee3\u7684\u6fc1\u6d41\u300d\u5f80\u4e0b\u62c9\u5440\uff1f\u000d\u000a\u6703\u4e0d\u6703\u88ab\u9019\u300c\u4e16\u4fd7\u7684\u6ce2\u76ea\u300d\u6c96\u8d70\u4e86\u5440\uff1f\u000d\u000a\u5230\u6642\u5019\u70ba\u5e2b\u8981\u53bb\u54ea\u5152\u627e\u4eba\u554a\uff1f\u000d\u000a\u4eca\u65e5\u4e5f\u6709\u4e0d\u5c11\u5e74\u8f15\u7684\u5f92\u5152\u9858\u610f\u4f86\u53c3\u52a0\u6e05\u53e3\u73ed\uff0c\u70ba\u5e2b\u770b\u4e86\u76f8\u7576\u6b23\u6170\u3002","14\u000d\u000a\u770b\u5230\u5e74\u8f15\u7684\u9762\u5b54\uff0c\u70ba\u5e2b\u5c31\u89ba\u5f97\u6bcf\u4e00\u4f4d\u5f92\u5152\u9700\u8981\u66f4\u7cbe\u9032\u4e86\u3002\u000d\u000a\u5982\u679c\uff0c\u524d\u9762\u7684\u4eba\u4e0d\u7cbe\u9032\uff0c\u000d\u000a\u5f8c\u9762\u7684\u4eba\u8981\u600e\u9ebc\u6a23\u6709\u300c\u699c\u6a23\u300d\u53ef\u4ee5\u5b78\u7fd2\u5462\uff1f\u000d\u000a\u4f60\u5011\u90fd\u5e0c\u671b\u9053\u5834\u597d\uff0c\u4f60\u5011\u6c92\u6709\u4e00\u500b\u4eba\u5e0c\u671b\u6709\u8ab0\u843d\u55ae\uff0c\u53ef\u662f\u5728\u73fe\u5be6\u000d\u000a\u7684\u884c\u70ba\u88e1\uff0c\u5f92\u5152\u5011\u6709\u6c92\u6709\u5920\u7a4d\u6975\u554a\uff1f\u4e86\u613f\u7684\u6642\u5019\uff0c\u6709\u6c92\u6709\u5e38\u5e38\u885d\u7b2c\u4e00\u000d\u000a\u554a\uff1f\u000d\u000a\u5982\u679c\u73fe\u5728\u70ba\u5e2b\u7d66\u5f92\u5152\u5011\u4e00\u500b\u300c\u4e86\u613f\u300d\u7684\u5de5\u4f5c\uff0c\u60f3\u8981\u505a\u7684\u8209\u624b\u3002\u000d\u000a\u25ce\u5168\u9ad4\u7d1b\u7d1b\u8209\u624b\u3002\u000d\u000a\u54c7\uff01\u4e00\u500b\u6bd4\u4e00\u500b\u5feb\u5462\uff01\u4e00\u500b\u4e86\u613f\u7684\u5de5\u4f5c\uff0c\u9019\u9ebc\u591a\u5f92\u5152\u90fd\u8981\u505a\u5440\uff1f\u000d\u000a\uff08\u662f\uff09\u90a3\u5982\u679c\u4eca\u5929\u9019\u500b\u4e86\u613f\u7684\u5de5\u4f5c\u662f\u975e\u5e38\u8f9b\u82e6\u7684\uff0c\u9084\u8981\u55ce\uff1f\uff08\u8981\uff09\u5982\u679c\u9019\u000d\u000a\u500b\u4e86\u613f\u7684\u5de5\u4f5c\u6703\u8b93\u4f60\u5403\u8667\uff0c\u9084\u8981\u55ce\uff1f\uff08\u8981\uff09\u8072\u97f3\u6108\u4f86\u6108\u5c0f\u4e86\uff0c\u70ba\u5e2b\u770b\u7b97\u000d\u000a\u4e86\u3001\u7b97\u4e86\uff0c\u70ba\u5e2b\u518d\u627e\u554a\uff01\u8981\u4e0d\u8981\u554a\uff1f\uff08\u8981\uff09\u5982\u679c\u9019\u4e00\u500b\u4e86\u613f\u7684\u5de5\u4f5c\uff0c\u6c92\u000d\u000a\u6709\u4eba\u770b\u898b\uff0c\u505a\u5b8c\u4e86\u4e5f\u4e0d\u6703\u6709\u4eba\u8a87\u734e\u4f60\u5462\uff1f\uff08\u6c92\u95dc\u4fc2\uff09\u6c92\u95dc\u4fc2\u5440\uff1f\u597d\uff01\u5f92\u000d\u000a\u5152\u5011\u9084\u4e0d\u932f\uff0c\u8acb\u653e\u4e0b\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09","15\u000d\u000a\u5176\u5be6\u5f92\u5152\u5011\u5c31\u662f\u56e0\u70ba\u9858\u610f\u9019\u6a23\u5b50\u505a\uff0c\u6240\u4ee5\u4eca\u5929\u9019\u5bf6\u8cb4\u7684\u5ea7\u4f4d\u624d\u6709\u000d\u000a\u5f92\u5152\u5011\u7684\u4f4d\u5b50\u5440\uff01\u5982\u679c\u4e0d\u9858\u610f\uff0c\u662f\u4e0d\u662f\u81ea\u5df1\u9084\u66f4\u60f3\u8981\u62ff\u9019\u5169\u5929\u7684\u6642\u9593\u000d\u000a\u53bb\u505a\u5176\u4ed6\u7684\u4e8b\u60c5\uff1f\u000d\u000a\u5f88\u591a\u6642\u5019\u4eba\u6703\u4e0d\u5c0f\u5fc3\u89ba\u5f97\uff1a\u4f5b\u5802\u8ddf\u81ea\u5df1\u7684\u751f\u6d3b\u597d\u50cf\u6c92\u6709\u4ec0\u9ebc\u4ea4\u96c6\u000d\u000a\u9ede\u3002\u62ff\u4f86\u4f5b\u5802\u7684\u6642\u9593\u53bb\u505a\u5176\u4ed6\u81ea\u5df1\u6a02\u65bc\u505a\u7684\u4e8b\uff0c\u6bd4\u8f03\u81ea\u7531\u3001\u6bd4\u8f03\u8212\u670d\uff0c\u000d\u000a\u662f\u4e0d\u662f\u6bd4\u8f03\u597d\u554a\uff1f\uff08\u4e0d\u662f\uff09\u000d\u000a\u82b1\u4e86\u5f88\u591a\u7684\u6642\u9593\uff0c\u53bb\u505a\u4e00\u4e9b\u99ac\u4e0a\u5c31\u6703\u770b\u5230\u6210\u679c\u7684\u4e8b\u60c5\uff0c\u000d\u000a\u4f46\u662f\uff0c\u5b83\u5c0d\u4f60\u7684\u751f\u547d\u4e26\u6c92\u6709\u5f88\u5927\u7684\u5e6b\u52a9\u3002\u000d\u000a\u5176\u5be6\u4eba\u751f\u5c31\u662f\u5982\u6b64\uff0c\u9084\u6c92\u7d93\u904e\u4e00\u9ede\u98a8\u6fe4\u99ed\u6d6a\u6642\uff0c\u7e3d\u662f\u4e0d\u77e5\u9053\u6709\u500b\u000d\u000a\u4f5b\u5802\u53ef\u4ee5\u56de\u4f86\u53c3\u8207\u3001\u53ef\u4ee5\u4e86\u613f\uff0c\u662f\u591a\u5c11\u4eba\u5922\u5bd0\u4ee5\u6c42\u90fd\u6c42\u4e0d\u5230\u7684\u4e8b\u5462\uff01\u000d\u000a\u7576\u5f92\u5152\u5011\u7d93\u904e\u6b72\u6708\u98a8\u971c\u7684\u78e8\u792a\u4e4b\u5f8c\uff0c\u76f8\u4fe1\u5167\u5fc3\u7684\u7b54\u6848\u3001\u751f\u6d3b\u4e2d\u7684\u000d\u000a\u8f15\u91cd\u7de9\u6025\uff0c\u662f\u4e0d\u662f\u5c31\u6703\u6709\u4e0d\u540c\u4e86\uff1f\uff08\u662f\uff09\u958b\u59cb\u628a\u300c\u4fee\u9053\u300d\u770b\u5f97\u66f4\u91cd\u8981\u4e86\uff0c\u000d\u000a\u958b\u59cb\u628a\u300c\u4f86\u4f5b\u5802\u300d\u64fa\u5728\u512a\u5148\u7684\u4f4d\u7f6e\u4e86\uff0c\u90a3\u70ba\u5e2b\u4e5f\u5c31\u5b89\u5fc3\u4e86\uff01","16\u000d\u000a\u4f86\u5230\u300c\u5929\u4f51\u5bae\u300d\u6b61\u4e0d\u6b61\u559c\uff1f\uff08\u6b61\u559c\uff09\u000d\u000a\u9019\u9ebc\u591a\u4eba\u53ef\u4ee5\u805a\u5728\u4e00\u8d77\uff0c\u5728\u9019\u6642\u5019\uff0c\u6709\u53ef\u80fd\u55ce\uff1f\u000d\u000a\u9019\u662f\u300c\u4e0d\u53ef\u80fd\u4e2d\u7684\u53ef\u80fd\u300d\u5440\uff0c\u6240\u4ee5\u5f92\u5152\u5011\u8981\u597d\u597d\u7684\u73cd\u60dc\u3002\u000d\u000a\u6d77\u5916\u6709\u5f88\u591a\u7684\u9053\u89aa\u90fd\u60f3\u8981\u9019\u6a23\u805a\u5728\u4e00\u8d77\uff0c\u5728\u4f5b\u5802\u958b\u6cd5\u6703\u3001\u807d\u9053\u7406\uff0c\u000d\u000a\u90fd\u9084\u6c92\u6709\u8fa6\u6cd5\u5462\uff01\u000d\u000a\u5404\u4f4d\u5f92\u5152\u5011\u8acb\u5750\u5427\uff01\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u518d\u8b93\u70ba\u5e2b\u770b\u4e00\u770b\u6bcf\u4e00\u4f4d\u5f92\u5152\u3002\u000d\u000a\u70ba\u5e2b\u6bcf\u4e00\u5929\u90fd\u5728\u4f60\u5011\u7684\u8eab\u65c1\uff0c\u4f46\u662f\u4f60\u5011\u7684\u5fc3\u4e2d\u6709\u6c92\u6709\u5e38\u5e38\u5c07\u70ba\u5e2b\u000d\u000a\u653e\u5728\u88e1\u982d\uff1f\uff08\u6709\uff09\u662f\u5440\uff01\u662f\u4eca\u5929\u8aaa\u7d66\u70ba\u5e2b\u9ad8\u8208\u7684\u55ce\uff1f\uff08\u4e0d\u662f\uff09\u000d\u000a\u56de\u53bb\u7684\u6642\u5019\uff0c\u70ba\u5e2b\u8981\u9a57\u6536\u770b\u770b\uff0c\u770b\u770b\u70ba\u5e2b\u53eb\u5f92\u5152\u5011\u7684\u6642\u5019\uff0c\u5f92\u5152\u000d\u000a\u5011\u662f\u9084\u5728\u904a\u5c71\u73a9\u6c34\uff1f\u9084\u662f\u8166\u7b4b\u5728\u60f3\u4e00\u4e9b\u56fa\u57f7\u4e0d\u901a\u7684\u4e8b\uff1f\u9084\u662f\u5728\u5de5\u4f5c\u65b9\u000d\u000a\u9762\u53c8\u9047\u5230\u4e86\u74f6\u9838\uff0c\u60f3\u4e5f\u60f3\u4e0d\u901a\uff0c\u600e\u9ebc\u5c31\u60f3\u4e0d\u5230\u70ba\u5e2b\u5462\uff1f\u000d\u000a\u5f88\u591a\u4eba\u73fe\u5728\u7684\u4eba\u751f\u8def\u624d\u525b\u958b\u59cb\uff0c\u5f88\u591a\u4eba\u73fe\u5728\u7684\u4eba\u751f\u5df2\u7d93\u8d70\u4e86\u8d85\u904e","17\u000d\u000a\u4e00\u534a\uff0c\u9019\u4e9b\u90fd\u4e0d\u662f\u91cd\u8981\u7684\uff0c\u5f92\u5152\u5011\u77e5\u9053\u70ba\u4ec0\u9ebc\u55ce\uff1f\u56e0\u70ba\u751f\u547d\u7684\u9577\u77ed\u4e0d\u000d\u000a\u662f\u81ea\u5df1\u53ef\u4ee5\u51b3\u5b9a\u7684\uff0c\u4e26\u4e0d\u662f\u6bcf\u4e00\u500b\u4eba\u90fd\u53ef\u4ee5\u6d3b\u5230\u4e00\u767e\u6b72\u3002\u000d\u000a\u5f92\u5152\u5011\u6709\u6c92\u6709\u60f3\u904e\uff0c\u6d3b\u4e00\u5929\u662f\u4e00\u5929\uff0c\u90a3\u9019\u4e00\u5929\u5f92\u5152\u505a\u4e86\u4ec0\u9ebc\u4e8b\uff1f\u000d\u000a\u6703\u628a\u9019\u4e00\u5929\u600e\u9ebc\u904b\u7528\u5462\uff1f\u70ba\u5e2b\u90fd\u80fd\u770b\u5230\u5f92\u5152\u5011\u73fe\u5728\u5fc3\u88e1\u9762\u600e\u9ebc\u60f3\u4e86\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8f15\u62cd\u4e7e 5-7\u25cb\u9716\u80a9\u8180\u4e26\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff0c\u5982\u679c\u6709\u4e00\u5929\u7684\u6642\u9593\u53ef\u4ee5\u000d\u000a\u7528\uff0c\u5f92\u5152\u8981\u7528\u5728\u54ea\u5152\uff1f\uff08\u7814\u7a76\u9053\u5b78\uff09\u7814\u7a76\u9053\u5b78\u55ce\uff1f\uff08\u9084\u6709\u6e21\u5316\u773e\u751f\u3001\u884c\u9053\uff09\u000d\u000a\u597d\uff0c\u90a3\u73fe\u5728\u5c31\u6bcf\u5929\u90fd\u9019\u6a23\u505a\uff0c\u9019\u500b\u5c31\u53eb\u505a\u300c\u5fd7\u5411\u300d\uff0c\u8acb\u5750\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u000d\u000a\u6148\u60b2\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764 1-9\u25cb\u73b2\u6148\u60b2\uff1a\u5f92\u5152\uff0c\u5982\u679c\u6709\u4e00\u5929\u7684\u6642\u9593\uff0c\u8981\u505a\u4ec0\u9ebc\u4e8b\u5440\uff1f\u000d\u000a\uff08\u8001\u5e2b\u6148\u60b2\uff0c\u5f92\u5152\u5b78\u7fd2\u7528\u5fc3\u82e5\u93e1\uff09\u597d\uff0c\u90a3\u9019\u500b\u300c\u93e1\u300d\u5c31\u8981\u5f88\u4eae\u3001\u5f88\u5149\u660e\u624d\u884c\u000d\u000a\u554a\uff01\u9019\u5c31\u4e0d\u662f\u4e00\u5929\u3001\u5169\u5929\u7684\u5de5\u592b\u4e86\u3002\u958b\u59cb\u5b78\uff0c\u73fe\u5728\u5728\u5b78\u7fd2\u7576\u4e2d\uff0c\u90a3\u5c31\u000d\u000a\u662f\u4e00\u7a2e\u300c\u6548\u6cd5\u300d\u2500\u2500\u6548\u6cd5\u8056\u8ce2\u7684\u8db3\u8de1\uff0c\u518d\u597d\u597d\u8d70\u5427\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e 1-3\u25cb\u9cf4\u6148\u60b2\uff1a\u5982\u679c\u6709\u4e00\u5929\u7684\u6642\u9593\u600e\u9ebc\u7528\uff1f\uff08\u6d3b\u4f5b\u8001\u5e2b\u6148","18\u000d\u000a\u60b2\uff0c\u591a\u966a\u966a\u5f8c\u5b78\u6bcd\u89aa\uff09\u9019\u662f\u300c\u5b5d\u9053\u300d\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u4fee\u9053\u5c31\u662f\u76e1\u5b5d\u9053\u3002\u000d\u000a\u90a3\u76e1\u5b5d\u9053\uff0c\u5b5d\u9806\u6bcd\u89aa\uff0c\u9084\u8981\u5b5d\u9806\u524d\u4eba\uff0c\u9084\u8981\u5b5d\u9806 \u7687 \u5462\uff01\u9019\u5b5d\u9053\u53ef\u000d\u000a\u5927\u4e86\uff01\u000d\u000a \u6bcf\u4e00\u4f4d\u5f92\u5152\u90fd\u662f\u4e00\u6a23\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764 2-5\u25cb\u5100\u6148\u60b2\uff1a\u5f92\u5152\uff0c\u5982\u679c\u6709\u5169\u5929\u7684\u6642\u9593\u8981\u505a\u4ec0\u9ebc\uff1f\uff08\u4e00\u000d\u000a\u5929\u5bb6\u4eba\u3001\u4e00\u5929\u9053\u5834\uff0c\u4fee\u9053\uff09\u597d\uff0c\u5c31\u7b49\u4f60\u9019\u53e5\u8a71\u4e86\u3002\u4eca\u5929\u9019\u6a23\u8aaa\u4e86\uff0c\u73fe\u5728\u4e0b\u000d\u000a\u4e00\u79d2\u5c31\u8981\u597d\u597d\u505a\uff0c\u8056\u51e1\u517c\u4fee\uff0c\u5c31\u8d70\u5f97\u9577\u4e45\u4e86\u3002\u000d\u000a\u5f92\u5152\u5011\uff0c\u6709\u591a\u4e45\u6c92\u6709\u5728\u4f5b\u5802\u807d\u9019\u9ebc\u591a\u5802\u8ab2\u4e86\uff1f\u6709\u4e00\u4e9b\u4eba\u9084\u6709\u53c3\u52a0\u000d\u000a\u73ed\u7a0b\uff0c\u9084\u807d\u5f97\u5230\uff1b\u6709\u4e00\u4e9b\u4eba\u5c31\u6bd4\u8f03\u5c11\u807d\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u90a3\u4eca\u5929\u7a81\u7136\u9593\u807d\u4e86\u9019\u9ebc\u591a\u9053\u7406\uff0c\u662f\u89ba\u5f97\u5f88\u75b2\u7d2f\uff0c\u9084\u662f\u89ba\u5f97\u5f88\u5feb\u6a02\u000d\u000a\u5440\uff1f\uff08\u5feb\u6a02\uff09\u53ef\u662f\u600e\u9ebc\u5f9e\u9019\u6a23\u7684\u8072\u97f3\u88e1\u807d\u4e0d\u51fa\u5feb\u6a02\u7684\u611f\u89ba\u5462\uff1f\u5176\u5be6\u5fc3\u8ddf\u000d\u000a\u8eab\u90fd\u662f\u6709\u9ede\u7d2f\u7684\uff0c\u56e0\u70ba\u9019\u500b\u9053\u7406\u5176\u5be6\u4e26\u4e0d\u597d\u807d\u3002\u000d\u000a\u300c\u5b54\u66f0\u6210\u4ec1\uff0c\u5b5f\u66f0\u53d6\u7fa9\u3002\u300d\u3010\u8a3b\u56db\u3011\u2500\u2500\u9019\u662f\u4ec0\u9ebc\u554a\uff1f","19\u000d\u000a\u662f\u4e0d\u662f\u807d\u4e86\u9053\u7406\uff0c\u5c31\u50cf\u662f\u5f88\u591a\u4e0d\u540c\u7684\u6587\u5b57\u6253\u5165\u81ea\u5df1\u7684\u8166\u4e2d\uff0c\u9084\u5728\u000d\u000a\u5206\u6790\u8aaa\uff1a\u8b1b\u8ab2\u7684\u524d\u8ce2\u5230\u5e95\u5728\u8aaa\u4ec0\u9ebc\uff1f\u77e5\u9053\u8b1b\u7684\u5f88\u597d\uff0c\u53ef\u662f\uff0c\u6211\u8981\u600e\u9ebc\u000d\u000a\u6a23\u5438\u6536\u3001\u6d88\u5316\u5462\uff1f\u000d\u000a\u6240\u4ee5\uff0c\u7576\u7136\u662f\u6703\u6709\u75b2\u7d2f\u7684\uff0c\u53ef\u662f\u9019\u500b\u75b2\u7d2f\u7e3d\u6bd4\u5f92\u5152\u5011\u5728\u5bb6\u88e1\u505a\u4e00\u000d\u000a\u4e9b\u5225\u7684\u4e8b\u597d\u5427\uff1f\uff08\u662f\uff09\u000d\u000a\u5f92\u5152\u5011\u4e0d\u8981\u5c0f\u770b\u958b\u9019\u5834\u6cd5\u6703\u554a\uff01\u5f92\u5152\u5011\u7684\u4e5d\u7384\u4e03\u7956\u4e5f\u597d\u4e45\u6c92\u6709\u4f86\u000d\u000a\u6cd5\u6703\u4e86\u3002\u4e0d\u8ad6\u662f\u6709\u5f62\u7121\u5f62\uff0c\u90fd\u662f\u5171\u540c\u52a9\u5316\u3002\u90a3\u5f92\u5152\u5011\u6709\u6c92\u6709\u89ba\u5f97\u81ea\u5df1\u000d\u000a\u5f88\u4e0d\u7c21\u55ae\u5440\uff1f\u597d\u50cf\u89ba\u5f97\u6709\uff0c\u4f46\u662f\u53c8\u4e0d\u592a\u6562\u8aaa\u3002\u5f92\u5152\u5011\u8981\u66f4\u770b\u91cd\u81ea\u5df1\u4e86\uff01\u000d\u000a\u300c\u770b\u91cd\u81ea\u5df1\u300d\u8ddf\u300c\u81ea\u4ee5\u70ba\u662f\u300d\u662f\u4e0d\u4e00\u6a23\u7684\u3002\u000d\u000a\u300c\u770b\u91cd\u81ea\u5df1\u300d\uff0c\u662f\u56e0\u70ba\u77e5\u9053\u3001\u60f3\u8981\u4fee\u9053\uff0c\u000d\u000a\u9019\u500b\u4fee\u9053\u662f\u6709\u6839\u672c\u53ef\u4ee5\u9075\u5faa\u7684\uff0c\u000d\u000a\u5728\u4f60\u7684\u5fc3\u5c31\u6709\u9019\u500b\u6839\u2500\u2500\u6240\u4ee5\u5f92\u5152\u770b\u91cd\u81ea\u5df1\u3002\u000d\u000a\u90a3\u300c\u81ea\u4ee5\u70ba\u662f\u300d\u662f\u4ec0\u9ebc\u5462\uff1f\u5c31\u662f\u81ea\u5df1\u5fc3\u88e1\u9762\u51fa\u73fe\u4e86\u5f88\u591a\u3001\u5f88\u591a\u7684","20\u000d\u000a\u60f3\u6cd5\uff0c\u9084\u5206\u4e0d\u6e05\u695a\u662f\u975e\u5c0d\u932f\u7684\u6642\u5019\uff0c\u90fd\u5148\u89ba\u5f97\uff1a\u300c\u6211\u60f3\u7684\u5c31\u662f\u5c0d\u7684\uff01\u300d\u000d\u000a\u5f92\u5152\u5011\u60f3\u7684\u771f\u7684\u662f\u5c0d\u7684\u55ce\uff1f\uff08\u4e0d\u662f\uff09\u5982\u679c\u5f92\u5152\u5011\u60f3\u7684\u662f\u5c0d\u7684\uff0c\u90a3\u5c31\u000d\u000a\u4e0d\u7528\u8056\u8ce2\u4ed9\u4f5b\u7684\u9053\u7406\u4e86\u5440\uff01\u000d\u000a\u4eca\u6642\u7684\u4eba\u5e38\u5e38\u300c\u6ce5\u6211\u4e0d\u5316\u300d\u3010\u8a3b\u4e94\u3011\uff0c\u000d\u000a\u62d8\u6ce5\u5728\u81ea\u5df1\u7684\u9019\u4e00\u500b\u60f3\u6cd5\u3002\u000d\u000a\u6bcf\u4e00\u500b\u4eba\u7684\u60f3\u6cd5\u591a\u4e0d\u591a\uff1f\uff08\u591a\uff09\u975e\u5e38\u7684\u591a\u5440\uff01\u5982\u679c\u9019\u500b\u60f3\u6cd5\u7684\u7d50\u000d\u000a\u69cb\uff0c\u53ef\u4ee5\u8b8a\u6210\u6709\u5f62\u6709\u76f8\u7684\uff0c\u6703\u4e0d\u6703\u7d50\u5408\u8d77\u4f86\u6bd4\u5929\u4f51\u5bae\u9084\u8981\u5927\uff1f\u5f88\u6709\u53ef\u000d\u000a\u80fd\u5440\uff01\u56e0\u70ba\u9084\u6709\u7d2f\u4e16\u7d2f\u52ab\u7684\u9019\u4e9b\u4eba\u5fc3\u3001\u601d\u60f3\u3001\u5984\u5ff5\uff0c\u90fd\u5728\u7121\u5f62\u7576\u4e2d\u7d50\u000d\u000a\u5408\u8457\uff0c\u8ddf\u8457\u5f92\u5152\u5011\u7684\u6bcf\u4e00\u5929\u4f5c\u606f\uff0c\u6240\u4ee5\u6709\u7684\u6642\u5019\u6703\u89ba\u5f97\uff1a\u300c\u660e\u660e\u6c92\u6709\u000d\u000a\u767c\u751f\u4ec0\u9ebc\u4e8b\uff0c\u6211\u4eca\u5929\u5fc3\u60c5\u5c31\u662f\u4e0d\u597d\u3002\u300d\u6709\u6c92\u6709\u554a\uff1f\uff08\u6709\uff09\u000d\u000a\u4e00\u5927\u65e9\u8d77\u4f86\u5c31\u6c92\u4ec0\u9ebc\u7cbe\u795e\uff0c\u770b\u5230\u4ec0\u9ebc\u5c31\u60f3\u8981\u62b1\u6028\u4e00\u5169\u53e5\u3002\u5728\u9019\u500b\u000d\u000a\u6642\u5019\uff0c\u5fc3\u88e1\u9762\u6709\u6c92\u6709\u53e6\u4e00\u500b\u8072\u97f3\u544a\u8a34\u81ea\u5df1\uff1a\u300c\u4e0d\u53ef\u4ee5\u9019\u6a23\u300d\uff1f\u9019\u662f\u81ea\u000d\u000a\u5df1\u7684\u7fd2\u6027\u53c8\u8dd1\u51fa\u4f86\u4e86\u3002\u8981\u8d95\u7dca\u5e73\u5fa9\u4e00\u4e0b\uff0c\u91cd\u65b0\u518d\u9762\u5c0d\u81ea\u5df1\uff0c\u90a3\u9019\u6a23\u9084\u000d\u000a\u4f86\u5f97\u53ca\u3002","21\u000d\u000a\u70ba\u5e2b\u6df1\u6015\u5f92\u5152\u5011\u4e0d\u5c0f\u5fc3\uff0c\u000d\u000a\u5c31\u628a\u5f88\u591a\u7684\u300c\u798d\u6b83\u300d\u90fd\u7a2e\u5728\u751f\u6d3b\u88e1\u9762\u4e86\u3002\u000d\u000a\u25ce\u8001\u5e2b\u7ad9\u5728\u5764 1-1\u25cb\u5982\u9762\u524d\u6148\u60b2\uff1a\u25cb\u5982\u5f92\u5152\uff0c\u770b\u770b\u5f92\u5152\u73fe\u5728\u8ddf\u70ba\u5e2b\u7684\u000d\u000a\u8ddd\u96e2\u662f\u8fd1\u9084\u662f\u9060\u554a\uff1f\uff08\u8fd1\uff09\u8ddd\u96e2\u662f\u8fd1\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u597d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8d70\u5230\u5764\u9053\u6700\u5916\u5074\u8d70\u9053\u908a\u6148\u60b2\uff1a\u90a3\u9019\u6a23\u9084\u8fd1\u4e0d\u8fd1\u554a\uff1f\u6bd4\u525b\u624d\u9060\u4e86\u000d\u000a\u5427\uff1f\uff08\u662f\uff09\u662f\u3002\u8ddd\u96e2\u9019\u6a23\u9060\uff0c\u90a3\u5982\u679c\u25cb\u5982\u5f92\u5152\u73fe\u5728\u7684\u5fc3\u662f\u5ff5\u8457\u70ba\u5e2b\u7684\uff0c\u000d\u000a\u54b1\u5011\u8fd1\u4e0d\u8fd1\uff1f\uff08\u8fd1\uff09\u662f\u5427\uff1f\uff08\u662f\uff09\u000d\u000a\u6240\u4ee5\uff0c\u9019\u4e00\u500b\u8ddd\u96e2\uff0c\u5b83\u4e0d\u4ee3\u8868\u771f\u7684\u9060\u548c\u8fd1\u3002\u5c31\u50cf\u73fe\u5728\u6d77\u5916\u7684\u5f92\u5152\u000d\u000a\u8ddf\u5404\u4f4d\u5f92\u5152\u5011\uff0c\u611f\u89ba\u597d\u50cf\u8ddd\u96e2\u5f88\u9060\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\u9053\u9ede\u50b3\u5e2b\u5011\u6148\u60b2\uff1a\u5f88\u591a\u5f88\u4e45\u90fd\u6c92\u53bb\u4e86\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u4f46\u000d\u000a\u662f\u4f60\u5011\u7684\u5fc3\u8fd1\u4e0d\u8fd1\uff1f\uff08\u8fd1\uff09\u6709\u6c92\u6709\u5e38\u5e38\u60f3\u8457\u4ed6\u5011\u554a\uff1f\uff08\u6709\uff09\u5e38\u5e38\u6253\u96fb\u8a71\u000d\u000a\u53bb\u95dc\u5fc3\u3002\u90a3\u5c31\u6c92\u6709\u8ddd\u96e2\u7684\u8a2d\u9650\u4e86\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u000d\u000a\u4e0d\u8ad6\u73fe\u5728\u9084\u6703\u518d\u9047\u5230\u4ec0\u9ebc\u4e8b\u60c5\uff0c","22\u000d\u000a\u90fd\u597d\u50cf\u8b8a\u6210\u5e73\u5e38\u4e86\uff0c\u000d\u000a\u56e0\u70ba\u9047\u591a\u4e86\u3001\u9047\u4e45\u4e86\uff0c\u5c31\u7fd2\u6163\u4e86\u3002\u000d\u000a\u53ef\u662f\uff0c\u4fee\u9053\u4eba\u9019\u500b\u300c\u6148\u60b2\u5fc3\u300d\u4e0d\u80fd\u8b93\u5b83\u300c\u9ebb\u6728\u300d\uff01\u000d\u000a\u4e00\u958b\u59cb\u5927\u5bb6\u5fc3\u88e1\u9762\u90fd\u5f88\u6709\u611f\u89f8\uff0c\u5fc3\u88e1\u60f3\uff1a\u300c\u600e\u9ebc\u6703\u9019\u6a23\u554a\uff1f\u9019\u4e16\u000d\u000a\u754c\u600e\u9ebc\u6703\u767c\u751f\u9019\u9ebc\u591a\u7684\u707d\u96e3\uff1f\u300d\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u6bcf\u5929\u90fd\u5728\u60f3\u554a\uff01\u8d70\u8def\u7684\u6642\u5019\u4e5f\u5728\u60f3\uff0c\u5de5\u4f5c\u7684\u6642\u5019\u5076\u723e\u4e5f\u6703\u60f3\u4e00\u4e0b\uff0c\u000d\u000a\u4f46\u662f\u5230\u73fe\u5728\u6642\u9593\u591a\u4e45\u4e86\uff1f\uff08\u9ede\u50b3\u5e2b\uff1a\u5169\u5e74\u591a\u4e86\uff09\u4e0d\u53ea\u5169\u5e74\uff0c\u7d2f\u4e16\u7d2f\u52ab\u600e\u9ebc\u000d\u000a\u7b97\uff1f\u70ba\u5e2b\u770b\u5230\u7684\u4e0d\u662f\u9019\u500b\u5462\uff01\u9019\u500b\u53ea\u662f\u4f60\u5011\u73fe\u5728\u6709\u9650\u7684\u751f\u547d\u7576\u4e2d\u9047\u5230\u000d\u000a\u7684\u4e00\u90e8\u5206\uff0c\u9019\u7d2f\u4e16\u7d2f\u52ab\u5df2\u7d93\u767c\u751f\u7121\u6578\u4e86\u2500\u2500\u000d\u000a\u6240\u4ee5\u5230\u4e86\u73fe\u5728\uff0c\u6709\u4e00\u4e9b\u4eba\u7684\u300c\u6148\u60b2\u5fc3\u300d\u6c89\u7761\u4e86\uff0c\u000d\u000a\u56e0\u70ba\uff0c\u5728\u592a\u591a\u9019\u6a23\u7684\u73fe\u8c61\u7576\u4e2d\uff0c\u4ed6\u7684\u5167\u5fc3\u5df2\u7d93\u75b2\u4e4f\uff0c\u000d\u000a\u552f\u4e00\u6c42\u7684\u662f\u4ec0\u9ebc\uff1f\u2500\u2500\u000d\u000a\u6c42\u81ea\u5df1\u4e0d\u8981\u9047\u5230\u3002","23\u000d\u000a\u6240\u4ee5\u6703\u767c\u73fe\uff0c\u4eba\u5728\u63a5\u8fd1\u5f88\u591a\u6709\u8da3\u3001\u80fd\u8b93\u81ea\u5df1\u99ac\u4e0a\u5c31\u5feb\u6a02\u7684\u4e8b\u60c5\u6642\uff0c\u000d\u000a\u662f\u4e0d\u662f\u7279\u5225\u6709\u7cbe\u795e\uff1f\uff08\u662f\uff09\u50cf\u5f92\u5152\u5011\u6709\u4e00\u4e9b\u8ab2\u807d\u4e86\u5c31\u660f\u660f\uff08\u6b32\u7761\uff09\u3002\u53ef\u000d\u000a\u662f\uff0c\u5728\u505a\u5f92\u5152\u81ea\u5df1\u559c\u6b61\u505a\u7684\u4e8b\u6642\uff0c\u6703\u7761\u89ba\u55ce\uff1f\u6703\u89ba\u5f97\u75b2\u4e4f\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u000d\u000a\u662f\u5440\uff01\u000d\u000a\u25ce\u8001\u5e2b\u8ddf\u5764 3-1\u25cb\u5a77\u6148\u60b2\uff1a\u50cf\u5f92\u5152\u559c\u6b61\u505a\u4ec0\u9ebc\u5440\uff1f\u96e3\u56de\u7b54\u5440\uff1f\uff08\u662f\uff09\u000d\u000a\u90a3\u5c31\u4e0d\u7528\u8aaa\u4e86\uff0c\u70ba\u5e2b\u5c31\u5e6b\u5f92\u5152\u56de\u7b54\u5427\uff01\u5c31\u559c\u6b61\u4f86\u4f5b\u5802\u3002\uff08\u662f\uff0c\u8b1d\u8b1d\u8001\u5e2b\u000d\u000a\u6148\u60b2\uff09\u000d\u000a\u25ce\u8001\u5e2b\u8ddf\u5764 2-4\u25cb\u5b9c\u6148\u60b2\uff1a\u90a3\u5f92\u5152\u559c\u6b61\u505a\u4ec0\u9ebc\u5462\uff1f\uff08\u8001\u5e2b\u6148\u60b2\uff0c\u6700\u559c\u6b61\u000d\u000a\u807d\u8ab2\u3001\u4f86\u4f5b\u5802\uff09\u662f\u5440\uff01\u6240\u4ee5\u4eca\u5929\u7cbe\u795e\u7279\u5225\u597d\u55ce\uff1f\uff08\u662f\uff09\u8acb\u5750\u3002\u000d\u000a\u6709\u6c92\u6709\u5f92\u5152\u559c\u6b61\u5225\u7684\u5462\uff1f\u8b93\u70ba\u5e2b\u8209\u500b\u4f8b\u5b50\u5427\u3002\u5047\u5982\u8aaa\u5f92\u5152\u559c\u6b61\u5929\u000d\u000a\u5929\u770b\u8457\u87a2\u5e55\uff0c\u8b8a\u4f86\u8b8a\u53bb\u7684\u87a2\u5e55\u662f\u4e0d\u662f\u5f88\u5438\u5f15\u4eba\uff1f\u770b\u90a3\u500b\u7684\u6642\u5019\u6703\u4e0d\u6703\u000d\u000a\u7d2f\uff1f\uff08\u6703\uff09\u90a3\u662f\u770b\u5b8c\u95dc\u6389\u7684\u6642\u5019\u624d\u6703\u7d2f\uff0c\u5728\u770b\u7684\u7576\u4e2d\u6b32\u7f77\u4e0d\u80fd\u554a\uff01\u9019\u300c\u6b32\u000d\u000a\u7f77\u4e0d\u80fd\u300d\u4e0d\u662f\u61c9\u8a72\u7528\u5728\u8ffd\u6c42\u8056\u8ce2\u7684\u9053\u7406\u65b9\u9762\u55ce\uff1f\u600e\u9ebc\u4e0d\u77e5\u4e0d\u89ba\u88ab\u5f8c\u4e16\u000d\u000a\u4eba\u7528\u5728\u6b32\u671b\u65b9\u9762\u554a\uff1f","24\u000d\u000a\u300c\u8ffd\u6c42\u6b32\u671b\u300d\uff0c\u5c31\u50cf\u662f\u4e00\u500b\u5927\u5751\u6d1e\u4e00\u6a23\uff0c\u000d\u000a\u628a\u5f92\u5152\u5011\u4e00\u500b\u53c8\u4e00\u500b\uff0c\u597d\u4e0d\u5bb9\u6613\u767b\u4e0a\u5e7e\u5c64\u8056\u8ce2\u968e\u68af\u4e86\uff0c\u000d\u000a\u53c8\u63a8\u3001\u63a8\u3001\u63a8\u4e0b\u53bb\u4e86\uff0c\u000d\u000a\u4e00\u5b9a\u8981\u6454\u5012\u4e86\u624d\u7518\u9858\u56de\u982d\u55ce\uff1f\u4e0d\u662f\u7684\uff01\u000d\u000a\u5982\u679c\u77e5\u9053\uff1a\u8d70\u4e00\u822c\u7684\u8def\uff0c\u6709\u4e00\u5929\u4ec0\u9ebc\u90fd\u5e36\u4e0d\u8d70\u3001\u4ec0\u9ebc\u90fd\u5f97\u4e0d\u5230\u3002\u000d\u000a\u8981\u5148\u60f3\u9060\u4e00\u9ede\uff0c\u5c31\u662f\u56e0\u70ba\u60f3\u5f97\u9084\u4e0d\u5920\u9060\uff0c\u53ea\u60f3\u8457\u73fe\u5728\u8981\u505a\u4ec0\u9ebc\uff1f\u73fe\u5728\u000d\u000a\u60f3\u8981\u5f97\u5230\u4ec0\u9ebc\uff1f\u73fe\u5728\u6211\u600e\u9ebc\u770b\uff0c\u624d\u53ef\u4ee5\u8b93\u6211\u7684\u4eba\u751f\u770b\u8d77\u4f86\u662f\u4eae\u9e97\u7684\u3001\u000d\u000a\u770b\u8d77\u4f86\u662f\u7cbe\u5f69\u7684\u3001\u770b\u8d77\u4f86\u662f\u4e0d\u8f38\u4eba\u7684\uff0c\u90a3\u5c31\u662f\u773c\u5149\u9084\u592a\u6dfa\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u000d\u000a\uff08\u662f\uff09\u000d\u000a\u5982\u679c\u518d\u5f80\u5f8c\u770b\uff0c\u5148\u770b\u5230\u76db\u2500\u2500\u76db\u6975\u5fc5\u8870\uff1b\u000d\u000a\u6a02\u8655\u53c8\u6709\u60b2\u4e8b\u4f86\u3002\u000d\u000a\u5982\u679c\u80fd\u5920\u770b\u900f\u9019\u4e00\u9ede\uff0c\u000d\u000a\u60f3\u8981\u6500\u6c42\u7684\uff0c\u9084\u6703\u53bb\u6500\u6c42\u55ce\uff1f\u4e0d\u6703\u4e86\u3002","25\u000d\u000a\u60f3\u8981\u99ac\u4e0a\u5c31\u5f97\u5230\u7684\u6771\u897f\uff0c\u9084\u6703\u60f3\u8981\u53bb\u5f97\u5230\u5b83\u55ce\uff1f\u4e0d\u6703\u3002\u000d\u000a\u9019\u5c31\u662f\u5f92\u5152\u6709\u9060\u898b\u4e86\u2500\u2500\u000d\u000a\u51e1\u4e8b\u6709\u9060\u616e\u624d\u6c92\u6709\u8fd1\u6182\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u6240\u4ee5\u7d93\u4e0a\u8b1b\uff1a\u000d\u000a\u300c\u82e5\u5f97\u4e94\u6b32\uff0c\u7576\u9858\u773e\u751f\uff0c\u62d4\u9664\u6b32\u7bad\uff0c\u7a76\u7adf\u5b89\u7a69\u3002\u300d\u3010\u8a3b\u516d\u3011\u000d\u000a\u5982\u679c\u5f88\u591a\u7684\u6b32\u671b\uff0c\u5e38\u5e38\u9ecf\u5728\u4f60\u7684\u5fc3\u4e0a\uff0c\u600e\u9ebc\u6a23\u60f3\uff0c\u600e\u9ebc\u6a23\u60f3\u8981\u6b62\u000d\u000a\u4f4f\u90fd\u629b\u4e0d\u6389\uff0c\u4e0d\u7ba1\u662f\u751f\u6c23\u7684\u6b32\u671b\u4e5f\u597d\uff0c\u6216\u662f\u6c42\u4ec0\u9ebc\u7684\u6b32\u671b\u4e5f\u597d\uff0c\u5c31\u9019\u000d\u000a\u6a23\u7dca\u7dca\u7684\u9ecf\u8457\u5f92\u5152\u4e86\uff0c\u600e\u9ebc\u8fa6\uff1f\u8981\u8d95\u5feb\u62d4\u9664\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u5c0d\uff09\u4f46\u662f\u8981\u600e\u000d\u000a\u9ebc\u62d4\u5462\uff1f\u53ef\u4ee5\u4e92\u76f8\u5e6b\u5fd9\u55ce\uff1f\u000d\u000a\u5f92\u5152\u5011\u5de6\u53f3\u73fe\u5728\u4e92\u76f8\u554f\u4e00\u554f\uff1a\u4f60\u73fe\u5728\u7684\u56f0\u96e3\u662f\u4ec0\u9ebc\u5440\uff1f\u6211\u5e6b\u4f60\u62d4\u000d\u000a\u9664\u3002\u6709\u8fa6\u6cd5\u55ce\uff1f\u4e92\u76f8\u5206\u4eab\u4e00\u4e0b\uff0c\u5206\u4eab\u4e00\u4e0b\u6700\u8fd1\u90fd\u5728\u6182\u6101\u4ec0\u9ebc\u5440\uff1f\u9019\u5e7e\u000d\u000a\u5929\u53c8\u70ba\u4e86\u4ec0\u9ebc\u4e8b\u96e3\u904e\u53c8\u54ed\u4e86\u5440\uff1f\u5982\u679c\uff0c\u4e92\u76f8\u53ef\u4ee5\u62d4\u9664\u7684\u8a71\uff0c\u90a3\u4fee\u9053\u662f\u000d\u000a\u4e0d\u662f\u5bb9\u6613\u4e00\u9ede\u9ede\u4e86\uff1f\uff08\u662f\uff09","26\u000d\u000a\u5176\u5be6\u4e92\u76f8\u662f\u53ef\u4ee5\u5e6b\u52a9\u7684\uff0c\u53ef\u4ee5\u4e92\u76f8\u5206\u4eab\u4e00\u9ede\u5fc3\u4e8b\u3001\u6d88\u6e1b\u4e00\u9ede\u7169\u60f1\u3002\u000d\u000a\u4f46\u662f\u8981\u771f\u6b63\u5b8c\u5168\u6e05\u9664\uff0c\u53ea\u80fd\u5920\u9760\u81ea\u5df1\u4e86\u3002\u000d\u000a\u5f92\u5152\u5011\u6709\u6c92\u6709\u66fe\u7d93\u50b7\u5fc3\u6b32\u7d55\u904e\uff1f\uff08\u6709\uff09\u54ed\u4e86\u5e7e\u5929\u554a\uff1f\u982d\u4e0a\u7684\u70cf\u96f2\u8ddf\u000d\u000a\u4e86\u5e7e\u5929\u554a\uff1f\u63ee\u4e4b\u4e0d\u53bb\u7684\u9670\u973e\uff0c\u5728\u4f60\u7684\u5fc3\u88e1\u8eb2\u8eb2\u85cf\u85cf\u4e86\u591a\u5c11\u6642\u5019\u554a\uff1f\u000d\u000a\u5e74\u8f15\u7684\u4eba\u6709\u7169\u60f1\u55ce\uff1f\uff08\u6709\uff09\u000d\u000a\u25ce\u8001\u5e2b\u8ddf\u4e7e 19-8\u25cb\u82b3\u6148\u60b2\uff1a\u5f92\u5152\u6709\u7169\u60f1\u55ce\uff1f\uff08\u6709\uff09\u597d\uff0c\u8acb\u5750\u3002\u000d\u000a\u7576\u7136\u6703\u6709\u7169\u60f1\uff0c\u53ea\u662f\u7169\u60f1\u7684\u5c64\u9762\u4e0d\u4e00\u6a23\uff0c\u90a3\u5728\u5f92\u5152\u5011\u6709\u7169\u60f1\u6642\uff0c\u000d\u000a\u6216\u662f\u9047\u5230\u4f86\u4f5b\u5802\u8ddf\u539f\u672c\u7684\u751f\u6d3b\u6709\u4e00\u4e9b\u885d\u7a81\u7684\u6642\u5019\uff0c\u5f92\u5152\u600e\u9ebc\u8fa6\uff1f\u8981\u5e74\u000d\u000a\u8f15\u7684\u5f92\u5152\u60f3\u5230\u70ba\u5e2b\u597d\u50cf\u6709\u9ede\u56f0\u96e3\u5440\uff01\u662f\u662f\u662f\uff0c\u6c92\u95dc\u4fc2\uff0c\u4e0d\u7528\u60f3\u70ba\u5e2b\uff0c\u000d\u000a\u70ba\u5e2b\u60f3\u4f60\u5011\u5c31\u5920\u4e86\uff0c\u70ba\u5e2b\u5e6b\u4f60\u5011\u64a5\u8f49\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u5927\u5bb6\u6709\u591a\u4e45\u6c92\u6709\u7b11\u4e00\u7b11\u4e86\u5440\uff1f\u770b\u770b\u73fe\u5728\u5929\u8272\u6108\u4f86\u6108\u6697\u4e86\uff0c\u672c\u4f86\u7684\u000d\u000a\u671d\u6c23\uff0c\u53ef\u4e0d\u80fd\u56e0\u70ba\u5929\u8272\u8f49\u6697\uff0c\u5c31\u771f\u7684\u8ddf\u8457\u8b8a\u5f97\u66ae\u6c23\u6c89\u6c89\u554a\uff01\u73fe\u5728\u90fd\u7b11\u000d\u000a\u51fa\u8072\u97f3\u4f86\u7d66\u70ba\u5e2b\u807d\u807d\u3002\uff08\u54c8\u54c8\u54c8......\uff09\u6709\u4e9b\u4eba\u662f\u7b11\u7d66\u70ba\u5e2b\u807d\uff0c\u6709\u4e9b\u4eba\u000d\u000a\u662f\u7b11\u525b\u624d\u7b11\u7684\u5f92\u5152\u554a\uff0c\u6240\u4ee5\u7b11\u8072\u4e0d\u592a\u4e00\u81f4\u5462\u3002\u518d\u7b11\u4e00\u4e0b\u597d\u4e0d\u597d\u554a\uff01\uff08\u597d\u3002","27\u000d\u000a\u54c8\u54c8\u54c8......\uff09\u000d\u000a\u7b11\u53ef\u4ee5\u7121\u7de3\u7121\u6545\u55ce\uff1f\u2500\u2500\u000d\u000a\u5982\u679c\u4f60\u7684\u5fc3\u4e2d\u5e38\u5e38\u77e5\u9053\uff0c\u4eba\u751f\u88e1\u9762\u552f\u6709\u300c\u4fee\u9053\u300d\u662f\u7b2c\u4e00\u8981\u4e8b\uff0c\u000d\u000a\u90a3\u4f60\u5c31\u5206\u5206\u79d2\u79d2\u3001\u65e5\u65e5\u591c\u591c\u90fd\u7b11\u5f97\u51fa\u4f86\u3002\u000d\u000a\u70ba\u4ec0\u9ebc\uff1f\u2500\u2500\u56e0\u70ba\u6101\u82e6\u6d88\u6e1b\u4e86\u3002\u000d\u000a\u4eba\u4e16\u9593\u7684\u6101\u82e6\u8ddf\u4fee\u9053\u7684\u5feb\u6a02\u6bd4\u8d77\u4f86\uff0c\u662f\u6bd4\u4e0d\u4e0a\u4fee\u9053\u7684\u5feb\u6a02\u3002\u000d\u000a\u4ec0\u9ebc\u610f\u601d\u5462\uff1f\u8056\u8ce2\u5728\u4e16\u6642\uff0c\u54ea\u6709\u6c92\u6709\u53d7\u904e\u82e6\u7684\u5440\uff1f\uff08\u662f\uff09\u000d\u000a\u56e0\u70ba\u53d7\u82e6\u7684\u4eba\uff0c\u000d\u000a\u4ed6\u80fd\u5920\u6709\u4e0d\u5c48\u4e0d\u6493\u7684\u610f\u5fd7\uff1b\u000d\u000a\u4ed6\u80fd\u5920\u4fdd\u6301\u5fc3\u4e2d\u7684\u9053\u5fb7\u539f\u5247\uff1b\u000d\u000a\u4ed6\u80fd\u5920\u76e1\u4eba\u502b\uff1b\u4ed6\u80fd\u5920\u5408\u79ae\u7fa9\uff1b\u000d\u000a\u8d70\u904e\u4f86\u4e86\uff0c\u5c31\u8b8a\u6210\u7210\u706b\u7d14\u9752\u7684\u8056\u8ce2\u3002","28\u000d\u000a\u90a3\u5f92\u5152\u5011\u8981\u5c0f\u5fc3\u5594\uff01\u73fe\u5728\u4f60\u5011\u5c31\u8d70\u5728\u9019\u500b\u904e\u7a0b\u4e0a\u9762\uff0c\u9019\u500b\u904e\u7a0b\u73fe\u000d\u000a\u5728\u6bcf\u4e00\u5206\u5c31\u8e0f\u4e00\u6b65\uff0c\u6bcf\u4e00\u79d2\u53c8\u8e0f\u4e0b\u4e00\u6b65\uff0c\u770b\u770b\u6aa2\u67e5\u4e00\u4e0b\uff0c\u5f80\u5f8c\u770b\uff0c\u81ea\u000d\u000a\u5df1\u8173\u6b65\u6709\u6c92\u6709\u8d70\u7684\u76f4\u76f4\u7684\uff0c\u6709\u55ce\uff1f\u6709\u7684\u6642\u5019\uff0c\u76f4\u63a5\u7dca\u6025\u8f49\u5f4e\u4e86\u90fd\u4e0d\u66c9\u000d\u000a\u5f97\u5462\uff01\u000d\u000a\u5728\u5e73\u5e38\u88e1\uff0c\u6709\u6c92\u6709\u5e38\u5e38\u7dca\u6025\u8f49\u5f4e\u5440\uff1f\u000d\u000a\u4fee\u9053\u53ef\u4e0d\u884c\u5462\uff01\u000d\u000a\u4fee\u9053\u7684\u8def\uff0c\u5982\u679c\u8d70\u7684\u4e0d\u5920\u300c\u76f4\u300d\uff0c\u000d\u000a\u90a3\u5c31\u4e0d\u662f\u300c\u539f\u672c\u300d\u7684\u8def\u4e86\uff0c\u662f\u55ce\uff1f\uff08\u662f\uff09\u000d\u000a\u90a3\u525b\u624d\u7684\u7b11\u8072\u518d\u7b11\u4e00\u6b21\uff08\u54c8\u54c8\u54c8......\uff09\u3002\u000d\u000a\u9019\u9ebc\u591a\u500b\u4eba\u600e\u9ebc\u807d\u8d77\u4f86\u7b11\u8072\u50cf\u5341\u5e7e\u500b\u4eba\u554a\uff1f\u70ba\u5e2b\u5982\u679c\u8499\u8457\u773c\uff0c\u9084\u000d\u000a\u4e0d\u77e5\u9053\u9019\u9ebc\u591a\u4eba\u5728\u9019\u5462\u3002\u518d\u7b11\u4e00\u6b21\uff01\uff08\u54c8\u54c8\u54c8\uff09\u70ba\u4ec0\u9ebc\u70ba\u5e2b\u8981\u8acb\u5f92\u5152\u5011\u000d\u000a\u9019\u6a23\u7b11\u5462\uff1f\u56e0\u70ba\u70ba\u5e2b\u5e0c\u671b\u5f92\u5152\u5011\u80fd\u5920\u300c\u5171\u540c\u50b3\u905e\u300d\u9019\u500b\u5feb\u6a02\u3002\u000d\u000a\u6709\u7684\u6642\u5019\u4e00\u500b\u4eba\u6703\u5feb\u6a02\u4e0d\u8d77\u4f86\uff0c\u56e0\u70ba\u4e00\u500b\u4eba\u5c31\u6703\u4e82\u60f3\uff0c\u5feb\u5feb\u4e0d\u5b89","29\u000d\u000a\u3010\u8a3b\u4e03\u3011\u3001\u60b6\u60b6\u4e0d\u6a02\u3002\u5982\u679c\u5bb6\u88e1\u53ea\u5269\u4f60\u4e00\u500b\u4eba\uff0c\u525b\u597d\u6700\u8fd1\u53c8\u9047\u5230\u4e00\u4e9b\u6101\u000d\u000a\u4e8b\uff0c\u597d\u6101\u5440\uff01\u5916\u9762\u51fa\u8457\u5927\u592a\u967d\uff0c\u4f60\u5750\u5728\u5bb6\u88e1\u9762\u7684\u5ba2\u5ef3\uff0c\u81ea\u5df1\u60f3\u554a\u3001\u60f3\u000d\u000a\u554a\uff01\u9019\u4ef6\u4e8b\u60c5\u8a72\u600e\u9ebc\u8fa6\u5440\uff1f\u597d\u50cf\u4e0d\u597d\u554a\uff0c\u8a72\u600e\u9ebc\u505a\u5440\uff1f\u000d\u000a\u4e00\u500b\u4eba\u8001\u5f80\u4e0d\u597d\u7684\u5730\u65b9\u53bb\u60f3\uff0c\u60f3\u5f97\u958b\u55ce\uff1f\uff08\u60f3\u4e0d\u958b\uff09\u662f\u5440\uff01\u56e0\u70ba\u4f60\u000d\u000a\u7684\u65b9\u5411\u60f3\u932f\u4e86\u5440\uff01\u5b9a\u4f4d\u4e00\u500b\u4e0d\u597d\u7684\u65b9\u5411\uff0c\u5f92\u5152\u5011\u5c31\u5f80\u88e1\u9762\u947d\uff0c\u4f60\u947d\u5f97\u000d\u000a\u6108\u5927\u529b\uff0c\u982d\u6108\u75bc\u554a\uff01\u000d\u000a\u70ba\u5e2b\u544a\u8a34\u5f92\u5152\uff1a\u5982\u679c\u9047\u5230\u9019\u6a23\u7684\u60c5\u6cc1\uff0c\u5c31\u4e0d\u8981\u518d\u947d\u4e86\uff0c\u56e0\u70ba\u947d\u4e0d\u000d\u000a\u51fa\u4e00\u500b\u6240\u4ee5\u7136\u7684\u3002\u9019\u500b\u65b9\u5411\u4e0d\u884c\uff0c\u662f\u4e0d\u662f\u5c31\u8981\u63db\u65b9\u5411\u5440\uff1f\uff08\u662f\uff09\u000d\u000a\u25ce\u5764\u25cb\u775b\u9ede\u50b3\u5e2b\u5e6b\u8001\u5e2b\u6574\u7406\u8863\u670d\uff0c\u8001\u5e2b\u6148\u60b2\uff1a\u000d\u000a\u5f92\u5152\u771f\u7d30\u5fc3\uff01\u9019\u300c\u838a\u56b4\u300d\u5c31\u5728\u81ea\u5df1\u7684\u5fc3\u88e1\u9762\uff0c\u5f92\u5152\u9084\u8981\u8d70\u5f88\u9577\u7684\u000d\u000a\u8def\uff01\u000d\u000a\u9019\u6642\u5019\uff0c\u5982\u679c\u65c1\u908a\u6709\u4e00\u500b\u4eba\uff0c\u544a\u8a34\u4f60\u4e0d\u8981\u9019\u6a23\u5b50\u60f3\uff0c\u9019\u6a23\u3001\u9019\u6a23\u000d\u000a\u60f3\u4e0d\u5c31\u597d\u4e86\u55ce\uff1f\u4f60\u662f\u4e0d\u662f\u8f15\u9b06\u8a31\u591a\uff1f\uff08\u662f\uff09\u90a3\u5f92\u5152\u5011\u8eab\u65c1\u6709\u6c92\u6709\u9019\u6a23\u7684","30\u000d\u000a\u4eba\u5440\uff1f\uff08\u6709\uff0c\u5584\u77e5\u8b58\uff09\u6709\u4e9b\u4eba\u8aaa\u6709\uff0c\u6709\u4e9b\u4eba\u6c92\u8aaa\u8a71\u5462\uff01\u000d\u000a\u9084\u662f\u65c1\u908a\u7684\u4eba\u90fd\u8ddf\u4f60\u8aaa\uff1a\u000d\u000a\u300c\u5c0d\uff01\u5c31\u662f\u8981\u9019\u6a23\u60f3\uff0c\u5c31\u662f\u4ed6\u7684\u932f\uff0c\u4f60\u6c92\u6709\u932f\uff0c\u7e7c\u7e8c\u9019\u6a23\u60f3\uff01\u300d\u000d\u000a\u300c\u4f86\u4f5b\u5802\u4e0d\u9700\u8981\uff0c\u751f\u6d3b\u904e\u597d\u5c31\u597d\u4e86\uff0c\u9084\u8981\u4f86\u4f5b\u5802\u505a\u4ec0\u9ebc\uff1f\u5403\u559d\u4eab\u6a02\u000d\u000a\u90fd\u4f86\u4e0d\u53ca\u4e86\uff0c\u70ba\u4ec0\u9ebc\u9084\u8981\u82b1\u6642\u9593\u53bb\u4f5b\u5802\u5440\uff1f\u300d\u000d\u000a\u300c\u8ff7\u4fe1\u3001\u8ff7\u4fe1\uff0c\u8ddf\u8457\u6211\u5011\u4e00\u8d77\u51fa\u53bb\u73a9\u4e0d\u662f\u5f88\u597d\u55ce\uff1f\u300d\u000d\u000a\u9019\u6642\u5019\uff0c\u5f92\u5152\u5011\u7684\u5fc3\u88e1\u9762\u8981\u5f88\u6e05\u695a\uff0c\u000d\u000a\u5176\u5be6\u662f\u4e0d\u597d\u610f\u601d\u8aaa\uff0c\u5c0d\u65b9\u624d\u662f\u300c\u8ff7\u4fe1\u300d\u2500\u2500\u000d\u000a\u8ff7\u4fe1\u9019\u4e9b\u7269\u8cea\u4eab\u53d7\uff0c\u53ef\u4ee5\u5e36\u4f86\u81ea\u5df1\u7684\u5feb\u6a02\uff1b\u000d\u000a\u8ff7\u4fe1\u4eba\u4e16\u9593\u7684\u91d1\u9322\u3001\u5bcc\u8cb4\uff0c\u53ef\u4ee5\u63db\u4f86\u4ed6\u4eba\u7684\u770b\u91cd\uff1b\u000d\u000a\u8ff7\u4fe1\u8f15\u9b06\u81ea\u5728\u7684\u751f\u6d3b\uff0c\u53ef\u4ee5\u63db\u4f86\u8eab\u9ad4\u7684\u5065\u5eb7\u3002\u000d\u000a\u90a3\u662f\u8ab0\u300c\u8ff7\u4fe1\u300d\u4e86\u5462\uff1f\u9019\u662f\u4eba\u4eba\u5728\u8b1b\u3002\u000d\u000a\u8ff7\u4fe1\u662f\u4ec0\u9ebc\u5462\uff1f\u2500\u2500","31\u000d\u000a\u4ed6\u76f8\u4fe1\u7684\uff0c\u6211\u4e0d\u76f8\u4fe1\uff0c\u6240\u4ee5\u4ed6\u8ff7\u4fe1\uff1b\u000d\u000a\u53ea\u8981\u6211\u76f8\u4fe1\uff0c\u90fd\u4e0d\u662f\u8ff7\u4fe1\u3002\u000d\u000a\u518d\u4f86\u5f92\u5152\u5011\uff0c\u660e\u767d\u9053\u7406\u4e86\u8981\u8df3\u958b\u9019\u4e9b\u8a71\u8a9e\uff0c\u9019\u4e9b\u8a71\u8a9e\u722d\u8ad6\u7684\u518d\u591a\uff0c\u000d\u000a\u5e74\u8f15\u7684\u5f92\u5152\u90fd\u5f88\u8070\u660e\uff0c\u4e00\u53e5\u8a71\u4f86\u662f\u4e0d\u662f\u53ef\u4ee5\u4e09\u53e5\u56de\u53bb\uff1f\u53f0\u4e0a\u7684\u5f92\u5152\u5728\u000d\u000a\u8b1b\u7684\u6642\u5019\uff0c\u5fc3\u88e1\u9762\u5728\u60f3\u5f88\u591a\u5176\u4ed6\u7684\u4e8b\u60c5\uff0c\u6709\u6642\u5019\u751a\u81f3\u53ef\u4ee5\u53cd\u99c1\u56de\u53bb\u5462\uff01\u000d\u000a\u53ea\u662f\u56e0\u70ba\u9019\u88e1\u6c92\u6709\u653e\u8b1b\u53f0\u4e0d\u80fd\u8b1b\u8a71\uff0c\u6c92\u95dc\u4fc2\uff0c\u4ee5\u5f8c\u9577\u5927\u4e86\u7576\u8b1b\u54e1\u3001\u000d\u000a\u7576\u8b1b\u5e2b\uff0c\u5c31\u53ef\u4ee5\u5c07\u6b63\u77e5\u6b63\u898b\u3001\u81ea\u5df1\u5e74\u8f15\u7684\u9ad4\u6703\u4f86\u5206\u4eab\uff0c\u597d\u4e0d\u597d\uff1f\u000d\u000a\u6240\u4ee5\u8aaa\u554a\uff1a\u300c\u79cb\u6beb\u4e0d\u80fd\u5fcd\uff0c\u5e73\u5730\u751f\u5e72\u6208\u3002\u300d\u3010\u8a3b\u516b\u3011\u000d\u000a\u5f92\u5152\u5011\u60f3\u4e00\u60f3\uff0c\u5f9e\u4fee\u9053\u7684\u4e00\u5165\u9580\u958b\u59cb\uff0c\u662f\u4e0d\u662f\u5c31\u5e38\u5e38\u544a\u8a34\u5f92\u5152\u5011\u000d\u000a\u8981\u300c\u542b\u548c\u5fcd\u8fb1\u300d\uff1f\u90a3\u73fe\u5728\u300c\u542b\u548c\u300d\u4e86\u55ce\uff1f\u000d\u000a\u56de\u5bb6\u770b\u5230\u5bb6\u4eba\u7684\u6642\u5019\uff0c\u5fc3\u88e1\u9762\u7a0d\u5fae\u7169\u8e81\u4e86\u4e00\u9ede\uff0c\u000d\u000a\u4f60\u7684\u8b1b\u8a71\u9084\u300c\u542b\u548c\u300d\u55ce\uff1f","32\u000d\u000a\u662f\u4e0d\u662f\u8b8a\u6210\u300c\u542b\u51f6\u300d\u4e86\uff1f\u000d\u000a\u53cd\u6b63\u5bb6\u4eba\u4e5f\u4e0d\u80fd\u8ddf\u8ab0\u8b1b\uff0c\u6240\u4ee5\u51fa\u6c23\u6c92\u95dc\u4fc2\uff0c\u9019\u6a23\u53ef\u4ee5\u55ce\uff1f\u000d\u000a\u5c0f\u5fc3\u9019\u6a23\u4e45\u4e86\uff0c\u95dc\u4fc2\u6703\u4e0d\u6703\u4e0d\u597d\uff1f\uff08\u6703\uff09\u000d\u000a\u4eca\u5929\u540c\u4fee\u5f9e\u524d\u9762\u8d70\u904e\u4f86\uff0c\u4f60\u8d70\u904e\u53bb\uff0c\u7576\u4f5c\u6c92\u770b\u5230\uff0c\u660e\u660e\u4f4f\u5728\u540c\u4e00\u000d\u000a\u500b\u5c4b\u5b50\u88e1\uff0c\u70ba\u4ec0\u9ebc\u5462\uff1f\u56e0\u70ba\u6628\u5929\u5435\u67b6\u4e86\u3002\u54ce\u5440\uff01\u5bb6\u88e1\u9084\u6709\u4f5b\u5802\uff0c\u53eb\u4ed9\u000d\u000a\u4f5b\u770b\u4f60\u5011\u5435\u67b6\u5440\uff1f\u9019\u6a23\u597d\u55ce\uff1f\uff08\u4e0d\u597d\uff09\u662f\u5440\uff01\u4e0d\u8981\u60f3\u8aaa\u6c92\u95dc\u4fc2\uff0c\u4f5b\u5802\u5728\u000d\u000a\u4e8c\u6a13\u3001\u4f5b\u5802\u5728\u4e09\u6a13\uff0c\u9ad8\u7684\u9ad8\u7684\uff0c\u6211\u5011\u5728\u81ea\u5df1\u7684\u623f\u9593\u5435\uff0c\u6c92\u554f\u984c\u3002\u000d\u000a\u4e0d\u662f\u9019\u6a23\u5462\uff01\u6709\u4f5b\u5802\u5c31\u6709\u8af8\u5929\u4ed9\u4f5b\u5728\uff0c\u5bb6\u88e1\u5e38\u5e38\u5435\u5435\u9b27\u9b27\uff0c\u53ef\u4ee5\u000d\u000a\u55ce\uff1f\uff08\u4e0d\u53ef\u4ee5\uff09\u662f\u5440\u3002\u90a3\u6c92\u95dc\u4fc2\uff0c\u51fa\u4e86\u9580\u5435\u53ef\u4ee5\u55ce\uff1f\uff08\u4e0d\u53ef\u4ee5\uff09\u662f\u5440\uff0c\u4e5f\u000d\u000a\u4e0d\u884c\u3002\u9019\u5c31\u662f\u5f92\u5152\u5011\u6bcf\u5929\u6703\u9047\u5230\u7684\u751f\u6d3b\u5440\uff01\u000d\u000a\u70ba\u5e2b\u975e\u5e38\u60f3\u8981\u5728\u5f92\u5152\u5011\u7684\u751f\u6d3b\u88e1\u9762\u6210\u5168\u5f92\u5152\u5011\uff0c\u000d\u000a\u90a3\u5f92\u5152\u5011\u73fe\u5728\u56de\u53bb\u5f8c\uff0c\u000d\u000a\u958b\u59cb\u8981\u300c\u8b39\u614e\u300d\u81ea\u5df1\u8aaa\u51fa\u4f86\u7684\u8a71\u8a9e\uff1b","33\u000d\u000a\u300c\u8b39\u614e\u300d\u81ea\u5df1\u5167\u5fc3\u6d6e\u8e81\u6642\u7684\u813e\u6c23\u3002\u000d\u000a\u5f88\u591a\u6642\u5019\uff0c\u813e\u6c23\u3001\u66b4\u6c23\u3001\u60b6\u6c23\uff0c\u90fd\u4f86\u5f92\u5152\u5bb6\u88e1\u505a\u5ba2\u554a\uff01\u9019\u6a23\u7684\u8a2a\u000d\u000a\u5ba2\u559c\u4e0d\u559c\u6b61\uff1f\uff08\u4e0d\u559c\u6b61\uff09\u6703\u4e0d\u6703\u9084\u7aef\u4e00\u676f\u6c34\u7d66\u4ed6\u559d\uff0c\u8acb\u4ed6\u518d\u8b1b\u591a\u4e00\u9ede\u8a71\u000d\u000a\u5440\uff1f\u53ef\u662f\uff0c\u60f3\u8981\u9001\u5ba2\u53c8\u9001\u4e0d\u8d70\u2500\u2500\u6709\u6c92\u6709\u62f3\u982d\u63e1\u5f97\u7dca\u7dca\u7684\u6642\u5019\uff1f\u81c9\u90fd\u000d\u000a\u8139\u7d05\u4e86\u3002\u9019\u6642\u5019\uff0c\u5c31\u662f\u4fee\u9053\u6700\u597d\u7684\u4f73\u6a5f\u4e86\u3002\u000d\u000a\u9019\u6642\u5f92\u5152\u80fd\u4e0d\u80fd\u5920\u5fcd\u4e0b\u4f86\uff1f\u4e26\u4e0d\u662f\u6108\u5fcd\u706b\u6c23\u6108\u5927\u5462\uff01\u800c\u662f\u5fcd\u5230\u771f\u000d\u000a\u7684\u300c\u542b\u548c\u300d\u3001\u771f\u7684\u5e73\u975c\u4e86\uff0c\u9019\u6642\u5019\u5f92\u5152\u5011\u7684\u5bb6\u88e1\u5c31\u6eab\u6696\u591a\u4e86\uff1b\u5bb6\u88e1\u6eab\u000d\u000a\u6696\u4e86\uff0c\u9080\u8acb\u5bb6\u4eba\u4f86\u4f5b\u5802\uff0c\u662f\u4e0d\u662f\u5bb9\u6613\u4e86\uff1f\uff08\u662f\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u591a\u4f4d\u677f\u66f8\u524d\u8ce2\u6148\u60b2\uff1a\u000d\u000a\u5927\u5bb6\u4e92\u76f8\u5b78\u7fd2\uff0c\u6709\u5b78\u7fd2\u5c31\u6709\u6210\u9577\uff0c\u90fd\u662f\u597d\u5f92\u5152\u3002\u000d\u000a\u9019\u6bb5\u6642\u9593\u4e0d\u662f\u7a7a\u8457\u5462\uff01\u60f3\u4e00\u60f3\uff0c\u56de\u53bb\u5bb6\u88e1\u600e\u9ebc\u6a23\u505a\u8abf\u7bc0\uff0c\u8abf\u7bc0\u8eab\u000d\u000a\u5fc3\u3001\u8abf\u7bc0\u813e\u6c23\uff0c\u8b93\u6bcf\u4e00\u5929\u904e\u5f97\u66f4\u6709\u9053\u3002\u000d\u000a\u25ce\u8001\u5e2b\u6279\u8a13\u671f\u9593\uff0c\u25cb\u826f\u9ede\u50b3\u5e2b\u5c0d\u73ed\u54e1\u6148\u60b2\uff1a","34\u000d\u000a\u8001\u5e2b\u8ddf\u6211\u5011\u8b1b\u4e86\u597d\u591a\u5e73\u5e38\u5bb6\u5c45\u751f\u6d3b\u7684\u4e8b\u60c5\uff0c\u9ede\u9ede\u6ef4\u6ef4\u7684\u4e8b\u60c5\uff0c\u597d\u50cf\u8ddf\u6211\u5011\u4f4f\u000d\u000a\u5728\u4e00\u8d77\u4e00\u6a23\u3002\u8001\u5e2b\u525b\u624d\u8aaa\uff0c\u6211\u5011\u5e38\u5e38\u628a\u300c\u9b54\u300d\u8acb\u5230\u6211\u5011\u5bb6\u53bb\uff0c\u9084\u9001\u4e0d\u8d70\uff0c\u9084\u8acb\u4ed6\u000d\u000a\u9032\u4f86\u559d\u676f\u8336\u3002\u6240\u4ee5\uff0c\u6709\u7684\u4eba\u4fee\u9053\u5f97\u529b\uff0c\u6709\u7684\u4eba\u4fee\u9053\u4e0d\u5f97\u529b\u3002\u4e0d\u559c\u6b61\u5728\u5bb6\u88e1\u9762\u4fee\u9053\u000d\u000a\u7684\u4eba\uff0c\u5c31\u4e0d\u6703\u5f97\u529b\uff0c\u53ea\u60f3\u5230\u4f5b\u5802\u4f86\u4fee\u9053\u3002\u4f5b\u5802\u4e0d\u662f\u4fee\u9053\u7684\u5730\u65b9\uff0c\u662f\u300c\u5b78\u9053\u300d\u7684\u5730\u000d\u000a\u65b9\u3002\u4fee\u9053\u6700\u597d\u7684\u5730\u65b9\u662f\u300c\u5bb6\u5ead\u300d\u3002\u000d\u000a\u6d3b\u4f5b\u8001\u5e2b\u8ddf\u6211\u5011\u8b1b\u7684\u90fd\u662f\u8a71\u4e2d\u6709\u8a71\uff0c\u6211\u5011\u597d\u597d\u53bb\u7701\u601d\u4e00\u4e0b\u3002\u8001\u5e2b\u525b\u624d\u8b1b\u7684\u8ff7\u000d\u000a\u4fe1\u3001\u4e0d\u8ff7\u4fe1\u662f\u4e0d\u662f\u5f88\u6709\u610f\u601d\uff0c\u5f88\u503c\u5f97\u7701\u601d\u3002\u6211\u5011\u4ee5\u524d\u807d\u5230\u300c\u9053\u5728\u751f\u6d3b\u300d\u5927\u6982\u4e0d\u61c2\u000d\u000a\u90a3\u500b\u610f\u601d\uff0c\u9053\u4e0d\u662f\u5728\u7d93\u5178\uff0c\u4e0d\u662f\u5728\u9053\u5b78\uff0c\u662f\u5728\u751f\u6d3b\u3002\u5b83\u662f\u4e00\u500b\u6d3b\u7684\u6771\u897f\uff0c\u8ddf\u4f60\u7684\u000d\u000a\u751f\u547d\u5171\u5b58\u7684\uff0c\u5982\u679c\u5b83\u6c92\u6709\u751f\u547d\u529b\uff0c\u90a3\u500b\u5c31\u4e0d\u662f\u9053\uff0c\u90a3\u500b\u53eb\u300c\u77e5\u8b58\u300d\u3002\u000d\u000a\u4eca\u5929\u8001\u5e2b\u5f9e\u958b\u59cb\u8ddf\u6211\u5011\u8b1b\uff0c\u90fd\u662f\u8b1b\u6211\u5011\u7684\u751f\u6d3b\uff0c\u5c0d\u5427\uff1f\u56e0\u70ba\uff0c\u6bcf\u4e00\u5929\u4f60\u600e\u9ebc\u000d\u000a\u60f3\uff0c\u6bcf\u4e00\u5929\u600e\u9ebc\u53d6\u6368\uff0c\u4f60\u5fc3\u88e1\u9762\u662f\u600e\u9ebc\u6a23\u53bb\u727d\u639b\uff0c\u5b83\u90fd\u662f\u6709\u4e00\u500b\u57fa\u790e\u5728\u3002\u57fa\u790e\u4e0d\u000d\u000a\u4e00\u6a23\u7684\u6642\u5019\uff0c\u5b83\u60f3\u7684\u6771\u897f\u90fd\u4e0d\u4e00\u6a23\uff0c\u6240\u4ee5\u8001\u5e2b\u8ddf\u6211\u5011\u8b1b\u7684\u662f\u975e\u5e38\u6d3b\u7684\u6771\u897f\u3002\u000d\u000a\u672b\u5f8c\u662f\u4e00\u500b\u5f88\u4e82\u3001\u5f88\u4e82\u7684\u6642\u5019\uff0c\u525b\u624d\u8001\u5e2b\u4e00\u4f86\u7684\u6642\u5019\u5c31\u8aaa\u4e86\uff0c\u5c07\u4f86\u4e82\u7684\u4e8b\u60c5\u000d\u000a\u6703\u6108\u4f86\u6108\u591a\uff0c\u600e\u9ebc\u627e\u5230\u4e00\u500b\u300c\u62ef\u6551\u81ea\u5df1\u300d\u7684\u8def\uff0c\u5728\u672b\u4e16\u771f\u7684\u662f\u975e\u5e38\u91cd\u8981\u7684\u3002\u6211\u5011","35\u000d\u000a\u6703\u770b\u5230\u597d\u591a\u7684\u4eba\u6c92\u9802\u4e86\uff0c\u9019\u4e9b\u73fe\u5be6\u662f\u5f88\u53ef\u6015\u7684\u3002\u000d\u000a\u662f\u5440\u3001\u662f\u5440\uff01\u9019\u500b\u300c\u4e82\u300d\u5230\u5e95\u6703\u6709\u591a\u4e82\u5462\uff1f\u4e0d\u80fd\u8aaa\u3001\u4e0d\u80fd\u8aaa\uff0c\u5929\u000d\u000a\u6a5f\u4e0d\u53ef\u6d29\u9732\u3002\u4f46\u662f\uff0c\u5f88\u591a\u4eba\u90fd\u5728\u8b1b\u5440\uff0c\u300c\u4e16\u754c\u672b\u65e5\u300d\u3001\u300c\u4e16\u754c\u672b\u65e5\u300d\uff0c\u000d\u000a\u662f\u4e0d\u662f\uff1f\u5f92\u5152\u5011\u770b\u591a\u4e86\u3001\u807d\u591a\u4e86\uff0c\u6703\u4e0d\u6703\u8ddf\u8457\u4e5f\u63d0\u5fc3\u5f14\u81bd\uff1f\u6bcf\u5929\u90fd\u5bb3\u000d\u000a\u6015\u51fa\u9580\u554a\uff1f\u000d\u000a\u70ba\u4ec0\u9ebc\u672b\u5f8c\u9019\u9ebc\u7dca\u6025\u5462\uff1f\u000d\u000a\u6025\u5728\u2500\u2500\u6bcf\u4e00\u500b\u4eba\u7684\u6a5f\u6703\u4e0d\u591a\u4e86\u3002\u000d\u000a\u4e00\u76f4\u5728\u8b1b\u300c\u4e16\u754c\u672b\u65e5\u300d\uff0c\u000d\u000a\u96e3\u9053\u5f92\u5152\u5011\u4e0d\u66c9\u5f97\u300c\u6bcf\u500b\u4eba\u90fd\u6709\u6bcf\u500b\u4eba\u7684\u4e16\u754c\u672b\u65e5\u300d\u55ce\uff1f\u000d\u000a\u300c\u4e16\u754c\u672b\u65e5\u300d\u4e00\u822c\u4eba\u610f\u601d\u662f\u8aaa\uff1a\u4eba\u4e0d\u5728\u9019\u500b\u4e16\u4e0a\u3002\u000d\u000a\u4f46\u662f\uff0c\u4e00\u500b\u4eba\u4e0d\u5728\u9019\u500b\u4e16\u4e0a\uff0c\u000d\u000a\u5c0d\u4ed6\u81ea\u5df1\u4f86\u8aaa\uff0c\u4e0d\u5c31\u662f\u4e16\u754c\u672b\u65e5\u55ce\uff1f\u000d\u000a\u600e\u9ebc\u9084\u9700\u8981\u53bb\u6c42\u9060\u4f86\u7406\u89e3\u5462\uff1f","36\u000d\u000a\u6bcf\u4e00\u500b\u4eba\u7684\u4e16\u754c\u672b\u65e5\u90fd\u8fd1\u4e86\uff0c\u56e0\u70ba\u2500\u2500\u000d\u000a\u6bcf\u4e00\u500b\u4eba\u7684\u300c\u751f\u300d\u5230\u300c\u6b7b\u300d\u90fd\u662f\u77ed\u66ab\u7684\u3002\u000d\u000a\u90a3\u4fee\u9053\u4e86\u9084\u9700\u8981\u614c\u5f35\u55ce\uff1f\u000d\u000a\u4fee\u9053\u61c9\u8a72\u8981\u5c07\u9019\u60f6\u6050\u3001\u614c\u5f35\u7684\u5fc3\u60c5\uff0c\u000d\u000a\u6539\u6210\u611f\u6069\u3001\u73cd\u60dc\u7684\u52d5\u529b\u3002\u000d\u000a\u56e0\u70ba\u77e5\u9053\u6642\u9593\u4e0d\u5920\u4e86\uff0c\u9019\u672c\u4f86\u5c31\u662f\u4e8b\u5be6\uff0c\u600e\u9ebc\u9700\u8981\u5bb3\u6015\u5462\uff1f\uff08\u662f\uff09\u000d\u000a\u4eca\u5929\u5982\u679c\u8aaa\u5bb3\u6015\u4e00\u4ef6\u4e0d\u77e5\u9053\u7684\u4e8b\u60c5\uff0c\u90a3\u624d\u771f\u7684\u5bb3\u6015\u3002\u000d\u000a\u53ef\u662f\u4fee\u9053\uff0c\u5927\u5bb6\u90fd\u807d\u904e\u300c\u56e0\u679c\u300d\uff0c\u5927\u5bb6\u4e5f\u90fd\u6703\u8b1b\uff0c\u4f46\u600e\u9ebc\u56e0\u679c\u767c\u000d\u000a\u751f\u5728\u81ea\u5df1\u8eab\u4e0a\u7684\u6642\u5019\uff0c\u6015\u7684\u4e0d\u5f97\u4e86\u5462\uff1f\u9019\u662f\u4e0d\u662f\u7576\u4e2d\u5c31\u6709\u4e00\u4e9b\u5f92\u5152\uff0c\u000d\u000a\u81ea\u5df1\u5c0d\u81ea\u5df1\u7684\u5fc3\u7406\u554f\u984c\u6c92\u6709\u91d0\u6e05\u554a\uff1f\u000d\u000a\u5982\u679c\u770b\u5f97\u6108\u6e05\u3001\u770b\u5f97\u6108\u900f\uff0c\u9019\u4e0d\u6210\u554f\u984c\u7684\uff0c\u9019\u4e9b\u90fd\u53ea\u662f\u4fee\u9053\u7684\u76ae\u000d\u000a\u6bdb\u3002\u4eba\u9593\u7684\u96e2\u5225\u3001\u6101\u7dd2\u90fd\u6703\u6709\uff0c\u53ef\u662f\u70ba\u5e2b\u5e0c\u671b\u6bcf\u4e00\u4f4d\u5f92\u5152\u5011\u90fd\u6210\u5c31\u2500\u000d\u000a\u2500","37\u000d\u000a\u4e0d\u662f\u6e3a\u5c0f\u7684\u6210\u5c31\u3001\u77ed\u6dfa\u7684\u6210\u5c31\u2500\u2500\u4e16\u9593\u7684\u6210\u5c31\uff1b\u000d\u000a\u800c\u662f\u5ee3\u5927\u7684\u6210\u5c31\u3001\u6c38\u6046\u7684\u6210\u5c31\u2500\u2500\u5929\u4e0a\u7684\u6210\u5c31\u3002\u000d\u000a\u5f92\u5152\u5011\u5982\u679c\u5bb3\u6015\uff0c\u5c31\u4e0d\u8981\u6015\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u90fd\u662f\u56e0\u7de3\u7684\u7d50\u5408\uff0c\u000d\u000a\u90fd\u662f\u6709\u524d\u56e0\u53ef\u5faa\uff0c\u4f46\u4e26\u4e0d\u662f\u8981\u4eba\u6d88\u6975\u5730\u5ea6\u904e\u4e00\u751f\u554a\uff01\u60f3\u8aaa\u53cd\u6b63\u547d\u904b\u4e0d\u000d\u000a\u5c31\u5b89\u6392\u597d\u4e86\u55ce\uff1f\u90a3\u5c31\u4ea4\u7d66\u547d\u904b\u5440\uff01\u53cd\u6b63\u4e0d\u7ba1\u81ea\u5df1\u600e\u9ebc\u52aa\u529b\uff0c\u9084\u662f\u5728\u9019\u000d\u000a\u500b\u547d\u9650\u88e1\u9762\uff0c\u9019\u6a23\u53c8\u662f\u5728\u56e0\u679c\u4e0a\u6709\u57f7\u8457\u4e86\u3002\u000d\u000a\u56e0\u70ba\uff0c\u4fee\u9053\u53ef\u4e0d\u53ef\u4ee5\u6539\u8b8a\u547d\u904b\u5440\uff1f\uff08\u53ef\u4ee5\uff09\u000d\u000a\u89ba\u5f97\u4fee\u9053\u6709\u6539\u8b8a\u81ea\u5df1\u547d\u904b\u7684\u4eba\uff0c\u8acb\u8209\u500b\u624b\u7d66\u70ba\u5e2b\u770b\u770b\u3002\u000d\u000a\u25ce\u5168\u9ad4\u90fd\u8209\u624b\u3002\u000d\u000a\u5f9e\u982d\u770b\u5230\u5c3e\uff0c\u90fd\u6539\u8b8a\u547d\u904b\u4e86\uff1f\uff08\u6709\uff09\u6539\u8b8a\u4ec0\u9ebc\u547d\u904b\u5462\uff1f\u5bb6\u5ead\u8b8a\u597d\uff0c\u000d\u000a\u9084\u6709\u5462\uff1f\u8aaa\u51fa\u8072\u97f3\u4f86\u554a\uff01\u6539\u8b8a\u4ec0\u9ebc\u547d\u904b\u90fd\u4e0d\u8aaa\uff0c\u4e0d\u662f\u8aaa\u7d66\u70ba\u5e2b\u807d\uff0c\u8aaa\u000d\u000a\u7d66\u65c1\u908a\u7684\u4eba\u807d\uff0c\u4e92\u76f8\u6210\u5168\u4e00\u4e0b\u554a\uff01\u597d\u3001\u5f88\u597d\uff01\u000d\u000a\u6709\u4e9b\u5e74\u7d00\u8f15\u7684\u5c31\u6539\u8b8a\u547d\u904b\uff0c\u662f\u6539\u8b8a\u4ec0\u9ebc\u547d\u904b\u554a\uff1f\u662f\u4e0d\u662f\u5728\u5e74\u7d00\u8f15","38\u000d\u000a\u7684\u6642\u5019\u5c31\u6b65\u5165\u4f5b\u5802\uff0c\u66f4\u6709\u6a5f\u6703\u4f86\u6539\u8b8a\u5462\uff1f\uff08\u662f\uff09\u662f\u3002\u5f92\u5152\u5011\u8acb\u653e\u4e0b\uff01\u000d\u000a\u4f46\u662f\uff0c\u4e5f\u6703\u6709\u4e0d\u5c11\u5e74\u8f15\u7684\u5f92\u5152\u89ba\u5f97\uff1a\u000d\u000a\u300c\u51fa\u751f\u5c31\u5728\u4f5b\u5802\uff0c\u6539\u8b8a\u4ec0\u9ebc\u547d\u904b\uff1f\u300d\u000d\u000a\u9019\u5176\u5be6\u4e0d\u662f\u5f92\u5152\u8a3b\u5b9a\u7684\u547d\u904b\uff0c\u000d\u000a\u800c\u662f\u5f92\u5152\u81ea\u5df1\u300c\u4fee\u300d\u4f86\u7684\u547d\u904b\u3002\u000d\u000a\u4e0d\u8981\u4ee5\u70ba\u81ea\u5df1\u5728\u4ec0\u9ebc\u90fd\u4e0d\u77e5\u9053\u7684\u6642\u5019\uff0c\u5c31\u62b1\u53bb\u4f5b\u5802\u6c42\u9053\u4e86\uff0c\u5c31\u4ee5\u000d\u000a\u70ba\u597d\u50cf\u6c92\u4ec0\u9ebc\u9ad4\u6703\u3002\u61f5\u61f5\u61c2\u61c2\u7684\u770b\u8457\u540c\u5b78\uff0c\u5f9e\u5c0f\u5c31\u662f\u904e\u8457\u4e00\u822c\u4eba\u7684\u751f\u000d\u000a\u6d3b\uff0c\u600e\u9ebc\u53ea\u6709\u6211\uff0c\u5047\u65e5\u90fd\u8981\u56de\u5230\u4f5b\u5802\u4f86\u3002\u5f9e\u5c0f\u5c31\u4e0a\u73ed\u7a0b\u4e0a\u5230\u5927\uff0c\u5230\u73fe\u000d\u000a\u5728\u9084\u88ab\u5927\u4eba\u8acb\u4f86\u5750\u5728\u9019\u88e1\u3002\u000d\u000a\u5176\u5be6\u5440\uff01\u9019\u662f\u5f92\u5152\u5011\u81ea\u5df1\u4fee\u4f86\u7684\uff0c\u53ea\u662f\u53ef\u80fd\u6b77\u7df4\u9084\u4e0d\u5920\uff0c\u9084\u6c92\u6709\u000d\u000a\u4e00\u4e9b\u80fd\u5920\u8b93\u5f92\u5152\u81ea\u5df1\u9192\u609f\u7684\u4e8b\u60c5\u767c\u751f\u3002\u6c92\u95dc\u4fc2\uff01\u9084\u53ef\u4ee5\u518d\u7422\u78e8\u3002\u000d\u000a\u4f46\u662f\uff0c\u5149\u9670\u7b49\u5f97\u4e86\u5f92\u5152\u5011\u591a\u4e45\u5462\uff1f\u000d\u000a\u5f92\u5152\u5011\u8dd1\u5f97\u518d\u5feb\uff0c\u4e5f\u8dd1\u4e0d\u6bd4\u5149\u9670\u5feb\uff1b","39\u000d\u000a\u5f92\u5152\u5011\u8dd1\u5f97\u518d\u5feb\uff0c\u4e5f\u8dd1\u4e0d\u4e86\u300c\u696d\u529b\u7684\u7bc4\u570d\u300d\u3002\u000d\u000a\u4e0d\u8ad6\u4fee\u9053\u5230\u4ec0\u9ebc\u7a0b\u5ea6\uff0c\u9053\u89aa\u3001\u58c7\u4e3b\u3001\u8b1b\u5e2b\u3001\u9ede\u50b3\u5f92\u5152\uff0c\u90fd\u9084\u662f\u5728\u000d\u000a\u300c\u696d\u529b\u7684\u7bc4\u7587\u300d\u88e1\uff0c\u70ba\u4ec0\u9ebc\u5462\uff1f\u56e0\u70ba\u9084\u6709\u9019\u500b\u8089\u9ad4\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u4f46\u000d\u000a\u662f\uff0c\u9019\u5c31\u6709\u5206\u5f88\u591a\u7a2e\u5440\uff01\u000d\u000a\u6709\u4e9b\u696d\u529b\u662f\u81ea\u5df1\u9020\u4e0b\u3001\u81ea\u5df1\u627f\u53d7\u7684\uff1b\u000d\u000a\u6709\u4e9b\u4fee\u9053\u4eba\u7684\u696d\u529b\uff0c\u662f\u4ed6\u81ea\u5df1\u300c\u767c\u613f\u300d\u60f3\u8981\u70ba\u773e\u751f\u627f\u53d7\u7684\u3002\u000d\u000a\u6240\u4ee5\u770b\uff0c\u70ba\u4ec0\u9ebc\u5f88\u591a\u7684\u524d\u4eba\u8f29\u3001\u5f88\u591a\u7684\u4ed9\u4f5b\u90fd\u662f\u75c5\u82e6\u53ca\u8eab\uff0c\u77e5\u9053\u000d\u000a\u539f\u56e0\u4e86\u55ce\uff1f\u56e0\u70ba\uff0c\u4f46\u9858\u773e\u751f\u5f97\u96e2\u82e6\uff0c\uff08\u4e0d\u70ba\u81ea\u5df1\u6c42\u5b89\u6a02\uff09\u53ea\u6c42\u81ea\u5df1\u80fd\u5206\u6524\u000d\u000a\u696d\u2500\u2500\u773e\u751f\u7684\u696d\u529b\u4e00\u4e9b\u7d66\u81ea\u5df1\u6c92\u95dc\u4fc2\u3002\u000d\u000a\u6240\u4ee5\u5f92\u5152\u770b\u770b\uff0c\u5f88\u591a\u4fee\u9053\u770b\u8d77\u4f86\u597d\u50cf\u5f88\u8aa0\u5fc3\u7684\u4eba\uff0c\u600e\u9ebc\u9084\u751f\u75c5\u4e86\uff1f\u000d\u000a\u6703\u4e0d\u6703\u6709\u5f88\u591a\u7591\u554f\u5462\uff1f\u7576\u7136\u4e0d\u6703\uff0c\u56e0\u70ba\u5f92\u5152\u5011\u660e\u7406\uff0c\u662f\u4e0d\u662f\uff1f\u000d\u000a\u73fe\u4e0b\u7684\u773e\u751f\uff0c\u82e6\u4e0d\u82e6\uff1f\uff08\u82e6\uff09\u000d\u000a\u8eab\u4e5f\u82e6\uff0c\u5fc3\u4e5f\u82e6\uff0c\u8981\u600e\u9ebc\u904e\u4e0b\u53bb\uff1f","40\u000d\u000a\u6709\u8fa6\u6cd5\u2500\u2500\u5c31\u662f\u4fee\u9053\uff01\u000d\u000a\u56e0\u70ba\u4fee\u9053\uff0c\u90a3\u4e9b\u539f\u672c\u611f\u53d7\u5230\u7684\u82e6\uff0c\u662f\u4e0d\u662f\u6703\u88ab\u5149\u660e\u7684\u529b\u91cf\u7d66\u64ab\u5e73\u000d\u000a\u5462\uff1f\u6709\u6c92\u6709\u5e38\u5e38\u611f\u89ba\u5230\u5728\u5f92\u5152\u5011\u56f0\u96e3\u4ea4\u932f\u3001\u62ac\u4e0d\u8d77\u982d\u7684\u6642\u5019\uff0c\u5fc3\u4f4e\u6c23\u000d\u000a\u6cae\u5440\uff01\u5c31\u6709\u4e00\u500b\u5f88\u5927\u7684\u6eab\u6696\u3001\u6709\u5fb7\u4e4b\u529b\uff0c\u64ab\u5e73\u4e86\u4f60\u5fc3\u4e2d\u7684\u7591\u60d1\u548c\u96e3\u904e\u3002\u000d\u000a\u90a3\u5c31\u662f\u5f92\u5152\u5011\u7684\u524d\u4eba\u5728\u5e6b\u52a9\u5f92\u5152\uff0c\u7121\u8ad6\u662f\u54ea\u4e00\u4f4d\u524d\u4eba\uff0c\u90fd\u662f\u5c0d\u81ea\u000d\u000a\u5df1\u7684\u5f8c\u5b78\u5fc3\u75bc\u842c\u5206\u3002\u65e5\u65e5\u90fd\u60f3\u8457\u4f60\u5011\u5440\uff01\u5929\u5929\u90fd\u62dc\u8a2a\u4f60\u5011\u5440\uff01\u6240\u4ee5\u5f92\u000d\u000a\u5152\u5011\u4e0d\u80fd\u5920\u5fc3\u4e0d\u5728\u7109\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u4e7e\u25cb\u9054\u9ede\u50b3\u5e2b\u3001\u25cb\u9577\u9ede\u50b3\u5e2b\u53d6\u679c\u4e26\u6148\u60b2\uff1a\u62ff\u5169\u500b\uff08\u860b\u679c\uff09\u904e\u4f86\uff0c\u000d\u000a\u62ff\u4e09\u500b\uff08\u860b\u679c\uff09\u4f86\uff01\u679c\u62ff\u4e45\u4e86\uff0c\u9019\u500b\u679c\u5c31\u662f\u62ff\u6108\u4e45\u6108\u751c\u5462\uff01\u4f86\uff01\u000d\u000a\u25ce\u4ed9\u4f5b\u8cdc\u679c\u7d66\u25cb\u6674\u9ede\u50b3\u5e2b\u8207\u9ed1\u677f\u524d\u7684\u6284\u8a13\u53ca\u6587\u66f8\u524d\u8ce2\u4e26\u6148\u60b2\uff1a\u5f88\u591a\u5e55\u5f8c\u000d\u000a\u7684\u5de5\u4f5c\u9084\u8981\u5f92\u5152\u5011\u53bb\u505a\u4e86\uff0c\u6703\u7d2f\u3001\u6703\u8f9b\u82e6\uff01\u4f46\u662f\uff0c\u9019\u5c31\u662f\u300c\u4e86\u613f\u300d\u3002\u000d\u000a\u8acb\u4e7e\u5764\u73ed\u9577\u5230\u524d\u9762\u4ee3\u8868\u4f86\u5708\u500b\u5b57\u3002\u5148\u62ff\u7b46\uff01\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u4e7e 1-1\u25cb\u9cf4\u5708\u8a13\u3002","41\u000d\u000a\u3010\u9053\u5fc3\u5e73\u7a69\u5b9a \u4fee\u8005\u7acb\u613f\u884c\u3011\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u5764 1-1\u25cb\u5982\u5708\u8a13\u4e2d\u8a13\u3002\u000d\u000a\u3010\u6b63\u6cd5\u672b\u8870\u6fdf \u5df1\u5ff5\u6d41\u5f0a\u7701\u3011\u5708\u5f97\u5feb\u5462\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5169\u4f4d\u73ed\u9577\u6148\u60b2\uff1a\u600e\u9ebc\u56de\u5ea7\u4e86\u5462\uff1f\u6c92\u95dc\u4fc2\uff01\u770b\u8457\u5927\u5bb6\u3002\u000d\u000a\u73ed\u9577\u8a8d\u4e0d\u8a8d\u8b58\uff1f\uff08\u8a8d\u8b58\uff09\u6bcf\u4e00\u6b21\u7684\u73ed\u9577\u90fd\u4e0d\u4e00\u6a23\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u000d\u000a\u9019\u4ee3\u8868\u4ec0\u9ebc\uff1f\u50b3\u627f\u5440\uff1f\u4ee3\u8868\u4ec0\u9ebc\u5462\uff1f\u9019\u4ee3\u8868\uff1a\u9019\u5169\u4f4d\u90fd\u6709\u4f86\u53c3\u73ed\uff0c\u90fd\u000d\u000a\u6709\u4e00\u500b\u4f4d\u5b50\uff0c\u8ddf\u4f60\u5011\u90fd\u662f\u4e00\u6a23\u7684\u3002\u000d\u000a\u8ab0\u7576\u73ed\u9577\u90fd\u662f\u4e00\u6a23\uff0c\u807d\u7684\u8ab2\u6709\u4e0d\u4e00\u6a23\u55ce\uff1f\uff08\u6c92\u6709\uff09\u770b\u5230\u7684\u89d2\u5ea6\u6709\u4e0d\u000d\u000a\u4e00\u6a23\u55ce\uff1f\u6709\u554a\uff01\u4f4d\u5b50\u4e0d\u4e00\u6a23\uff0c\u89d2\u5ea6\u4e0d\u4e00\u6a23\u5440\uff01\u000d\u000a\u6709\u6c92\u6709\u5f92\u5152\u4e0a\u8ab2\u4e0a\u5230\u8116\u5b50\u75e0\u7684\u554a\uff1f\u60f3\u8981\u770b\u8b1b\u8005\uff0c\u4f46\u662f\u5750\u5f97\u592a\u65c1\u908a\u000d\u000a\u4e86\u3002\u4f46\u5df2\u7d93\u5f88\u597d\u4e86\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u53ef\u4ee5\u5927\u5bb6\u5728\u4e00\u65c1\u4e00\u8d77\u5b78\u7fd2\u3002\u597d\uff0c\u70ba\u000d\u000a\u5e2b\u958b\u500b\u73a9\u7b11\u554a\uff01\u8acb\u5750\uff01\u8acb\u5750\uff01\u9084\u6709\u54ea\u4f4d\u5f92\u5152\u60f3\u5708\u5462\uff1f\u000d\u000a\u25ce\u25cb\u826f\u9ede\u50b3\u5e2b\u8209\u8d77\u624b\u8a62\u554f\u73ed\u54e1\uff1a\u6709\u54ea\u4e00\u4f4d\u60f3\u5708\u8acb\u8209\u624b\u3002","42\u000d\u000a\u25ce\u8001\u5e2b\u770b\u5230\u25cb\u826f\u9ede\u50b3\u5e2b\u8209\u8d77\u624b\uff0c\u5c0d\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u5f92\u5152\uff0c\u4f60\u8209\u624b\u4e86\uff0c\u5c31\u662f\u000d\u000a\u5f92\u5152\u4e86\uff0c\u62ff\u7b46\u5427\uff01\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u25cb\u826f\u9ede\u50b3\u5e2b\u5708\u8a13\u3002\u000d\u000a\u3010\u5bdf\u5f7c\u56b4\u8cac\u5099 \u52d9\u5916\u8208\u9839\u98a8\u3011\u627e\u5f88\u4e45\u5462\uff01\u9019\u6a23\u5708\u5920\u4e0d\u5920\uff1f\uff08\u5920\uff09\u597d\u3002\u000d\u000a\u9084\u6709\u54ea\u4f4d\u5f92\u5152\u8981\u5708\u5462\uff1f\u597d\uff0c\u4f86\u4f86\u4f86\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u5764 5-5\u25cb\u83ef\u5708\u8a13\u3002\u000d\u000a\u3010\u5167\u5316\u904e\u6106\u6ecc \u8aa0\u61ff\u5fb7\u79c9\u660e\u3011\u9019\u6b21\u5c31\u4e0d\u767c\u679c\u4e86\uff0c\u4fee\u9053\uff0c\u8981\u7e7c\u7e8c\u4fee\uff0c\u000d\u000a\u70ba\u5e2b\u8b1b\u7684\u518d\u591a\uff0c\u9084\u662f\u4e0d\u96e2\u958b\u300c\u4fee\u300d\u3002\u597d\uff0c\u8acb\u8d77\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u5764\u6284\u8a13\u4eba\u54e1\u25cb\u6674\u63cf\u7e6a\u8a13\u4e2d\u8a13\u4e26\u6148\u60b2\uff1a\u5f92\u5152\u518d\u628a\u9019\u4e9b\u9023\u8d77\u4f86\u3002\u000d\u000a\u53ea\u8981\u771f\u5fc3\u5728\u4fee\u9053\u7684\u5f92\u5152\uff0c\u000d\u000a\u90fd\u662f\u5e38\u5e38\u548c\u70ba\u5e2b\u300c\u5fc3\u8cbc\u5fc3\u300d\u3002\u000d\u000a\u5f88\u591a\u6642\u5019\u6210\u5168\u4eba\u4e0d\u9700\u8981\u6587\u5b57\u8a00\u8a9e\uff0c\u000d\u000a\u5c31\u662f\u4e00\u7a2e\u2500\u2500","43\u000d\u000a\u5f92\u5152\u77e5\u9053\u70ba\u5e2b\u61c2\u5f92\u5152\uff0c\u70ba\u5e2b\u77e5\u9053\u5f92\u5152\u61c2\u70ba\u5e2b\u3002\u000d\u000a\u90a3\u9019\u5c31\u5920\u4e86\uff0c\u9001\u7d66\u5f92\u5152\u5011\u4e00\u500b\u300c\u771f\u300d\u5b57\u3002\u000d\u000a\u73fe\u4e0b\u662f\u4e0d\u662f\u5f88\u7f3a\u4e4f\u9019\u4e00\u500b\u300c\u771f\u300d\u5b57\u5462\uff1f\uff08\u662f\uff09\u6bcf\u4e00\u500b\u4eba\u7684\u5fc3\u90fd\u4e0d\u540c\uff0c\u000d\u000a\u5c31\u5982\u540c\u6bcf\u4e00\u500b\u4eba\u9762\u8c8c\u90fd\u9577\u5f97\u4e0d\u4e00\u6a23\u3002\u5230\u5e95\u6709\u8ab0\u662f\u771f\u5fc3\uff0c\u6709\u8ab0\u662f\u5047\u610f\u7684\u000d\u000a\u5462\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\u9053\u9ede\u50b3\u5e2b\u5011\u6148\u60b2\uff1a\u9ede\u50b3\u5f92\u5152\u770b\u5f97\u4e8b\u60c5\u591a\uff0c\u662f\u4e0d\u662f\u5f88\u591a\u4eba\u770b\u000d\u000a\u8d77\u4f86\u4e00\u958b\u59cb\u597d\u50cf\u5f88\u771f\uff0c\u5230\u6700\u5f8c\u5047\u610f\u8dd1\u51fa\u4f86\u4e86\uff1f\uff08\u662f\uff09\u5f88\u597d\uff0c\u5f92\u5152\u5011\u8d70\u5230\u000d\u000a\u73fe\u5728\uff0c\u90fd\u662f\u300c\u771f\u300d\u7684\u591a\u3002\u000d\u000a\u9053\u5fc3\u8981\u771f\uff0c\u81f3\u771f\u9022\u8003\u4e0d\u96e2\u9053\uff1b\u000d\u000a\u5fb7\u884c\u8981\u771f\uff0c\u81f3\u771f\u53d7\u8fb1\u4e0d\u5931\u5fb7\uff1b\u000d\u000a\u8aa0\u4fe1\u8981\u771f\uff0c\u81f3\u771f\u9006\u5883\u4e0d\u6096\u8aa0\uff1b\u000d\u000a\u613f\u529b\u8981\u771f\uff0c\u81f3\u771f\u9047\u9b54\u4e0d\u9055\u613f\uff1b\u000d\u000a\u606d\u656c\u8981\u771f\uff0c\u81f3\u771f\u805e\u6bc0\u4e0d\u6e1b\u606d\uff1b","44\u000d\u000a\u4fee\u6301\u8981\u771f\uff0c\u81f3\u771f\u56f0\u5384\u4e0d\u6028\u4fee\u3002\u000d\u000a\u9019\u9001\u7d66\u6bcf\u4e00\u4f4d\u5f92\u5152\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u56e0\u70ba\u73fe\u5728\u4e16\u9593\u5df2\u7d93\u6dea\u55aa\uff0c\u8981\u000d\u000a\u80fd\u5920\u505a\u5230\u9019\u516d\u9ede\u90fd\u771f\uff0c\u5c11\u4e4b\u53c8\u5c11\u3002\u5927\u591a\u90fd\u53ea\u662f\u505a\u4e00\u534a\uff1b\u6216\u8005\u662f\u525b\u8d77\u6b65\uff1b\u000d\u000a\u53c8\u6216\u8005\u967d\u5949\u9670\u9055\uff1b\u53c8\u6216\u8005\u64c5\u4f5c\u8a08\u8f03\uff1b\u53c8\u6216\u8005\u8868\u9762\u884c\u4e8b\uff1b\u53c8\u6216\u8005\u501f\u9053\u5229\u000d\u000a\u5df1\u3002\u000d\u000a\u5f92\u5152\u5011\u8aaa\u9019\u500b\u300c\u771f\u300d\u5b57\u91cd\u4e0d\u91cd\u8981\uff1f\uff08\u91cd\u8981\uff09\u000d\u000a\u7576\u5f92\u5152\u6162\u6162\u5bdf\u89ba\u5230\u81ea\u5df1\u7684\u771f\u5fc3\uff0c\u000d\u000a\u597d\u50cf\u4e00\u9ede\u4e00\u6ef4\u7684\u6d88\u78e8\u3001\u6d41\u901d\u7684\u6642\u5019\uff0c\u000d\u000a\u5c31\u8981\u5c0f\u5fc3\u4e86\u2500\u2500\u000d\u000a\u5c0f\u5fc3\u6210\u4e86\u300c\u4e0d\u771f\u7684\u4fee\u9053\u4eba\u300d\uff0c\u90a3\u9084\u7b97\u4fee\u9053\u4eba\u55ce\uff1f\uff08\u4e0d\u7b97\uff09\u000d\u000a\u8981\u4fee\u5c31\u8981\u4fee\u5f97\u771f\uff0c\u5982\u679c\u4e0d\u60f3\u8981\u4fee\u5f97\u771f\uff0c\u5c31\u4e0d\u8981\u8e0f\u5165\u4f5b\u9580\uff0c\u000d\u000a\u56e0\u70ba\u53cd\u800c\u6703\u803d\u8aa4\u773e\u751f\u7684\u6167\u547d\u3002\u000d\u000a\u8a31\u591a\u4eba\u4e00\u80a1\u8166\u7684\u60f3\u8981\u6210\u5c31\u81ea\u5df1\uff0c","45\u000d\u000a\u60f3\u8981\u5f9e\u4e2d\u7372\u5f97\u6210\u5c31\u611f\u3001\u69ae\u8b7d\u611f\uff0c\u6b8a\u4e0d\u77e5\u5fd8\u4e86\uff1a\u000d\u000a\u6700\u5927\u7684\u6210\u5c31\uff0c\u662f\u6210\u5c31\u773e\u751f\u7684\u6167\u547d\uff1b\u000d\u000a\u6700\u5ee3\u7684\u6148\u60b2\uff0c\u662f\u4e0d\u70ba\u81ea\u5df1\u7684\u6148\u60b2\u3002\u000d\u000a\u5f88\u591a\u7684\u9053\u5b78\u540d\u8a5e\uff0c\u90fd\u88ab\u4eba\u7528\u4f86\u6eff\u8db3\u81ea\u5df1\u7684\u91ce\u5fc3\u3002\u000d\u000a\u5728\u9019\u500b\u6642\u5019\uff0c\u5f92\u5152\u5011\u9858\u4e0d\u9858\u610f\u2500\u2500\u000d\u000a\u300c\u91cd\u632f\u9053\u5fd7\uff0c\u5171\u6e21\u672b\u52ab\u300d\uff1f\uff08\u9858\u610f\uff09\u000d\u000a\u82e5\u8981\u5171\u6e21\u9019\u500b\u672b\u52ab\uff0c\u5f92\u5152\u5011\u662f\u7f3a\u4e00\u90fd\u4e0d\u53ef\u3002\u4e0d\u8ad6\u5e74\u7d00\u8f15\u3001\u5c11\u3001\u9577\uff0c\u000d\u000a\u5728\u9019\u500b\u672b\u52ab\u95dc\u88e1\uff0c\u5171\u53d7\u8003\u3001\u5171\u53d7\u696d\uff0c\u5e0c\u671b\u5f92\u5152\u5011\u80fd\u5920\u4e92\u52a9\u3002\u000d\u000a\u5f92\u5152\u5011\u9858\u610f\u5e6b\u52a9\u8eab\u65c1\u7684\u4eba\u55ce\uff1f\uff08\u9858\u610f\uff09\u9019\u9ebc\u5c0f\u8072\u5440\uff1f\uff08\u9858\u610f\uff09\u96d6\u7136\u000d\u000a\u8072\u97f3\u4e0d\u662f\u5f88\u5927\uff0c\u4f46\u662f\u70ba\u5e2b\u807d\u5230\u5f92\u5152\u5011\u7684\u300c\u5fc3\u97f3\u300d\u5f88\u5927\u2500\u2500\u5fc3\u88e1\u9762\u767c\u51fa\u000d\u000a\u60f3\u8981\u62ef\u6551\u672b\u4e16\u7684\u8072\u97f3\u662f\u85cf\u4e0d\u4f4f\u7684\u3002\u000d\u000a\u8ab0\u6709\u591a\u5927\u7684\u5fd7\u5411\uff1f\u8ab0\u6709\u591a\u5927\u7684\u613f\u529b\uff1f\u70ba\u5e2b\u4e00\u807d\u4fbf\u66c9\u3002","46\u000d\u000a\u4e26\u4e0d\u662f\u807d\u53e3\u4e0a\u600e\u9ebc\u8b1b\uff0c\u000d\u000a\u800c\u662f\u807d\u5f92\u5152\u5fc3\u88e1\u9762\u7684\u8072\u97f3\u600e\u9ebc\u767c\u2500\u2500\u000d\u000a\u5fc3\u88e1\u9762\u7684\u8072\u97f3\u662f\u779e\u4e0d\u904e \u4e0a\u5929\u7684\u3002\u000d\u000a\u4f46\u662f\uff0c\u9019\u500b\u300c\u5fc3\u88e1\u9762\u7684\u8072\u97f3\u300d\u5f92\u5152\u5011\u8981\u6642\u5e38\u6aa2\u9a57\u3001\u6aa2\u9a57\u5440\uff0c\u597d\u4e0d\u000d\u000a\u597d\uff1f\uff08\u597d\uff09\u5982\u679c\uff0c\u4e0d\u5c0f\u5fc3\u6dfb\u5165\u4e86\u4e00\u9ede\u300c\u4eba\u610f\u300d\u7684\u8072\u97f3\uff0c\u8981\u4e0d\u8981\u8d95\u5feb\u8acb\u5b83\u000d\u000a\u96e2\u958b\u5440\uff1f\u000d\u000a\u5167\u5fc3\u8981\u4e7e\u4e7e\u6de8\u6de8\uff0c\u624d\u80fd\u5920\u505a\u5230\u9019\u500b\u300c\u771f\u300d\u3002\u000d\u000a\u672b\u4e16\u8981\u5b88\u771f\uff0c\u662f\u4e00\u5927\u96e3\u95dc\uff01\u000d\u000a\u8ab0\u5b88\u5f97\u4f4f\u771f\uff1f\u4e0d\u77e5\u9053\uff1b\u000d\u000a\u8ab0\u6e21\u5f97\u904e\u672b\u52ab\u5e74\uff1f\u70ba\u5e2b\u77e5\u9053\uff01\u000d\u000a\u662f\u8ab0\u5462\uff1f\u90fd\u60f3\u807d\u5230\u81ea\u5df1\u7684\u540d\u5b57\uff0c\u000d\u000a\u4f46\uff0c\u70ba\u5e2b\u53ea\u80fd\u8aaa\u2500\u2500\u000d\u000a\u9053\u5fc3\u5805\u56fa\uff0c\u4e0d\u53d7\u9b54\u4fb5\u7684\u4eba\uff0c\u5b9a\u6e21\u672b\u52ab\u5e74\u3002","47\u000d\u000a\u60f3\u60f3\u770b\u81ea\u5df1\uff0c\u8981\u505a\u90a3\u4e00\u500b\u53ef\u4ee5\u5728\u7d42\u9ede\u548c\u524d\u4eba\u6703\u9762\u7684\u4eba\uff0c\u000d\u000a\u9084\u662f\u8981\u5728\u6700\u5f8c\uff0c\u770b\u8457\u524d\u9762\u4e00\u7247\u6f06\u9ed1\uff0c\u000d\u000a\u5c31\u77e5\u9053\u81ea\u5df1\u300c\u8d70\u932f\u8def\u300d\u4e86\u3002\u000d\u000a\u25ce\u8001\u5e2b\u6279\u8a13\u4e2d\uff0c\u8acb\u25cb\u826f\u9ede\u50b3\u5e2b\u6148\u60b2\u8056\u8a13\uff1a\u5f92\u5152\u8b1b\u8b1b\u8a71\u554a\uff01\u000d\u000a\u25cb\u826f\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u611f\u89ba\u4eca\u5929\u8001\u5e2b\u8ddf\u6211\u5011\u8b1b\u7684\u8a71\uff0c\u5927\u5bb6\u53d7\u7528\u4e0d\u53d7\u7528\u554a\uff1f\uff08\u53d7\u7528\uff09\u000d\u000a\u597d\u50cf\u6211\u5011\u53ef\u4ee5\u5728\u6211\u5011\u751f\u6d3b\u88e1\u9762\u53bb\u5be6\u8e10\u3002\u525b\u525b\u524d\u9762\u9019\u516d\u500b\u771f\uff1a\u000d\u000a\u300c\u9053\u5fc3\u8981\u771f\uff0c\u81f3\u771f\u9022\u8003\u4e0d\u96e2\u9053\u3002\u300d\u6211\u5011\u78b0\u5230\u4e00\u4e9b\u4e0d\u9806\u3001\u8003\u9a57\u6703\u4e0d\u6703\u62b1\u6028\uff1f\u62b1\u000d\u000a\u6028\u8aaa\uff1a\u6211\u4e0d\u4fee\u4e86\uff01\u5c31\u96e2\u9053\u4e86\uff0c\u662f\u4e0d\u662f\uff1f\u4ee5\u524d\u767d\u6c34\u8001\u4eba\u8b1b\uff1a\u4f60\u4e0d\u4fee\uff0c\u798f\u5c0f\u547d\u8584\u3002\u000d\u000a\u300c\u5fb7\u884c\u8981\u771f\uff0c\u81f3\u771f\u53d7\u8fb1\u4e0d\u5931\u5fb7\u3002\u300d\u4eba\u5bb6\u662f\u4e82\u4e03\u516b\u7cdf\uff0c\u7f75\u4f60\u3001\u6bc0\u8b17\u4f60\u2018\u4f60\u80fd\u4e0d\u000d\u000a\u80fd\u4e0d\u8981\u53bb\u6bc0\u8b17\u5225\u4eba\uff1f\u4f60\u8b1b\u6211\u3001\u6211\u8b1b\u4f60\uff0c\u9019\u5c31\u5931\u5fb7\u4e86\u3002\u4eba\u5bb6\u8aaa\u6211\u5011\uff0c\u6211\u4e0d\u8aaa\u4eba\u5bb6\uff0c\u000d\u000a\u5b88\u4f4f\u90a3\u500b\u9053\u5fb7\u7684\u898f\u7bc4\u3002\u000d\u000a\u300c\u8aa0\u4fe1\u8981\u771f\uff0c\u81f3\u771f\u9006\u5883\u4e0d\u6096\u8aa0\u3002\u300d\u5728\u9006\u5883\u4e0d\u9806\u7684\u6642\u5019\uff0c\u525b\u624d\u8001\u5e2b\u4e0d\u662f\u8b1b\u55ce\uff1f\u000d\u000a\u78b0\u5230\u4e00\u500b\u4e8b\u60c5\u8981\u8f49\u5f4e\uff0c\u4ec0\u9ebc\u53eb\u8f49\u5f4e\u5462\uff1f\u5c31\u662f\u7070\u8272\u5730\u5e36\uff0c\u9055\u80cc\u81ea\u5df1\u7684\u539f\u5247\u4e86\uff1b\u8aa0\u4fe1\uff0c","48\u000d\u000a\u5c31\u6096\u4fe1\u3001\u6096\u8aa0\u4e86\uff0c\u597d\u50cf\u9019\u500b\u6642\u5019\u5c31\u4e0d\u8aa0\u4e86\uff0c\u9019\u6a23\u5b50\u6211\u9084\u53ef\u4ee5\u8aa0\u55ce\uff1f\u8aa0\u4e0d\u662f\u50bb\u74dc\u000d\u000a\u55ce\uff1f\u000d\u000a\u300c\u613f\u7acb\u8981\u771f\uff0c\u81f3\u771f\u9047\u9b54\u4e0d\u9055\u613f\u3002\u300d\u613f\u8981\u7acb\u7684\u771f\uff0c\u9047\u5230\u9b54\u4e0d\u9055\u80cc\u81ea\u5df1\u7684\u613f\u3002\u9b54\u000d\u000a\u662f\u4ec0\u9ebc\u5462\uff1f\u5c31\u662f\u4e0d\u8b1b\u7406\u7684\uff0c\u5c31\u662f\u8b1b\u4e0d\u901a\u7684\uff0c\u600e\u9ebc\u6703\u9019\u6a23\uff1f\u4ed6\u5c31\u662f\u4f86\u8003\u9a57\u4f60\u7684\u3002\u000d\u000a\u300c\u606d\u656c\u8981\u771f\uff0c\u81f3\u771f\u805e\u6bc0\u4e0d\u6ec5\u606d\u3002\u300d\u4eba\u5bb6\u6bc0\u8b17\u6211\u5011\uff0c\u6211\u5011\u9084\u6709\u79ae\u8c8c\u3001\u4fdd\u6301\u79ae\u8c8c\u3002\u000d\u000a\u6bc0\u8b17\u6211\u5011\u5c31\u6fc0\u61a4\uff0c\u5c31\u4e0d\u884c\u4e86\u3002\u606d\u656c\u5fc3\uff0c\u525b\u624d\u8001\u5e2b\u8b1b\u4e86\uff0c\u6211\u5011\u9762\u5c0d\u6211\u5011\u5bb6\u88e1\u4eba\u7684\u6642\u000d\u000a\u5019\uff0c\u600e\u9ebc\u8b1b\u8a71\u3001\u4ec0\u9ebc\u8a9e\u8abf\uff0c\u4ec0\u9ebc\u614b\u5ea6\uff0c\u5931\u53bb\u606d\u656c\u5fc3\u4e86\u3002\u000d\u000a\u300c\u4fee\u6301\u8981\u771f\uff0c\u81f3\u771f\u56f0\u5384\u4e0d\u6028\u4fee\u3002\u300d\u597d\u50cf\u525b\u624d\u6211\u5011\u8aaa\uff0c\u6211\u5011\u4fee\u9053\u5e0c\u671b\u6539\u8b8a\u547d\u904b\uff0c\u000d\u000a\u53ef\u662f\u4fee\u9053\u6108\u4fee\u6108\u56f0\u3001\u6108\u4fee\u6108\u4e0d\u9806\uff0c\u5c31\u60f3\uff1a\u54ce\u5440\uff01\u5fc3\u88e1\u9762\u597d\u591a\u62b1\u6028\u554a\uff0c\u6211\u9019\u9ebc\u8aa0\uff1b\u000d\u000a\u54c7\uff01\u5929\u5728\u54ea\u88e1\u554a\uff1f\u4f5b\u5728\u54ea\u88e1\u554a\uff1f\u600e\u9ebc\u90fd\u6c92\u770b\u5230\u6211\u9019\u9ebc\u82e6\u554a\uff1f\u5c31\u6028\u3002\u6211\u4fee\u7684\u9019\u9ebc\u000d\u000a\u771f\uff0c\u6211\u9019\u9ebc\u72a7\u7272\u5949\u737b\uff0c\u70ba\u4ec0\u9ebc \u4e0a\u5929\u9019\u6a23\u5c0d\u6211\u554a\uff1f\u9019\u5c31\u662f\u6028\u554a\uff01\u81f3\u771f\u7684\u8a71\uff0c\u4e0d\u6703\u000d\u000a\u6028\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u25cb\u9054\u9ede\u50b3\u5e2b\u6148\u60b2\u5206\u4eab\u8056\u8a13\uff1a\u000d\u000a\u597d\uff01\u63db\u4e0b\u4e00\u4f4d\u8b1b\u3002\u4eba\u4eba\u6709\u6a5f\u6703\u554a\uff01\u9053\u5834\u5171\u540c\u5206\u64d4\u8cac\u4efb\u3002","49\u000d\u000a\u25cb\u9054\u9ede\u50b3\u5e2b\uff1a\u300c\u4eba\u5fc3\u591a\u6d6e\u52d5\uff0c\u4e94\u97f3\u4ee4\u8033\u807e\u3002\u300d\u8001\u5e2b\u6148\u60b2\u8aaa\u4e94\u8272\u4ee4\u4eba\u76ee\u76f2\uff1b\u4e94\u000d\u000a\u97f3\u4ee4\u4eba\u8033\u807e\uff1b\u4e94\u5473\u4ee4\u4eba\u53e3\u723d\u3002\u73fe\u5728\u9019\u500b\u4e16\u754c\u7433\u746f\u6eff\u76ee\u3001\u82b1\u82b1\u4e16\u754c\uff0c\u662f\u4e0d\u662f\u5f88\u5438\u5f15\u000d\u000a\u4eba\uff1f\uff08\u662f\uff09\u6240\u4ee5\u8aaa\u6211\u5011\u5e38\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u6211\u5011\u4eba\u5fc3\u5c31\u51fa\u4f86\u4e86\uff0c\u56e0\u70ba\u96e3\u5f97\u6211\u5011\u4f86\u958b\u9019\u500b\u000d\u000a\u6cd5\u6703\uff0c\u57f9\u990a\u9019\u500b\u9053\u5fc3\u6ecb\u6f64\uff0c\u8b93\u5b83\u6df1\u6839\u767c\u82bd\uff0c\u6211\u5011\u5e7c\u82d7\u7684\u6210\u9577\uff0c\u5c31\u662f\u8981\u4f5b\u5149\u666e\u7167\u3002\u000d\u000a\u5e38\u5e38\u63a5\u8fd1\u4f5b\u5802\uff0c\u6211\u5011\u81ea\u5df1\u624d\u80fd\u5920\u628a\u9019\u500b\u5e7c\u82d7\u57f9\u80b2\u7684\u6210\u9577\u3001\u8301\u58ef\u3001\u958b\u82b1\u3001\u7d50\u679c\uff0c\u662f\u000d\u000a\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u300c\u8ff7\u60d1\u5883\u4e2d\u4e0d\u77e5\u904e\u4e86\u6625\u79cb\u591a\u5c11\u51ac\u3002\u300d\u8207\u5929\u5730\u5408\u5176\u5fb7\uff0c\u8207\u65e5\u6708\u5408\u5176\u660e\uff0c\u8207\u56db\u6642\u000d\u000a\u5408\u5176\u5e8f\u3002\u3010\u8a3b\u4e5d\u3011\u6211\u5011\u6bcf\u4e00\u500b\u4eba\u5728\u5929\u5730\u4e4b\u9593\u7684\u751f\u9577\uff0c\u5929\u8986\u5730\u8f09\uff0c\u5929\u90a3\u9ebc\u5bec\u95ca\uff0c\u4f46\u000d\u000a\u662f\u6211\u5011\u4eba\u5fc3\u5f88\u72f9\u7a84\u3002\u6211\u5011\u9019\u6a23\u53c8\u904e\u4e86\u591a\u5c11\u51ac\uff1f\u591a\u5c11\u6625\u5929\uff1f\u4e00\u65e5\u904e\u4e00\u65e5\u3002\u6240\u4ee5\u8aaa\uff1a\u000d\u000a\u300c\u8fc5\u901f\u5149\u9670\u4e0d\u53ef\u7559\uff0c\u5e74\u5e74\u53ea\u898b\u6c34\u6771\u6d41\uff1b\u4e0d\u4fe1\u8a66\u628a\u9752\u83f1\u7167\uff0c\u6614\u65e5\u6731\u984f\u4eca\u767d\u982d\u3002\u300d\u3010\u8a3b\u000d\u000a\u5341\u3011\u5feb\u4e0d\u5feb\uff1f\u000d\u000a\u597d\uff0c\u4f73\u53e5\u5c31\u756b\u4e00\u500b\u53e5\u9ede\uff0c\u518d\u63db\u4e0b\u4e00\u4f4d\u3002\u63a5\u529b\u8cfd\u554a\uff01\u90fd\u8b1b\u5f97\u597d\u554a\uff01\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u25cb\u9577\u9ede\u50b3\u5e2b\u6148\u60b2\u5206\u4eab\u8056\u8a13\uff1a","50\u000d\u000a\u25cb\u9577\u9ede\u50b3\uff1a\u300c\u6642\u81f3\u672b\u5f8c\uff0c\u767e\u6b65\u4e5d\u6298\u5f4e\u5761\u3002\u300d\u73fe\u5df2\u7d93\u5230\u6700\u672b\u5f8c\u4e86\uff0c\u4eba\u5fc3\u771f\u7684\u662f\u000d\u000a\u597d\u50cf\u90fd\u6c89\u6eba\u5728\u6c34\u88e1\u4e00\u6a23\uff0c\u9748\u6027\u627e\u4e0d\u56de\u4f86\u4e86\u3002\u6240\u4ee5\u767e\u6b65\u4e5d\u6298\u5f4e\u5761\uff0c\u6709\u7684\u4eba\u8d70\u4e00\u767e\u6b65\uff0c\u000d\u000a\u5c31\u9000\u56de\u4e5d\u5341\u6b65\uff0c\u5c31\u5012\u9000\u8d70\uff0c\u628a\u90a3\u672c\u4f86\u662f\u5f88\u771f\u8aa0\u7684\u5fc3\uff0c\u5df2\u7d93\u9000\u6389\u4e86\u3002\u000d\u000a\u300c\u56db\u842c\u516b\u5343\u5f15\u8a98\u3002\u300d\u6bcf\u4e00\u500b\u4eba\u4e00\u5929\u5230\u665a\u52d5\u4e86\u5e7e\u500b\u5ff5\u982d\uff0c\u6578\u4e0d\u6e05\u5c0d\u55ce\uff1f\u7e3d\u800c\u8a00\u000d\u000a\u4e4b\uff0c\u5dee\u4e0d\u591a\u662f\u56db\u842c\u516b\u5343\u7684\u601d\u7dd2\u3001\u5ff5\u982d\uff1b\u56e0\u70ba\u6211\u5011\u773c\u5c0d\u5916\u770b\uff0c\u5931\u6389\u4e86\u672c\u771f\u3002\u000d\u000a\u300c\u5e7e\u500b\u5f92\u5152\u660e\u61c2\u3002\u300d\u6240\u4ee5\u8001\u5e2b\u5f88\u611f\u6168\uff0c\u56e0\u6bcf\u4e00\u500b\u4eba\u6c42\u4e86\u9053\uff0c\u627e\u5230\u81ea\u5df1\u7684\u826f\u77e5\uff0c\u000d\u000a\u6240\u4ee5\u6211\u5011\u8981\u5b88\u8457\u6bcf\u4e00\u500b\u4eba\u7684\u771f\u672c\u5fc3\uff0c\u6240\u8b02\u4fee\u9053\u5c31\u662f\u300c\u62b1\u8457\u826f\u5fc3\u505a\u4eba\u3002\u300d\u000d\u000a\u597d\uff01\u7d50\u675f\u5728\u5b88\uff0c\u5b88\u6210\u5c31\u662f\u4fee\u9053\u6709\u6210\u3002\u4e0b\u4e00\u4f4d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u25cb\u677e\u9ede\u50b3\u5e2b\u6148\u60b2\u5206\u4eab\u8056\u8a13\uff1a\u000d\u000a\u25cb\u677e\u9ede\u50b3\u5e2b\uff1a\u300c\u6025\u5982\u65cb\u98a8\uff0c\u5982\u9677\u4e7e\u8349\u9ede\u706b\u3002\u300d\u6211\u5011\u4fee\u9053\uff0c\u9019\u5929\u6642\u5f88\u7dca\u6025\uff0c\u5c31\u000d\u000a\u50cf\u65cb\u98a8\u4e00\u6a23\uff0c\u6240\u4ee5\u5e0c\u671b\u6211\u5011\u6bcf\u4e00\u4f4d\u524d\u8ce2\uff0c\u6211\u5011\u4fee\u9053\u8981\u5c0f\u5fc3\uff0c\u975e\u5e38\u5371\u96aa\uff0c\u50cf\u4e7e\u8349\u9ede\u000d\u000a\u706b\u4e00\u6a23\uff0c\u6211\u5011\u5982\u679c\u770b\u5230\u7a2e\u7530\u7684\uff0c\u90a3\u500b\u82e5\u9ede\u706b\uff0c\u6574\u7247\u90fd\u71d2\u5149\u4e86\u4f60\u770b\u3002\u300c\u55df\u5606\u9b54\u5f37\u800c\u000d\u000a\u6cd5\u5f31\u3002\u300d\u2026\u2026","51\u000d\u000a\u6709\u56f0\u96e3\u55ce\uff1f\uff08\u25cb\u677e\u9ede\u50b3\u5e2b\uff1a\u7a0d\u5fae\u56f0\u96e3\uff0c\u770b\u4e0d\u6e05\uff09\u90a3\u5f92\u5152\uff0c\u9019\u500b\u300c\u771f\u300d\u5b57\u000d\u000a\u5920\u4e0d\u5920\u5927\uff1f\uff08\u25cb\u677e\u9ede\u50b3\u5e2b\uff1a\u5927\uff09\u5c31\u8b1b\u300c\u771f\u300d\u3002\u000d\u000a\u25cb\u677e\u9ede\u50b3\u5e2b\uff1a\u597d\uff0c\u771f\uff01\u6211\u5011\u4fee\u9053\uff0c\u9053\u771f\u7406\u771f\u5929\u547d\u771f\uff0c\u6211\u5011\u5fc3\u4e00\u5b9a\u8981\u771f\uff0c\u8981\u771f\u000d\u000a\u8aa0\u3002\u5fc3\u771f\u4e00\u5207\u771f\uff0c\u6240\u4ee5\u6211\u5011\u5e0c\u671b\u6bcf\u4e00\u4f4d\u524d\u8ce2\u5728\u9019\u500b\u4eba\u4e16\u9593\u771f\u8aa0\u5f85\u4eba\uff0c\u7136\u5f8c\u8981\u5fe0\u65bc\u000d\u000a\u9053\u5834\uff0c\u5c24\u5176\u6211\u5011\u4e00\u689d\u91d1\u7dda\uff0c\u4e00\u5b9a\u8981\u597d\u597d\u7684\u7dca\u7dca\u6293\u4f4f\u3002\u56e0\u70ba\u771f\u7684\u5047\u4e0d\u4e86\uff0c\u5047\u7684\u771f\u4e0d\u000d\u000a\u4e86\u3002\u4f60\u5982\u679c\u5047\u4e86\uff0c\u6709\u4e00\u5929\u4f60\u81ea\u500b\u5152\u5c31\u96e2\u958b\u4e86\u3002\u5e0c\u671b\u6211\u5011\u6bcf\u4e00\u4f4d\u524d\u8ce2\uff0c\u4f60\u771f\uff0c\u5bb6\u5ead\u000d\u000a\u624d\u6703\u771f\uff0c\u5927\u5bb6\u771f\u8aa0\u76f8\u5f85\uff0c\u5c24\u5176\u5144\u5f1f\u59d0\u59b9\uff0c\u6211\u5011\u8981\u4e92\u76f8\u5fcd\u8fb1\uff0c\u5f88\u91cd\u8981\uff0c\u8b1b\u8a71\u8981\u548c\u548c\u000d\u000a\u6c23\u6c23\uff0c\u50cf\u6211\u5011\u524d\u4eba \u81f3\u5fb7\u5927\u5e1d\u4e00\u6a23\uff0c\u8981\u548c\u548c\u6c23\u6c23\uff0c\u56e0\u70ba\u552f\u6709\u548c\u6c23\uff0c\u5927\u5bb6\u624d\u4e0d\u6703\u722d\u000d\u000a\u5435\u3002\u5e0c\u671b\u6211\u5011\u6bcf\u4e00\u4f4d\u524d\u8ce2\uff0c\u6211\u5011\u5728\u9019\u500b\u5927\u5bb6\u5ead\u7576\u4e2d\uff0c\u8981\u628a\u4f5b\u5802\u6240\u5b78\u7684\u79ae\u7bc0\uff0c\u8981\u5e36\u000d\u000a\u5230\u5bb6\u88e1\uff0c\u5c31\u597d\u6bd4\u8aaa\uff0c\u6211\u5011\u4f86\u4f5b\u5802\u5b78\u9019\u500b......\u000d\u000a\u7b49\u4e00\u4e0b\uff0c\u518d\u8b1b\uff0c\u5f92\u5152\u7684\u8ab2\u9084\u8981\u8b1b\u55ce\uff1f\uff08\u25cb\u677e\u9ede\u50b3\u5e2b\uff1a\u5f92\u5152\u7684\u8ab2\uff0c\u4e0d\u7528\u000d\u000a\u8b1b\u4e86\u5427\uff1f\uff09\u4e0b\u4e00\u4f4d\u3001\u4e0b\u4e00\u4f4d\u3002\u7559\u8457\u5230\u6642\u5019\u8b1b\uff0c\u7cbe\u83ef\u90fd\u8b1b\u5b8c\u4e86\u3002\u000d\u000a\u25ce\u25cb\u826f\u9ede\u50b3\u5e2b\uff1a\u8001\u5e2b\u61c2\u4e0d\u61c2\u5f92\u5152\uff1f\uff08\u61c2\uff09\u56e0\u70ba\u4e0b\u9762\u4e00\u5802\u8ab2\u5c31\u662f\u25cb\u677e\u7d93\u7406\u7684\u000d\u000a\u8ab2\uff0c\u8001\u5e2b\u64d4\u5fc3\u4ed6\u90fd\u8b1b\u5b8c\u4e86\uff0c\u4e0b\u9762\u6c92\u6771\u897f\u8b1b\u4e86\u3002\u8001\u5e2b\u5920\u4e0d\u5920\u5e7d\u9ed8\uff1f\uff08\u5920\uff09","52\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u25cb\u745e\u9ede\u50b3\u5e2b\u6148\u60b2\u8056\u8a13\uff1a\u518d\u4f86\u554a\uff0c\u25cb\u745e\u5f92\u5152\u4e5f\u4f86\u8b1b\u554a\uff01\u5927\u5bb6\u90fd\u000d\u000a\u5b78\u3002\u000d\u000a\u25cb\u745e\u9ede\u50b3\u5e2b\uff1a\u4eca\u5929\u7684\u8a13\u4e2d\u8a13\u662f\u771f\uff0c\u90a3\u6211\u5011\u6d3b\u4f5b\u8001\u5e2b......\u000d\u000a\u7b49\u7b49\u7b49\uff0c\u300c\u771f\u300d\u525b\u624d\u8b1b\u904e\u4e86\uff0c\u8981\u7d66\u5f92\u5152\u96e3\u4e00\u9ede\u7684\u3002\u8b1b\u8b1b\u770b\u300c\u4fee\u9053\u000d\u000a\u4f55\u70ba\u9996\uff1f\u300d\u4ec0\u9ebc\u662f\u4fee\u9053\u6700\u91cd\u8981\u7684\uff1f\u000d\u000a\u25cb\u745e\u9ede\u50b3\u5e2b\uff1a\u662f\u662f\u662f\u3002\u4ec0\u9ebc\u53eb\u4fee\u9053\uff1f\u5176\u5be6\u4fee\u9053\u5c31\u662f\u5728\u4fee\u6211\u5011\u7684\u7a1f\u6027\u3002\u6211\u5011\u7684\u000d\u000a\u7fd2\u6c23\u3001\u6bdb\u75c5\u4e00\u5927\u5806\uff1b\u5982\u679c\u7fd2\u6c23\u3001\u6bdb\u75c5\uff0c\u6211\u5011\u7d2f\u4e16\u4f86\u7684\u7a1f\u6027\uff0c\u5982\u679c\u4e0d\u9664\u3001\u4e0d\u4fee\uff0c\u90a3\u000d\u000a\u6c92\u6709\u8fa6\u6cd5\u8ac7\u4fee\u9053\u3002\u6240\u4ee5\u4fee\u9053\u4f55\u70ba\u9996\uff1f\u5c31\u662f\u8981\u300c\u660e\u7406\u65b9\u5bf8\u5b88\u300d\u3002\u65b9\u5bf8\u5c31\u662f\u6211\u5011\u7684\u672c\u000d\u000a\u6027\u3001\u9748\u6027\uff1b\u6240\u4ee5\u4fee\u9053\u8981\u660e\u7406\uff0c\u660e\u7406\u9084\u8981\u901a\u7406\u3002\u4e00\u822c\u7684\u4eba\u8b1b\u300c\u660e\u7406\u3001\u660e\u7406\u300d\u5f88\u7c21\u55ae\uff0c\u000d\u000a\u5176\u5be6\u6211\u5011\u4eca\u5929\u4fee\u9053\uff0c\u771f\u6b63\u8981\u660e\u7406\u662f\u4e0d\u5bb9\u6613\uff0c\u901a\u7406\u624d\u80fd\u901a\u9054\u4e00\u5207\u9053\u7406\uff1b\u5f9e\u6211\u5011\u81ea\u5df1\u000d\u000a\u7684\u672c\u6027\u826f\u77e5\u505a\u8d77\uff0c\u5fb9\u5e95\u7684\u5c07\u6211\u5011\u7684\u7fd2\u6c23\u6bdb\u75c5\u628a\u5b83\u6539\u6389\u3002\u000d\u000a\u597d\uff01\u8b1b\u5f97\u5f88\u597d\u3002\u000d\u000a\u73fe\u5728\u9019\u500b\u8981\u4ea4\u7d66\u5927\u5bb6\uff0c\u5f9e\u300c\u4eba\u5fc3\u591a\u6d6e\u52d5\u300d\u5538\uff0c\u958b\u59cb\u3002","53\u000d\u000a\u25ce\u5168\u9ad4\u9f4a\u5538\u8056\u8a13\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\u25cb\u862d\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u000d\u000a\u70ba\u5e2b\u89aa\u611b\u7684\u5f92\u5152\u5440\uff0c\u8eab\u9ad4\u9084\u597d\u4e0d\u597d\u554a\uff1f\u9053\u5fc3\u6c38\u9060\u90fd\u5728\uff0c\u8eab\u9ad4\u5c31\u597d\u000d\u000a\u4e86\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\u25cb\u5b9c\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u5f92\u5152\u9084\u597d\u55ce\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\u25cb\u798e\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u6709\u6a5f\u6703\u5c31\u7e7c\u7e8c\u8dd1\u554a\uff01\u5e6b\u70ba\u5e2b\u505a\u66f4\u591a\u4e8b\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\u25cb\u83ef\u3001\u25cb\u9cf3\u3001\u25cb\u83ef\u3001\u25cb\u7d79\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u000d\u000a\u5f92\u5152\u5011\uff0c\u90fd\u8981\u628a\u63e1\u73fe\u5728\u7684\u6b72\u6708\uff0c\u5c07\u9019\u500b\u9053\u52d9\u7e7c\u7e8c\u52aa\u529b\u7684\u7c4c\u5283\uff0c\u8b93\u000d\u000a\u6bcf\u4e00\u500b\u84bc\u751f\u90fd\u5f97\u6c90\u7518\u9716\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u25cb\u826f\u9ede\u50b3\u5e2b\u6284\u8a13\uff0c\u4e26\u5c0d\u9ede\u50b3\u5e2b\u5011\u6148\u60b2\uff1a\u000d\u000a\u518d\u4f86\uff0c\u8acb\u25cb\u826f\u5f92\u5152\u62ff\u7b46\u3002\u525b\u624d\u8209\u7684\u624b\uff0c\u5c31\u4e0d\u8981\u8b93\u9019\u500b\u8209\u624b\u7d66\u767d\u8cbb\u000d\u000a\u6389\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u73fe\u5728\u5beb\u7684\u8981\u8a18\u4f4f\uff0c\u6bcf\u4e00\u4f4d\u9ede\u50b3\u5f92\u5152\u8981\u8a18\u4f4f\uff0c\u597d\u4e0d\u000d\u000a\u597d\uff1f\uff08\u597d\uff09","54\u000d\u000a\u3010\u7a4d\u6975\u63a8\u52d5 \u6148\u60b2\u6fdf\u4e16\u6e21\u773e\u3011\u000d\u000a\u597d\u4e45\u4ee5\u524d\u5beb\u904e\uff0c\u9694\u4e86\u591a\u5c11\u5e74\u4e86\uff0c\u5927\u5bb6\u5e74\u7d00\u90fd\u9577\u4e86\u3002\u4f46\u662f\uff0c\u80fd\u8fa6\u7684\u000d\u000a\u4e8b\u4e00\u6a23\u591a\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u56e0\u70ba\uff0c\u9053\u4e0d\u662f\u5e74\u7d00\uff0c\u6c38\u9060\u4e0d\u6703\u8870\u3002\uff08\u662f\uff09\u000d\u000a\u3010\u842c\u8056\u547d\u8108\u50b3\u85aa\u706b \u5f92\u5152\u5fc3\u624b\u540c\u821f\u3011\u000d\u000a\u9019\u662f\u4ec0\u9ebc\u610f\u601d\u5462\uff1f\u5c31\u662f\u9019\u500b\u9053\u52d9\u63a8\u52d5\u8981\u66f4\u7a4d\u6975\u4e86\uff0c\u6108\u662f\u7dca\u5f35\u7684\u6642\u000d\u000a\u671f\u6108\u8981\u7a4d\u6975\u3002\u000d\u000a\u9019\u500b\u85aa\u706b\uff0c\u5982\u679c\u4f60\u5011\u7684\u6839\u624e\u5f97\u7a69\uff0c\u000d\u000a\u6015\u98a8\u5439\u55ce\uff1f\uff08\u4e0d\u6015\uff09\u6015\u96e8\u6dcb\u55ce\uff1f\uff08\u4e0d\u6015\uff09\u5c0d\uff0c\u000d\u000a\u4e0d\u6015\u2500\u2500\u56e0\u70ba\u6709\u4f60\u5011\u5b88\u4f4f\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff0c\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u25ce\u8001\u5e2b\u62cd\u62cd\u4e7e\u25cb\u756a\u9ede\u50b3\u5e2b\u7684\u80a9\u4e26\u6148\u60b2\uff1a\u5f92\u5152\u4e5f\u662f\u5462\uff0c\u723e\u5011\u524d\u4eba\u4e5f\u8981\u4f60\u5011\u597d\u000d\u000a\u597d\u5b88\u4f4f\u3002\u000d\u000a\u5404\u4f4d\u5f92\u5152\u5011\uff0c\u662f\u4e0d\u662f\u66ab\u505c\u8072\u97f3\u7684\u6642\u9593\u592a\u4e45\u4e86\uff1f\u5e73\u5e38\u5728\u5bb6\u88e1\u9084\u6c92\u6709\u000d\u000a\u9019\u500b\u6a5f\u6703\u975c\u4e00\u975c\uff0c\u96e3\u5f97\u4f86\u5230\u5169\u5929\u6e05\u53e3\u6cd5\u6703\uff0c\u5927\u5bb6\u5750\u5728\u6905\u5b50\u4e0a\u4e5f\u8981\u975c\u4e00","55\u000d\u000a\u975c\u3002\u000d\u000a\u70ba\u5e2b\u77e5\u9053\u6bcf\u4e00\u4f4d\u5f92\u5152\u90fd\u6709\u751f\u6d3b\u8981\u9762\u5c0d\u7684\u7463\u4e8b\uff0c\u9019\u4e5f\u662f\u4e00\u5b9a\u8981\u53bb\u89e3\u000d\u000a\u6c7a\u7684\uff0c\u4e26\u4e0d\u662f\u8aaa\u773c\u4e0d\u898b\u5c31\u5fc3\u4e0d\u7169\u3001\u9003\u907f\uff0c\u61c9\u8a72\u8981\u60f3\u600e\u9ebc\u6a23\u53bb\u628a\u9019\u500b\u7d50\u000d\u000a\u6253\u958b\u3002\u000d\u000a\u7e3d\u800c\u8a00\u4e4b\uff0c\u8981\u80fd\u5920\u300c\u5c08\u6ce8\u8056\u4e8b\uff0c\u653e\u5c0f\u4fd7\u4e8b\u300d\uff0c\u000d\u000a\u4e0d\u662f\u8aaa\u5bb6\u88e1\u7684\u4e8b\u90fd\u4e0d\u505a\u4e86\uff0c\u000d\u000a\u800c\u662f\u5c07\u9019\u500b\u300c\u4fd7\u5fc3\u300d\u653e\u4e0b\uff0c\u000d\u000a\u9019\u6a23\u4f60\u505a\u7684\u4e8b\u5c31\u7b97\u662f\u5728\u5bb6\u88e1\u4e5f\u662f\u8056\u4e8b\u3002\u000d\u000a\u25ce\u9ede\u50b3\u5e2b\u6148\u60b2\u8acb\u793a\u8cdc\u58c7\u540d\uff1a\u000d\u000a\u25ce\u5409\u9686\u5761\u8b1d\u6c0f\u4f5b\u5802\u2500\u2500\u3010\u6e05\u6bd3\u4f5b\u5802\u3011\u000d\u000a\u4e0d\u7c21\u55ae\u554a\uff01\u6e05\u6bd3\u4f5b\u5802\u3002\u6e05\u975c\u7684\u6e05\uff0c\u937e\u9748\u6bd3\u79c0\u7684\u6bd3\u3002\u6e05\u6bd3\u4f5b\u5802\u3002\u000d\u000a\u25ce\u8001\u5e2b\u6148\u60b2\u8acb\u25cb\u826f\u9ede\u50b3\u5e2b\u8f49\u9054\u5409\u9686\u5761\u25cb\u6e05\u9ede\u50b3\u5e2b\uff1a","56\u000d\u000a\u4ea4\u4ee3\u25cb\u6e05\u5f92\u5152\uff0c\u521d\u4e00\u5341\u4e94\u6e96\u5099\u4e00\u4f9b\u6a58\u5b50\u3002\u5e0c\u671b\u5927\u5bb6\u90fd\u53ef\u4ee5\u5e73\u5b89\u5409\u000d\u000a\u7965\u3002\u000d\u000a\uff08\u9ede\u50b3\u5e2b\uff1a\u99ac\u4f86\u897f\u4e9e\u75ab\u60c5\u975e\u5e38\u7684\u56b4\u91cd\uff09\u000d\u000a\u73fe\u5728\uff0c\u4e0d\u6015\u5916\u5883\u7684\u56b4\u91cd\uff0c\u6015\u5fc3\u88e1\u9762\u7684\u75c5\u6bd2\u56b4\u91cd\u3002\u000d\u000a\u6709\u6642\u5019\uff0c\u5fc3\u88e1\u7684\u6050\u61fc\u52dd\u904e\u5916\u4f86\u7684\u58d3\u529b\uff0c\u000d\u000a\u6240\u4ee5\uff0c\u5e0c\u671b\u6d77\u5916\u7684\u5f92\u5152\uff0c\u5728\u9019\u6a23\u8a31\u591a\u7d1b\u4e82\u3001\u8a31\u591a\u5e72\u64fe\u7684\u5916\u5883\u4e4b\u4e0b\uff0c\u000d\u000a\u9053\u5fc3\u66f4\u8981\u63d0\u632f\u4e86\uff01\u000d\u000a\u70ba\u5e2b\u4e00\u518d\u5730\u8207\u5f92\u5152\u5011\u8aaa\u8981\u2500\u2500\u000d\u000a\u300c\u91cd\u632f\u9053\u5fc3\uff0c\u5171\u6e21\u672b\u52ab\u300d\uff0c\u000d\u000a\u4e0d\u662f\u53ea\u6709\u8ddf\u5728\u5ea7\u7684\u5f92\u5152\u8aaa\uff0c\u000d\u000a\u800c\u662f\u6d77\u5167\u3001\u6d77\u5916\uff0c\u6240\u6709\u7684\u5f92\u5152\u90fd\u8981\u4e00\u8d77\u300c\u5354\u529b\u540c\u5fc3\u300d\u3002\u000d\u000a\u25ce\u53f0\u5317\u5357\u6e2f\u67f3\u6c0f\u4f5b\u5802\u2500\u2500\u3010\u771f\u6bd3\u4f5b\u5802\u3011","57\u000d\u000a\u597d\u3002\u771f\u6bd3\u4f5b\u5802\u3002\u9019\u500b\u771f\uff0c\u540c\u6a23\u7684\u6bd3\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764 17-5\u25cb\u91cc\u58c7\u4e3b\u6148\u60b2\uff1a\u000d\u000a\u5f88\u597d\u3002\u4f86\u500b\u9cf3\u68a8\u9165\u3002\u4e00\u6574\u500b\u3001\u4e00\u76e4\u3002\u5f88\u591a\u5462\uff01\u597d\uff0c\u4e00\u500b\u4eba\u5403\u5f97\u5b8c\u000d\u000a\u55ce\uff1f\u9019\u9ebc\u591a\u5440\uff01\u6709\u6c92\u6709\u4eba\u60f3\u8981\u554a\uff1f\u6c92\u8fa6\u6cd5\uff0c\u4f5b\u5802\u958b\u904e\u4e86\u3002\u4f86\uff0c\u9019\u500b\u5f92\u000d\u000a\u5152\u50c5\u51c6\u5690\u4e00\u500b\u3002\u591a\u9080\u4e00\u4e9b\u5de6\u9130\u53f3\u820d\u3001\u89aa\u670b\u597d\u53cb\u4f86\u4f5b\u5802\u737b\u9999\u5c31\u7d66\u4ed6\u5011\uff0c\u000d\u000a\u6210\u5168\u9053\u89aa\u5c31\u5e36\u4e0a\u5e7e\u500b\uff0c\u597d\u55ce\uff1f\u5728\u671f\u9650\u5167\u767c\u5b8c\u5440\uff01\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u771f\u6bd3\u4f5b\u5802\uff0c\u5e0c\u671b\u5f92\u5152\u5e36\u8457\u771f\u5fc3\uff0c\u505a\u6bcf\u4e00\u4ef6\u4e86\u613f\u7684\u4e8b\u60c5\u3002\u5fc3\u771f\u4e86\uff0c\u000d\u000a\u4e86\u613f\u7684\u529f\u5fb7\u6703\u5047\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u662f\u5440\uff01\u8acb\u8d77\u5427\u3002\u000d\u000a\u5f92\u5152\u5011\uff0c\u70ba\u5e2b\u60f3\u8981\u518d\u807d\u5f92\u5152\u5011\u7684\u7b11\u8072\u554a\uff01\uff08\u54c8\u54c8\u54c8\uff01\uff09\u7b11\u5f97\u6bd4\u8f03\u958b\u000d\u000a\u5fc3\u4e86\uff0c\u56e0\u70ba\u77e5\u9053\u9019\u662f\u7d50\u5c3e\u7684\u7b11\u8072\u5440\uff01\u000d\u000a\u70ba\u5e2b\u975e\u5e38\u60f3\u8981\u6478\u6478\u6bcf\u4e00\u4f4d\u5f92\u5152\u7684\u80a9\u8180\uff0c\u4f46\u662f\u9019\u500b\u8089\u9ad4\u6709\u9650\uff0c\u6c92\u8fa6\u000d\u000a\u6cd5\u4e00\u4e00\u7684\u95dc\u5fc3\u3002\u4e0d\u904e\u5440\uff01\u70ba\u5e2b\u65e9\u5728\u6bcf\u4e00\u500b\u6642\u5019\uff0c\u90fd\u5230\u4f60\u5011\u7684\u5bb6\u88e1\u505a\u5ba2\u000d\u000a\u4e86\uff01","58\u000d\u000a\u70ba\u4ec0\u9ebc\u8aaa\u662f\u300c\u505a\u5ba2\u300d\u5462\uff1f\u000d\u000a\u505a\u5ba2\u505c\u7559\u5f97\u4e45\u55ce\uff1f\uff08\u4e0d\u4e45\uff09\u000d\u000a\u90a3\u70ba\u5e2b\u70ba\u4ec0\u9ebc\u505a\u5ba2\uff1f\u2500\u2500\u56e0\u70ba\u70ba\u5e2b\u770b\u5230\u4f60\u5011\uff0c\u000d\u000a\u5e38\u70ba\u4e86\u90a3\u4e00\u4e01\u9ede\u51e1\u9593\u7684\u4e8b\u60c5\uff0c\u5435\u5f97\u4e0d\u53ef\u958b\u4ea4\uff0c\u000d\u000a\u770b\u4e0d\u4e0b\u53bb\uff0c\u6240\u4ee5\u8981\u96e2\u958b\u4e00\u4e0b\u3002\u000d\u000a\u80fd\u4e0d\u80fd\u5920\u8b93\u70ba\u5e2b\u5728\u4f60\u5011\u5bb6\u88e1\uff0c\u5f85\u500b\u5e7e\u500b\u6708\u3001\u4e00\u5e74\u3001\u5169\u5e74\uff0c\u000d\u000a\u4e0d\u518d\u662f\u300c\u505a\u5ba2\u300d\u800c\u662f\u8b8a\u300c\u5bb6\u4eba\u300d\uff0c\u000d\u000a\u90a3\u5c31\u8981\u770b\u770b\u6bcf\u4e00\u4f4d\u5f92\u5152\uff0c\u000d\u000a\u5728\u81ea\u5df1\u7684\u4fee\u6301\u4e0a\u60f3\u8981\u505a\u4ec0\u9ebc\u6a23\u7684\u300c\u5713\u878d\u300d\u3002\u000d\u000a\u4e26\u4e0d\u662f\u70ba\u5e2b\u60f3\u96e2\u958b\uff0c\u4e5f\u4e0d\u662f\u5f92\u5152\u5011\u7684\u5bb6\u88e1\u7f3a\u5c11\u4e86\u4ec0\u9ebc\u3002\u800c\u662f\u70ba\u5e2b\u000d\u000a\u6703\u5e38\u5e38\u53bb\u95dc\u5fc3\u3001\u62dc\u8a2a\u300c\u9053\u5fc3\u6709\u5e36\u5728\u8eab\u4e0a\u300d\u7684\u5f92\u5152\uff0c\u9019\u4e0d\u662f\u504f\u5fc3\uff0c\u800c\u662f\u000d\u000a\u56e0\u70ba\u9019\u6a23\u7684\u62dc\u8a2a\u3001\u6210\u5168\uff0c\u624d\u80fd\u5920\u64f4\u5c55\u5b83\u7684\u6548\u679c\u2500\u2500\u6210\u5168\u4e86\u5f92\u5152\uff0c\u5f92\u5152\u000d\u000a\u6703\u518d\u53bb\u6210\u5168\u4eba\u3002\u6240\u4ee5\u7576\u5f92\u5152\u767c\u73fe\uff0c\u81ea\u5df1\u6162\u6162\u53ef\u4ee5\u6210\u5168\u5225\u4eba\u7684\u6642\u5019\uff0c\u70ba","59\u000d\u000a\u5e2b\u4e00\u5b9a\u4e5f\u5728\u8eab\u908a\u6210\u5168\u5f92\u5152\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff0c\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u6700\u8fd1\u5929\u6c23\u6bd4\u8f03\u6dbc\uff0c\u4e0d\u8981\u4e0d\u5c0f\u5fc3\u6253\u5674\u568f\u4e86\u3002\u70ba\u5e2b\u5f88\u95dc\u5fc3\u6bcf\u4e00\u4f4d\u5f92\u5152\u000d\u000a\u7684\u5065\u5eb7\uff0c\u8981\u6709\u5065\u5eb7\u7684\u8eab\u9ad4\uff0c\u4f86\u4f5b\u5802\u624d\u80fd\u5920\u505a\u66f4\u591a\u4e8b\uff0c\u5e73\u5e38\u5728\u5929\u6069\u5bae\u3001\u000d\u000a\u5929\u4f51\u5bae\uff0c\u6216\u662f\u5728\u5404\u5340\u7684\u4f5b\u5802\u88e1\u6253\u6383\u3001\u8fa6\u4e8b\uff0c\u70ba\u5e2b\u90fd\u770b\u5728\u5fc3\u88e1\u3002\u90fd\u662f\u719f\u000d\u000a\u9762\u5b54\u4e86\uff01\u000d\u000a\u6642\u9593\u5e38\u8b93\u4eba\u8981\u9762\u81e8\u5206\u96e2\u7684\u75db\u82e6\u3001\u96e3\u904e\u3002\u000d\u000a\u4f46\u662f\uff0c\u4fee\u9053\u6c92\u6709\u9019\u500b\u554f\u984c\uff0c\u000d\u000a\u56e0\u70ba\u5982\u679c\u5f92\u5152\u5011\u90fd\u9858\u610f\u4fee\u9053\uff0c\u5c31\u6c92\u6709\u5206\u96e2\u3001\u6c92\u6709\u5206\u5225\uff0c\u53ea\u6709\u73cd\u000d\u000a\u91cd\u3002\u000d\u000a\u4eca\u5929\uff0c\u5e0c\u671b\u6bcf\u4e00\u4f4d\u5f92\u5152\u300c\u91cd\u632f\u9053\u5fd7\uff0c\u5171\u6e21\u672b\u52ab\u300d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u25cb\u826f\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u000d\u000a\u7b49\u7b49\u5f92\u5152\u628a\u9019\u4e9b\u679c\u6536\u4e00\u6536\uff0c\u8acb\u9910\u52d9\u7d44\u7684\u5f92\u5152\u716e\u7d66\u5927\u5bb6\u90fd\u8981\u5920\u559d\uff0c\u000d\u000a\u770b\u9700\u8981\u591a\u5c11\u5c31\u6536\u591a\u5c11\uff0c\u4e00\u5b9a\u8981\u559d\u71b1\u71b1\u7684\uff0c\u5927\u5bb6\u6696\u4e00\u6696\u8eab\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148","60\u000d\u000a\u60b2\uff09\u000d\u000a\u90a3\u518d\u4f86\uff0c\u9019\u5305\u7cd6\u679c\uff0c\u7d66\u5e7c\u4fdd\u7d44\u7684\u5c0f\u5f92\u5152\uff0c\u597d\u53ef\u611b\u5462\uff01\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u000d\u000a\u60b2\uff09\u5927\u5bb6\u90fd\u4e56\u4e56\u7684\u3002\u5c0f\u5f92\u5152\u4e56\uff0c\u5927\u5f92\u5152\u8981\u4e0d\u8981\u4e56\uff1f\uff08\u8981\uff09\u4ee5\u5f8c\u5c0f\u5f92\u5152\u4e5f\u000d\u000a\u6703\u8b8a\u5927\u5f92\u5152\u5462\uff1b\u90a3\u5927\u5f92\u5152\u6703\u4e0d\u6703\u8b8a\u8001\u5f92\u5152\u554a\uff1f\u4e0d\u6703\uff0c\u56e0\u70ba\u4fee\u9053\u4e0d\u6703\u3002\u000d\u000a\u6c38\u9060\u5f92\u5152\u5011\uff0c\u90fd\u662f\u70ba\u5e2b\u6700\u95dc\u5fc3\u7684\u3002\uff08\u9ede\u50b3\u5e2b\uff1a\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff0c\u4fee\u9053\u4e0d\u000d\u000a\u6703\u8001\uff09\u4f46\u662f\u8eab\u9ad4\u9084\u662f\u6703\u8001\u7684\uff0c\u6240\u4ee5\u5927\u5bb6\u8d70\u8def\u7684\u6642\u5019\u90fd\u8981\u5c0f\u5fc3\u4e00\u9ede\u554a\uff01\u5e73\u000d\u000a\u5e38\u8863\u670d\u591a\u7a7f\u4e00\u4e9b\uff0c\u7167\u9867\u597d\u81ea\u5df1\uff0c\u624d\u80fd\u7167\u9867\u597d\u773e\u751f\u3002\u000d\u000a\u597d\u4e86\uff0c\u6642\u9593\u5dee\u4e0d\u591a\u4e86\u3002\u000d\u000a\u723e\u5011 \u524d\u4eba\u5bc4\u4e88\u8a31\u591a\u76fc\u671b\u5462\uff01\u000d\u000a\u70ba\u5e2b\u53ef\u611b\u7684\u5f92\u5152\u5011\u5728\u54ea\u88e1\u554a\uff1f\uff08\u5728\u9019\u88e1\uff09\u000d\u000a\u662f\u554a\uff01\u90fd\u53ef\u611b\u3001\u90fd\u53ef\u611b\uff0c\u6368\u4e0d\u5f97\u5440\uff01\u000d\u000a\u4f46\u662f\u2500\u2500\u000d\u000a\u5927\u6368\uff0c\u624d\u6709\u5927\u5f97\uff0c","61\u000d\u000a\u6240\u4ee5\uff0c\u70ba\u5e2b\u8981\u958b\u59cb\u6368\u5f97\u5f92\u5152\u5011\u53d7\u82e6\u4e86\u3002\u000d\u000a\u5f9e\u82e6\u4e2d\u78e8\u7df4\u51fa\u4f86\u7684\u624d\u53eb\u8056\u8ce2\u3002\u000d\u000a \u5df2\u5c07\u000d\u000a\u7687 \u53e9\u8fad\u4e86 \u8fd4\u5929\u904a~ ","62\u000d\u000a\u4e2d\u83ef\u6c11\u570b\u4e00\u4e00\u4e00\u5e74\u6b72\u6b21\u8f9b\u4e11\u000d\u000a\u570b\u66c6 \u4e00 \u6708\u4e8c\u65e5\u000d\u000a\u8fb2\u66c6\u5341\u4e00\u6708\u5345\u65e5 \u4ed9\u4f5b\u6148\u8a13\u000d\u000a \u4e16\u5c40\u7d1b\u6182\u64fe\u5fc3\u982d \u8ff7\u8005\u722d\u5435\u4e0d\u77e5\u4f11\u000d\u000a \u5bf6\u5728\u81ea\u8eab\u5584\u77e5\u7528 \u82e6\u6028\u5883\u76f8\u73fe\u6696\u6d41\u000d\u000a\u543e\u662f\u000d\u000a\u54ea\u5412\u5e2b\u5144 \u5949\u000d\u000a\u65e8 \u964d\u81f3\u4f5b\u5bae \u9032\u9580\u53c3\u53e9\u000d\u000a\u7687 \u99d5 \u554f\u8072\u5f1f\u59b9\u5011\u53ef\u5b89\u5e73\u000d\u000a\u8f15\u8072\u547c\u559a\u5f1f\u59b9\u6b78\u4f86 \u671d\u76fc\u66ae\u671b\u7ff9\u9996\u5f85","63\u000d\u000a\u61b6\u7406\u5929\u540c\u5728 \u8aaa\u8981\u4e58\u613f \u800c\u4f86\u000d\u000a\u81f3\u4eca\u9059\u671b\u9060\u884c\u4eba\u5f98\u5f8a\u000d\u000a\u642d\u4e0a\u8ca1\u8272\u540d\u5229\u8239\u6416\u64fa\u000d\u000a\u6d6a\u6fe4\u4e2d\u85cf\u8457\u8208\u8870 \u6c89\u5165\u832b\u832b\u82e6\u6d77\u000d\u000a\u6b78\u4f86\u6b78\u4f86 \u773c\u524d\u5047\u76f8\u5e7b\u5316\u96f2\u5f69 \u5982\u96f2\u5f69\u000d\u000a\u83ab\u5c07\u8070\u660e\u4ee5\u70ba\u8ce2\u624d \u8001\u5be6\u4fee\u9053\u5929\u4f51\u611b\u000d\u000a\u6602\u9996\u95ca\u6b65\u8e29 \u8981\u60f3\u5927\u6536\u7a6b\u5148\u57f9\u683d\u000d\u000a\u70ba\u5c07\u4f86 \u9f8d\u83ef\u6703\u8b49\u84ee\u53f0\u000d\u000a\u8f15\u8072\u547c\u559a\u5f1f\u59b9\u6b78\u4f86 \u671d\u76fc\u66ae\u671b\u7ff9\u9996\u5f85\u000d\u000a\u9858\u7406\u5929\u540c\u5728 \u518d\u884c\u4e58\u613f \u800c\u4f86\u000d\u000a\u8abf\u5bc4\uff1a\u6a44\u6b16\u6a39","64\u000d\u000a\u3010\u54ea\u5412\u5e2b\u5144\u6148\u60b2\u3011\u000d\u000a\u25ce\u25cb\u6674\u9ede\u50b3\u5e2b\u8b1b\u8ab2\u6b63\u597d\u63a5\u8fd1\u7d50\u8a9e\uff0c\u5e2b\u5144\u6148\u60b2\uff1a\u000d\u000a\u518d\u7d66\u4f60\u4e94\u5206\u9418\u5594\uff0c\u8b1b\u7684\u6b63\u7cbe\u5f69\u5462\uff01\u000d\u000a\u25ce\u25cb\u6674\u9ede\u50b3\u5e2b\u6148\u60b2\u7d50\u8a9e\uff0c\u5e2b\u5144\u62c9\u5764 1-1\u25cb\u5982\u7684\u6905\u5b50\u81f3\u8d70\u9053\u5750\u4e0b\u8046\u807d\uff0c\u25cb\u6674\u000d\u000a\u9ede\u50b3\u5e2b\u8b1b\u7562\uff0c\u5e2b\u5144\u6148\u60b2\uff1a\u000d\u000a\u25ce\u25cb\u6674\u9ede\u50b3\u5e2b\uff1a\u5e2b\u5144\u6148\u60b2\uff0c\u5f8c\u5b78\u8b1b\u5230\u9019\u88e1\u597d\u55ce\uff1f\u000d\u000a\u6709\u6c92\u6709\u807d\u5920\uff1f\uff08\u6709\uff09\u597d\uff01\u90fd\u807d\u5f97\u5f88\u8a8d\u771f\u5594\uff01\u000d\u000a\u4eca\u5929\u662f\u7b2c\u5e7e\u5929\uff1f\uff08\u7b2c\u4e8c\u5929\uff09\u4f60\u5011\u662f\u4e0d\u662f\u90fd\u5728\u7b49\u5e2b\u5144\u6211\u4f86\uff1f\u5f88\u559c\u6b61\u770b\u000d\u000a\u5230\u4ed9\u4f5b\uff1f\uff08\u662f\uff09\u5f1f\u59b9\u5011\u6700\u8fd1\u597d\u4e0d\u597d\u554a\uff1f\uff08\u597d\uff0c\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u90a3\u9ebc\u591a\u4eba\u8ddf\u5e2b\u5144\u6211\u4e00\u8d77\u56de\u7b54\u300c\u597d\u300d\uff0c\u4f46\u662f\uff0c\u5e2b\u5144\u6211\u77e5\u9053\uff0c\u5176\u5be6\u5f88\u000d\u000a\u591a\u6642\u5019\u4e26\u4e0d\u597d\u3002\u70ba\u4ec0\u9ebc\u5462\uff1f\u6709\u6c92\u6709\u5f88\u591a\u82e6\uff1f\u6709\u6c92\u6709\u5f88\u591a\u50b7\u5fc3\u7684\u4e8b\u60c5\uff1f\u000d\u000a\u6709\u6c92\u6709\u5f88\u591a\u4e0d\u9806\u5229\u7684\u4e8b\u60c5\u570d\u7e5e\u8457\u6211\u5011\uff1f\u5e38\u5e38\u5728\u6df1\u591c\u88e1\u9762\u81ea\u5df1\u90fd\u5077\u5077\u7684\u000d\u000a\u5728\u54ed\u5462\uff01\u5e2b\u5144\u6211\u90fd\u6709\u770b\u5230\u5594\uff01","65\u000d\u000a\u5e2b\u5144\u770b\u5230\u5f1f\u59b9\u5011\u90a3\u9ebc\u591a\u7684\u7169\u60f1\uff0c\u7576\u4f60\u5011\u89ba\u5f97\u8d70\u4e0d\u4e0b\u53bb\u7684\u6642\u5019\uff0c\u5e2b\u000d\u000a\u5144\u6211\u4e5f\u5f88\u96e3\u904e\u3002\u73fe\u5728\u9019\u500b\u4e16\u9593\u592a\u4e82\u4e86\uff0c\u6211\u5011\u8981\u597d\u597d\u628a\u63e1\u4fee\u9053\uff0c\u597d\u55ce\uff1f\u000d\u000a\uff08\u597d\uff09\u000d\u000a\u8001\u5e2b\u6709\u6c92\u6709\u5f88\u95dc\u5fc3\u4f60\u5011\uff1f\u6709\u6c92\u6709\u5f88\u611b\u4f60\u5011\uff1f\uff08\u6709\uff09\u6211\u5011\u628a\u9019\u6a23\u7684\u95dc\u000d\u000a\u5fc3\uff0c\u6211\u5011\u628a\u9019\u6a23\u7684\u6148\u60b2\u5fc3\uff0c\u4e5f\u5e36\u7d66\u5225\u4eba\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u000d\u000a\u5f1f\u59b9\u5011\u6709\u6c92\u6709\u300c\u8cac\u4efb\u300d\u548c\u300c\u4f7f\u547d\u300d\uff1f\uff08\u6709\uff09\u6709\uff0c\u9084\u6709\u66f4\u591a\u300c\u6709\u300d\uff0c\u000d\u000a\u6709\u4e0d\u5b8c\u7684\u300c\u6709\u300d\uff0c\u624d\u662f\u5e2b\u5144\u6211\u6700\u5e0c\u671b\u770b\u5230\u7684\u6a23\u5b50\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff09\u000d\u000a\u4f60\u5011\u660e\u767d\u4eca\u5929\u80fd\u805a\u5728\u9019\u88e1\u662f\u591a\u9ebc\u7684\u4e0d\u5bb9\u6613\u55ce\uff1f\uff08\u662f\uff09\u000d\u000a\u5927\u5bb6\u90fd\u662f\u5bbf\u4e16\u57f9\u6df1\u6839\u3001\u57f9\u4f5b\u7de3\uff0c\u624d\u80fd\u5920\u7ad9\u5728\u9019\u88e1\u554a\uff01\u000d\u000a\u6211\u5011\u7684\u5fc3\u6709\u6c92\u6709\u6bd4\u4ee5\u524d\u66f4\u300c\u5411\u9053\u300d\u4e86\uff1f\uff08\u6709\uff09\u5e2b\u5144\u5e0c\u671b\u5f1f\u59b9\u5011\u662f\u771f\u000d\u000a\u6b63\u7684\u660e\u767d\u81ea\u5df1\u7684\u5167\u5fc3\uff0c\u9084\u6709\u591a\u5c11\u7684\u773e\u751f\u6b63\u5728\u8207\u6211\u5011\u6230\u9b25\u8457\u3001\u6b63\u5728\u8207\u6211\u000d\u000a\u5011\u9762\u5c0d\u9762\u8457\uff0c\u53ea\u6709\u6211\u5011\u81ea\u5df1\u6e05\u695a\u3002\u000d\u000a\u5982\u679c\u6211\u5011\u6c92\u8fa6\u6cd5\u53bb\u89e3\u6c7a\u6211\u5011\u5167\u5fc3\u7684\u6bdb\u75c5\u3001\u554f\u984c\uff0c\u7576\u4e8b\u60c5\u4e00\u4f86\u4e86\uff0c","66\u000d\u000a\u5bb9\u4e0d\u5bb9\u6613\u89e3\u6c7a\uff1f\u5fc3\u60c5\u6703\u4e0d\u6703\u53d7\u5f71\u97ff\uff1f\u5225\u4eba\u8b1b\u4e00\u53e5\u8a71\u6703\u4e0d\u6703\u751f\u6c23\uff1f\uff08\u6703\uff09\u000d\u000a\u2500\u2500\u5f88\u751f\u6c23\uff0c\u4ed6\u600e\u9ebc\u9019\u6a23\u8b1b\u6211\u5462\uff1f\u000d\u000a\u4f60\u5011\u662f\u4e0d\u662f\u6700\u5e38\u8ddf\u8eab\u908a\u7684\u4eba\u8aaa\uff1a\u300c\u90fd\u662f\u4f60\u7684\u554f\u984c\uff0c\u4f60\u8981\u6539\u9032\u3002\u300d\u000d\u000a\u4f46\u662f\u6211\u5011\u8aaa\u4e86\u9019\u9ebc\u591a\u8a71\uff0c\u81ea\u5df1\u6709\u6c92\u6709\u6539\u9032\u554a\uff1f\u000d\u000a\u4eca\u5929\u5e2b\u5144\u4f86\uff0c\u4e5f\u662f\u5949\u8001\u5e2b\u7684\u547d\uff0c\u4f86\u7d66\u5f1f\u59b9\u5011\u66f4\u591a\u7684\u52d5\u529b\u3002\u000d\u000a\u6211\u5011\u8981\u6709\u52d5\u529b\uff0c\u6211\u5011\u8981\u6709\u7a4d\u6975\uff0c\u000d\u000a\u6211\u5011\u8981\u4e0d\u65b7\u5730\u5411\u524d\uff0c\u000d\u000a\u8b93\u9019\u500b\u4e16\u754c\u82e6\u7684\u773e\u751f\uff0c\u80fd\u5920\u5f97\u5230\u7d13\u7de9\u3002\u000d\u000a\u5c31\u50cf\u662f\u5728\u5f88\u51b7\u5f88\u51b7\u7684\u5929\u6c23\u88e1\uff0c\u6211\u5011\u8981\u4e0d\u8981\u4f86\u4e00\u676f\u5f88\u6696\u5f88\u6696\u7684\u71b1\u000d\u000a\u98f2\uff1f\uff08\u8981\uff09\u559d\u4e0b\u53bb\u662f\u4e0d\u662f\u6bd4\u8f03\u8212\u670d\uff1f\uff08\u662f\uff09\u90a3\u6211\u5011\u7684\u5fc3\uff0c\u51b7\u4e0d\u51b7\u554a\uff1f\u51b7\u000d\u000a\u4e0d\u51b7\uff1f\uff08\u4e0d\u51b7\uff09\u4e0d\u51b7\u7684\u95dc\u9375\u662f\u4ec0\u9ebc\uff1f\u000d\u000a\u4e0d\u51b7\u7684\u95dc\u9375\u662f\u56e0\u70ba\u2500\u2500\u000d\u000a\u6211\u5011\u90fd\u77e5\u9053\uff1a\u6211\u5011\u662f\u8d70\u5728\u4e00\u689d\u56de\u5bb6\u7684\u8def\u4e0a\uff1b","67\u000d\u000a\u6211\u5011\u90fd\u77e5\u9053\uff1a\u6211\u5011\u662f\u671d\u5411\u771f\u4fee\u7684\u8def\u3002\u000d\u000a\u6240\u4ee5\u6211\u5011\u660e\u767d\uff0c\u000d\u000a\u6211\u5011\u5728\u9019\u9ebc\u591a\u7684\u4e0d\u8db3\u7576\u4e2d\uff0c\u6211\u9084\u53ef\u4ee5\u66f4\u597d\uff01\u000d\u000a\u5927\u5bb6\u9084\u6709\u6c92\u6709\u7cbe\u795e\u554a\uff1f\uff08\u6709\uff09\u7cbe\u795e\u8981\u4fdd\u6301\u5230\u4ec0\u9ebc\u6642\u5019\uff1f\uff08\u6700\u5f8c\uff09\u000d\u000a\u4ec0\u9ebc\u662f\u6700\u5f8c\u4e00\u53e3\u6c23\u7684\u7cbe\u795e\uff1f\u61c9\u8a72\u662f\u73fe\u5728\u5e2b\u5144\u8981\u4f86\u554f\u554f\u5f1f\u59b9\u5011\u3002\u5728\u000d\u000a\u5834\u6709\u591a\u5c11\u4eba\uff1f\uff08\u4e94\u767e\u591a\u4eba\uff09\u4e94\u767e\u591a\u3002\u000d\u000a\u5e2b\u5144\u8981\u4f86\u554f\u554f\u9019\u9ebc\u591a\u7684\u5f1f\u59b9\u5011\uff1a\u5f1f\u59b9\u5011\u6709\u9019\u500b\u6c7a\u5fc3\u55ce\uff1f\uff08\u6709\uff09\u6709\u6c7a\u000d\u000a\u5fc3\uff0c\u518d\u4f86\u8ddf\u5e2b\u5144\u8aaa\uff1a\u300c\u6211\u53ef\u4ee5\u4fee\u9053\u4fee\u5230\u6700\u5f8c\u4e00\u53e3\u6c23\u3002\u300d\u6c92\u6709\u6c7a\u5fc3\uff0c\u90a3\u000d\u000a\u5f1f\u59b9\u5011\u4e0d\u80fd\u8f15\u6613\u5730\u5c31\u8aaa\u51fa\u53e3\u3002\u56e0\u70ba\u9019\u500b\u4e16\u9593\u771f\u771f\u5047\u5047\uff0c\u592a\u591a\u6df7\u6dc6\u4e0d\u6e05\u000d\u000a\u4e86\uff0c\u5e2b\u5144\u6015\u5f1f\u59b9\u5011\u592a\u6709\u4fe1\u5fc3\u7684\u6642\u5019\uff0c\u6703\u4e0d\u5c0f\u5fc3\u300c\u8a8d\u5047\u70ba\u771f\u300d\u3002\u000d\u000a\u6211\u5011\u5920\u300c\u771f\u300d\u55ce\uff1f\u73fe\u5728\u5c31\u8b8a\u6210\u4e0d\u6562\u56de\u7b54\u4e86\uff1f\u88ab\u5e2b\u5144\u9019\u9ebc\u4e00\u554f\u5c31\u958b\u000d\u000a\u59cb\u61f7\u7591\u81ea\u5df1\u4e86\uff1f\u4fee\u9053\u4e0d\u53ef\u4ee5\u61f7\u7591\u81ea\u5df1\u3002\u6211\u5011\u5f97\u7684\u300c\u9053\u300d\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u000d\u000a\u5e2b\u5144\u518d\u554f\u4e00\u904d\uff1a\u6211\u5011\u771f\u55ce\uff1f\uff08\u771f\uff09\u4fe1\u5fc3\u56de\u4f86\u4e86\uff1f\uff08\u662f\uff09","68\u000d\u000a\u4fe1\u5fc3\u91cd\u4e0d\u91cd\u8981\uff1f\uff08\u91cd\u8981\uff09\u000d\u000a\u300c\u4fe1\u5176\u53ef\u884c\u300d\u8ddf\u300c\u4fe1\u5176\u4e0d\u53ef\u884c\u300d\u662f\u4e0d\u662f\u5169\u7a2e\u8def\uff1f\u2500\u2500\u7d50\u679c\u4e0d\u4e00\u000d\u000a\u6a23\u3002\u000d\u000a\u73fe\u5728\u300c\u5e73\u5b89\u300d\u91cd\u4e0d\u91cd\u8981\uff1f\uff08\u91cd\u8981\uff09\u9084\u6709\u4ec0\u9ebc\u6bd4\u9019\u500b\u91cd\u8981\u55ce\uff1f\uff08\u6c92\u6709\u000d\u000a\uff0f\u4fee\u9053\uff09\u597d\u50cf\u6709\u5169\u500b\u7b54\u6848\uff0c\u70ba\u4ec0\u9ebc\uff1f\u56e0\u70ba\u6211\u5011\u662f\u501f\u5047\u4fee\u771f\u554a\uff01\u4f46\u662f\u6211\u5011\u000d\u000a\u5e38\u5e38\u88ab\u9019\u500b\u300c\u5047\u300d\u7d66\u6253\u6557\u4e86\uff01\u000d\u000a\u5f1f\u59b9\u5011\u73fe\u5728\u6709\u6c92\u6709\u82e6\uff1f\uff08\u6709\uff09\u5982\u679c\u9019\u500b\u82e6\uff0c\u5e2b\u5144\u8aaa\uff1a\u000d\u000a\u300c\u518d\u8b93\u5b83\u66f4\u82e6\uff01\u300d\u6709\u8ab0\u6562\u8ddf\u5e2b\u5144\u8aaa\u300c\u597d\u300d\uff1f\u000d\u000a\u5982\u679c\u9019\u500b\u82e6\uff0c\u5e2b\u5144\u8aaa\uff1a\u300c\u8b93\u5b83\u82e6\u4e0d\u5b8c\u3001\u4e0d\u8981\u505c\uff0c\u7e7c\u7e8c\u4e0b\u53bb\u3002\u300d\u000d\u000a\u6709\u4eba\u6562\u8ddf\u5e2b\u5144\u8aaa\u300c\u597d\u300d\u55ce\uff1f\u000d\u000a\u56e0\u70ba\u5f1f\u59b9\u5011\u8981\u554f\u554f\u81ea\u5df1\uff1a\u6211\u5011\u73fe\u5728\u7684\u5fc3\uff0c\u6709\u5e73\u9759\u3001\u5b89\u5b9a\u55ce\uff1f\uff08\u6709\uff09\u000d\u000a\u7576\u7136\u6709\uff01\u73fe\u5728\u6709\u3001\u4ee5\u5f8c\u6709\u3001\u6bcf\u4e00\u5929\u90fd\u6709\uff0c\u4e0d\u662f\u53ea\u6709\u4e00\u500b\u6709\uff0c\u597d\u4e0d\u597d\uff1f\u000d\u000a\uff08\u597d\uff09","69\u000d\u000a\u8173\u75e0\u4e0d\u75e0\uff1f\uff08\u4e0d\u75e0\uff09\u7d2f\u4e0d\u7d2f\uff1f\uff08\u4e0d\u7d2f\uff09\u6709\u6642\u5019\u5f88\u7d2f\u3001\u5f88\u75e0\u9f41\uff1f\u8170\u75e0\u000d\u000a\u80cc\u4e5f\u75e0\uff0c\u80a9\u8180\u819d\u84cb\u4e5f\u5f88\u75e0\u3002\u5f1f\u59b9\u5011\u8acb\u5750\u5427\uff01\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u5764\u9053\u9ede\u50b3\u5e2b\u5011\u6148\u60b2\uff1a\u4f60\u5011\u8981\u4e0d\u8981\u5750\uff1f\u000d\u000a\u6709\u6642\u5019\u8981\u5f9e\u4e0d\u540c\u7684\u89d2\u5ea6\u770b\u770b\u73fe\u5728\u7684\u4eba\u751f\u3001\u73fe\u5728\u7684\u81ea\u5df1\uff0c\u56e0\u70ba\u6642\u9593\u000d\u000a\u4e0d\u591a\uff0c\u4f46\u662f\u4ed9\u4f5b\u5011\u53c8\u6015\u5f1f\u59b9\u5011\u53d7\u82e6\u53d7\u7d2f\uff1b\u6015\u5f1f\u59b9\u5011\u8d70\u5230\u4e00\u534a\uff0c\u5c31\u89ba\u5f97\u000d\u000a\u5f88\u8f9b\u82e6\u4e86\uff0c\u6240\u4ee5\u4e0d\u6562\u8981\u6c42\u592a\u591a\uff0c\u4f60\u5011\u6709\u611f\u53d7\u5230\u55ce\uff1f\uff08\u6709\uff09\u000d\u000a\u4f46\u662f\uff0c\u6211\u5011\u81ea\u5df1\u7684\u5fc3\u4e2d\u8981\u80fd\u5920\u2500\u2500\u300c\u4e0d\u80fd\u5fd8\u8a18\u9053\u300d\u3002\u000d\u000a\u5728\u6c92\u6709\u4eba\u8981\u6c42\u6211\u5011\u7684\u6642\u5019\uff0c\u6211\u5011\u8981\u81ea\u5df1\u61c2\u5f97\u596e\u767c\u4e0a\u9032\uff0c\u662f\u4e0d\u662f\uff1f\u000d\u000a\uff08\u662f\uff09\u5f1f\u59b9\u5011\u8aaa\u73fe\u5728\u76f8\u805a\u5728\u4e00\u8d77\uff0c\u9084\u5bb9\u6613\u55ce\uff1f\uff08\u4e0d\u5bb9\u6613\uff09\u4f60\u5011\u6709\u9019\u5ea7\u5929\u000d\u000a\u4f51\u5bae\u5bb9\u4e0d\u5bb9\u6613\uff1f\uff08\u4e0d\u5bb9\u6613\uff09\u000d\u000a\u591a\u9ebc\u5f97\u4f86\u4e0d\u6613\uff0c\u80fd\u5920\u6709\u9019\u4e00\u500b\u5c07\u4f86\u80fd\u5920\u63a5\u5f15\u773e\u751f\u7684\u6e05\u975c\u4f5b\u5730\uff0c\u8981\u000d\u000a\u4e0d\u8981\u597d\u597d\u5584\u52a0\u5229\u7528\uff1f\u8981\u4e0d\u8981\u8b93\u5168\u9ad4\u4e0a\u4e0b\u7684\u9053\u89aa\uff0c\u90fd\u80fd\u5920\u53c3\u52a0\u9053\u5834\u4e0a\u7684\u000d\u000a\u6bcf\u4e00\u4ef6\u5927\u5c0f\u4e8b\uff1f\u8981\u4e0d\u8981\u8b93\u6bcf\u4e00\u500b\u4eba\uff0c\u5c0d\u9053\u7684\u51dd\u805a\u529b\u66f4\u591a\u3001\u66f4\u6fc3\uff1f\uff08\u8981\uff09","70\u000d\u000a\u4f46\u662f\uff0c\u5e2b\u5144\u8a8d\u70ba\u525b\u624d\u9019\u500b\u300c\u8981\u300d\u597d\u50cf\u592a\u5c0f\u8072\u4e86\uff0c\u8981\u4e0d\u8981\u8b93\u66f4\u591a\u7684\u000d\u000a\u4eba\u56de\u4f86\u4f5b\u5802\uff1f\uff08\u5927\u8072\u7684\u8aaa\uff1a\u8981\uff09\u000d\u000a\u6bcf\u4e00\u5929\u90fd\u5728\u8b8a\uff0c\u000d\u000a\u5728\u5f1f\u59b9\u5011\u770b\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u6642\u6642\u90fd\u5728\u8b8a\u3002\u000d\u000a\u6211\u5011\u4eba\u80fd\u770b\u5230\u7684\u592a\u6e3a\u5c0f\u4e86\uff1b\u000d\u000a\u6211\u5011\u4eba\u6240\u77e5\u9053\u7684\u592a\u72f9\u9698\u4e86\u3002\u000d\u000a\u662f\u4ec0\u9ebc\u8b93\u6211\u5011\uff0c\u80fd\u5920\u4ea6\u6b65\u4ea6\u8da8\uff0c\u80fd\u5920\u6210\u9577\u8301\u58ef\u5462\uff1f\u000d\u000a\u4f60\u5011\u662f\u4e0d\u662f\u90fd\u60f3\u8981\u8ddf\u4f60\u5011\u7684 \u524d\u4eba\u4e00\u6a23\uff1f\u000d\u000a\u4f60\u5011\u662f\u4e0d\u662f\u90fd\u60f3\u8981\u8ffd\u96a8\u8457 \u524d\u4eba\u5011\uff1f\uff08\u662f\uff09\u000d\u000a\u8ffd\u96a8\u7684\u8173\u6b65\u4e0d\u80fd\u5920\u8b8a\uff0c\u000d\u000a\u5c31\u50cf\u662f\u6211\u5011\u7684\u300c\u521d\u767c\u5fc3\u300d\u4e00\u6a23\uff0c\u4e5f\u4e0d\u80fd\u5920\u8b8a\u3002\u000d\u000a\u7576\u6211\u5011\u767c\u73fe\u6709\u4e00\u9ede\u9ede\u5c0d\u300c\u9053\u300d\u958b\u59cb\u6a21\u7cca\u4e86\uff0c\u958b\u59cb\u89ba\u5f97\u610f\u7fa9\u6c92\u6709\u90a3\u000d\u000a\u9ebc\u591a\u7684\u6642\u5019\uff0c\u8981\u8d95\u5feb\u628a\u5fc3\u62c9\u56de\u4f86\u3002\u60f3\u60f3\u81ea\u5df1\uff1a\u600e\u9ebc\u96e2\u7576\u521d\u7684\u300c\u53e4\u9053\u71b1","71\u000d\u000a\u8178\u300d\u6108\u4f86\u6108\u9060\u4e86\uff1f\u662f\u4e0d\u662f\u56e0\u70ba\u6211\u5e73\u5e38\u7684\u8a00\u884c\u8209\u6b62\u3001\u6211\u5c0d\u9053\u7684\u7406\u5ff5\uff0c\u9084\u000d\u000a\u4e0d\u5920\u6e96\u78ba\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u5f1f\u59b9\u5011\u60f3\u4e0d\u60f3\u8981\u6709\u529b\u91cf\uff1f\uff08\u60f3\uff09\u529b\u91cf\u662f\u9760\u300c\u60f3\u300d\u7684\u55ce\uff1f\uff08\u4e0d\u662f\uff09\u8a72\u000d\u000a\u600e\u9ebc\u505a\uff1f\u6709\u6c92\u6709\u53cd\u7701\u81ea\u5df1\u7684\u6642\u5019\uff1f\uff08\u6709\uff09\u2500\u2500\u662f\u4e0d\u662f\u9084\u8a18\u5f97\u300c\u6211\u4e0a\u4e00\u6b21\uff0c\u000d\u000a\u505a\u90a3\u4ef6\u4e8b\u60c5\u7684\u6642\u5019\uff0c\u9084\u6709\u53ef\u4ee5\u6539\u9032\u7684\u5730\u65b9\uff1f\u300d\u3001\u300c\u6211\u4e0a\u4e00\u6b21\u8a71\u8aaa\u51fa\u53e3\u000d\u000a\u7684\u6642\u5019\uff0c\u53ef\u80fd\u9084\u53ef\u4ee5\u6709\u518d\u60f3\u4e00\u60f3\u7684\u5730\u65b9\uff1f\u300d\u3001\u300c\u6211\u5c0d\u5f85\u8eab\u908a\u4eba\u7684\u6642\u5019\uff0c\u000d\u000a\u662f\u4e0d\u662f\u9084\u53ef\u4ee5\u6709\u66f4\u6eab\u548c\u7684\u5730\u65b9\uff1f\u300d\u000d\u000a\u7a7a\u9593\u90fd\u662f\u6709\u7684\uff0c\u000d\u000a\u4f46\u662f\u6211\u5011\u5e38\u5e38\u903c\u8457\u81ea\u5df1\u9023\u547c\u5438\u7684\u7a7a\u9593\u90fd\u6c92\u6709\u4e86\uff1b\u000d\u000a\u6211\u5011\u5e38\u5e38\u903c\u8457\u81ea\u5df1\uff0c\u5168\u8eab\u90fd\u5f88\u7dca\u7e43\uff1b\u000d\u000a\u6211\u5011\u5e38\u5e38\u7d81\u8457\u81ea\u5df1\uff0c\u4f7f\u81ea\u5df1\u5fd8\u8a18\u4e86\u8981\u518d\u600e\u9ebc\u5411\u524d\u9032\u3002\u000d\u000a\u25ce\u5e2b\u5144\u5411\u524d\u4f38\u76f4\u624b\u4e26\u6148\u60b2\uff1a\u000d\u000a\u5f1f\u59b9\u5011\u77e5\u9053\u55ce\uff1f\u7576\u4f60\u5011\u5e73\u5e38\u558a\u300c\u597d\u82e6\u554a\uff01\u300d\u7684\u6642\u5019\uff0c\u5e2b\u5144\u6211\u7684\u624b\uff0c","72\u000d\u000a\u662f\u9858\u610f\u9019\u6a23\u4f38\u51fa\u4f86\u7684\u3002\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u7576\u4f60\u5011\u5e73\u5e38\u4e0d\u66c9\u5f97\u8a72\u600e\u9ebc\u505a\u7684\u000d\u000a\u6642\u5019\uff0c\u5e2b\u5144\u6211\u4e5f\u6703\u9019\u6a23\u4f38\u51fa\u4f86\u3002\u4f46\u662f\u5f1f\u59b9\u5011\u6709\u7dca\u7dca\u7684\u62c9\u4f4f\u55ce\uff1f\u000d\u000a\uff08\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u4e0d\u77e5\u9053\u6211\u5011\u54ea\u5412\u5e2b\u5144\u662f\u958b\u9053\u5148\u92d2\uff0c\u7942\u90fd\u662f\u8dd1\u5728\u524d\u9762\u7684\u3002\uff09\u000d\u000a\u5f1f\u59b9\u5011\uff0c\u6709\u5e38\u5e38\u628a\u300c\u4ed9\u4f5b\u7684\u8a71\u300d\u7262\u8a18\u5728\u5fc3\u55ce\uff1f\u000d\u000a\u7262\u8a18\u4e00\u53e5\u8a71\u5c31\u5920\u4e86\uff0c\u70ba\u4ec0\u9ebc\uff1f\u2500\u2500\u000d\u000a\u56e0\u70ba\u771f\u7406\uff0c\u662f\u80fd\u5920\u8b93\u6211\u5011\u8cab\u5fb9\u59cb\u7d42\uff0c\u000d\u000a\u6709\u7406\u8d70\u904d\u5929\u4e0b\u3002\u000d\u000a\u544a\u8a34\u81ea\u5df1\u518d\u52a0\u628a\u52c1\u597d\u55ce\uff1f\u544a\u8a34\u81ea\u5df1\u8981\u518d\u66f4\u52aa\u529b\u597d\u55ce\uff1f\uff08\u597d\uff09\u5e2b\u5144\u000d\u000a\u4e5f\u77e5\u9053\u5f88\u591a\u5f1f\u59b9\u5011\u554a\uff0c\u6bd4\u5e73\u6642\u66f4\u7528\u529f\u4e86\uff0c\u5e2b\u5144\u4e5f\u77e5\u9053\uff0c\u5f1f\u59b9\u5011\u6bd4\u4e4b\u524d\u000d\u000a\u5c0d\u9053\u66f4\u6709\u6c7a\u5fc3\u4e86\uff0c\u5c0d\u4e0d\u5c0d\u554a\uff1f\uff08\u662f\uff09\u000d\u000a\u6709\u6c92\u6709\u591a\u591a\u53c3\u8207\uff1f\u6709\u6c92\u6709\u5c0d\u773e\u751f\u61f7\u62b1\u8457\u5e0c\u671b\uff1f\u000d\u000a\u6709\u6c92\u6709\u544a\u8a34\u81ea\u5df1\uff1a\u8981\u6539\u8b8a\u3001\u8981\u86fb\u8b8a\uff1f\uff08\u6709\uff09\u000d\u000a\u5f1f\u59b9\u5011\u559c\u6b61\u81ea\u5df1\u55ce\uff1f\uff08\u559c\u6b61\uff09\u5f1f\u59b9\u5011\u559c\u6b61\u4f86\u4f5b\u5802\u55ce\uff1f\uff08\u559c\u6b61\uff09\u5f1f\u59b9","73\u000d\u000a\u5011\u559c\u6b61\u6551\u6e21\u6709\u7de3\u7684\u773e\u751f\u55ce\uff1f\uff08\u559c\u6b61\uff09\u73fe\u5728\u5f1f\u59b9\u5011\u662f\u975e\u5e38\u7684\u5e78\u798f\uff0c\u5728\u60c5\u6cc1\u000d\u000a\u9084\u4e0d\u662f\u592a\u56b4\u91cd\u7684\u6642\u5019\uff0c\u6211\u5011\u8981\u61c2\u5f97\uff1a\u4e00\u5929\u6bd4\u4e00\u5929\u9084\u8981\u66f4\u5bf6\u8cb4\uff0c\u4e00\u5929\u6bd4\u000d\u000a\u4e00\u5929\u9084\u8981\u66f4\u6709\u8cac\u4efb\u3002\u000d\u000a\u58eb\u4e0d\u53ef\u4ee5\u4e0d\u5f18\u6bc5\uff0c\u4efb\u91cd\u800c\uff08\u9053\u9060\uff09\u3010\u8a3b\u5341\u4e00\u3011\uff1b\u000d\u000a\u6211\u5011\u8981\u7acb\u4e0b\u66f4\u591a\u7684\u5fd7\u5411\uff0c\u7acb\u4e0b\u66f4\u591a\u7684\u5b8f\u613f\uff0c\u000d\u000a\u53ea\u8981\u6211\u6709\u6a5f\u6703\uff0c\u5c31\u53bb\u5ee3\u6e21\u6709\u7de3\u7684\u56db\u6d77\u773e\u751f\u5011\u3002\u000d\u000a\u96d6\u7136\u6709\u6642\u5019\uff0c\u9019\u500b\u8eab\u9ad4\u6703\u75b2\u618a\uff1b\u6709\u6642\u5019\uff0c\u9019\u500b\u5fc3\u6703\u75b2\u5026\u3002\u4f46\u662f\uff0c\u000d\u000a\u60f3\u5230\u773e\u751f\u9084\u9700\u8981\u6211\u5011\u7684\u540c\u6642\uff0c\u5c07\u4f86\u4e16\u754c\u5404\u5730\u300c\u767e\u5ee2\u5f85\u8208\u300d\u7684\u6642\u5019\uff0c\u5f1f\u000d\u000a\u59b9\u5011\u8981\u4e0d\u8981\u51fa\u4e00\u4efd\u529b\uff1f\u8981\u4e0d\u8981\u591a\u95dc\u61f7\u4ed6\u5011\uff1f\uff08\u8981\uff09\u8046\u807d\u773e\u751f\u7684\u9178\u751c\u82e6\u000d\u000a\u6a02\uff0c\u89e3\u958b\u773e\u751f\u5fc3\u4e2d\u7684\u7a2e\u7a2e\u67b7\u9396\uff0c\u8b93\u4ed6\u5011\u91cd\u62fe\u5c0d\u9053\u7684\u4fe1\u5fc3\u3001\u8b93\u4ed6\u5011\u91cd\u65b0\u000d\u000a\u7ad9\u8d77\u4f86\u3001\u8b93\u4ed6\u5011\u91cd\u65b0\u627e\u56de\u5c0d\u9053\u7684\u521d\u8877\u3002\u000d\u000a\u5e2b\u5144\u554f\u554f\u4f60\u5011\uff1a\u000d\u000a\u8ab0\u6709\u628a\u9053\u770b\u5f97\u6bd4\u81ea\u5df1\u7684\u751f\u547d\u9084\u8981\u5bf6\u8cb4\uff1f","74\u000d\u000a\u8ab0\u80fd\u628a\u81ea\u5df1\u8eab\u4e0a\u7684\u9053\uff0c\u5c55\u73fe\u7d66\u5927\u5bb6\u770b\uff1f\u000d\u000a\u8b93\u6709\u7de3\u7684\u4eba\u767c\u73fe\uff1a\u000d\u000a\u9053\uff0c\u4e0d\u662f\u6211\u60f3\u50cf\u7684\u9019\u9ebc\u9059\u9060\uff1b\u000d\u000a\u9053\uff0c\u5c31\u5728\u6211\u7684\u8eab\u4e0a\u3002\u000d\u000a\u5f1f\u59b9\u5011\u7684\u5fc3\uff0c\u6709\u6642\u8d77\u3001\u6709\u6642\u843d\uff0c\u662f\u4e0d\u662f\u5c31\u50cf\u90a3\u500b\u300c\u6d6a\u6fe4\u300d\u4e00\u6a23\uff1f\u000d\u000a\uff08\u662f\uff09\u6709\u6c92\u6709\u770b\u904e\u6d6a\u6fe4\uff1f\u5b83\u662f\u4e0d\u662f\u4e00\u6ce2\u8d77\u53c8\u4e00\u6ce2\u843d\uff1f\u88e1\u982d\u85cf\u8457\u8b8a\u5316\u4e0d\u000d\u000a\u5b9a\uff0c\u85cf\u8457\u8b8a\u6578\uff0c\u85cf\u8457\u8ca1\u8272\u540d\u5229\u3001\u798d\u798f\u69ae\u8fb1\u5f97\u5931\uff0c\u751a\u81f3\u662f\u6538\u95dc\u6211\u5011\u751f\u547d\u3002\u000d\u000a\u5f1f\u59b9\u5011\uff0c\u8981\u5728\u88e1\u982d\u6253\u8f49\u55ce\uff1f\uff08\u4e0d\u8981\uff09\u000d\u000a\u5e2b\u5144\u60f3\u8981\u9001\u5f1f\u59b9\u5011\uff1a\u300c\u6d6a\u6fe4\u4e2d\u85cf\u8457\u8208\u8870\u300d\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u5e0c\u671b\u9019\u000d\u000a\u53e5\u8a71\u53ef\u4ee5\u5e38\u5e38\u63d0\u9192\u5f1f\u59b9\u5011\u3002\u000d\u000a\u300c\u6d6a\u6fe4\u4e2d\u85cf\u8457\u8208\u8870\u300d\uff0c\u5f1f\u59b9\u5011\u5e38\u5e38\u6372\u5165\u5176\u4e2d\uff0c\u81ea\u5df1\u80fd\u77e5\u66c9\u55ce\uff1f\u90a3\u662f\u000d\u000a\u4e0d\u662f\u5c31\u662f\u4e00\u7a2e\u57f7\u8457\uff1f\u4e00\u7a2e\u5728\u610f\uff1f\uff08\u662f\uff09\u9322\u5728\u610f\u5f97\u5b8c\u55ce\uff1f\u4eba\u5728\u610f\u5f97\u5b8c\u55ce\uff1f\u000d\u000a\u5982\u679c\u6211\u5011\u5728\u610f\u592a\u591a\u4e86\u3001\u592a\u4e45\u4e86\u5c31\u6703\u8b8a\u6210\u4ec0\u9ebc\u6a23\uff1f\u300c\u6c89\u5165\u832b\u832b\u82e6\u6d77\u300d\uff0c\u000d\u000a\u5c31\u6703\u6c89\u5165\u82e6\u6d77\u3002","75\u000d\u000a\u6709\u6c92\u6709\u770b\u904e\u4e00\u7a2e\u4eba\uff0c\u672c\u4f86\u770b\u5230\u4ed6\u5c0d\u300c\u9053\u300d\u5145\u6eff\u8457\u4fe1\u5fc3\uff0c\u53ef\u662f\u6709\u4e00\u000d\u000a\u5929\u600e\u9ebc\u5c31\u8b8a\u4e0d\u4e00\u6a23\u4e86\uff1f\u6709\u6c92\u6709\u770b\u904e\u4e00\u7a2e\u4eba\uff0c\u672c\u4f86\u4fee\u9053\u4fee\u5f97\u597d\u597d\u7684\uff0c\u53ef\u000d\u000a\u662f\u6709\u4e00\u5929\uff0c\u600e\u9ebc\u5c31\u50cf\u8b8a\u4e00\u500b\u4eba\u4e86\uff1f\uff08\u6709\uff09\u000d\u000a\u300c\u6b78\u4f86\u6b78\u4f86\u300d\uff0c\u5728\u4e0b\u4e00\u6b21\uff0c\u5e2b\u5144\u3001\u4ed9\u4f5b\u5011\u3001\u8001\u5e2b\u559a\u5f1f\u59b9\u5011\u56de\u4f86\u6642\uff0c\u000d\u000a\u5f1f\u59b9\u5011\u90fd\u8981\u4e00\u8d77\u56de\u4f86\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff09\u559a\u4e00\u8072\uff0c\u4e26\u4e0d\u6703\u6240\u6709\u4eba\u90fd\u56de\u4f86\uff1b\u518d\u000d\u000a\u559a\u4e00\u8072\uff0c\u5f1f\u59b9\u5011\u56de\u4f86\u4e86\u3002\u591a\u5c11\u4eba\u807d\u5230\u5e2b\u5144\u7684\u8072\u97f3\uff1f\u4e0d\u505c\u5730\u5728\u559a\uff0c\u8f15\u8f15\u000d\u000a\u5730\u559a\u4f60\u5011\u56de\u4f86\uff0c\u4e0d\u8981\u5728\u5916\u9762\u6d41\u6d6a\u5f98\u5f8a\uff0c\u56de\u5230\u4ed9\u4f5b\u7684\u8eab\u908a\uff0c\u597d\u4e0d\u597d\u554a\uff1f\u000d\u000a\uff08\u597d\uff09\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u677f\u66f8\u25cb\u775b\u9ede\u50b3\u5e2b\u3001\u6284\u8a13\u4eba\u54e1\u25cb\u6674\u6148\u60b2\uff1a\u5f1f\u59b9\u624b\u75e0\u4e0d\u75e0\uff1f\u4e00\u4eba\u4e00\u000d\u000a\u534a\uff0c\u4ea4\u63db\uff0c\u5927\u5bb6\u540c\u4e00\u8d77\u8f2a\u6d41\u6284\u3002\u8f9b\u82e6\u4e86\u3002\uff08\u8b1d\u8b1d\u4ed9\u4f5b\u6148\u60b2\uff09\u000d\u000a\u5f1f\u59b9\u5011\uff0c\u525b\u624d\u5e2b\u5144\u6b63\u5728\u6279\u8a13\u6642\uff0c\u662f\u4e0d\u662f\u5c31\u6709\u4e00\u6bb5\u6642\u9593\u5462\uff1f\uff08\u662f\uff09\u6211\u000d\u000a\u5011\u5e73\u5e38\u7684\u6642\u5019\u5fd9\u6b78\u5fd9\uff0c\u5176\u5be6\u8aaa\u6709\u6642\u9593\u7684\u6642\u5019\uff0c\u4e5f\u662f\u627e\u5f97\u51fa\u4f86\u7684\uff0c\u5c0d\u4e0d\u000d\u000a\u5c0d\uff1f\uff08\u662f\uff09\u4e00\u9ede\u9ede\u7684\u6642\u9593\u4e5f\u597d\uff0c\u4e00\u9ede\u4e00\u9ede\u7d2f\u7a4d\u8d77\u4f86\uff0c\u90fd\u662f\u6211\u5011\u53ef\u4ee5\u7cbe\u9032\u000d\u000a\u7684\u6642\u9593\u3002\u5c31\u50cf\u5728\u525b\u624d\u6709\u9019\u6a23\u7684\u7a7a\u9592\u8655\uff0c\u8acb\u554f\u2500\u2500","76\u000d\u000a\u6709\u5e7e\u500b\u5f1f\u59b9\u5011\u5fc3\u4e2d\u662f\u5728\u60f3\uff1a\u300c\u6211\u8981\u5982\u4f55\u66f4\u7cbe\u9032\uff1f\u300d\u000d\u000a\u6211\u5011\u5c07\u4f86\u8981\u4e0d\u8981\u56de\u7406\u5929\uff1f\uff08\u8981\uff09\u8981\u4e0d\u8981\u8ddf\u6069\u5e2b\u5728\u4e00\u584a\u5152\uff1f\uff08\u8981\uff09\u4e16\u000d\u000a\u9593\u82e6\u4e0d\u82e6\uff1f\uff08\u82e6\uff09\u000d\u000a\u5f1f\u59b9\u5011\u8981\u77e5\u9053\uff1a\u000d\u000a\u82e6\uff0c\u6211\u5011\u53ef\u4ee5\u8ddf\u5b83\u5171\u5b58\uff1b\u000d\u000a\u82e6\uff0c\u672a\u5fc5\u8981\u628a\u5b83\u63a8\u5f97\u9060\u9060\u7684\uff1b\u000d\u000a\u82e6\uff0c\u53ef\u4ee5\u8ddf\u6211\u5728\u4e00\u8d77\u3002\u000d\u000a\u56e0\u70ba\u4e0d\u82e6\u3001\u56e0\u70ba\u9806\u9042\u3001\u56e0\u70ba\u9806\u5883\u000d\u000a\u5e2b\u5144\u770b\u5230\u5c31\u662f\u9055\u80cc\u9053\u800c\u96e2\u53bb\u4e86\u3002\u000d\u000a\u5c31\u662f\u56e0\u70ba\u82e6\uff0c\u6240\u4ee5\u6211\u5011\u4eca\u5929\u624d\u77e5\u9053\uff0c\u6211\u5011\u80fd\u4fee\u9053\u3001\u6211\u5011\u80fd\u4fee\u8fa6\uff0c\u000d\u000a\u662f\u591a\u9ebc\u7684\u5f97\u4f86\u4e0d\u6613\uff0c\u662f\u591a\u9ebc\u7684\u4e0d\u5bb9\u6613\uff0c\u4e00\u500b\u3001\u4e00\u500b\u3001\u4e00\u500b\u597d\u5e7e\u500b\u5f1f\u59b9\u000d\u000a\u5011\uff0c\u4eca\u5929\u624d\u5750\u5728\u9019\u88e1\u3002\u000d\u000a\u5f1f\u59b9\u5011\uff0c\u5e2b\u5144\u591a\u9ebc\u5e0c\u671b\uff0c\u500b\u500b\u90fd\u662f\u5927\u624d\uff1b","77\u000d\u000a\u5e2b\u5144\u591a\u9ebc\u5e0c\u671b\uff0c\u4f60\u5011\u5230\u6700\u5f8c\u90fd\u80fd\u8207\u5e2b\u5144\u6211\u4e00\u8d77\u642d\u5e6b\u52a9\u9053\uff1b\u000d\u000a\u5e2b\u5144\u591a\u9ebc\u5e0c\u671b\uff0c\u5f1f\u59b9\u5011\u80fd\u8aa0\u5fc3\u4e0d\u8b8a\u3002\u000d\u000a\u6709\u6642\u5019\uff0c\u90a3\u500b\u4e8b\u60c5\u4f86\u627e\u6211\u5011\u7684\u6642\u5019\uff0c\u662f\u4e0d\u662f\u600e\u9ebc\u89e3\u90fd\u89e3\u4e0d\u958b\uff1f\uff08\u662f\uff09\u000d\u000a\u597d\u50cf\u4e00\u500b\u89e3\u4e0d\u958b\u7684\u9396\uff0c\u600e\u9ebc\u8f49\uff0c\u8f49\u5440\u8f49\uff0c\u5b83\u600e\u9ebc\u90fd\u9084\u662f\u4e0d\u958b\uff1f\u6703\u4e0d\u6703\u000d\u000a\u5c31\u9677\u9032\u53bb\u4e86\uff1f\u60f3\u8aaa\u5b83\u5c31\u767c\u751f\u5728\u6211\u9762\u524d\u554a\uff01\u000d\u000a\u8acb\u554f\u7576\u5b83\u767c\u751f\u5728\u5f1f\u59b9\u5011\u7684\u9762\u524d\u6642\uff0c\u5f1f\u59b9\u5011\u5c0d\u9053\u7684\u71b1\u5ff1\u9084\u5728\u55ce\uff1f\u000d\u000a\uff08\u5728\uff09\u5f1f\u59b9\u5011\u7684\u8aa0\u5fc3\u9084\u5728\u55ce\uff1f\u5728\u55ce\uff1f\uff08\u5728\uff09\u597d\uff0c\u7b49\u4e00\u4e0b\u73ed\u7a0b\u7d50\u675f\u4e86\uff0c\u56de\u5230\u000d\u000a\u5bb6\uff0c\u8ddf\u5bb6\u4eba\u76f8\u8655\u7684\u6642\u5019\uff0c\u6211\u5011\u7b2c\u4e00\u53e5\u8a71\u8981\u8aaa\u4ec0\u9ebc\uff1f\u000d\u000a\u5c0d\u9053\u7684\u71b1\u5ff1\u9084\u5728\u4e0d\u5728\uff1f\u6cd5\u6703\u7d50\u675f\u4e86\uff0c\u56de\u53bb\u5bb6\u88e1\uff0c\u8981\u5c0d\u81ea\u5df1\u8eab\u908a\u7684\u000d\u000a\u4eba\u8aaa\u4e00\u53e5\u95dc\u61f7\u7684\u8a71\uff0c\u96e3\u4e0d\u96e3\uff1f\uff08\u4e0d\u96e3\uff09\u662f\u4e0d\u662f\u56e0\u70ba\u5f1f\u59b9\u5011\u73fe\u5728\u770b\u8457\u5e2b\u5144\uff0c\u000d\u000a\u6240\u4ee5\u89ba\u5f97\u4e0d\u96e3\uff1f\u7b49\u5230\u5e2b\u5144\u7684\u81c9\uff0c\u63db\u6210\u53e6\u4e00\u500b\u4eba\u7684\u6642\u5019\uff0c\u96e3\u4e0d\u96e3\uff1f\u7b49\u5230\u000d\u000a\u7ad9\u5728\u5f1f\u59b9\u5011\u9762\u524d\u7684\u4eba\uff0c\u4e0d\u662f\u5e2b\u5144\u6642\uff0c\u96e3\u4e0d\u96e3\uff1f\uff08\u96e3\uff09\u000d\u000a\u5e2b\u5144\u6211\u70ba\u4e86\u734e\u52f5\u4f60\u5011\uff0c\u7b49\u4e00\u4e0b\u7d50\u675f\u7684\u6642\u5019\u8a18\u5f97\uff0c\u4eba\u4eba\u90fd\u6709\u500b\u679c\uff0c","78\u000d\u000a\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff0c\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u5982\u679c\u679c\u4e0d\u5920\uff0c\u90a3\u5c31\u4e00\u8d77\u4f86\u7684\u4eba\u5171\u540c\u4eab\u7528\u4e00\u500b\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u4e00\u8d77\u000d\u000a\u5206\u4eab\u662f\u4e0d\u662f\u6108\u5403\u6108\u597d\u5403\uff1f\uff08\u662f\uff09\u000d\u000a\u5728\u5bb6\u88e1\u7684\u6642\u5019\uff0c\u6bcf\u4e00\u5929\u90fd\u904e\u4e00\u6a23\u7684\u751f\u6d3b\uff0c\u90fd\u9762\u5c0d\u540c\u6a23\u7684\u4eba\uff0c\u5403\u7684\u000d\u000a\u98ef\u597d\u4e0d\u597d\u5403\uff1f\uff08\u597d\u5403\uff09\u4f86\u9019\u908a\u770b\u5230\u5927\u5bb6\u90fd\u7b11\u54aa\u54aa\u7684\uff0c\u90fd\u807d\u5230\u5927\u5bb6\u6eab\u6696\u7684\u000d\u000a\u7b11\u8072\u3001\u95dc\u61f7\uff0c\u5403\u7684\u98ef\u662f\u4e0d\u662f\u66f4\u597d\u5403\uff1f\uff08\u662f\uff09\u5f1f\u59b9\u5011\u8981\u76f8\u4fe1\uff0c\u9019\u6a23\u7684\u300c\u7965\u000d\u000a\u548c\u300d\uff0c\u662f\u53ef\u4ee5\u5e36\u56de\u53bb\u5bb6\u88e1\u7684\u3002\u000d\u000a\u6709\u4e00\u53e5\u8a71\u4e0d\u662f\u8aaa\u55ce\uff1a\u300c\u4e00\u5ff5\u6148\u7965\uff0c\u53ef\u4ee5\u919e\u91c0\u5169\u9593\u548c\u6c23\u3002\u300d\u3010\u8a3b\u5341\u4e8c\u3011\u000d\u000a\u5f1f\u59b9\u5011\u6642\u9593\u662f\u4e0d\u662f\u4e0d\u5920\u4e86\uff1f\uff08\u662f\uff09\u4f60\u770b\uff0c\u5e2b\u5144\u4f86\u770b\u4f60\u5011\uff0c\u662f\u5e0c\u671b\u5f1f\u59b9\u5011\uff0c\u000d\u000a\u90fd\u80fd\u5920\u5929\u5929\u7169\u60f1\u6e1b\u5f97\u6108\u4f86\u6108\u5c11\u3002\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u5f1f\u59b9\u5011\u8981\u77e5\u9053\uff1a\u000d\u000a\u9053\uff0c\u4e0d\u662f\u7acb\u99ac\u8b93\u4f60\u7684\u7169\u60f1\u4e0d\u898b\uff1b\u000d\u000a\u9053\uff0c\u4e0d\u662f\u8b93\u4f60\u7acb\u523b\u9054\u5230\u4f60\u60f3\u8981\u7684\uff1b","79\u000d\u000a\u9053\uff0c\u662f\u8b93\u6211\u5011\u6bcf\u4e00\u500b\u4eba\uff0c\u000d\u000a\u8b93\u5f1f\u59b9\u5011\u81ea\u5df1\u6709\u529b\u91cf\u3001\u6709\u80fd\u529b\uff0c\u000d\u000a\u89e3\u6c7a\u6211\u6b64\u6642\u6b64\u523b\u6b63\u5728\u7169\u60f1\u6211\u7684\u6771\u897f\u3002\u000d\u000a\u25ce\u5e2b\u5144\u62ff\u8d77\u8b1b\u684c\u4e0a\u7684\u6c34\u676f\u3002\u000d\u000a\u6709\u6c92\u6709\u770b\u5230\u9019\u500b\u676f\u5b50\uff1f\uff08\u6709\uff09\u5e2b\u5144\u505a\u4e00\u500b\u5047\u8a2d\uff0c\u5047\u8a2d\u5b83\u4eca\u5929\u662f\u9676\u74f7\u000d\u000a\u505a\u7684\u676f\u5b50\uff0c\u8acb\u554f\u5b83\u9084\u6c92\u6709\u8b8a\u6210\u9019\u9ebc\u597d\u770b\u4ee5\u524d\u5b83\u662f\u4ec0\u9ebc\uff1f\uff08\u6ce5\u571f\uff09\u6ce5\u571f\uff0c\u000d\u000a\u8981\u7d93\u904e\u706b\uff0c\u624d\u80fd\u8b8a\u6210\u5b8c\u6574\u7684\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u90a3\u5f1f\u59b9\u5011\u5462\uff1f\u8981\u4e0d\u8981\u7d93\u904e\u935b\u7149\uff1f\u8981\u4e0d\u8981\u7d93\u904e\u8003\u9a57\uff1f\uff08\u8981\uff09\u4f60\u6c92\u6709\u7d93\u000d\u000a\u904e\u8003\u9a57\uff0c\u4f60\u5f9e\u4f86\u90fd\u4e0d\u6703\u66c9\u5f97\uff1a\u539f\u4f86\u6211\u662f\u53ef\u4ee5\u7684\uff01\u539f\u4f86\u6211\u662f\u53ef\u4ee5\u8ddf\u4ed9\u4f5b\u000d\u000a\u8d70\u5728\u4e00\u8d77\u7684\uff01\u000d\u000a\u7576\u5f1f\u59b9\u5011\u6709\u9019\u6a23\u7684\u5fc3\u9858\u6642\uff0c\u000d\u000a\u4e0d\u7528\u518d\u61f7\u7591\u81ea\u5df1\u3002\u000d\u000a\u4f60\u8ddf\u4f5b\u4ed9\u5011\u4e00\u6a23\u6b8a\u52dd\uff1b","80\u000d\u000a\u4f60\u5011\u5c31\u662f\u4f5b\u3002\u000d\u000a\u7576\u9019\u500b\u6ce5\u571f\uff0c\u9084\u6c92\u6709\u7d93\u904e\u706b\u7149\u6642\uff0c\u5e2b\u5144\u544a\u8a34\u5f1f\u59b9\u5011\uff0c\u53ea\u8981\u5e2b\u5144\u62ff\u000d\u000a\u4e00\u6876\u6c34\u6f51\u4e0b\u53bb\uff0c\u5f1f\u59b9\u5011\u77e5\u9053\u6703\u767c\u751f\u4ec0\u9ebc\u4e8b\u60c5\u55ce\uff1f\u5b83\u5c31\u958b\u59cb\u51fa\u73fe\u88c2\u7e2b\u4e86\uff0c\u000d\u000a\u5b83\u5c31\u958b\u59cb\u5012\u584c\u4e86\uff0c\u5b83\u5c31\u958b\u59cb\u878d\u5316\u4e86\uff0c\u676f\u5b50\u9084\u5728\u55ce\uff1f\uff08\u4e0d\u5728\uff09\u9084\u60f3\u8981\u6709\u4e00\u000d\u000a\u500b\u5b8c\u6574\u7684\u676f\u5b50\uff0c\u5bb9\u6613\u55ce\uff1f\uff08\u4e0d\u5bb9\u6613\uff09\u000d\u000a\u540c\u6a23\u7684\uff0c\u5f1f\u59b9\u5011\u4eca\u5929\u90fd\u6709\u4f7f\u547d\uff0c\u6211\u5011\u90fd\u6709\u8cac\u4efb\uff0c\u000d\u000a\u300c\u6b32\u505a\u7cbe\u91d1\u7f8e\u7389\u7684\u4fee\u9053\u4eba\uff0c\u5b9a\u5f9e\u70c8\u706b\u4e2d\u7145\u4f86\u3002\u300d\u3010\u8a3b\u5341\u4e09\u3011\u000d\u000a\u5e0c\u671b\u5f1f\u59b9\u5011\u5728\u6bcf\u4e00\u6b21\u3001\u4eba\u751f\u8def\u5feb\u8981\u4e0d\u884c\u6642\uff0c\u90fd\u6709\u5e2b\u5144\u7684\u96d9\u624b\u5728\u5f8c\u000d\u000a\u9762\u63a8\u8457\u4f60\u5011\u5411\u524d\u9032\u3002\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u592a\u591a\u8a71\u4e5f\u9053\u4e0d\u76e1\u5c0d\u5f1f\u59b9\u5011\u7684\u95dc\u5fc3\uff1b\u592a\u591a\u8a71\u4e5f\u9053\u4e0d\u76e1\u5c0d\u5f1f\u59b9\u5011\u7684\u64d4\u000d\u000a\u6182\u3002\u8acb\u554f\u5f1f\u59b9\u5011\uff1a\u4fee\u9053\u8def\u662f\u4e0d\u662f\u8981\u81ea\u5df1\u5411\u524d\u9032\u554a\uff1f\uff08\u662f\uff09\u4fee\u9053\u8def\u662f\u4e0d\u662f\u000d\u000a\u8981\u81ea\u5df1\u8d70\u5b8c\u554a\uff1f\uff08\u662f\uff09\u000d\u000a\u597d\u7684\u6a5f\u7de3\u8981\u80fd\u5920\u5584\u63e1\uff0c","81\u000d\u000a\u56e0\u70ba\u53ea\u8981\u6211\u5011\u60f3\u4fee\u9053\u7684\u90a3\u4e00\u5929\u3001\u6211\u5011\u60f3\u4fee\u9053\u7684\u90a3\u500b\u6642\u5019\uff0c\u000d\u000a\u6211\u5011\u5c31\u662f\u767c\u5149\u3001\u767c\u4eae\u7684\u3002\u000d\u000a\u53ea\u8981\u6211\u5011\u6c38\u9060\u90fd\u9084\u6709\u529b\u6c23\uff1b\u6211\u5011\u6c38\u9060\u90fd\u9084\u6709\u613f\u529b\u3002\u000d\u000a\u613f\u529b\u662f\u6c92\u6709\u7d50\u675f\u7684\u4e00\u5929\u5594\uff01\u000d\u000a\u9019\u500b\u8089\u8eab\u6c92\u6709\u4e86\uff0c\u9084\u6709\u300c\u613f\u300d\u5728\uff0c\u000d\u000a\u8981\u77e5\u9053\u6709\u7121\u9650\u7684\u773e\u751f\uff0c\u6b63\u5728\u5411\u6211\u5011\u5436\u558a\u3002\u000d\u000a\u9913\u4e0d\u9913\uff1f\uff08\u4e0d\u9913\uff09\u662f\u4e0d\u662f\u9084\u6709\u8ab2\u6c92\u6709\u4e0a\u5b8c\uff1f\u4e0b\u5348\u9084\u6709\u8ab2\u662f\u4e0d\u662f\uff1f\u000d\u000a\uff08\u662f\uff09\u9084\u6709\u5e7e\u5802\u8ab2\uff1f\uff08\u5169\u5802\uff09\u8981\u7e7c\u7e8c\u5c08\u5fc3\u5594\uff01\u5df2\u7d93\u5f88\u4e45\u6c92\u6709\u9019\u6a23\u8046\u807d\u9053\u7406\u000d\u000a\u4e86\u3002\u000d\u000a\u4e0b\u4e00\u6b21\uff0c\u518d\u8b93\u5e2b\u5144\u770b\u5230\u5f1f\u59b9\u5011\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff09\u6642\u9593\u5feb\u4e0d\u5feb\uff1f\uff08\u5feb\uff09\u000d\u000a\u4fee\u9053\u7684\u6642\u9593\u4e5f\u4e0d\u77ed\u4e86\uff0c\u770b\u5e2b\u5144\u8a8d\u8b58\u4f60\u5011\u591a\u4e45\u4e86\uff01\u6211\u5011\u8981\u628a\u63e1\u4f4f\u6709\u9650\u7684\u000d\u000a\u751f\u547d\uff0c\u5728\u6709\u9650\u7684\u6642\u9593\u88e1\uff0c\u591a\u5c11\u4eba\u96e2\u958b\u4e86\u6211\u5011\uff1f\u5728\u6709\u9650\u7684\u6642\u9593\u88e1\uff0c\u932f\u904e\u000d\u000a\u4e86\u4f55\u66fe\u518d\u91cd\u4f86\uff1f\u5f1f\u59b9\u5011\u6709\u6c92\u6709\u4fe1\u5fc3\u554a\uff1f\uff08\u6709\uff09","82\u000d\u000a\u9019\u9996\u6b4c\u5531\u4e00\u904d\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u90fd\u807d\u904e\u5427\uff1f\u807d\u904e\u55ce\uff1f\u4e0d\u6562\u8d77\u4f86\u800c\u000d\u000a\u5df2\uff0c\u5f1f\u59b9\u5011\u4e00\u8d77\u554a\uff01\u000d\u000a\u25ce\u5168\u9ad4\u9f4a\u5531\u8056\u6b4c\uff0c\u5e2b\u5144\u5c0d\u591a\u4f4d\u73ed\u54e1\u6148\u60b2\uff1a\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u5764 3-10\u25cb\u9999\u5764\u73ed\u54e1\u6148\u60b2\uff1a\u8981\u5c0d\u81ea\u5df1\u6709\u4fe1\u5fc3\u5594\uff01\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u5764 3-8\u25cb\u8431\u3001\u5764 3-9\u25cb\u82b3\u6148\u60b2\uff1a\u4f86\u9019\u88e1\u958b\u4e0d\u958b\u5fc3\uff1f\u4f86\u9019\u88e1\u958b\u4e0d\u000d\u000a\u958b\u5fc3\uff1f\uff08\u958b\u5fc3\uff09\u56de\u53bb\u8981\u52a0\u6cb9\u5594\uff01\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u4e7e 5-7\u25cb\u9716\u524d\u8ce2\u6148\u60b2\uff1a\u4f86\u9019\u88e1\u6709\u6c92\u6709\u89ba\u5f97\u611f\u60f3\u5f88\u591a\u554a\uff1f\uff08\u6709\uff0c\u000d\u000a\u8b1d\u8b1d\u5e2b\u5144\uff09\u5f1f\u59b9\u4e5f\u6539\u8b8a\u5f88\u591a\u5594\uff01\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u4e7e 1-8\u25cb\u548c\u6148\u60b2\uff1a\u6709\u6c92\u6709\u66f4\u60f3\u8981\u4fee\u9053\u4e86\uff1f\u8981\u61f7\u8457\u300c\u6b63\u77e5\u6b63\u898b\u300d\u000d\u000a\u7e7c\u7e8c\u52a0\u6cb9\u5594\uff01\u000d\u000a\u25ce\u65cb\u5f8b\u4e0d\u719f\u6089\u5c0e\u81f4\u4e2d\u65b7\uff0c\u5e2b\u5144\u6148\u60b2\u3002\u5764\u25cb\u775b\u9ede\u50b3\u5e2b\u63a5\u7e8c\u8d77\u97f3\uff0c\u5168\u9ad4\u9f4a\u5531\u8056\u000d\u000a\u6b4c\u3002\u000d\u000a\u8dd1\u8abf\u4e86\uff01","83\u000d\u000a\u5f1f\u59b9\u5011\u770b\u5718\u7d50\u5bb9\u6613\u55ce\uff1f\uff08\u4e0d\u5bb9\u6613\uff09\u000d\u000a\u6211\u5011\u90fd\u6709\u4e00\u9846\u5f88\u597d\u7684\u5fc3\uff0c\u4f46\u662f\uff0c\u70ba\u4ec0\u9ebc\u5e38\u5e38\u5728\u5e73\u65e5\u6642\uff0c\u6703\u8207\u4eba\u76f8\u000d\u000a\u8655\u4e0d\u4f86\u5462\uff1f\u56e0\u70ba\u6bcf\u500b\u4eba\u500b\u6027\u7684\u4e0d\u540c\u3001\u60f3\u6cd5\u7684\u4e0d\u540c\u3001\u505a\u4e8b\u65b9\u6cd5\u7684\u4e0d\u540c\uff1b\u000d\u000a\u6211\u89ba\u5f97\u9019\u6a23\uff0c\u4f60\u89ba\u5f97\u90a3\u6a23\uff0c\u6240\u4ee5\u6211\u5011\u6703\u6709\u5f88\u591a\u7684\u6469\u64e6\u3002\u000d\u000a\u4f46\u662f\uff0c\u6211\u5011\u6709\u6642\u5019\u8981\u9069\u6642\u7684\u505c\u4e0b\u4f86\u3002\u000d\u000a\u300c\u4f60\u5c0d\u6211\u932f\u3001\u6211\u662f\u4f60\u975e\u300d\u000d\u000a\u9019\u662f\u722d\u57f7\u4e0d\u5b8c\u7684\uff0c\u000d\u000a\u90a3\u662f\u4e00\u500b\u7121\u5e95\u6d1e\u3002\u000d\u000a\u5e2b\u5144\u4e5f\u6015\u803d\u8aa4\u4f60\u5011\u7684\u6642\u9593\u554a\uff01\u6bcf\u4e00\u500b\u4eba\u7684\u6642\u9593\u90fd\u5f88\u5bf6\u8cb4\u3002\u000d\u000a\u25ce\u5e2b\u5144\u5c0d\u9ede\u50b3\u5e2b\u5011\u6148\u60b2\uff1a\u000d\u000a\u524d\u9762\u7b56\u5283\u7684\u5f1f\u59b9\u5011\uff0c\u8981\u80fd\u5920\u7d66\u4e88\u773e\u751f\u4fe1\u5fc3\uff0c\u8981\u80fd\u5920\u7b56\u5283\u51fa\u3001\u898f\u5283\u000d\u000a\u51fa\u66f4\u597d\u7684\u65b9\u5411\uff1b\u8981\u80fd\u5920\u5e36\u9818\u66f4\u591a\u7684\u773e\u751f\uff0c\u9032\u5165\u5230\u5929\u4f51\u5bae\u4f86\u3002","84\u000d\u000a \u5df2\u5c07\u000d\u000a\u99d5 \u8fad\u53e9\u000d\u000a\u4fee\u9053\u4e0d\u8ad6\u5e74\u7d00\uff0c\u6e3e\u8eab\u90fd\u6709\u52c1\uff01\u000d\u000a\u4e0d\u8ad6\u4fee\u9053\u591a\u4e45\uff0c\u6c38\u9060\u90fd\u8981\u8b19\u865b\u3002\u000d\u000a\u5982\u679c\u5f1f\u59b9\u5011\u505a\u5230\u4e86\uff0c\u4e00\u5b9a\u6703\u767c\u73fe\uff0c\u5f1f\u59b9\u5011\u662f\u4e0d\u662f\u5f88\u5e38\u8aaa\uff1a\u5e0c\u671b\u4ed9\u000d\u000a\u4f5b\u7d66\u6211\u52a0\u667a\u6167\uff1f\uff08\u662f\uff09\u5e0c\u671b\u4ed9\u4f5b\u53ef\u4ee5\u591a\u591a\u5e6b\u52a9\u6211\u5011\uff0c\u4e00\u5b9a\u8981\u5148\u505a\u5230\uff1a\u81ea\u000d\u000a\u5df1\u9858\u610f\u5e6b\u52a9\u81ea\u5df1\u3002\u000d\u000a\u5982\u679c\u662f\u6b63\u8d70\u5728\u300c\u771f\u4fee\u8def\u4e0a\u300d\u7684\u5f1f\u59b9\u5011\uff0c\u000d\u000a\u4e0d\u8981\u5bb3\u6015\u524d\u65b9\u7684\u963b\u7919\u3001\u963b\u6514\uff0c\u000d\u000a\u56e0\u70ba\u4f60\u4e00\u5b9a\u6703\u8de8\u8d8a\u904e\u90a3\u4e00\u4e9b\uff0c\u53ea\u6703\u4e0d\u65b7\u5730\u8981\u5411\u524d\u3002\u000d\u000a\u7b49\u4e0b\u4e00\u6b21\uff0c\u5e2b\u5144\u770b\u5230\u4f60\u5011\u7684\u6642\u5019\uff0c\u8981\u8b8a\u66f4\u4e0d\u4e00\u6a23\uff1b\u000d\u000a\u7b49\u5230\u4e0b\u4e00\u6b21\uff0c\u5e6b\u52a9\u773e\u751f\u7684\u4e8b\u60c5\uff0c\u90fd\u8981\u6709\u4f60\u5011\u3002\u000d\u000a\u5f1f\u59b9\u5011\u7d55\u5c0d\u662f\u4ed9\u4f5b\u5011\u7684\u597d\u52a9\u624b\uff1b","85\u000d\u000a\u5f1f\u59b9\u5011\u7d55\u5c0d\u662f\u8001\u5e2b\u7684\u597d\u5f92\u5152\u3002\u000d\u000a\u5f1f\u59b9\u5011\u5fc3\u4e2d\u662f\u4e0d\u662f\u5e38\u5e38\u60f3\u8981\u81ea\u5df1\u80fd\u5920\u81f3\u5c11\u5148\u505a\u5230\u4e00\u500b\u4ec0\u9ebc\u6a23\u7684\u6a23\u000d\u000a\u5b50\uff1f\u4ec0\u9ebc\u6a23\u7684\u4fee\u9053\u4eba\u5462\uff1f\u81f3\u5c11\u73fe\u5728\u5f1f\u59b9\u5011\u5c31\u662f\u3002\u6709\u9019\u6a23\u7684\u5fc3\u9858\uff0c\u662f\u975e\u000d\u000a\u5e38\u597d\u7684\u4e8b\u60c5\u3002\u56e0\u70ba\u5e2b\u5144\u76f8\u4fe1\uff0c\u5f1f\u59b9\u5011\u4e00\u5b9a\u80fd\u6210\u529f\u3002\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u4e0d\u8981\u518d\u947d\u9032\u53bb\u81ea\u5df1\u7684\u60f3\u6cd5\u4e86\u5594\uff01\u8981\u8df3\u812b\u51fa\u4f86\u5594\uff01\u90a3\u4e0d\u7136\u5e2b\u5144\u4e00\u559a\u000d\u000a\u518d\u559a\uff0c\u4eba\u5462\uff1f\u600e\u9ebc\u90fd\u6c92\u6709\u5728\u5e2b\u5144\u7684\u65c1\u908a\uff1f\u000d\u000a\u8981\u6d3b\u51fa\u300c\u6709\u9053\u7684\u4eba\u751f\u300d\uff0c\u000d\u000a\u4e0d\u662f\u9053\u7406\u96e2\u6211\u5f88\u9060\uff0c\u662f\u6211\u96e2\u9053\u7406\u592a\u9060\u3002\u000d\u000a\u597d\u4e86\uff0c\u8981\u5403\u98ef\u4e86\uff0c\u7b49\u4e00\u4e0b\u6162\u6162\u5403\u5594\uff01\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u000d\u000a\u5403\u4e0d\u5920\u9084\u53ef\u4ee5\u518d\u4f86\u4e00\u500b\uff01\u000d\u000a\u5e0c\u671b\u518d\u807d\u807d\u5f1f\u59b9\u5011\u7684\u8072\u97f3\u5462\uff01\uff08\u5e2b\u5144\u597d\uff09\u000d\u000a\u56e0\u70ba\u5f1f\u59b9\u5011\u90fd\u662f\u5e2b\u5144\u7684\u5bf6\u8c9d\u3002\uff08\u8b1d\u8b1d\u5e2b\u5144\u6148\u60b2\uff09\u52a0\u6cb9\uff01","86\u000d\u000a\u8fd4\u5929 \u222b","87\u000d\u000a\u3010\u8a3b\u91cb\u9644\u9304\u3011\u000d\u000a\u3010\u8a3b \u4e00\u3011\u300c\u4e9f\u5931\u6a5f\u300d\u3002\u4e00\u518d\u7684\u932f\u5931\u826f\u6a5f\u3002\u4e9f\uff0c\u97f3\u540c\u5668\uff0c\u4e00\u518d\u3001\u5c62\u000d\u000a\u6b21\u3002\u8a9e\u672c\u300a\u8ad6\u8a9e\u2027\u967d\u8ca8\u7b2c\u5341\u4e03\u300b\uff1a\u300c\u597d\u5f9e\u4e8b\u800c\u4e9f\u5931\u6642\uff0c\u53ef\u000d\u000a\u8b02\u77e5\u4e4e\uff1f\u300d\u610f\u55bb\uff1a\u81ea\u53e4\u6b32\u4fee\u9053\u8005\u4e0d\u6613\u9047\u660e\u5e2b\u3001\u5f97\u4f5b\u7de3\uff0c\u4eca\u000d\u000a\u6642\u6211\u5011\u7de3\u9022\u767d\u967d\u666e\u6e21\u662f\u591a\u9ebc\u5343\u8f09\u96e3\u9022\uff0c\u5207\u52ff\u5165\u5bf6\u5c71\u7a7a\u624b\u800c\u000d\u000a\u56de\uff1b\u7576\u6709\u6a5f\u6703\u53ef\u4ee5\u4e86\u613f\u6642\uff0c\u4e5f\u52ff\u56e0\u4fd7\u60c5\u727d\u7d46\u800c\u4e00\u518d\u932f\u5931\u6a5f\u000d\u000a\u6703\u3002\u000d\u000a\u3010\u8a3b \u4e8c\u3011\u300c\u5750\u81e5\u79aa\u8005\u8a9e\u9ed8\u5b89\u300d\u3002\u8a9e\u672c\u300a\u6c38\u5609\u5927\u5e2b\u8b49\u9053\u6b4c\u300b\uff1a\u300c\u884c\u4ea6\u000d\u000a\u79aa\uff0c\u5750\u4ea6\u79aa\uff0c\u8a9e\u9ed8\u52d5\u975c\u9ad4\u5b89\u7136\u3002\u300d\u610f\u55bb\uff1a\u4fee\u9053\u4fee\u5fc3\u3001\u8fa6\u9053\u000d\u000a\u76e1\u5fc3\uff0c\u5747\u4e0d\u96e2\u958b\u8d77\u5fc3\u52d5\u5ff5\u9593\u7684\u4fee\u884c\u2500\u2500\u5373\u884c\u4f4f\u5750\u81e5\uff0c\u90fd\u8981\u000d\u000a\u6642\u5e38\u89ba\u5bdf\u89c0\u7167\uff0c\u4e45\u4e4b\u6db5\u7149\u539a\u5be6\u5fc3\u6027\uff0c\u9047\u4e8b\u3001\u81e8\u5883\u6709\u672c\u6709\u000d\u000a\u4e3b\uff0c\u81ea\u7136\u751f\u6d3b\u662f\u9053\uff0c\u96a8\u8655\u5b89\u7136\u81ea\u5728\u3002","88\u000d\u000a\u3010\u8a3b \u4e09\u3011\u300c\u767e\u6b65\u4e5d\u6298\u5f4e\u5761\u300d\u3002\u8a9e\u672c\u5510\u2027\u674e\u767d\u3008\u8700\u9053\u96e3\u3009\uff1a\u300c\u9752\u6ce5\u4f55\u000d\u000a\u76e4\u76e4\uff0c\u767e\u6b65\u4e5d\u6298\u7e08\u5dd6\u5dd2\u3002\u300d\u7c21\u7fa9\uff1a\u6307\u8def\u9014\u4e4b\u66f2\u6298\uff0c\u65bc\u767e\u6b65\u000d\u000a\u5167\u5c31\u62d0\u4e86\u6578\u6b21\u5f4e\u9053\uff0c\u884c\u8def\u8271\u96e3\u3002\u610f\u55bb\uff1a\u4fee\u9053\u4eba\u82e5\u5e0c\u671b\u80fd\u4e00\u000d\u000a\u4e16\u4fee\u3001\u4e00\u4e16\u6210\uff0c\u9808\u5b78\u7fd2\u63a5\u53d7\u935b\u934a\u8207\u8003\u9a57\uff1b\u81ea\u53e4\u8056\u8ce2\u591a\u9b54\u000d\u000a\u8003\uff0c\u5728\u82e6\u4e2d\u65b9\u80fd\u9ad4\u609f\u4e16\u9593\u82e6\u6d77\uff0c\u77ed\u66ab\u865b\u5e7b\uff0c\u4e0d\u53ef\u8ff7\u5931\u3002\u6545\u000d\u000a\u672b\u5f8c\u4fee\u9053\u85c9\u7531\u4eba\u4e8b\u78e8\u7df4\u3001\u7a2e\u7a2e\u8003\u9a57\u7576\u4e2d\uff0c\u5fb9\u5e95\u6ecc\u9664\u7fd2\u6027\u75fc\u000d\u000a\u75be\uff0c\u65b9\u53ef\u7167\u898b\u5982\u5982\u5149\u660e\u672c\u6027\uff0c\u9032\u800c\u9054\u672c\u9084\u6e90\u3002\u000d\u000a\u3010\u8a3b \u56db\u3011\u300c\u5b54\u66f0\u6210\u4ec1\uff0c\u5b5f\u66f0\u53d6\u7fa9\u3002\u300d\u8a9e\u672c\u300a\u8ad6\u8a9e\u2027\u885b\u9748\u516c\u7b2c\u5341\u000d\u000a\u4e94\u300b\uff1a\u300c\u5fd7\u58eb\u4ec1\u4eba\uff0c\u7121\u6c42\u751f\u4ee5\u5bb3\u4ec1\uff0c\u6709\u6bba\u8eab\u4ee5\u6210\u4ec1\u3002\u300d\u53c8\u000d\u000a\u300a\u5b5f\u5b50\u2027\u544a\u5b50\u4e0a\u300b\uff1a\u300c\u751f\uff0c\u4ea6\u6211\u6240\u6b32\u4e5f\uff1b\u7fa9\uff0c\u4ea6\u6211\u6240\u6b32\u000d\u000a\u4e5f\uff0c\u4e8c\u8005\u4e0d\u53ef\u5f97\u517c\uff0c\u6368\u751f\u800c\u53d6\u7fa9\u8005\u4e5f\u3002\u300d\u64da\u300a\u5b8b\u53f2\u300b\u8a18\u000d\u000a\u8f09\uff0c\u6587\u5929\u7965\u5c31\u7fa9\u5f8c\uff0c\u5728\u5176\u8863\u5e36\u4e2d\u767c\u73fe\u4e00\u6bb5\u8d0a\u6587\uff1a\u300c\u5b54\u66f0\u6210\u000d\u000a\u4ec1\uff0c\u5b5f\u66f0\u53d6\u7fa9\uff0c\u60df\u5176\u7fa9\u76e1\uff0c\u6240\u4ee5\u4ec1\u81f3\u3002\u8b80\u8056\u8ce2\u66f8\uff0c\u6240\u5b78\u4f55\u000d\u000a\u4e8b\uff1f\u800c\u4eca\u800c\u5f8c\uff0c\u5eb6\u5e7e\u7121\u6127\uff01\u300d\u610f\u55bb\uff1a\u4fee\u9053\u4eba\u9808\u7d42\u751f\u4f9d\u5faa\u4ec1","89\u000d\u000a\u7fa9\u5408\u9053\u7684\u65b9\u5411\u4e0d\u53ef\u6539\u8b8a\uff0c\u5be7\u53ef\u70ba\u4e86\u5b88\u4f4f\u4ec1\u7fa9\u4e2d\u9053\u800c\u72a7\u7272\u81ea\u000d\u000a\u5df1\u751f\u547d\u3001\u540d\u8b7d\u3001\u5916\u7269\u2026\u2026\uff0c\u4e5f\u4e0d\u53ef\u70ba\u4e86\u6027\u547d\u6216\u751f\u6d3b\u3001\u6b32\u671b\u000d\u000a\u6240\u9700\u800c\u9055\u80cc\u4ec1\u7fa9\u9053\u5fb7\u3002\u000d\u000a\u3010\u8a3b \u4e94\u3011\u300c\u6ce5\u300e\u6211\u300f\u4e0d\u5316\u300d\u3002\u6ce5\uff0c\u97f3\u540c\u9006\uff0c\u62d8\u6ce5\u3002\u6b64\u7531\u300c\u6ce5\u53e4\u4e0d\u000d\u000a\u5316\u300d\u4e00\u8a5e\u800c\u4f86\u3002\u539f\u610f\u70ba\u62d8\u6ce5\u65bc\u53e4\u6642\u6210\u898f\u800c\u4e0d\u77e5\u8b8a\u901a\u3002\u610f\u000d\u000a\u55bb\uff1a\u4eba\u5bb9\u6613\u9677\u65bc\u81ea\u6211\u6210\u898b\u3001\u56fa\u57f7\u3001\u57f7\u8457\u7576\u4e2d\u800c\u4e0d\u81ea\u77e5\uff1b\u8207\u000d\u000a\u4eba\u76f8\u8655\u3001\u6e9d\u901a\u6642\uff0c\u82e5\u57f7\u65bc\u300c\u6211\u300d\u5247\u6703\u947d\u725b\u89d2\u5c16\u3001\u4e00\u610f\u5b64\u884c\u000d\u000a\u6216\u932f\u89e3\u5584\u8a00\u6210\u60e1\u610f\uff0c\u800c\u9020\u6210\u5f7c\u6b64\u8aa4\u6703\u6216\u81ea\u8003\u3001\u8003\u4ed6\u3002\u000d\u000a\u3010\u8a3b \u516d\u3011\u300c\u82e5\u5f97\u4e94\u6b32\uff0c\u7576\u9858\u773e\u751f\uff0c\u62d4\u9664\u6b32\u7bad\uff0c\u7a76\u7adf\u5b89\u7a69\u3002\u300d\u8a9e\u672c\u000d\u000a\u300a\u83ef\u56b4\u7d93\u2027\u6de8\u884c\u54c1\u300b\u3002\u4e94\u6b32\u70ba\u8ca1\u3001\u8272\u3001\u540d\u3001\u98df\u3001\u7761\u3002\u7c21\u000d\u000a\u7fa9\uff1a\u82e6\u6d77\u773e\u751f\u591a\u6578\u7d42\u65e5\u8655\u5728\u70ba\u9322\u300c\u8ca1\u300d\u8ffd\u9010\u3001\u722d\u596a\u4e4b\u4e2d\uff0c\u000d\u000a\u56e0\u6c42\u4e0d\u5f97\u82e6\u800c\u82e6\u4e0d\u582a\u8a00\uff1b\u53c8\u5bb9\u6613\u53d7\u5230\u5f62\u5f62\u300c\u8272\u300d\u8272\u5e72\u64fe\u8eab\u000d\u000a\u5fc3\uff0c\u7a2e\u4e0b\u8af8\u591a\u60e1\u56e0\u3001\u60e1\u5ff5\u7d42\u53d7\u5176\u60e1\u679c\uff1b\u56e0\u559c\u597d\u8caa\u6200\u6b0a\u529b","90\u000d\u000a\u300c\u540d\u300d\u4f4d\uff0c\u8eab\u5fc3\u675f\u7e1b\u9020\u6210\u8a00\u884c\u626d\u66f2\uff0c\u60a3\u5f97\u60a3\u5931\u4e0d\u5f97\u81ea\u5728\uff1b\u000d\u000a\u6ce8\u91cd\u300c\u5403\u300d\u7a7f\u4eab\u53d7\uff0c\u6c89\u6d78\u5176\u4e2d\u667a\u6167\u96e3\u958b\uff1b\u300c\u7761\u300d\u5373\u662f\u8ffd\u6c42\u000d\u000a\u8212\u9069\u4f11\u9592\uff0c\u61f6\u6563\u5ea6\u65e5\u96e3\u4ee5\u7cbe\u9032\u3002\u83e9\u85a9\u6148\u60b2\u5fc3\u9858\uff0c\u5e0c\u671b\u80fd\u559a\u000d\u000a\u9192\u773e\u751f\u89ba\u609f\u800c\u8df3\u812b\u4e94\u6b32\uff0c\u82e5\u80fd\u4f7f\u6b32\u671b\u6e1b\u5c11\u4e26\u6539\u9664\u7a1f\u6027\uff0c\u5247\u000d\u000a\u8eab\u5fc3\u65b9\u5f97\u7a76\u7adf\u5b89\u7a69\u3002\u000d\u000a\u3010\u8a3b \u4e03\u3011\u300c\u600f\u600f\u4e0d\u5b89\u300d\u3002\u600f\uff0c\u97f3\u540c\u6a23\uff1b\u600f\u600f\uff0c\u4e0d\u6eff\u610f\u6216\u4e0d\u9ad8\u8208\u7684\u6a23\u000d\u000a\u5b50\u3002\u7c21\u7fa9\uff1a\u60c6\u60b5\u4e0d\u5b89\u3002\u000d\u000a\u3010\u8a3b \u516b\u3011\u300c\u79cb\u6beb\u4e0d\u80fd\u5fcd\uff0c\u5e73\u5730\u751f\u5e72\u6208\u3002\u300d\u8a9e\u51fa\u5b8b\u2027\u9678\u6e38\u3008\u805e\u91cc\u4e2d\u6709\u000d\u000a\u9b25\u8005\u4f5c\u6b64\u793a\u4e4b\u3009\uff1a\u300c\u79cb\u6beb\u4e0d\u80fd\u5fcd\uff0c\u5e73\u5730\u5c0b\u5e72\u6208\uff1b\u553e\u9762\u807d\u81ea\u000d\u000a\u4e7e\uff0c\u5f7c\u5ff5\u81ea\u6d88\u78e8\u3002\u300d\u79cb\u6beb\uff0c\u6307\u79cb\u5929\u9ce5\u7378\u8eab\u4e0a\u65b0\u9577\u7684\u7d30\u6bdb\uff0c\u000d\u000a\u5f8c\u6bd4\u55bb\u7d30\u5fae\u7684\u4e8b\u7269\u3002\u7c21\u7fa9\uff1a\u82e5\u4e00\u500b\u4eba\u7684\u5fc3\u91cf\u4e0d\u8db3\uff0c\u9023\u4e9b\u5fae\u000d\u000a\u5c0f\u4e8b\u90fd\u7121\u6cd5\u5fcd\u53d7\u3001\u5305\u5bb9\uff0c\u90a3\u9ebc\u5373\u4f7f\u5982\u540c\u5e73\u5730\u822c\u5e73\u5766\u3001\u9806\u9042\u000d\u000a\u7684\u751f\u6d3b\uff0c\u4e5f\u5bb9\u6613\u8207\u4eba\u8d77\u722d\u5435\u3001\u885d\u7a81\u3002","91\u000d\u000a\u3010\u8a3b \u4e5d\u3011\u300c\u8207\u5929\u5730\u5408\u5176\u5fb7\uff0c\u8207\u65e5\u6708\u5408\u5176\u660e\uff0c\u8207\u56db\u6642\u5408\u5176\u5e8f\u3002\u300d\u8a9e\u672c\u000d\u000a\u300a\u5468\u6613\u2027\u4e7e\u5366\u300b\u3002\u7c21\u7fa9\uff1a\u771f\u4fee\u9053\u8005\uff0c\u6642\u6642\u523b\u523b\u4ee5\u81f3\u8aa0\u990a\u5fc3\u000d\u000a\u4fee\u6027\u4e0d\u6562\u5fd8\u5931\uff0c\u5176\u5fb7\u6027\u80fd\u8207\u5929\u5730\u76f8\u5408\uff0c\u5373\u4fdd\u6709\u5ee3\u95ca\u7684\u80f8\u895f\u000d\u000a\u8207\u5fb7\u91cf\uff1b\u4ea6\u5982\u540c\u65e5\u6708\u7684\u5149\u660e\u667a\u6167\uff0c\u80fd\u70ba\u6709\u7de3\u773e\u751f\u7167\u4eae\u9ed1\u000d\u000a\u6697\u3001\u7834\u9664\u7591\u60d1\uff1b\u5176\u8a00\u884c\u5408\u4e00\u3001\u5167\u52d5\u5916\u975c\u5982\u540c\u56db\u6642\u6625\u590f\u79cb\u51ac\u000d\u000a\u4e00\u6a23\uff0c\u6709\u6625\u6696\u82b1\u958b\u6642\u4e5f\u6709\u5bd2\u51ac\u4e4b\u842c\u7269\u5bc2\u975c\uff1b\u8056\u8ce2\u4f9d\u7167\u5929\u000d\u000a\u5730\u3001\u65e5\u6708\u3001\u56db\u6642\u4e4b\u300c\u9053\u300d\u4e26\u884c\u800c\u4e0d\u6096\u3002\u000d\u000a\u3010\u8a3b \u5341\u3011\u300c\u8fc5\u901f\u5149\u9670\u4e0d\u53ef\u7559\uff0c\u5e74\u5e74\u53ea\u898b\u6c34\u6771\u6d41\uff1b\u4e0d\u4fe1\u8a66\u628a\u9752\u83f1\u7167\uff0c\u000d\u000a\u6614\u65e5\u6731\u984f\u4eca\u767d\u982d\u3002\u300d\u8a9e\u672c\u300a\u4e03\u771f\u53f2\u50b3\u300b\u3002\u610f\u55bb\uff1a\u4eba\u751f\u4e00\u4e16\u000d\u000a\u4f55\u5176\u77ed\u66ab\uff0c\u5982\u540c\u5e74\u5e74\u53ea\u898b\u5230\u6c34\u5f80\u6771\u6d41\u53bb\uff0c\u4e00\u53bb\u4e0d\u5fa9\uff0c\u4f55\u66fe\u000d\u000a\u5411\u897f\u6d41\u8fd4\uff1f\u5c31\u50cf\u6211\u5011\u90fd\u66fe\u7d93\u5e74\u8f15\u7f8e\u9e97\u904e\uff0c\u8f49\u773c\u9593\u5169\u9b22\u5df2\u96d9\u000d\u000a\u767d\u3002\u8d81\u6b64\u53ef\u4ee5\u4fee\u8fa6\u9053\u7684\u826f\u6a5f\uff0c\u61c9\u7a4d\u6975\u628a\u63e1\u4e86\u613f\u6a5f\u6703\uff0c\u83ab\u8b93\u000d\u000a\u751f\u547d\u7559\u767d\uff0c\u83ab\u8b93\u6b72\u6708\u7a7a\u8e49\u8dce\u3002","92\u000d\u000a\u3010\u8a3b\u5341\u4e00\u3011\u300c\u58eb\u4e0d\u53ef\u4ee5\u4e0d\u5f18\u6bc5\uff0c\u4efb\u91cd\u800c\u9053\u9060\u3002\u300d\u8a9e\u672c\u300a\u8ad6\u8a9e\u2027\u6cf0\u4f2f\u7b2c\u000d\u000a\u516b\u300b\u66fe\u5b50\u66f0\uff1a\u300c\u58eb\u4e0d\u53ef\u4ee5\u4e0d\u5f18\u6bc5\uff0c\u4efb\u91cd\u800c\u9053\u9060\u3002\u4ec1\u4ee5\u70ba\u5df1\u000d\u000a\u4efb\uff0c\u4e0d\u4ea6\u91cd\u4e4e\uff1f\u6b7b\u800c\u5f8c\u5df2\uff0c\u4e0d\u4ea6\u9060\u4e4e\uff1f\u300d\u5f18\u6bc5\uff0c\u5bec\u5b8f\u5805\u000d\u000a\u6bc5\uff0c\u6307\u62b1\u8ca0\u9060\u5927\uff0c\u610f\u5fd7\u5805\u5f37\u3002\u610f\u55bb\uff1a\u4fee\u9053\u4eba\u61c9\u6709\u6fdf\u4e16\u6551\u4eba\u000d\u000a\u7684\u6148\u5fc3\u60b2\u61f7\uff0c\u625b\u8d77\u91cd\u4efb\u4e0d\u6015\u56f0\u96e3\uff0c\u4e0d\u8ad6\u9053\u9014\u591a\u9ebc\u8271\u96e3\u9059\u000d\u000a\u9060\uff0c\u5176\u5fc3\u5fd7\u59cb\u7d42\u5982\u4e00\uff0c\u76f4\u5230\u751f\u547d\u7684\u7d42\u9ede\u4ea6\u4e0d\u7b97\u9577\u9060\uff0c\u6167\u547d\u000d\u000a\u7684\u5ef6\u7e8c\u4e43\u6c38\u7121\u6b62\u5883\u3002\u000d\u000a\u3010\u8a3b\u5341\u4e8c\u3011\u300c\u4e00\u5ff5\u6148\u7965\uff0c\u53ef\u4ee5\u919e\u91c0\u5169\u9593\u548c\u6c23\u3002\u300d\u8a9e\u672c\u300a\u83dc\u6839\u8b5a\u300b\uff1a\u000d\u000a\u300c\u4e00\u5ff5\u6148\u7965\uff0c\u53ef\u4ee5\u919e\u91c0\u5169\u9593\u548c\u6c23\uff1b\u5bf8\u5fc3\u6f54\u767d\uff0c\u53ef\u4ee5\u662d\u5782\u767e\u000d\u000a\u4ee3\u6e05\u82ac\u3002\u300d\u610f\u55bb\uff1a\u5fc3\u5ff5\u7965\u548c\u3001\u6148\u60b2\uff0c\u53ef\u4f7f\u4eba\u8207\u4eba\u4e4b\u9593\u7684\u78c1\u000d\u000a\u5834\u5145\u6eff\u6696\u6d41\uff0c\u6c23\u6c1b\u878d\u6d3d\u3001\u548c\u548c\u6c23\u6c23\uff1b\u4e00\u500b\u4eba\u7684\u5fc3\u4e2d\u6c92\u6709\u60e1\u000d\u000a\u5ff5\uff0c\u5fc3\u5730\u6f54\u6de8\u7121\u67d3\uff0c\u5176\u8a00\u884c\u81ea\u7136\u80fd\u5c55\u73fe\u9053\u6c23\u8207\u4fee\u990a\uff0c\u4ee5\u8eab\u000d\u000a\u793a\u9053\u7684\u7cbe\u795e\u80fd\u5982\u8056\u8ce2\u524d\u8f29\u4e00\u6a23\uff0c\u904e\u5316\u5b58\u795e\u3001\u767e\u4ee3\u99a8\u9999\u3002","93\u000d\u000a\u3010\u8a3b\u5341\u4e09\u3011\u300c\u6b32\u505a\u7cbe\u91d1\u7f8e\u7389\u7684\u4fee\u9053\u4eba\uff0c\u5b9a\u5f9e\u70c8\u706b\u4e2d\u7145\u4f86\u3002\u300d\u8a9e\u672c\u300a\u83dc\u000d\u000a\u6839\u8b5a\u300b\uff1a\u300c\u6b32\u505a\u7cbe\u91d1\u7f8e\u7389\u7684\u4eba\u54c1\uff0c\u5b9a\u5f9e\u70c8\u706b\u4e2d\u7145\u4f86\uff1b\u601d\u7acb\u000d\u000a\u6380\u5929\u63ed\u5730\u7684\u4e8b\u529f\uff0c\u9808\u5411\u8584\u51b0\u4e0a\u5c65\u904e\u3002\u300d\u610f\u55bb\uff1a\u4fee\u9053\u60f3\u8981\u66f4\u000d\u000a\u9032\u4e00\u6b65\u7684\u4e0a\u9054\u8207\u9032\u6b65\uff0c\u5c31\u5982\u540c\u4e00\u584a\u7f8e\u597d\u7684\u7470\u5bf6\uff0c\u4ea6\u9700\u8981\u7d93\u000d\u000a\u904e\u5207\u78cb\u7422\u78e8\u4e0d\u65b7\u96d5\u7422\uff0c\u65b9\u80fd\u986f\u5176\u50f9\u503c\uff1b\u7336\u5982\u4e00\u584a\u771f\u91d1\u4e43\u5f9e\u000d\u000a\u70c8\u706b\u4e2d\u6dec\u7149\u8207\u9451\u5b9a\uff1b\u800c\u4fee\u9053\u8005\u5e0c\u671b\u80fd\u70ba \u4e0a\u5929\u8207\u773e\u751f\u505a\u4e00\u000d\u000a\u756a\u767d\u967d\u5927\u8056\u696d\uff0c\u66f4\u9808\u5982\u540c\u6b65\u6b65\u8d70\u5728\u51ac\u65e5\u5bd2\u5929\u8584\u51b0\u4e0a\uff0c\u5176\u5fc3\u000d\u000a\u7576\u8981\u6230\u6230\u5162\u5162\uff0c\u4e0d\u6562\u6709\u4e00\u7d72\u6beb\u7684\u9b06\u61c8\uff0c\u5982\u6b64\u81f3\u7d42\u4e0d\u8b8a\uff0c\u65b9\u000d\u000a\u80fd\u6709\u6210\u3002"]