var textForPages =["1\u000d\u000a\u4e2d\u83ef\u6c11\u570b\u4e00\u4e00\u3007\u5e74\u6b72\u6b21\u58ec\u5bc5 \u570b\u66c6\u4e09\u6708\u5341\u4e8c\u65e5\u000d\u000a\u8fb2\u66c6\u4e8c\u6708\u521d\u5341\u65e5 \u4ed9\u4f5b\u6148\u8a13\u000d\u000a\u6b72\u666f\u5b17\u905e\u7d05\u5875\u767e\u614b \u96e2\u5408\u4ea4\u7e54\u5fc3\u96a8\u5883\u64fa\u3010\u8a3b\u4e00\u3011\u000d\u000a\u5584\u7de3\u9022\u9053\u77e5\u6b62\u82e5\u7d20 \u9676\u990a\u5bec\u8ad2\u6566\u539a\u7965\u85f9\u000d\u000a\u543e\u4e43\u000d\u000a \u6fdf\u516c\u6d3b\u4f5b \u5949\u000d\u000a\u65e8 \u964d\u4f5b\u6a13 \u9032\u9580\u5148\u53c3","2\u000d\u000a\u7687 \u5bb9 \u518d\u554f\u5f92\u5152\u5011\u53ef\u5b89\u5426\u000d\u000a\u58ec\u5bc5\u5e74\u9f4a\u805a\u5728\u838a\u56b4\u6bbf\u000d\u000a\u6709\u7de3\u7684\u4e7e\u5764\u805a\u4e09\u5929\u000d\u000a\u96d6\u8aaa\u662f\u56db\u96e3\u4e0d\u89ba\u96e3\u000d\u000a\u662f\u56e0\u70ba\u5919\u4e16\u6839\u57fa\u975e\u51e1\u7109\u000d\u000a\u4eba\u751f\u88e1\u969b\u9047\u6709\u8d77\u4f0f\u6380\u000d\u000a\u60d1\u696d\u7684\u570d\u7e5e\u82e6\u96e3\u8a00\u000d\u000a\u4e0d\u77e5\u66c9\u56e0\u679c\u6709\u679c\u56e0\u000d\u000a\u53ea\u6015\u90a3\u5929\u8d95\u4eba\u50ac\u7121\u60c5\u9762\u000d\u000a\u8d81\u8457\u90a3\u4f73\u671f\u5728\u773c\u524d\u000d\u000a\u8981\u9192\u609f\u7d05\u5875\u662f\u5875\u57a2\u5708","3\u000d\u000a\u8b93\u771f\u7406\u5951\u5165\u5fc3\u7530\u000d\u000a\u958b\u667a\u6167\u4e0d\u518d\u96a8\u6d41\u9023\u000d\u000a\u4e16\u4eba\u82e5\u4fee\u9053\u80fd\u7acb\u5fd7\u5805\u000d\u000a\u5f9e\u53cd\u7701\u5165\u624b\u7169\u60f1\u6e1b\u000d\u000a\u5929\u4e0b\u672c\u7121\u4e8b\u5eb8\u4fd7\u7e6d\u000d\u000a\u5225\u8b93\u90a3\u6b72\u6708\u8e49\u8dce\u5e74\u5fa9\u5e74\u000d\u000a\u5225\u8b93\u90a3\u6b72\u6708\u8e49\u8dce\u5e74\u5fa9\u5e74\u000d\u000a\u8abf\u5bc4\uff1a\u6625\u98a8\u543b\u4e0a\u6211\u7684\u81c9\u000d\u000a ","4\u000d\u000a\u6069\u5e2b\u6148\u52c9\uff1a\u000d\u000a\u9053\u89aa\u4e43\u56e0\u9053\u800c\u89aa \u67d4\u8edf\u5584\u5f85\u6df1\u9ad4\u95dc\u61f7\u000d\u000a\u540c\u5fc3\u4e43\u6b64\u5fc3\u76f8\u540c \u5929\u6069\u5bb6\u4eba\u7406\u89e3\u5305\u5bb9\u000d\u000a\u9075\u524d\u4e43\u91cd\u9053\u57fa\u790e \u606d\u656c\u865a\u5df1\u5167\u5916\u4e00\u5982\u000d\u000a\u63d0\u5f8c\u4e43\u5c0a\u5e2b\u6bcb\u5fd8 \u6148\u60b2\u548c\u6696\u8010\u6027\u63d0\u651c\u000d\u000a\u5b78\u4fee\u4e43\u4ee5\u4fee\u70ba\u5b78 \u8a00\u884c\u8b19\u7766\u53cd\u6c42\u8eac\u8eab\u000d\u000a\u53c3\u8fa6\u4e43\u8fa6\u8056\u53c3\u5fb7 \u78e8\u6ecc\u7fd2\u6c23\u5713\u878d\u4e92\u6210\u000d\u000a\u6210\u5c31\u4e43\u84bc\u751f\u5f97\u6551 \u4e09\u671f\u61c9\u904b\u6e21\u5316\u6709\u7de3\u000d\u000a ","5\u000d\u000a\u3010\u6fdf\u516c\u6d3b\u4f5b \u6148\u60b2\u3011 \u807d\u8ab2\uff01\u807d\u8ab2\uff01\u6709\u5fc3\u5f97\u4e86\u55ce\uff1f\u70ba\u5e2b\u4f86\u770b\u770b\u5404\u4f4d\u5f92\u5152\u5011\u554a\uff01\uff08\u8b1d\u8b1d\u8001\u5e2b\u000d\u000a\u6148\u60b2\uff09\u5404\u4f4d\u5f92\u5152\u5011\u597d\u554a\uff01\u000d\u000a\u4eca\u5929\u80fd\u5920\u4f86\u9019\u5834\u6cd5\u6703\uff0c\u662f\u6839\u57fa\u975e\u51e1\u554a\uff01\u5982\u679c\u4e0d\u662f\u7956\u4e0a\u6709\u5fb7\uff0c\u6839\u57fa\u000d\u000a\u6df1\u539a\uff0c\u53ef\u662f\u9032\u4e0d\u4e86\u4f5b\u9580\u3002\u5230\u73fe\u5728\u9084\u9ad8\u8208\u55ce\uff1f\uff08\u9ad8\u8208\uff09\u81ea\u5df1\u770b\u770b\u81ea\u5df1\u7684\u5fc3\uff0c\u000d\u000a\u5f9e\u4e00\u958b\u59cb\u5230\u73fe\u5728\u6709\u6c92\u6709\u6b61\u559c\u4e86\u8a31\u591a\u554a\uff1f\uff08\u6709\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff11\u865f\u6148\u60b2\uff1a\u7f8e\u83ef\u5f92\u5152\u6709\u55ce\uff1f\uff08\u6709\uff09\u662f\u4e0d\u662f\u611f\u89ba\u5230\u4f86\u9019\u88e1\u751f\u000d\u000a\u547d\u597d\u50cf\u91cd\u65b0\u958b\u59cb\uff0c\u70ba\u5e2b\u5c31\u662f\u5e0c\u671b\u5f92\u5152\u4ee5\u5f8c\u5e38\u56de\u5230\u4f5b\u5802\u4f86\uff0c\u8ddf\u8457\u9019\u4e9b\u5144\u000d\u000a\u5f1f\u59ca\u59b9\u4e00\u8d77\u5b78\u7fd2\u4fee\u9053\uff0c\u9019\u6a23\u7684\u751f\u547d\u624d\u6709\u610f\u7fa9\u3002\u000d\u000a\u8fa6\u4e8b\u4eba\u54e1\u7b49\u9019\u4e00\u5929\u4e5f\u7b49\u5f88\u4e45\u4e86\u5462\uff01\u671d\u76fc\u66ae\u60f3\u90fd\u5728\u60f3\u8457\uff1a\u300c\u4ec0\u9ebc\u6642\u000d\u000a\u5019\u958b\u6cd5\u6703\u554a\uff1f\u53ea\u8981\u91d1\u9580\u6709\u6cd5\u6703\uff0c\u5927\u5bb6\u90fd\u60f3\u8981\u4e00\u8d77\u4f86\u5171\u8944\u76db\u8209\u3002\u300d\u9019\u4e5f\u000d\u000a\u662f\u70ba\u5e2b\u770b\u5230\u6703\u611f\u5230\u5f88\u6b23\u6170\u7684\u3002","6\u000d\u000a\u3010\u8001\u5e2b\u6279\u8a13\u4e2d\u3011\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u6709\u798f\u6c23\uff0c\u6fdf\u516c\u6d3b\u4f5b\u4f86\u7d66\u6211\u5011\u6307\u9ede\uff09\u000d\u000a\u4eca\u65e5\u5927\u5bb6\u5171\u805a\u4e00\u5802\u5c31\u662f\u6709\u7de3\uff0c\u5de6\u53f3\u770b\u4e00\u770b\u662f\u4e0d\u662f\u672c\u4f86\u5c31\u8a8d\u8b58\u554a\uff1f\u000d\u000a\uff08\u4e0d\u662f\uff09\u4e0d\u662f\uff0c\u4f46\u662f\u5230\u4e86\u4eca\u5929\u6709\u6c92\u6709\u5f7c\u6b64\u66f4\u52a0\u719f\u6089\u4e00\u9ede\u3002\u90fd\u662f\u70ba\u5e2b\u7684\u597d\u000d\u000a\u5f92\u5152\uff0c\u6709\u5e74\u7d00\u8f15\u7684\u3001\u4e5f\u6709\u5e74\u7d00\u9577\u7684\uff0c\u5927\u5bb6\u540c\u4f86\u4fee\u9019\u500b\u9053\uff0c\u6709\u6c92\u6709\u5206\u5462\uff1f\u000d\u000a\uff08\u6c92\u6709\uff09\u4f46\u662f\u70ba\u5e2b\u5f88\u64d4\u5fc3\u5f92\u5152\u5011\uff0c\u5728\u751f\u6d3b\u88e1\u9762\u5fc3\u4e2d\u6709\u592a\u591a\u7684\u5206\u5225\uff0c\u6703\u4e0d\u000d\u000a\u6703\uff1f\uff08\u6703\uff09\u6bcf\u4e00\u500b\u4eba\u6709\u4e0d\u540c\u7684\u4eba\u751f\u3001\u6709\u4e0d\u540c\u7684\u5bb6\u5ead\uff0c\u6bcf\u4e00\u5929\u9047\u5230\u7684\u4e8b\u60c5\u000d\u000a\u591a\u4e0d\u591a\u5440\uff1f\uff08\u591a\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\uff11\u865f\u6148\u60b2\u4e26\u8cdc\u679c\uff1a\u000d\u000a\u90a3\u5f92\u5152\u90fd\u662f\u600e\u9ebc\u6a23\u53bb\u9762\u5c0d\u5440\uff1f\uff08\u52c7\u5f80\u76f4\u524d\uff0c\u4e0d\u61fc\uff09\u597d\uff0c\u4f86\u4e00\u500b\u679c\u3002\u90a3\u000d\u000a\u73fe\u5728\u5f92\u5152\u5728\u4fee\u9053\u9019\u4e00\u689d\u8def\u4e0a\uff0c\u5c31\u8981\u7528\u9019\u4e00\u9846\u5fc3\u4f86\u8d70\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u7b54\u000d\u000a\u61c9\u70ba\u5e2b\u4e86\u554a\uff0c\u9019\u6a23\u4eba\u751f\u624d\u662f\u671d\u5411\u5149\u660e\u7684\u8def\uff01\u8ddf\u5bb6\u4eba\u4e00\u8d77\u5206\u4eab\u597d\u4e0d\u597d\uff1f\u000d\u000a\uff08\u597d\uff09\u9084\u6709\u5f88\u591a\u5bb6\u4eba\u53ef\u4ee5\u6e21\u5462\uff01\u70ba\u5e2b\u66c9\u5f97\u3002","7\u000d\u000a\u5f92\u5152\u5011\uff0c\u8acb\u5750\u4e0b\u5427\uff01\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u70ba\u5e2b\u771f\u662f\u60f3\u5ff5\u6bcf\u4e00\u4f4d\u5f92\u5152\u5440\uff01\u000d\u000a\u5f92\u5152\u5011\u6709\u6c92\u6709\u60f3\u8457\u70ba\u5e2b\u554a\uff01\uff08\u6709\uff0c\u4e00\u76f4\u90fd\u5728\u60f3\uff09\u4e0d\u8ad6\u6642\u9593\u518d\u600e\u9ebc\u4e45\u300c\u5e2b\u000d\u000a\u5f92\u4e4b\u60c5\u300d\u6c38\u9060\u90fd\u4e0d\u6703\u65b7\uff0c\u70ba\u4ec0\u9ebc\u5462\uff1f\u56e0\u70ba\u9019\u4e0d\u662f\u300c\u4e16\u4fd7\u7684\u4eba\u60c5\u300d\u662f\u4e0d\u000d\u000a\u662f\uff1f\uff08\u662f\uff09\u770b\u770b\u4eba\u4e16\u9593\u7684\u670b\u53cb\uff0c\u80fd\u5920\u4ea4\u5230\u77e5\u5fc3\u7684\u6709\u5e7e\u500b\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\uff17\u865f\u6148\u60b2\uff1a\u5f92\u5152\u7684\u670b\u53cb\u5f88\u591a\u5462\uff0c\u662f\u4e0d\u662f\uff1f\u4f46\u662f\u5f9e\u5c0f\u5230\u5927\u90fd\u000d\u000a\u662f\u670b\u53cb\u7684\u6709\u5e7e\u500b\uff1f\uff08\u4e00\u5230\u4e8c\u500b\uff09\u662f\uff0c\u90a3\u9084\u7b97\u4e0d\u932f\uff0c\u5c0d\u4e0d\u5c0d\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u8f49\u5411\u8fa6\u4e8b\u4eba\u54e1\u6148\u60b2\uff1a\u4f60\u5011\u770b\u770b\uff0c\u5230\u4e86\u4f60\u5011\u9019\u500b\u5e74\u7d00\uff0c\u670b\u53cb\u6709\u5e7e\u000d\u000a\u500b\uff1f\u5f88\u5c11\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u25ce\u8001\u5e2b\u53c8\u8f49\u904e\u8eab\u5c0d\u8457\u4e7e\uff17\u865f\u6148\u60b2\uff1a\u552f\u6709\u300c\u540c\u4fee\u9053\u4f34\u300d\u662f\u771f\u6b63\u7684\u9053\u53cb\u3002\u000d\u000a\u90a3\u5f92\u5152\uff0c\u70ba\u5e2b\u5e0c\u671b\u5f92\u5152\u628a\u9019\u500b\u9053\u6162\u6162\u653e\u5165\u81ea\u5df1\u5fc3\u88e1\u9762\u597d\u55ce\uff1f\uff08\u597d\uff09\u96d6\u7136\u000d\u000a\u73fe\u5728\u770b\u4e00\u770b\uff0c\u9084\u6709\u5f88\u591a\u81ea\u5df1\u8981\u60f3\u7684\u5730\u65b9\uff0c\u662f\u5427\uff1f\uff08\u662f\uff09\u6bcf\u4e00\u5802\u807d\u7684\u8ab2\u6709\u000d\u000a\u4e0d\u540c\u7684\u9ad4\u6703\uff0c\u7576\u7136\u6709\u53ef\u80fd\u4e0d\u592a\u8a8d\u540c\uff0c\u4f46\u662f\u5462\uff01\u4eba\u751f\u8d70\u8d8a\u4e45\u8d8a\u6703\u767c\u73fe\uff1a\u000d\u000a\u54ce\u554a\uff01\u600e\u9ebc\u6a23\u8d70\u7684\u8def\uff0c\u5012\u904e\u4f86\u9084\u662f\u8981\u8d70\u56de\u4f60\u5011\u73fe\u5728\u8d70\u7684\u300c\u4fee\u9053\u8def\u300d\u3002","8\u000d\u000a\u4e0d\u8ad6\u5f92\u5152\u5230\u5916\u9762\u8f49\u4e86\u5e7e\u5708\uff0c\u982d\u6688\u4e86\u3001\u632b\u6298\u4e86\u3001\u8dcc\u5012\u4e86\u3001\u7d2f\u4e86\u3001\u88ab\u000d\u000a\u80cc\u53db\u4e86\u3001\u767c\u751f\u4e8b\u60c5\u4e86\uff0c\u7d50\u679c\u5462\uff1f\u56de\u5230\u6700\u521d\u7684\u539f\u9ede\uff0c\u9084\u662f\u5728\u4fee\u9053\uff0c\u9019\u5c31\u000d\u000a\u662f\u300c\u4fee\u6027\u4e86\u547d\u300d\u7684\u91cd\u8981\u3002\u5f92\u5152\u4f86\u9078\u500b\u6c34\u679c\uff0c\u4f86\u505a\u500b\u7d00\u5ff5\u5427\uff01\u4f86\u4f5b\u5802\u600e\u000d\u000a\u9ebc\u80fd\u6c92\u6709\u62ff\u500b\u6771\u897f\u7d00\u5ff5\u5462\uff1f\u000d\u000a\u4f46\u662f\u9019\u500b\u6c34\u679c\u80fd\u7d00\u5ff5\u591a\u4e45\uff1f\u6c34\u679c\u73fe\u5728\u737b\u4e0a\u53bb\u7684\u90fd\u5f88\u6574\u9f4a\u3001\u90fd\u5f88\u65b0\u000d\u000a\u9bae\uff0c\u90a3\u5b83\u6703\u4e0d\u6703\u6709\u8150\u6557\u7684\u4e00\u5929\u554a\uff1f\uff08\u6703\uff09\u662f\u554a\uff01\u6240\u4ee5\u4e16\u9593\u7684\u6771\u897f\u54ea\u4e00\u6a23\u000d\u000a\u662f\u771f\u7684\uff1f\uff08\u6c92\u6709\uff09\u6709\u4eba\u60f3\u8aaa\uff1a\u4ed9\u4f5b\u662f\u771f\u7684\u554a\uff01\u8b49\u660e\u7d66\u6211\u770b\u5440\uff01\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u5764\uff19\u865f\u8d77\u7acb\uff0c\u4e26\u5c0d\u5979\u6148\u60b2\uff1a\u5f92\u5152\u8acb\u8d77\uff0c\u634f\u634f\u70ba\u5e2b\u7684\u624b\uff0c\u662f\u771f\u7684\u000d\u000a\u55ce\uff1f\u90a3\u518d\u6478\u6478\u81ea\u5df1\u7684\u624b\u54ea\u88e1\u4e0d\u4e00\u6a23\uff1f\u4e00\u6a23\u554a\uff01\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u5764\uff13\u865f\u3001\u5764\uff14\u865f\u8d77\u7acb\uff0c\u4e26\u5c0d\u5979\u5011\u6148\u60b2\uff1a\u770b\u70ba\u5e2b\u7684\u624b\u6307\u982d\uff0c\u662f\u771f\u000d\u000a\u7684\u55ce\uff1f\u771f\u7684\u55ce\uff1f\uff08\u662f\uff09\u770b\u4ed4\u7d30\u4e00\u9ede\u5440\uff01\u90a3\u518d\u6478\u6478\u81ea\u5df1\u7684\u6709\u6c92\u6709\u4e00\u6a23\uff1f\uff08\u73ed\u000d\u000a\u54e1\u9ede\u982d\u8868\u793a\u4e00\u6a23\uff09\u597d\uff0c\u8acb\u5750\u3002\u000d\u000a\u70ba\u5e2b\u4e5f\u662f\u501f\u9019\u500b\u8089\u9ad4\u4f86\u8ddf\u5927\u5bb6\u7d50\u500b\u7de3\uff0c\u90a3\u5f92\u5152\u5011\u4f55\u5617\u4e0d\u662f\u501f\u9019\u500b","9\u000d\u000a\u8089\u9ad4\u4f86\u8ddf\u4e16\u9593\u7d50\u7de3\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u6240\u4ee5\u554a\uff01\u9019\u500b\u8eab\u9ad4\u4e0d\u662f\u771f\u7684\u3001\u4e0d\u662f\u000d\u000a\u9577\u4e45\u7684\uff0c\u6709\u4e00\u5929\u4e5f\u6703\u4e0d\u5728\u9019\u500b\u4e16\u4e0a\u3002\u000d\u000a\u90a3\u4ec0\u9ebc\u662f\u771f\u7684\u5462\uff1f\u4eca\u5929\u5e2b\u5f92\u6703\u9762\uff0c\u4ec0\u9ebc\u7b97\u771f\u5462\uff1f\u2500\u2500\u000d\u000a\u5fc3\u8ddf\u5fc3\u7684\u76f8\u898b\u3001\u5fc3\u8ddf\u5fc3\u7684\u9818\u6703\u3001\u5fc3\u8ddf\u5fc3\u7684\u7d50\u5408\uff0c\u9019\u624d\u662f\u7d14\u771f\u7684\u3002\u000d\u000a\u4e0d\u6703\u56e0\u70ba\u5225\u4eba\u8aaa\uff1a\u300c\u597d\u5047\u554a\uff0c\u5728\u8aaa\u4ec0\u9ebc\u554a\uff01\u300d\u771f\u7684\u5c31\u8b8a\u5047\uff1b\u000d\u000a\u4e5f\u4e0d\u6703\u56e0\u70ba\u300c\u9019\u4f5b\u5177\u771f\u838a\u56b4\u554a\uff0c\u597d\u50cf\u771f\u7684\u3002\u300d\u5c31\u8b8a\u771f\u7684\u3002\u000d\u000a\u53ea\u8981\u662f\u4eba\u4e16\u9593\u7684\u7269\u3001\u8c61\u3001\u5f62\uff0c\u6709\u4e00\u5929\u90fd\u6703\u5e7b\u6ec5\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff14\u865f\u6148\u60b2\uff1a\u4f46\u662f\u5f92\u5152\u6709\u6c92\u6709\u89ba\u5f97\uff0c\u9019\u4e9b\u5f62\u8c61\u3001\u7269\u8cea\u5f88\u8a98\u60d1\u000d\u000a\u4eba\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u5764\uff14\u865f\u9ede\u9ede\u982d\uff09\u662f\u7684\uff0c\u6c92\u932f\uff0c\u70ba\u5e2b\u4e5f\u89ba\u5f97\uff0c\u70ba\u4ec0\u9ebc\u4eba\u90fd\u000d\u000a\u6703\u88ab\u9019\u4e9b\u300c\u98df\u8863\u4f4f\u884c\u300d\u8a98\u60d1\u5462\uff0c\u662f\u4e0d\u662f\uff1f\u4f46\u662f\u660e\u660e\u770b\u9019\u500b\u6771\u897f\u5c31\u662f\u5f88\u000d\u000a\u597d\u7684\uff0c\u670b\u53cb\u5011\u90fd\u6709\uff0c\u81ea\u5df1\u662f\u4e0d\u662f\u4e5f\u8981\u76e1\u91cf\u6709\uff1f\uff08\u9ede\u50b3\u5e2b\uff1aiphone \u624b\u6a5f\uff09\u53ef\u000d\u000a\u662f\u5982\u679c\u9019\u500b\u5fc3\u5e38\u5e38\u653e\u5728\u5916\u9762\uff0c\u8ddf\u4eba\u5bb6\u9019\u6a23\u2500\u2500\u5225\u4eba\u9019\u6a23\uff0c\u6211\u4e5f\u8981\u9019\u6a23\uff1b\u000d\u000a\u5225\u4eba\u6709\uff0c\u6211\u4e5f\u8981\u6709\u3002\u90a3\u5f92\u5152\u5011\u7684\u5fc3\uff0c\u662f\u4e0d\u662f\u5e38\u5e38\u653e\u5728\u5916\u9762\u3002\uff08\u662f\uff09\u501f\u70ba","10\u000d\u000a\u5e2b\u4f5c\u500b\u6bd4\u55bb\uff0c\u8acb\u5750\u3001\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u525b\u9078\u597d\u4f9b\u679c\u7684\u4e7e\uff17\u865f\u6148\u60b2\uff1a\u9078\u4e86\u9019\u500b\u679c\u5440\uff01\u597d\uff0c\u62ff\u9ad8\u4e00\u9ede\u7d66\u5927\u000d\u000a\u5bb6\u770b\u4e00\u770b\u3002\u60f3\u4e0d\u60f3\u8981\u5f97\u5230\u9019\u500b\u679c\uff1f\uff08\u60f3\uff09\u597d\u3002\u4e0d\u662f\u53ea\u6709\u62ff\u9019\u500b\u679c\u56de\u5bb6\uff0c\u000d\u000a\u800c\u662f\u8981\u9ad4\u6703\u4e00\u4e0b\uff1a\u9019\u500b\u9053\u8ddf\u4f60\u7684\u4eba\u751f\u63a5\u4e0b\u4f86\u8981\u7dca\u5bc6\u7684\u7d50\u5408\uff0c\u90a3\u624d\u662f\u771f\u000d\u000a\u7684\u6709\u63a5\u6536\u5230\u70ba\u5e2b\u9001\u7d66\u4f60\u7684\u5bf6\uff0c\u8acb\u5750\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u6240\u4ee5\u6709\u53e5\u8a71\u8aaa\uff1a\u300c\u8ca1\u7269\u8f15\uff0c\u6028\u4f55\u751f\uff1b\u8a00\u8a9e\u5fcd\uff0c\u5fff\u81ea\u6c11\u3002\u300d\u3010\u8a3b\u4e8c\u3011\u000d\u000a\u8207\u4eba\u76f8\u8655\u4e0d\u65a4\u65a4\u8a08\u8f03\u8ca1\u7269\uff0c\u6028\u6068\u5c31\u7121\u5f9e\u751f\u8d77\uff1b\u8a00\u8a9e\u80fd\u5920\u5305\u5bb9\u5fcd\u8b93\uff0c\u000d\u000a\u591a\u8aaa\u597d\u8a71\uff0c\u4e0d\u8aaa\u58de\u8a71\uff0c\u5fcd\u4f4f\u6c23\u8a71\uff0c\u4e0d\u5fc5\u8981\u7684\u885d\u7a81\u3001\u6028\u6068\u7684\u4e8b\u60c5\u81ea\u7136\u6d88\u000d\u000a\u5931\u4e0d\u751f\u3002\u000d\u000a\u5f92\u5152\u70ba\u4ec0\u9ebc\u6703\u89ba\u5f97\uff1a\u6709\u6642\u5019\u5fc3\u574e\u597d\u50cf\u75db\u75db\u7684\uff0c\u597d\u50cf\u5361\u4e86\u500b\u6771\u897f\u5728\u000d\u000a\u4e0a\u9762\u3002\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6709\u6c92\u6709\uff1f\u5fc3\u5361\u4e86\u500b\u6771\u897f\u3002\uff09\u6bcf\u500b\u4eba\u90fd\u6709\u9019\u500b\u72c0\u6cc1\u904e\uff0c\u662f\u000d\u000a\u4e0d\u662f\u554a\uff1f\uff08\u662f\uff09\u4f46\u662f\u5462\uff01\u73fe\u5728\u5fc3\u88e1\u9762\u5361\u7684\u662f\u4ec0\u9ebc\u5462\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u7528\u6247\u5b50\u62cd\u62cd\u4e7e\uff15\u865f\u80a9\u8180\uff0c\u4e26\u5c0d\u4ed6\u6148\u60b2\uff1a\u5f92\u5152\uff0c\u4e5f\u662f\u597d\u5f92\u5152\u5462\uff01\u770b","11\u000d\u000a\u770b\u70ba\u5e2b\u6709\u6c92\u6709\u4f86\u8ddf\u4f60\u8aaa\u8aaa\u8a71\u5462\uff01\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u4ed6\u5fc3\u88e1\u5728\u60f3\u9019\u500b\u3002\uff09\u6c92\u95dc\u4fc2\uff0c\u000d\u000a\u73fe\u5728\u773c\u775b\u770b\u5230\u7684\uff0c\u53c8\u6709\u4ec0\u9ebc\u662f\u7d55\u5c0d\u5b58\u5728\u7684\u5462\uff1f\u4f46\u662f\u773c\u775b\u770b\u4e0d\u5230\uff0c\u90a3\u624d\u000d\u000a\u662f\u6c38\u6046\u4e0d\u8870\uff0c\u5c31\u662f\u9019\u500b\u4fee\u9053\uff01\u70ba\u4ec0\u9ebc\u4e00\u76f4\u8aaa\u8981\u4fee\u9053\u5462\uff1f\u9577\u8f29\u3001\u5f15\u4fdd\u5e2b\u000d\u000a\u4e00\u76f4\u8aaa\u8981\u4fee\u9053\u3002\u5176\u5be6\u4e0d\u662f\u4f86\u4f5b\u5802\u597d\u50cf\u505a\u500b\u4f5b\u898f\u79ae\u7bc0\uff0c\u5c31\u662f\u4fee\u9053\u5462\uff01\u4fee\u000d\u000a\u9053\u4e0d\u53ea\u662f\u5728\u9019\u4e9b\u4e8b\u7269\u4e0a\u3002\u000d\u000a\u5c31\u50cf\u8fa6\u4e8b\u4eba\u54e1\u554a\uff0c\u554f\u554f\u4ed6\u5011\uff1a\u5982\u679c\u5728\u8fa6\u4e8b\u7684\u6642\u5019\uff0c\u4e0d\u5c0f\u5fc3\u8ddf\u65c1\u908a\u000d\u000a\u7684\u4eba\u767c\u751f\u4e86\u78e8\u64e6\uff0c\u4ed6\u5fc3\u88e1\u6703\u4e0d\u6703\u5feb\u6a02\uff1f\uff08\u4e0d\u5feb\u6a02\uff09\u662f\u4e0d\u662f\u4e5f\u6703\u6709\u6028\u5c24\u5f9e\u000d\u000a\u88e1\u9762\u8dd1\u51fa\u4f86\u554a\uff1f\u58d3\u4e0d\u4f4f\u3001\u58d3\u4e0d\u4f4f\uff01\u90a3\u9019\u662f\u4fee\u9053\u55ce\uff1f\uff08\u4e0d\u662f\uff09\u6240\u4ee5\uff0c\u4f86\u4f5b\u000d\u000a\u5802\u53ef\u80fd\u4e00\u958b\u59cb\u4e0d\u89ba\u5f97\u6709\u8da3\uff0c\u4f46\u662f\u7b49\u5230\u5f92\u5152\u5167\u5fc3\u6162\u6162\u53bb\u770b\u770b\u751f\u6d3b\u767c\u751f\u7684\u000d\u000a\u4e8b\u60c5\u60f3\u4e00\u60f3\uff0c\u76f8\u4fe1\u5f92\u5152\u90fd\u662f\u6709\u9019\u500b\u4fee\u9053\u7684\u8cc7\u8cea\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u000d\u000a\u9019\u662f\u6709\u6839\u57fa\u7684\uff0c\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff0c\u4ed6\u5fc3\u88e1\u9762\u4e00\u76f4\u5e0c\u671b\u8001\u5e2b\u8ddf\u4ed6\u8b1b\u5e7e\u53e5\u8a71\u554a\uff01\uff09\u8acb\u000d\u000a\u8d77\u3002\u000d\u000a\u5927\u5bb6\u5171\u805a\u4e00\u5802\uff0c\u767e\u822c\u4e0d\u6613\uff0c\u56f0\u96e3\u90fd\u89e3\u6c7a\u4e86\uff0c\u6240\u4ee5\u624d\u6709\u4eca\u65e5\u7684\u6cd5","12\u000d\u000a\u6703\u3002\u000d\u000a\u90a3\u54b1\u5011\u5c31\u8981\u4e00\u8d77\u628a\u5fc3\u56de\u5230\u300c\u6700\u521d\u7684\u539f\u9ede\u300d\u2500\u2500\u000d\u000a\u627e\u56de\u5584\u826f\u7684\u5ff5\u982d\uff0c\u5fa9\u56de\u5e73\u975c\u7684\u5fc3\u9748\uff0c\u91cd\u56de\u6709\u9053\u7684\u5bb6\u5712\u3002\u000d\u000a\u6709\u5bb6\u7684\u4eba\u6eab\u4e0d\u6eab\u6696\u5440\uff1f\uff08\u6eab\u6696\uff09\u90a3\u54ea\u4e00\u500b\u5bb6\u662f\u771f\u6b63\u7684\u5bb6\uff1f\uff08\u7406\u5929\u7684\u000d\u000a\u5bb6\uff09\u90fd\u8aaa\u5f97\u597d\u9060\uff0c\u7406\u5929\u5728\u54ea\u88e1\uff1f\uff08\u5728\u5fc3\u88e1\uff09\u662f\u554a\uff0c\u5728\u5f92\u5152\u7684\u5fc3\u88e1\uff0c\u5e38\u5e38\u000d\u000a\u8aaa\u300c\u7406\u5929\u8981\u56de\u53bb\uff0c\u4e86\u613f\u8981\u5b8c\u6574\uff0c\u4fee\u8fa6\u8981\u7a4d\u6975\u3002\u300d\u4f46\u662f\u5f92\u5152\u5011\u60f3\u4e00\u60f3\uff1a\u000d\u000a\u662f\u4e0d\u662f\u5ffd\u7565\u4e86\u4ec0\u9ebc\uff1f\u9019\u500b\u5fc3\u8981\u4e0d\u8981\u548c\u7766\uff0c\u8981\u4e0d\u8981\u548c\u5e73\uff1f\uff08\u8981\uff09\u60f3\u60f3\u770b\uff1a\u000d\u000a\u4e00\u500b\u4eba\u5728\u4ec0\u9ebc\u6642\u5019\u9762\u76ee\u662f\u919c\u964b\u7684\uff1f\u4e0d\u8981\u8ddf\u70ba\u5e2b\u8aaa\u751f\u4e0b\u4f86\u5c31\u9577\u5f97\u4e0d\u597d\u000d\u000a\u770b\uff0c\u9019\u4e0d\u662f\u7406\u7531\u3002\u4e00\u500b\u4eba\u5728\u5167\u5fc3\u61a4\u6068\u4e0d\u5e73\u7684\u6642\u5019\uff0c\u4ed6\u7684\u9762\u5bb9\u662f\u6700\u919c\u964b\u000d\u000a\u7684\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u5c0d\uff09\u800c\u4e0d\u662f\u4e16\u9593\u50f9\u503c\u4e0a\u7684\u7f8e\u919c\uff0c\u4e0d\u662f\u773c\u775b\u770b\u5230\u7684\u597d\u770b\u6216\u000d\u000a\u4e0d\u597d\u770b\uff0c\u800c\u662f\u6211\u5011\u9019\u500b\u5fc3\u6709\u6c92\u6709\u5e38\u5e38\u653e\u5728\u548c\u5e73\u7684\u72c0\u614b\u5462\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8f03\u5e74\u8f15\u5764\uff12\u865f\u3001\u5764\uff13\u865f\u3001\u5764\uff14\u865f\u6148\u60b2\uff1a\u5e74\u7d00\u8f15\u8f15\u6703\u4e0d\u6703\u6709\u751f\u6c23\u000d\u000a\u7684\u6642\u5019\uff1f\uff08\u6703\uff09\u8acb\u5750\u3001\u8acb\u5750\u3002","13\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u7a0d\u5e74\u9577\u7684\u4e7e\uff12\u865f\u3001\u4e7e\uff13\u865f\u6148\u60b2\uff1a\u90a3\u5e74\u7d00\u6bd4\u8f03\u9577\u4e86\uff0c\u6703\u4e0d\u6703\u6709\u751f\u000d\u000a\u6c23\u7684\u6642\u5019\uff1f\uff08\u6703\uff09\u8acb\u5750\u3001\u8acb\u5750\u3002\u000d\u000a\u90a3\u9ebc\uff0c\u9019\u4e00\u500b\u60c5\u7dd2\uff0c\u70ba\u4ec0\u9ebc\u5e38\u5e38\u8b93\u4fee\u9053\u4eba\u4e0d\u50cf\u4fee\u9053\u4eba\u5462\uff1f\u000d\u000a\u56e0\u70ba\uff0c\u6211\u5011\u7684\u5fc3\u5e38\u5e38\u90fd\u592a\u4ee5\u81ea\u5df1\u70ba\u4e3b\u4e86\u3002\u000d\u000a\u7576\u8ddf\u5225\u4eba\u767c\u751f\u53e3\u89d2\u722d\u57f7\u7684\u6642\u5019\uff0c\u5fc3\u88e1\u7684\u7b2c\u4e00\u500b\u5ff5\u982d\u5c31\u662f\u4e00\u5b9a\u8981\u66ff\u000d\u000a\u81ea\u5df1\u8a0e\u56de\u516c\u9053\uff0c\u90a3\u9019\u500b\u6642\u5019\u8b1b\u8a71\u6703\u4e0d\u6703\u90fd\u662f\u5e6b\u81ea\u5df1\u8b1b\uff1f\uff08\u6703\uff09\u660e\u660e\u81ea\u5df1\u000d\u000a\u4e5f\u6709\u4e00\u4e9b\u4e0d\u5c0d\u7684\u5730\u65b9\uff0c\u4f46\u662f\u901a\u901a\u90fd\u6703\u8b1b\u5230\u662f\u81ea\u5df1\u5c0d\u3002\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6709\u6c92\u000d\u000a\u6709\u9019\u6a23\u7684\u60c5\u5f62\uff1f\uff09\uff08\u6709\uff09\u7576\u5169\u65b9\u90fd\u662f\u7528\u9019\u6a23\u7684\u51fa\u767c\u9ede\u4f86\u6e9d\u901a\u7684\u6642\u5019\uff0c\u6e9d\u901a\u000d\u000a\u5f97\u4e86\u55ce\uff1f\uff08\u6e9d\u901a\u4e0d\u4e86\uff09\u9019\u5c31\u4e0d\u662f\u6709\u9053\u3001\u548c\u5584\u7684\u6e9d\u901a\uff0c\u800c\u662f\u5e36\u8457\u6210\u898b\u3001\u4e0d\u000d\u000a\u6eff\u7684\u6e9d\u901a\u3002\u5fc3\u88e1\u9762\u90fd\u5df2\u7d93\u5806\u7a4d\u5f88\u591a\u4e0d\u597d\u7684\u5fc3\uff0c\u90a3\u5728\u8ddf\u5225\u4eba\u8b1b\u8a71\u6703\u6709\u597d\u000d\u000a\u8a71\u55ce\uff1f\uff08\u6c92\u6709\uff09\u6240\u4ee5\uff0c\u4fee\u9053\u8981\u5f9e\u54ea\u88e1\u958b\u59cb\uff1f\uff08\u5fc3\uff09\u4fee\u5fc3\u958b\u59cb\u3002\u000d\u000a\u5f92\u5152\u5011\u6e96\u5099\u597d\u4fee\u5fc3\u4e86\u55ce\uff1f\uff08\u6e96\u5099\u597d\u4e86\uff09\u83ab\u8981\u8aaa\u7b49\u5230\u5e74\u7d00\u5927\u4e00\u9ede\u518d\u8aaa\uff0c\u000d\u000a\u9700\u8981\u77e5\u9053\u73fe\u4eca\u4eba\u751f\u7121\u4e0d\u7121\u5e38\uff1f\uff08\u7121\u5e38\uff09","14\u000d\u000a\u5176\u5be6\uff0c\u4eba\u751f\u672c\u4f86\u5c31\u662f\u7121\u5e38\uff0c\u000d\u000a\u53ea\u662f\u5982\u679c\u8eab\u908a\u6c92\u6709\u592a\u591a\u7684\u63d0\u9192\u3001\u8b66\u544a\uff0c\u000d\u000a\u4eba\u5e38\u89ba\u5f97\u597d\u50cf\u7121\u5e38\u4e0d\u6703\u767c\u751f\u5728\u81ea\u5df1\u8eab\u4e0a\u3002\u000d\u000a\u96a8\u8457\u5e74\u7d00\u6108\u4f86\u6108\u9577\uff0c\u770b\u5230\u7684\u4e8b\u60c5\u6108\u4f86\u6108\u591a\u4e86\uff0c\u6703\u767c\u89ba\u7121\u5e38\u96a8\u6642\u5c31\u000d\u000a\u5728\u81ea\u5df1\u7684\u9762\u524d\u3002\u6709\u8005\u662f\u64e6\u8eab\u800c\u904e\uff0c\u90a3\u662f\u56e0\u70ba\u6709\u8457\u5584\u7de3\u7684\u642d\u6551\uff0c\u9700\u8981\u611f\u000d\u000a\u6069\uff1b\u6709\u8005\u5927\u4e8b\u5316\u5c0f\uff0c\u53ef\u80fd\u9084\u662f\u6ce2\u53ca\u5230\u4e00\u9ede\u9ede\uff0c\u4e0d\u904e\u9084\u662f\u8981\u611f\u6069\u3002\u70ba\u4ec0\u000d\u000a\u9ebc\uff1f\u56e0\u70ba\u672c\u4f86\u53ef\u80fd\u6703\u66f4\u56b4\u91cd\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u5c0d\uff09\u000d\u000a\u90a3\u6709\u4e9b\u4eba\u8eab\u4e0a\u6709\u8457\u5927\u75c5\u5927\u75db\u600e\u9ebc\u8fa6\uff1f\u662f\u4e0d\u662f\u66f4\u8981\u611f\u6069\u554a\uff1f\uff08\u662f\uff09\u4f46\u000d\u000a\u662f\uff0c\u5982\u679c\u5c0d\u4e00\u500b\u8eab\u9ad4\u5065\u5eb7\u7684\u4eba\u4f86\u8aaa\u611f\u6069\u597d\u5bb9\u6613\u554a\uff0c\u56e0\u70ba\u4e5f\u4e0d\u662f\u767c\u751f\u5728\u000d\u000a\u81ea\u5df1\u8eab\u4e0a\uff0c\u90fd\u77e5\u9053\u8981\u611f\u6069\u3002\u53ef\u662f\uff0c\u6709\u4e00\u5929\u767c\u751f\u5728\u81ea\u5df1\u8eab\u4e0a\u6642\uff0c\u5f92\u5152\u5011\u000d\u000a\u9084\u611f\u6069\u5f97\u4e86\u55ce\uff1f\u5982\u679c\u6709\u4e00\u5929\u707d\u798d\u662f\u767c\u751f\u5728\u5f92\u5152\u5011\u7684\u8eab\u4e0a\uff0c\u5f92\u5152\u5011\u5fc3\u4e2d\u000d\u000a\u9084\u76f8\u4fe1\u9053\u55ce\uff1f\uff08\u76f8\u4fe1\uff09\u90a3\u5c31\u662f\u5728\u5e73\u6642\u9053\u7406\u7684\u6db5\u990a\u3001\u767c\u5fc3\u7684\u7a0b\u5ea6\u8981\u63d0\u8d77\u4f86\uff0c\u000d\u000a\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09","15\u000d\u000a\u4f86\u4f5b\u5802\u4e0d\u662f\u53ea\u6709\u73ed\u54e1\u5728\u4fee\u9053\uff0c\u8fa6\u4e8b\u4eba\u54e1\u4e5f\u5728\u4fee\u9053\uff0c\u4efb\u4f55\u7684\u4eba\u90fd\u5728\u000d\u000a\u4fee\u9053\uff0c\u9019\u6a23\u624d\u662f\u4e00\u500b\u771f\u6b63\u4fee\u9053\u7684\u4f5b\u5802\u3002\uff08\u662f\uff09\u000d\u000a\u3010\u4ed9\u4f5b\u6279\u8a13\u4e2d\u3011\u25cb\u6674\u5f92\u5152\u5beb\u5427\uff01\u5f92\u5152\u62ff\u7b46\u5beb\u6c92\u95dc\u4fc2\uff0c\u4e92\u76f8\u5e6b\u52a9\u662f\u5f88\u597d\u7684\u3002\uff08\u5f35\u000d\u000a\u9ede\u50b3\u5e2b\uff1a\u4ed9\u4f5b\u7684\u8a13\u4e0d\u5bb9\u6613\u6284\uff0c\u807d\u4e0d\u6e05\u695a\u3002\uff09\u000d\u000a\u25ce\u8001\u5e2b\u8d70\u5230\u5764\u9053\u9673\u9ede\u50b3\u5e2b\u25cb\u82f1\u524d\u9762\uff0c\u7528\u6247\u5b50\u62cd\u62cd\u5979\u7684\u80a9\u8180\u3002\u000d\u000a\u5f92\u5152\u5011\u73fe\u5728\u600e\u9ebc\u597d\u50cf\u6c92\u4ec0\u9ebc\u8868\u60c5\u5462\uff1f\u662f\u4e0d\u662f\u6234\u8457\u9019\u500b\uff08\u6307\u53e3\u7f69\uff09\uff0c\u000d\u000a\u5c31\u8b93\u81ea\u5df1\u7684\u8868\u60c5\u6f38\u6f38\u96b1\u85cf\u5728\u5b83\u7684\u5f8c\u9762\u4e86\uff0c\u53cd\u6b63\u4e5f\u6c92\u6709\u4eba\u770b\u5f97\u5230\uff0c\u591a\u65b9\u000d\u000a\u4fbf\u5440\uff01\u770b\u4e0d\u51fa\u4f86\u6211\u73fe\u5728\u662f\u4ec0\u9ebc\u72c0\u614b\u3002\u6c92\u95dc\u4fc2\uff0c\u4f46\u662f\u70ba\u5e2b\u5e0c\u671b\u5f92\u5152\u5011\u81f3\u000d\u000a\u5c11\u5fc3\u88e1\u9762\uff0c\u8981\u662f\u7709\u958b\u773c\u7b11\u7684\uff0c\u8981\u662f\u5982\u540c\u78a7\u6d77\u9752\u5929\u4e00\u6a23\uff0c\u6709\u8457\u809a\u91cf\u3001\u6709\u000d\u000a\u8457\u5bec\u5bb9\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u96e3\u5f97\u6fdf\u516c\u6d3b\u4f5b\u8001\u5e2b\u4f86\uff0c\u5927\u5bb6\u8981\u4e0d\u8981\u7528\u771f\u9762\u76ee\u8ddf\u8001\u5e2b\u898b\u9762\uff1f\u53e3\u7f69\u000d\u000a\u62ff\u4e0b\uff0c\u96e3\u5f97\u4e00\u6b21\uff09\u000d\u000a\u7b49\u7b49\uff0c\u6234\u8457\u3001\u6234\u8457\u3002\u9019\u70ba\u5e2b\u8981\u8ca0\u9019\u500b\u8cac\u4efb\u561b\uff0c\u96d6\u7136\u662f\u6c92\u6709\u95dc\u4fc2\uff0c","16\u000d\u000a\u4f46\u662f\u85c9\u7531\u9019\u500b\u6a5f\u6703\uff0c\u96e3\u5f97\u70ba\u5e2b\u60f3\u8981\u771f\u6b63\u7684\u4e86\u89e3\u6bcf\u4e00\u4f4d\u5f92\u5152\u7684\u5fc3\uff0c\u800c\u4e0d\u000d\u000a\u662f\u81c9\u9577\u4ec0\u9ebc\u6a23\u5b50\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u60f3\u60f3\u770b\uff0c\u5e73\u5e38\u6700\u5e38\u8ddf\u8ab0\u5435\u67b6\u554a\uff1f\uff08\u7236\u6bcd\uff09\u7236\u6bcd\u9084\u6709\u8eab\u65c1\u6700\u89aa\u8fd1\u7684\u4eba\uff0c\u000d\u000a\u5c0d\u4e0d\u5c0d\uff1f\u56e0\u70ba\u8ddd\u96e2\u8d8a\u8fd1\u8d8a\u4e0d\u6703\u906e\u906e\u63a9\u63a9\u7684\uff0c\u60f3\u8b1b\u4ec0\u9ebc\u5c31\u8b1b\u4ec0\u9ebc\uff0c\u6703\u4e0d\u000d\u000a\u6703\u5f88\u5bb9\u6613\u8d77\u53e3\u89d2\uff0c\u6709\u7684\u5bb9\u6613\uff0c\u4f46\u662f\u6709\u7684\u5435\u4e45\u4e86\uff0c\u77e5\u9053\u5c0d\u65b9\u70ba\u4ec0\u9ebc\u6703\u751f\u000d\u000a\u6c23\uff0c\u6240\u4ee5\u76e1\u91cf\u907f\u958b\u90a3\u500b\u5730\u65b9\u5c0d\u4e0d\u5c0d\uff1f\uff08\u5c0d\uff09\u9019\u5c31\u662f\u4e92\u76f8\u5305\u5bb9\uff0c\u4f46\u6709\u7684\u6642\u000d\u000a\u5019\u9084\u662f\u6703\u5167\u5fc3\u50cf\u662f\u85cf\u8457\u4e00\u982d\u731b\u7378\u4e00\u6a23\uff0c\u60f3\u8981\u885d\u51fa\u4f86\uff0c\u60f3\u8981\u5147\u5c0d\u65b9\u5e7e\u53e5\uff0c\u000d\u000a\u6709\u6c92\u6709\u554a\uff01\uff08\u6709\uff09\u90a3\u5f92\u5152\u5011\u5167\u5fc3\uff0c\u73fe\u5728\u81ea\u5df1\u671f\u8a31\u81ea\u5df1\uff0c\u5e73\u5e38\u6700\u5e38\u8ddf\u8ab0\u5435\u000d\u000a\u67b6\uff0c\u56de\u53bb\u4e4b\u5f8c\uff0c\u6bcf\u4e00\u5929\u90fd\u8981\u5c0d\u90a3\u500b\u4eba\u8aaa\u4e09\u53e5\u597d\u8a71\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u9019\u6a23\u000d\u000a\u5b50\u6709\u56f0\u96e3\u55ce\uff1f\uff08\u6c92\u6709\uff09\u4e0d\u56f0\u96e3\u561b\uff01\u56e0\u70ba\uff0c\u4f60\u5011\u90fd\u5750\u5728\u9019\u908a\u807d\u4e86\u9019\u9ebc\u591a\u53f0\u000d\u000a\u4e0a\u7684\u4eba\u8aaa\u4e86\u90a3\u9ebc\u591a\u7684\u597d\u8a71\uff0c\u597d\u50cf\u4e5f\u6c92\u6709\u9019\u9ebc\u96e3\u5427\uff01\uff08\u662f\uff09\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6bcf\u4e00\u5929\u5594\uff01\u4e0d\u662f\u4e00\u8f29\u5b50\u8aaa\u4e09\u53e5\u597d\u8a71\u5594\uff01\uff09\u000d\u000a\u3010\u4ed9\u4f5b\u6279\u8a13\u4e2d\u3011\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6284\u4e0d\u5bb9\u6613\u5594\uff01\u9019\u662f\u570b\u6587\u8001\u5e2b\u8036\uff01\uff09","17\u000d\u000a\u6642\u9593\u771f\u662f\u975e\u5e38\u5f97\u5feb\uff0c\u4e0d\u662f\u53ea\u6709\u9019\u5e7e\u5929\u7684\u6642\u9593\u5feb\uff0c\u662f\u4eba\u7684\u4e00\u751f\u975e\u5e38\u000d\u000a\u5f97\u5feb\uff0c\u5e38\u8b1b\u300c\u4e00\u5e74\u6625\u4e8b\u90fd\u4f86\u5e7e\uff0c\u65e9\u904e\u4e86\uff0c\u4e09\u4e4b\u4e8c\u300d\u3010\u8a3b\u4e09\u3011\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u6d2a\u59d0\u25cb\u672c\u6148\u60b2\uff1a\u6bcf\u4e00\u5e74\u6625\u590f\u79cb\u51ac\u9019\u6a23\u5b50\u8f2a\u8f49\u8f2a\u8f49\uff0c\u000d\u000a\u5f92\u5152\u5011\u90fd\u6709\u4ec0\u9ebc\u6a23\u7684\u611f\u60f3\u5440\uff1f\uff08\u8001\u4e86\u982d\u9aee\u8b8a\u767d\u4e86\uff09\u597d\uff0c\u662f\u554a\uff01\u9019\u662f\u751f\u7406\u000d\u000a\u4e0a\u7684\u8b8a\u5316\uff0c\u5c0d\u4e0d\u5c0d\uff1f\u4f46\u662f\u5fc3\u7406\u4e0a\u6709\u4ec0\u9ebc\u8b8a\u5316\uff1f\uff08\u958b\u5fc3\u554a\uff09\u70ba\u4ec0\u9ebc\u800c\u958b\u5fc3\u000d\u000a\u5462\uff1f\uff08\u8ddf\u5927\u5bb6\u5728\u4e00\u8d77\uff09\u597d\uff0c\u90a3\u5927\u5bb6\u8981\u5e38\u5e38\u5728\u4e00\u8d77\u5440\uff01\u8acb\u5750\uff01\u000d\u000a\u4eba\u771f\u7684\u80fd\u5f97\u5230\u9577\u4e45\u7684\u958b\u5fc3\uff0c\u662f\u56e0\u70ba\u627e\u5230\u4e86\u76ee\u6a19\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff15\u865f\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u627e\u5230\u9019\u500b\u4fee\u9053\u7684\u76ee\u6a19\u4e86\u55ce\uff1f\u70ba\u5e2b\u77e5\u9053\u000d\u000a\u5f92\u5152\u751f\u6d3b\u4e5f\u662f\u5f88\u8f9b\u82e6\u7684\uff0c\uff08\u73ed\u54e1\u88ab\u9ede\u5230\u540d\u99ac\u4e0a\u8dea\u5728\u5730\u4e0a\u4e26\u958b\u59cb\u9010\u6f38\u5927\u8072\u555c\u6ce3\uff09\u000d\u000a\u4f46\u662f\u6c92\u95dc\u4fc2\uff0c\u8981\u5f9e\u9019\u500b\u82e6\u4e2d\u81ea\u5df1\u5f97\u5230\u89e3\u812b\uff0c\u4e0d\u9700\u8981\u74b0\u5883\u6539\u8b8a\u4ec0\u9ebc\uff0c\u53ea\u000d\u000a\u9700\u8981\u6211\u5011\u7684\u5fc3\u5e38\u5e38\u8ddf\u4f5b\u5802\u5728\u4e00\u8d77\uff0c\u56e0\u70ba\u4f5b\u5802\u6703\u6709\u5149\u660e\u7684\u529b\u91cf\u3002\u000d\u000a\u4f86\u500b\u679c\u5427\uff01\uff08\u6b64\u6642\u73ed\u54e1\u66f4\u662f\u611f\u52d5\u5f97\u54ed\u5f97\u5f88\u50b7\u5fc3\uff0c\u5468\u906d\u7684\u8fa6\u4e8b\u4eba\u54e1\u90fd\u6709\u8da8\u000d\u000a\u524d\u6170\u554f\uff09\u6c92\u4e8b\uff01\u6c92\u4e8b\uff01\u975c\u975c\u5fc3\u3001\u975c\u975c\u5fc3\uff0c\u9019\u4eba\u90fd\u4e0d\u597d\u505a\u7684\uff01\u4f86\uff01\u62ff\u8457\uff01","18\u000d\u000a\u62ff\u8457\uff01\u6c92\u4e8b\u4e86\uff01\u597d\u5f92\u5152\u3001\u597d\u5f92\u5152\uff0c\u5750\u8457\uff01\u5750\u8457\uff01\u000d\u000a\u770b\u5f92\u5152\u5011\u8f9b\u82e6\uff0c\u70ba\u5e2b\u5fc3\u88e1\u9762\u66f4\u8f9b\u82e6\u3002\u70ba\u5e2b\u770b\u76e1\u4e86\u5404\u7a2e\u767e\u614b\u4eba\u751f\uff0c\u000d\u000a\u4ec0\u9ebc\u90fd\u6709\uff0c\u96d6\u7136\uff0c\u70ba\u5e2b\u5728\u65c1\u908a\u90fd\u5f88\u60f3\u8981\u5e6b\u52a9\uff0c\u4f46\u6709\u7684\u6642\u5019\uff0c\u6211\u5011\u5c31\u662f\u000d\u000a\u6709\u9019\u500b\u7de3\uff0c\u9700\u8981\u53bb\u628a\u5b83\u4e86\u4e00\u4e86\uff0c\u7528\u6b63\u5411\u7684\u5fc3\u53bb\u8d70\u5c07\u4f86\u7684\u8def\uff0c\u5f92\u5152\u6703\u66f4\u000d\u000a\u597d\u7684\u3002\u000d\u000a\u8acb\u5750\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u5442\u59d0\u25cb\u7389\u6148\u60b2\uff1a\u5f92\u5152\u5440\uff0c\u60f3\u4e0d\u60f3\u8981\u6bcf\u4e00\u5929\u66f4\u958b\u5fc3\uff1f\u000d\u000a\u4e0d\u8981\u6bcf\u5929\u4e00\u5927\u65e9\u8d77\u4f86\uff0c\u5c31\u9762\u5c0d\u5f88\u591a\u4e8b\u60c5\uff0c\u5f88\u7169\u60f1\uff01\u662f\u4e0d\u662f\uff1f\u597d\u50cf\u6574\u5929\u000d\u000a\u4eba\u751f\u90fd\u8ddf\u4e8b\u60c5\u7d81\u5728\u4e00\u8d77\uff0c\u8ddf\u4eba\u7d81\u5728\u4e00\u8d77\u597d\u50cf\u5f88\u4e0d\u8212\u670d\u5462\uff01\u6c92\u95dc\u4fc2\uff01\u6211\u000d\u000a\u5011\u628a\u5fc3\u62bd\u51fa\u4f86\uff0c\u5fc3\u4e0d\u8981\u653e\u5728\u90a3\u88e1\u982d\u8ddf\u5b83\u4e00\u8d77\u652a\uff0c\u4eca\u5929\u5f97\u5230\u4e86\u9019\u500b\u9053\uff0c\u000d\u000a\u77e5\u9053\u53ef\u4ee5\u4fee\uff0c\u90a3\u5c31\u5e38\u5e38\u56de\u4f86\u4f5b\u5802\uff0c\u7814\u7fd2\u9053\u7406\uff0c\u7576\u4e2d\u5c31\u6703\u627e\u5230\u600e\u9ebc\u8b93\u81ea\u000d\u000a\u5df1\u66f4\u5feb\u6a02\uff0c\u4f86\u500b\u679c\uff0c\u8acb\u8d77\u3002\u4f86\u4f86\u4f86\uff0c\u8ddf\u5bb6\u4eba\u4e00\u8d77\u5206\u4eab\u3002","19\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u8001\u5e2b\u90fd\u5f88\u4e86\u89e3\u6211\u5011\u7684\u72c0\u6cc1\uff0c\u5f88\u95dc\u5fc3\u6211\u5011\uff0c\u8b1d\u8b1d\u6d3b\u4f5b\u8001\u5e2b\uff09\u000d\u000a\u8001 \u6148\u60b2\u554a\uff01\u5982\u679c\u9019\u500b\u4e16\u754c\u4e0a\u9023\u70ba\u5e2b\u90fd\u4e0d\u95dc\u5fc3\u5f92\u5152\u4e86\uff0c\u90a3\u9084\u7b97\u000d\u000a\u662f\u70ba\u5e2b\u55ce\uff1f\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u5f92\u5152\u5011\uff01\u4eba\u751f\u6703\u9047\u5230\u5f88\u591a\u7684\u5927\u8d77\u5927\u843d\uff0c\u6709\u4e9b\u4eba\u53ef\u80fd\u9084\u6c92\u9047\u5230\uff0c\u6709\u000d\u000a\u4e9b\u4eba\u9084\u56f0\u5728\u7576\u4e2d\uff0c\u6709\u4e9b\u4eba\u5df2\u7d93\u8d70\u51fa\u4f86\uff0c\u4e26\u4e14\u6d3b\u51fa\u4e0d\u540c\u7684\u6c23\u8c61\uff0c\u9019\u5c31\u662f\u000d\u000a\u4e00\u500b\u4eba\u751f\u7684\u904e\u7a0b\uff0c\u4f46\u662f\u5728\u9019\u500b\u904e\u7a0b\u88e1\u9762\uff0c\u662f\u76f8\u7576\u8271\u8f9b\u7684\uff0c\u6709\u5e7e\u500b\u4eba\u80fd\u000d\u000a\u5920\u7528\u6a02\u89c0\u7684\u5fc3\u3001\u6709\u9053\u7684\u5fc3\u8d70\u51fa\u4f86\u5462\uff1f\u000d\u000a\u770b\u770b\u540c\u6a23\u662f\u4e00\u4ef6\u4e8b\u60c5\uff0c\u6bcf\u4e00\u4f4d\u5f92\u5152\u9762\u5c0d\uff0c\u627f\u53d7\u7684\u529b\u9053\u4e00\u4e0d\u4e00\u6a23\uff1f\u000d\u000a\uff08\u4e0d\u4e00\u6a23\uff09\u6709\u4e9b\u4eba\u53ef\u80fd\u53ea\u662f\u88ab\u5538\u4e86\u4e00\u5169\u53e5\uff0c\u5c31\u4e0d\u60f3\u4f86\u4f5b\u5802\u4e86\uff0c\u6216\u8005\u662f\uff0c\u000d\u000a\u5c31\u4e0d\u518d\u8ddf\u9019\u500b\u4eba\u8aaa\u8a71\u4e86\uff0c\u6709\u4e9b\u4eba\u88ab\u5538\u4e86\u4e00\u5169\u53e5\uff0c\u89ba\u5f97\u6c92\u95dc\u4fc2\uff0c\uff08\u9084\u662f\u4f86\uff09\u000d\u000a\u662f\uff0c\u81ea\u5df1\u505a\u5f97\u4e0d\u597d\u81ea\u5df1\u6539\u9032\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u5225\u4eba\u600e\u9ebc\u6a23\u6c92\u95dc\u4fc2\uff1f\u5982\u679c\u000d\u000a\u628a\u5225\u4eba\u7684\u4e8b\u60c5\uff0c\u62c9\u5230\u81ea\u5df1\u5fc3\u88e1\u9762\u4f4f\u4e0b\u4f86\uff0c\u4f60\u8ddf\u9019\u4e9b\u7169\u60f1\u4f4f\u5728\u4e00\u8d77\uff0c\u600e\u000d\u000a\u9ebc\u53ef\u80fd\u5feb\u6a02\u5462\uff1f\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\uff0c\u4f46\u662f\uff0c\u4eba\u5e38\u6709\u4e00\u500b\u5fc3\u5ff5\uff0c\u8b93\u81ea\u5df1\u5f88\u7169","20\u000d\u000a\u60f1\uff0c\u7169\u60f1\u662f\u81ea\u5df1\u7684\u5fc3\u88fd\u9020\u51fa\u4f86\u7684\uff0c\u660e\u767d\u55ce\uff1f\uff08\u660e\u767d\uff09\u000d\u000a\u53e4\u4eba\u8b1b\uff1a\u300c\u8a18\u61b6\u7d30\u6545\u975e\u9ad8\u8ce2\u300d\u3010\u8a3b\u56db\u3011\u000d\u000a\u5982\u679c\u4f60\u5fc3\u88e1\u9762\u5e38\u5e38\u8a18\u9019\u4e9b\u5c0f\u4e8b\u60c5\u3001\u5c0f\u7d1b\u722d\u3001\u9019\u4e9b\u5c0f\u7169\u60f1\uff0c\u9019\u4e9b\u5c0f\u000d\u000a\u8a08\u3001\u5c0f\u4ec7\u6068\uff0c\u8b93\u81ea\u5df1\u5e38\u5e38\u775a\u7725\u5fc5\u5831\u3010\u8a3b\u4e94\u3011\uff0c\u4e00\u9ede\u9ede\u5c0f\u5c0f\u7684\u4ec7\u6068\u90fd\u4e00\u5b9a\u000d\u000a\u8981\u5831\u9084\u7d66\u5225\u4eba\uff0c\u9019\u6a23\u7684\u4eba\u4e0d\u5c31\u662f\u628a\u6574\u500b\u751f\u547d\u90fd\u4ea4\u7d66\u5225\u4eba\u53bb\u9867\u4e86\u55ce\uff1f\u8ddf\u000d\u000a\u5728\u5225\u4eba\u5f8c\u9762\uff0c\u5225\u4eba\u600e\u9ebc\u6a23\uff0c\u81ea\u5df1\u600e\u9ebc\u6a23\uff0c\u90a3\u9019\u6a23\u80fd\u6d3b\u51fa\u5c6c\u65bc\u81ea\u5df1\u7684\u4e00\u000d\u000a\u689d\u8def\u55ce\uff1f\uff08\u4e0d\u80fd\uff09\u70ba\u4ec0\u9ebc\u6703\u611f\u89ba\u4e0d\u81ea\u5728\uff0c\u56e0\u70ba\u5f92\u5152\u5011\u9084\u6c92\u6d3b\u51fa\u81ea\u5df1\u7684\u4e00\u000d\u000a\u689d\u8def\uff0c\u4ec0\u9ebc\u53eb\u505a\u81ea\u5df1\u7684\u4e00\u689d\u8def\u5462\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff14\u865f\u6148\u60b2\uff1a\u5c31\u662f\u7576\u5225\u4eba\u8aaa\u4f60\u600e\u9ebc\u6a23\u7684\u6642\u5019\uff0c\u4f60\u4e0d\u5728\u610f\uff0c\u9019\u000d\u000a\u6a23\u662f\u4e0d\u662f\u6d3b\u51fa\u81ea\u5df1\u7684\u4e00\u689d\u8def\uff1f\uff08\u662f\uff09\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff12\u865f\u6148\u60b2\uff1a\u7576\u5225\u4eba\u8aaa\u4f60\u54ea\u88e1\u4e0d\u597d\u7684\u6642\u5019\uff0c\u4f60\u8ddf\u4ed6\u8b1d\u8b1d\uff01\u7136\u000d\u000a\u5f8c\u81ea\u5df1\u6162\u6162\u5730\u6539\u9032\uff0c\u9019\u662f\u4e0d\u662f\u81ea\u5728\uff1f\uff08\u662f\uff09\u8acb\u5750\uff01\u8acb\u5750\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\uff11\u865f\u6148\u60b2\uff1a\u90a3\u5982\u679c\u6709\u4eba\u8cac\u5099\u5f92\u5152\u7684\u6642\u5019\uff0c\u80fd\u4e0d\u80fd\u5920\u4e0d\u653e\u5728","21\u000d\u000a\u5fc3\u4e0a\uff0c\u9084\u80fd\u5920\u7b11\u81c9\u53bb\u5c0d\u5f85\u4ed6\u5462\uff1f\u53bb\u6210\u5168\u4ed6\u5462\uff1f\uff08\u53ef\u4ee5\uff09\u000d\u000a\u5c31\u662f\u6709\u9019\u500b\u5fd7\u5411\uff0c\u6240\u4ee5\u8981\u7167\u8457\u53bb\u505a\uff0c\u5982\u679c\u5f92\u5152\u80fd\u5920\u771f\u7684\u9019\u6a23\u505a\u51fa\u000d\u000a\u4f86\u4e86\uff0c\u505a\u4e45\u4e86\uff0c\u9019\u500b\u300c\u9053\u4e4b\u82ac\u82b3\u3001\u9053\u4e4b\u990a\u5206\u300d\uff0c\u5c31\u6703\u5728\u5f92\u5152\u5fc3\u4e2d\uff0c\u6c92\u000d\u000a\u6709\u4eba\u6436\u5f97\u8d70\u7684\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u70ba\u4ec0\u9ebc\u70ba\u5e2b\u770b\u8d77\u4f86\u597d\u50cf\u6c92\u6709\u4ec0\u9ebc\uff1f\u771f\u7684\u662f\u70ba\u5e2b\u6c92\u6709\u9047\u5230\u4ec0\u9ebc\u7169\u60f1\u000d\u000a\u7684\u4e8b\u60c5\u55ce\uff1f\u70ba\u5e2b\u65e5\u65e5\u773c\u524d\u770b\u898b\u7684\uff0c\u90fd\u662f\u773e\u751f\u82e6\u6d77\u7684\u73fe\u8c61\uff0c\u600e\u9ebc\u53ef\u80fd\u4e0d\u000d\u000a\u7169\u60f1\u5462\uff1f\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u4f60\u5011\u8aaa\u70ba\u5e2b\u6d41\u4e86\u591a\u5c11\u6dda\u5462\uff1f\u70ba\u5e2b\u7684\u5fc3\u75db\u4e86\u591a\u5c11\u000d\u000a\u5462\uff1f\u4f46\u662f\uff0c\u70ba\u5e2b\u76f8\u4fe1\u6bcf\u4e00\u4f4d\u5f92\u5152\uff0c\u90fd\u6703\u6709\u96e8\u904e\u5929\u6674\u7684\u90a3\u4e00\u5929\uff0c\u96d6\u7136\u8eab\u000d\u000a\u9677\u56f0\u5883\u74f6\u9838\uff0c\u4f46\u662f\u70ba\u5e2b\u76f8\u4fe1\uff0c\u53ea\u8981\u70ba\u5e2b\u4e0d\u65b7\u7684\u7d66\u4e88\u95dc\u611b\uff0c\u7d66\u4e88\u95dc\u61f7\uff0c\u000d\u000a\u90a3\u9ebc\u5f92\u5152\u5011\u6c38\u9060\u90fd\u6703\u77e5\u9053\uff0c\u4fee\u9053\u9019\u4e00\u689d\u8def\uff0c\u4e0d\u53ef\u4ee5\u534a\u9014\u800c\u5ee2\uff0c\u662f\u4e0d\u662f\uff1f\u000d\u000a\uff08\u662f\uff09\u90a3\u70ba\u4ec0\u9ebc\u6709\u4e9b\u4eba\u6703\u60f3\u8981\u534a\u9014\u800c\u5ee2\u5462\uff1f\u662f\u56e0\u70ba\u9019\u689d\u8def\u771f\u7684\u4e0d\u597d\u8d70\u000d\u000a\u55ce\uff1f\u9084\u662f\u56e0\u70ba\u4f86\u4f5b\u5802\u8981\u505a\u7684\u4e8b\u60c5\u592a\u591a\u5462\uff1f\uff08\u4e0d\u662f\uff09\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6703\u4e0d\u6703\u89ba\u5f97\u7d2f\uff0c\u4f86\u4f5b\u5802\uff1f\uff09\uff08\u4e0d\u6703\uff09","22\u000d\u000a\u4fee\u9053\u4e0d\u80fd\u5920\u8b93\u5fc3\u6c92\u6709\u4e86\u300c\u9053\u529b\u300d\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8cf4\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u770b\u770b\u5f92\u5152\u5982\u679c\u4fee\uff0c\u80fd\u6709\u9053\u529b\uff0c\u80f8\u4e2d\u80fd\u6709\u5fd7\u5411\uff0c\u000d\u000a\u90a3\u9084\u6703\u89ba\u5f97\u4fee\u9053\u56f0\u96e3\u55ce\uff1f\uff08\u4e0d\u56f0\u96e3\uff09\u662f\u5440\uff01\u000d\u000a\u73fe\u5728\u662f\u4e0d\u662f\u5f88\u5b89\u975c\u5440\uff01\uff08\u662f\uff09\u90a3\u5728\u8046\u807d\u8b1b\u8005\u8b1b\u8ab2\u7684\u6642\u5019\uff0c\u80fd\u4fdd\u6301\u9019\u000d\u000a\u6a23\u7684\u5b89\u975c\u55ce\uff1f\uff08\u80fd\uff09\u662f\u5440\uff01\u5b89\u975c\u5c08\u5fc3\u7684\u807d\u8b1b\uff0c\u9019\u662f\u4e0d\u662f\u5c0a\u91cd\u81ea\u5df1\u53c3\u52a0\u9019\u000d\u000a\u5834\u6cd5\u6703\u3002\uff08\u662f\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u912d\u58c7\u4e3b\u25cb\u6cbb\u7684\u5b6b\u5973\u6148\u60b2\uff1a\u5c0f\u5f92\u5152\u5440\uff01\u4ee5\u5f8c\u5e38\u5e38\u4f86\uff0c\u8ddf\u000d\u000a\u8457\u9577\u8f29\u4e00\u8d77\u4f86\u958b\u667a\u6167\u3002\u000d\u000a\u3010\u4ed9\u4f5b\u6279\u8a13\u4e2d\u3011\u000d\u000a\u662f\u770b\u7684\u4eba\u8f15\u9b06\uff0c\u9084\u662f\u5beb\u7684\u4eba\u8f15\u9b06\u554a\uff1f\uff08\u662f\u770b\u7684\u4eba\u8f15\u9b06\u554a\uff01\uff09\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5beb\u4e86\u4e00\u982d\u6c57\uff0c\u597d\u50cf\u662f\u4e00\u9996\u6b4c\uff09\u000d\u000a\u5f92\u5152\u5011\u6709\u6c92\u6709\u7b49\u8457\u770b\u70ba\u5e2b\u7b49\u4e45\u4e86\u5462\uff1f\uff08\u6709\u554a\uff09\u90fd\u5728\u60f3\u554a\uff01\u4e0d\u662f\u8aaa\u6709\u000d\u000a\u4ed9\u4f5b\u55ce\uff1f\u4ec0\u9ebc\u6642\u4faf\u4f86\u5440\uff1f\u90a3\u5230\u5e95\u662f\u4f60\u5011\u4f86\u770b\u4ed9\u4f5b\uff0c\u9084\u662f\u4ed9\u4f5b\u4f86\u770b\u4f60\u5011","23\u000d\u000a\u5440\uff1f\uff08\u6709\u7684\u4eba\u56de\u7b54\uff1a\u4f86\u770b\u4ed9\u4f5b\uff1b\u6709\u7684\u4eba\u56de\u7b54\uff1a\u4ed9\u4f5b\u770b\u6211\u5011\u3002\uff09\u000d\u000a\u5728\u4f60\u5011\u9084\u6c92\u6709\u4f86\u4e4b\u524d\uff0c\u4ed9\u4f5b\u65e9\u5c31\u5728\u9019\u88e1\u5566\uff01\u6240\u4ee5\u662f\u4ed9\u4f5b\u770b\u4f60\u5011\u000d\u000a\u5440\uff01\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u770b\u5230\u6d3b\u4f5b\u8001\u5e2b\u9ad8\u8208\u4e0d\u9ad8\u8208\uff1f\uff09\uff08\u9ad8\u8208\uff09\u70ba\u5e2b\u770b\u5230\u5f92\u5152\u4e5f\u000d\u000a\u5f88\u9ad8\u8208\u5462\uff01\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u4f46\u662f\u73fe\u5728\u770b\u5230\u662f\u9ad8\u8208\uff0c\u56e0\u70ba\u5927\u5bb6\u90fd\u5750\u5f97\u633a\u000d\u000a\u633a\u7684\uff0c\u5f88\u6709\u9053\u6c23\u7684\u6a23\u5b50\u3002\u53ef\u662f\u5440\uff01\u6cd5\u6703\u7d50\u675f\u4e4b\u5f8c\uff0c\u70ba\u5e2b\u4e5f\u662f\u6703\u770b\u5230\u5f92\u000d\u000a\u5152\u5440\uff01\u90a3\u500b\u6642\u5019\u600e\u9ebc\u6c92\u6709\u50cf\u73fe\u5728\u4e00\u6a23\u5462\uff1f\u5e73\u5e38\u8d70\u5728\u8def\u4e0a\u70ba\u5e2b\u5c31\u5728\u65c1\u908a\u000d\u000a\u770b\u8457\u5f92\u5152\uff0c\u5fc3\u4e2d\u6709\u6c92\u6709\u8907\u7fd2\u9053\u7406\u554a\uff01\u5728\u8ddf\u7236\u6bcd\u89aa\u9802\u5634\u7684\u6642\u5019\uff0c\u70ba\u5e2b\u4e5f\u000d\u000a\u5728\u770b\u8457\u5f92\u5152\u4ec0\u9ebc\u6642\u5019\u60f3\u901a\u5440\uff1f\u592b\u59bb\u4e4b\u9593\u5728\u722d\u57f7\u7684\u6642\u5019\uff0c\u70ba\u5e2b\u4e5f\u5728\u65c1\u908a\u000d\u000a\u6427\u8457\u6247\u5b50\uff0c\u6709\u8a71\u4e5f\u642d\u4e0d\u4e0a\u5440\uff01\u90a3\u9ebc\uff0c\u770b\u8d77\u4f86\u597d\u50cf\u70ba\u5e2b\u6c92\u6709\u8fa6\u6cd5\u5e6b\u5f92\u5152\u000d\u000a\u5011\u89e3\u6c7a\u751f\u6d3b\u4e0a\u7684\u554f\u984c\uff0c\u4f46\u5176\u5be6\u70ba\u5e2b\u6bcf\u5206\u6bcf\u523b\u90fd\u60f3\u5e6b\u5f92\u5152\u5011\u89e3\u6c7a\u751f\u6d3b\u4e0a\u000d\u000a\u7684\u554f\u984c\u3002\u53ea\u662f\uff0c\u5dee\u5225\u5728\u5f92\u5152\u63a5\u4e0d\u63a5\u6536\u5f97\u5230\u3002\u000d\u000a\u00a9\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u6797\u5144\u25cb\u525b\u6148\u60b2\uff1a\u5f92\u5152\u6709\u63a5\u53d7\u5230\u55ce\uff1f\uff08\u6709\uff09\u662f\u5440\uff0c\u63a5\u6536\u000d\u000a\u5230\u4e86\uff0c\u7136\u5f8c\u5462\uff1f\uff08\u80fd\u505a\uff09\u662f\u5440\uff01\u90a3\u63a5\u4e0b\u4f86\u7684\u8def\uff0c\u76f8\u4fe1\u5f92\u5152\u77e5\u9053\u8981\u600e\u9ebc\u53bb","24\u000d\u000a\u8d70\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u5230\u524d\u9762\u9078\u4e00\u500b\u679c\u4f86\u3002\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6d3b\u4f5b\u8001\u5e2b\u5f88\u6ce8\u610f\u4f60\u000d\u000a\u5594\uff01\uff09\u000d\u000a\u70ba\u5e2b\u662f\u6ce8\u610f\u6bcf\u4e00\u4f4d\u5f92\u5152\u3002\u6c92\u6709\u4e00\u4f4d\u70ba\u5e2b\u4e0d\u95dc\u5fc3\u7684\u5462\uff01\u000d\u000a\u6709\u53e5\u8a71\u8b1b\uff1a\u300c\u9022\u5c71\u958b\u8def\uff0c\u9047\u6c34\u67b6\u6a4b\u3002\u300d\u3010\u8a3b\u516b\u3011\u524d\u9762\u6709\u4e00\u5ea7\u5c71\u8981\u904e\u000d\u000a\u53bb\u8981\u600e\u9ebc\u6a23\uff1f\u5c31\u50cf\u4eba\u751f\u9762\u5c0d\u56f0\u96e3\uff0c\u5982\u679c\u60f3\u8981\u8d70\u904e\u53bb\uff0c\u8981\u8b93\u81ea\u5df1\u7684\u5fc3\u66f4\u000d\u000a\u5805\u5f37\uff0c\u7576\u4f60\u7684\u5fc3\u5f37\u904e\u4e86\u9019\u4e9b\u4f86\u963b\u6b62\u4f60\u4fee\u9053\u7684\u7a2e\u7a2e\u56e0\u7de3\u6642\uff0c\u90a3\u9019\u4e9b\u56e0\u7de3\u000d\u000a\u4e5f\u4e0d\u518d\u662f\u969c\u9053\u56e0\u7de3\uff0c\u56e0\u70ba\u5f92\u5152\u7684\u5fc3\u6bd4\u9019\u4e9b\u969c\u7919\u4f60\u7684\u56e0\u7de3\u66f4\u9ad8\u4e86\u3002\u4e00\u500b\u000d\u000a\u5728\u4f4e\u8655\u4e00\u500b\u5728\u9ad8\u8655\uff0c\u6703\u76f8\u9022\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u000d\u000a\u5982\u679c\u5e38\u5e38\u8ddf\u9019\u4e9b\u963b\u7919\u76f8\u9022\uff0c\u5c31\u8981\u60f3\u4e00\u60f3\u4e86\u2500\u2500\u000d\u000a\u6211\u5011\u662f\u4e0d\u662f\u53ef\u4ee5\u518d\u628a\u300c\u5fc3\u300d\u64f4\u5927\u4e00\u4e9b\uff1f\u000d\u000a\u6211\u5011\u662f\u4e0d\u662f\u53ef\u4ee5\u518d\u628a\u300c\u91cf\u300d\u57f9\u990a\u5f97\u6df1\u4e00\u4e9b\uff1f\u000d\u000a\u672c\u4f86\u9019\u500b\u5bb9\u4e0d\u4e0b\uff0c\u73fe\u5728\u5b78\u7fd2\u8981\u5bb9\u5f97\u4e0b\uff1b\u000d\u000a\u672c\u4f86\u9019\u4ef6\u4e8b\u5fcd\u4e0d\u4e86\uff0c\u73fe\u5728\u8981\u5b78\u7fd2\u5fcd\u5f97\u4e86\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09","25\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u7b54\u61c9\u8001\u5e2b\u4e86\u5594\uff01\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u6797\u5144\u25cb\u525b\u6148\u60b2\uff1a\u9019\u500b\u679c\u662f\u8981\u548c\u8ab0\u5206\u4eab\uff1f\uff08\u8ddf\u5bb6\u4eba\u5206\u4eab\uff09\u000d\u000a\u597d\uff0c\u6050\u6015\u4e0d\u5920\u5427\uff1f\u518d\u591a\u62ff\u5e7e\u500b\u3002\u000d\u000a\u70ba\u5e2b\u8981\u544a\u8a34\u5f92\u5152\uff1a\u751f\u6c23\u6642\u662f\u50b7\u5bb3\u81ea\u5df1\uff0c\u8a08\u8f03\u6642\u662f\u50b7\u5bb3\u81ea\u5df1\uff0c\u000d\u000a\u61f6\u60f0\u6642\u662f\u50b7\u5bb3\u81ea\u5df1\uff0c\u722d\u5f37\u6642\u662f\u50b7\u5bb3\u81ea\u5df1\uff0c\u540c\u6a23\u7684\u4e5f\u662f\u50b7\u5bb3\u5230\u5225\u000d\u000a\u4eba\uff1b\u000d\u000a\u90a3\u548c\u6c23\u6642\u80fd\u5e6b\u52a9\u5225\u4eba\uff0c\u611f\u6069\u6642\u80fd\u5e6b\u52a9\u5225\u4eba\uff0c\u000d\u000a\u52e4\u52de\u6642\u80fd\u5e6b\u52a9\u5225\u4eba\uff0c\u8b19\u865b\u6642\u80fd\u5e6b\u52a9\u5225\u4eba\uff0c\u540c\u6a23\u7684\u4e5f\u5e6b\u52a9\u5230\u81ea\u000d\u000a\u5df1\u3002\u000d\u000a\u25ce\u6797\u5144\u62ff\u4e86\u4e09\u9846\u860b\u679c\u56de\u5230\u5ea7\u4f4d\uff0c\u8001\u5e2b\u770b\u4e86\u4e4b\u5f8c\u7e7c\u7e8c\u5c0d\u6797\u5144\u6148\u60b2\uff1a\u9019\u9ebc\u5c11\u000d\u000a\u554a\uff1f\u5225\u602a\u70ba\u5e2b\u4e0d\u591a\u7d66\u5462\uff1f\uff08\u662f\uff01\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\u3002\uff09\u597d\uff0c\u4e09\u500b\u5c31\u662f\u773e\uff0c\u4ee5\u5f8c\u000d\u000a\u5462\uff1f\u8981\u6e21\u300c\u773e\u300d\u4eba\u4fee\u9053\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff09\u8acb\u5750\u3001\u8acb\u5750\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u8abf\u5bc4\u300a\u6625\u98a8\u543b\u4e0a\u6211\u7684\u81c9\u300b\u3002","26\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u6703\u5531\u9019\u500b\u6b4c\u5427\uff1f\uff09\uff08\u6703\uff09\u4e0d\u9700\u8981\u5148\u7df4\u7fd2\u4e00\u4e0b\u55ce\uff1f\u5927\u5bb6\u000d\u000a\u5fc3\u4e2d\u6709\u500b\u5e95\uff0c\u8a72\u7df4\u7684\u7df4\u4e00\u7df4\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\u9053\uff17\u865f\u6148\u60b2\u4e26\u8cdc\u679c\uff1a\u5f92\u5152\u8acb\u8d77\uff01\u4f86\uff0c\u5230\u524d\u9762\u4f86\u3002\u770b\u770b\u9019\u4f4d\u000d\u000a\u5f92\u5152\u4f86\u53c3\u52a0\u6cd5\u6703\u662f\u4e0d\u662f\u4e0d\u5bb9\u6613\uff1f\uff08\u662f\uff09\u5982\u679c\u4f60\u5011\u8aaa\u5750\u6905\u5b50\u5750\u5f97\u75e0\uff0c\u90a3\u9019\u000d\u000a\u4f4d\u5f92\u5152\u662f\u4e0d\u662f\u66f4\u75e0\u5440\uff1f\u662f\u4e0d\u662f\u8a72\u9f13\u500b\u638c\u5440\uff1f\uff08\u73fe\u5834\u97ff\u8d77\u4e00\u7247\u638c\u8072\uff09\u4f86\uff0c\u7d66\u000d\u000a\u5f92\u5152\u52a0\u52a0\u667a\u6167\uff0c\u8acb\u8d77\u3002\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff0c\u7d66\u6211\u5011\u52a0\u667a\u6167\u3002\uff09\u000d\u000a\u5f92\u5152\u5011\u662f\u4e0d\u662f\u4e5f\u5f88\u60f3\u8981\u554a\uff1f\u6bcf\u500b\u4eba\u90fd\u6703\u6709\u7684\uff0c\u770b\u770b\u53ea\u8981\u6709\u4e00\u500b\u4eba\u000d\u000a\u6709\u4e86\u4e4b\u5f8c\uff0c\u5176\u4ed6\u4eba\u5c31\u6703\u958b\u59cb\u60f3\uff0c\u6211\u4ec0\u9ebc\u6642\u5019\u6709\uff1f\u4ec0\u9ebc\u6642\u5019\u63db\u5230\u6211\uff1f\u8001\u000d\u000a\u5e2b\u6709\u6c92\u6709\u5728\u770b\u554a\uff1f\u597d\u50cf\u770b\u5230\u6211\uff0c\u53c8\u597d\u50cf\u6c92\u770b\u5230\u3002\u5509\u5440\uff01\u9019\u4e9b\u60f3\u6cd5\u7528\u5728\u000d\u000a\u9019\u88e1\u6c92\u95dc\u4fc2\uff0c\u4f46\u662f\u5343\u842c\u4e0d\u8981\u51fa\u4e86\u9019\u500b\u9580\u9084\u6709\u9019\u4e9b\u60f3\u6cd5\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u000d\u000a\u5176\u5be6\uff0c\u300c\u751f\u6b7b\u3001\u5b58\u4ea1\u3001\u7aae\u9054\u3001\u8ca7\u5bcc\u3001\u90fd\u662f\u4e8b\u4e4b\u8b8a\uff0c\u547d\u4e4b\u884c\u3002\u300d\u000d\u000a\u3010\u8a3b\u516d\u3011\u9019\u90fd\u53ea\u662f\u5916\u76f8\u7684\u4e8b\u5728\u8b8a\u5316\uff0c\u6bcf\u4e00\u500b\u4eba\u7684\u547d\u9650\u4e0d\u4e00\u6a23\u3002\u5f92\u5152\u5011\u6703\u000d\u000a\u7fa8\u6155\u9577\u58fd\u7684\u4eba\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u4e0d\u6703\u5440\uff1f\u56e0\u70ba\u4eba\u751f\u592a\u82e6\u4e86\u561b\uff01","27\u000d\u000a\uff08\u25ce\u8fa6\u4e8b\u4eba\u54e1\u64cd\u4f5c\u6295\u5f71\u8a2d\u5099\u6642\u5f15\u767c\u8072\u97ff\u3002\uff09\u9019\u500b\uff08\u6307\u6295\u5f71\u8a2d\u5099\uff09\u6bd4\u70ba\u5e2b\u8b1b\u000d\u000a\u8a71\u9084\u6709\u5438\u5f15\u529b\uff0c\u5168\u90e8\u773c\u775b\u90fd\u770b\u904e\u53bb\u4e86\u3002\u000d\u000a\u4f60\u5011\u8aaa\u5c31\u9023\u70ba\u5e2b\u73fe\u5728\u5728\u9019\u88e1\u90fd\u80fd\u8b93\u4f60\u5011\u76ee\u5149\u4e0d\u5b9a\uff0c\u4f55\u6cc1\u662f\u5e73\u5e38\u4f60\u000d\u000a\u5011\u90fd\u770b\u4e0d\u5230\u70ba\u5e2b\u7684\u6642\u5019\uff0c\u76ee\u5149\u53c8\u4f55\u5728\u5462\uff1f\u5728\u9019\u4e9b\u4e94\u984f\u516d\u8272\uff0c\u5728\u9019\u4e9b\u8072\u000d\u000a\u8272\u72ac\u99ac\u4e0a\uff0c\u4e0d\u77e5\u9053\u7528\u4e86\u591a\u5c11\u5fc3\u529b\u5440\uff1f\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5fc3\u5916\u653e\u6bd4\u8f03\u591a\u3002\uff09\u000d\u000a\u90a3\u5fc3\u8981\u600e\u9ebc\u300c\u5167\u6536\u300d\u5462\uff1f\u5f92\u5152\u5011\u6709\u5e73\u975c\u7684\u6642\u5019\u55ce\uff1f\u4e0d\u8981\u8ddf\u70ba\u5e2b\u8aaa\u000d\u000a\u53ea\u6709\u73fe\u5728\u5440\uff01\u5e73\u5e38\u770b\u5f92\u5152\u5011\u5fd9\u5fd9\u5fd9\u554a\uff01\u4e00\u6574\u5929\u90fd\u662f\u52d5\u4f5c\u4e0a\u7684\uff0c\u662f\u4e0d\u662f\u000d\u000a\u5440\uff1f\u6383\u5730\u62d6\u5730\uff0c\u5728\u9019\u4e9b\u52d5\u4f5c\u4e0a\u6642\uff0c\u5f92\u5152\u5011\u7684\u5fc3\u662f\u975c\u7684\u55ce\uff1f\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u000d\u000a\u9084\u662f\u8ddf\u8457\u4e00\u8d77\u52d5\uff1f\uff09\u8ddf\u8457\u4e00\u8d77\u52d5\uff0c\u6050\u6015\u6bd4\u8f03\u591a\uff0c\u5c24\u5176\u662f\u9084\u914d\u4e0a\u8072\u97f3\u7684\u6642\u000d\u000a\u5019\u3002\u000d\u000a\u5225\u4eba\u8b1b\u8a71\u6703\u4e0d\u6703\u5c31\u4f60\u4e00\u8a00\u6211\u4e00\u8a9e\u7684\u8a0e\u8ad6\u8d77\u4f86\uff1f\u4f46\u662f\u8a0e\u8ad6\u7684\u9019\u4e9b\u5167\u000d\u000a\u5bb9\uff0c\u80fd\u505c\u7559\u5e7e\u5929\uff1f\u4eca\u5929\u53c8\u767c\u751f\u4e86\u4e00\u4ef6\u4e8b\u60c5\uff0c\u5927\u5bb6\u4e00\u7aa9\u8702\u7684\u8d77\u4f86\u8a0e\u8ad6\uff0c\u000d\u000a\u904e\u4e86\u4e00\u5169\u500b\u79ae\u62dc\uff0c\u8a0e\u8ad6\u7684\u4e8b\u60c5\u53c8\u8b8a\u6210\u5225\u7684\u4e86\uff0c\u8a0e\u8ad6\u5f97\u5b8c\u55ce\uff1f\uff08\u8a0e\u8ad6\u4e0d","28\u000d\u000a\u5b8c\uff09\u90a3\u4ec0\u9ebc\u6642\u5019\u8a0e\u8ad6\u5230\u81ea\u5df1\u5462\uff1f\u2500\u2500\u000d\u000a\u8a0e\u8ad6\u5230\u81ea\u5df1\u7684\u5fb7\u696d\u600e\u9ebc\u8fa6\uff1f\u8a0e\u8ad6\u5230\u81ea\u5df1\u7684\u4eba\u751f\u4ee5\u4f55\u70ba\u6b78\u5bbf\uff1f\u000d\u000a\u8a0e\u8ad6\u5230\u81ea\u5df1\u7684\u5152\u5973\u5c07\u4f86\u80fd\u4e0d\u80fd\u4fee\u9053\uff1f\u000d\u000a\u70ba\u4ec0\u9ebc\u4eba\u9593\u7684\u4eba\u90fd\u559c\u6b61\u8a0e\u8ad6\u5916\u9762\u5916\u8868\uff1f\u70ba\u4ec0\u9ebc\u5462\uff1f\u000d\u000a\u5169\u500b\u5b57\uff1a\u300c\u6709\u8da3\u300d\uff0c\u662f\u4e0d\u662f\uff1f\u56e0\u70ba\u6c92\u807d\u904e\uff0c\u6240\u4ee5\u8981\u807d\u4e00\u4e0b\u3002\u000d\u000a\u4f46\u662f\u8056\u8ce2\u662f\u5b78\u7fd2\u4ec0\u9ebc\u5462\uff1f\u300c\u5b78\u5982\u4e0d\u53ca\uff0c\u7336\u6050\u5931\u4e4b\u300d\u3010\u8a3b\u4e03\u3011\u000d\u000a\u662f\u5728\u5b78\u5916\u76f8\u7684\u77e5\u898b\u55ce\uff1f\uff08\u4e0d\u662f\uff09\u6709\u770b\u5230\u54ea\u4e00\u4f4d\u8056\u8ce2\u662f\u5728\u5b78\u9019\u4e9b\u5916\u8868\u000d\u000a\u4e0a\u7684\u6771\u897f\u4e00\u76f4\u8ffd\u9010\u55ce\uff1f\uff08\u6c92\u6709\uff09\u90fd\u662f\u600e\u9ebc\u6a23\u5462\uff1f\u90fd\u662f\u53cd\u7701\u81ea\u5df1\uff0c\u6709\u6c92\u6709\u000d\u000a\u5c0d\u5225\u4eba\u597d\u4e00\u9ede\uff1f\u6709\u6c92\u6709\u5c0d\u5bb6\u4eba\u95dc\u5fc3\u4e00\u9ede\uff1f\u6709\u6c92\u6709\u5c0d\u9053\u4f34\u591a\u9f13\u52f5\u4e00\u9ede\uff1f\u000d\u000a\u70ba\u5e2b\u770b\u6bcf\u4e00\u4f4d\u5f92\u5152\u5440\uff01\u6b63\u895f\u5371\u5750\uff0c\u597d\u50cf\u96a8\u6642\u70ba\u5e2b\u90fd\u6703\u9ede\u540d\u4e00\u6a23\u000d\u000a\u554a\uff01\u662f\u4e0d\u662f\u554a\uff0c\u5f92\u5152\u5011\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u65c1\u807d\u524d\u8ce2\u5468\u59d0\u4f73\u25cb\u3001\u5468\u59d0\u83ef\u25cb\u6148\u60b2\uff1a\u8fa6\u4e8b\u4eba\u54e1\u3001\u9084\u6709\u5f92\u5152\u5462\uff0c\u000d\u000a\u6709\u6c92\u6709\u559c\u6b61\u4f86\u4f5b\u5802\u554a\uff1f\uff08\u559c\u6b61\uff09\u771f\u7684\u55ce\uff1f\u70ba\u5e2b\u4e5f\u559c\u6b61\u5f92\u5152\u5011\u4f86\uff0c\u9053\u5834\u662f","29\u000d\u000a\u4e0d\u662f\u8981\u4e0d\u65b7\u7684\u50b3\u627f\u5462\uff1f\u6709\u8457\u5e74\u8f15\u7684\u5f92\u5152\u4f86\u53ef\u4ee5\u591a\u5b78\u7fd2\u591a\u8fa6\u4e8b\uff0c\u662f\u4e0d\u662f\u000d\u000a\u5f88\u597d\u5462\uff1f\u8acb\u8d77\u3001\u8acb\u8d77\uff0c\u8acb\u5750\uff0c\u5225\u5fd8\u4e86\u5e38\u5e38\u8981\u4f86\u5462\uff01\u5927\u5bb6\u628a\u9019\u9996\u6b4c\u5531\u4e00\u000d\u000a\u904d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u9ec3\u59d0\u25cb\u82b3\u6148\u60b2\uff1a\u5f92\u5152\u5440\uff01\u591a\u4f86\u670d\u52d9\uff0c\u5176\u5be6\u670d\u52d9\u5927\u5bb6\u000d\u000a\u5c31\u662f\u670d\u52d9\u81ea\u5df1\uff0c\u77e5\u9053\u70ba\u4ec0\u9ebc\u55ce\uff1f\u56e0\u70ba\u4f60\u8b93\u5927\u5bb6\u5feb\u6a02\u4e86\uff0c\u81ea\u5df1\u4e0d\u5c31\u5feb\u6a02\u000d\u000a\u4e86\u55ce\uff1f\u90a3\u5c31\u670d\u52d9\u5230\u81ea\u5df1\u5566\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\uff13\u865f\u6148\u60b2\uff1a\u80a9\u8180\u75e0\u5440\uff1f\u70ba\u5e2b\u5e6b\u4f60\u62cd\u4e00\u62cd\u5427\uff01\u628a\u75e0\u75db\u90fd\u62cd\u000d\u000a\u6389\uff0c\u4e0b\u6b21\u4f86\u4f5b\u5802\u53ef\u4ee5\u5750\u4e94\u3001\u516d\u5929\u554a\uff0c\u597d\u55ce\uff1f\uff08\u597d\uff09\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u54ea\u4e00\u4f4d\u53ef\u4ee5\u5e36\u5531\uff1f\uff09\uff08\u8fa6\u4e8b\u4eba\u54e1\u25cb\u99a8\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u937e\u9ede\u50b3\u5e2b\u6148\u60b2\u4e26\u8cdc\u4e00\u9846\u8461\u8404\uff1a\u7d66\u5f92\u5152\u4e00\u500b\u7a2e\u5b50\uff0c\u8981\u600e\u9ebc\u64ad\u7a2e\u662f\u000d\u000a\u770b\u81ea\u5df1\u4e86\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\uff18\u865f\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u8981\u628a\u63e1\u9053\uff0c\u5c31\u8981\u5b58\u5728\u5fc3\u88e1\uff0c\u56e0\u70ba\u4efb\u4f55","30\u000d\u000a\u7684\u4e8b\uff0c\u4efb\u4f55\u7684\u9053\u7406\uff0c\u53ea\u6709\u5728\u5fc3\u4e0a\u6703\u6709\u4f5c\u7528\uff0c\u4f86\uff0c\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u9910\u52d9\u8fa6\u4e8b\u4eba\u54e1\u8449\u59d0\u25cb\u6cbb\u6148\u60b2\uff1a\u7092\u83dc\u6703\u4e0d\u6703\u8f9b\u82e6\u554a\uff1f\u5927\u5bb6\u4e00\u8d77\u000d\u000a\u4f86\u662f\u4e0d\u662f\u5f88\u5feb\u6a02\uff0c\u6d41\u6c57\u4e5f\u503c\u5f97\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6587\u66f8\u8fa6\u4e8b\u4eba\u54e1\u97d3\u59d0\u25cb\u679d\u6148\u60b2\uff1a\u25cb\u679d\u5f92\u5152\u554a\uff0c\u4f86\u4f5b\u5802\u5b78\u4ec0\u9ebc\uff1f\uff08\u5b78\u000d\u000a\u9053\u3001\u5b78\u4fee\u5fc3\uff09\u5b78\u4fee\u9053\u3001\u5b78\u4fee\u5fc3\uff0c\u5c31\u9019\u9ebc\u7c21\u55ae\u662f\u5427\uff01\u6c92\u932f\uff0c\u7c21\u55ae\u7684\u5fc3\uff0c\u662f\u000d\u000a\u6700\u96e3\u4fee\u7684\uff0c\u5225\u60f3\u592a\u591a\u4e86\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u58c7\u52d9\u8fa6\u4e8b\u4eba\u54e1\u6797\u59d0\u25cb\u7487\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u4f86\u5b78\u4ec0\u9ebc\u554a\uff1f\uff08\u5b78\u4fee\u9053\uff09\u000d\u000a\u5b78\u4fee\u9053\uff0c\u5e74\u8f15\u5b78\uff0c\u90a3\u8def\u66f4\u9577\uff0c\u6709\u6c92\u6709\u9019\u500b\u6c7a\u5fc3\u5462\uff1f\uff08\u6709\uff09\u6709\u6c7a\u5fc3\uff0c\u70ba\u5e2b\u000d\u000a\u5c31\u966a\u5f92\u5152\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6587\u66f8\u7d44\u9577\u674e\u8b1b\u5e2b\u25cb\u73b2\u6148\u60b2\uff1a\u90a3\u5f92\u5152\u5462\uff0c\u9019\u500b\u9053\u662f\u5f88\u6df1\u7684\uff0c\u7e7c\u7e8c\u000d\u000a\u6df1\u5165\u4e0b\u53bb\uff0c\u624d\u6703\u6709\u4e0d\u540c\u7684\u9ad4\u6703\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5289\u58c7\u4e3b\u25cb\u7bea\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u5fc3\u6709\u6c92\u6709\u8b93\u4ed6\u5e73\u5b9a\uff0c\u5f92\u5152\u5011\u77e5\u9053\u000d\u000a\u5b9a\u5fc3\u7684\u529f\u8ab2\uff0c\u8981\u5e38\u5e38\u53bb\u505a\uff0c\u56e0\u70ba\u9019\u6a23\u722d\u5f37\u4e4b\u4eba\uff0c\u6703\u56e0\u70ba\u770b\u5230\u4f60\u5c31\u5b89\u5b9a\uff0c","31\u000d\u000a\u597d\uff0c\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u912d\u5144\u25cb\u52dd\u6148\u60b2\uff1a\u5f92\u5152\uff0c\u8015\u8018\u4e0d\u770b\u6709\u6c92\u6709\u56de\u5831\uff0c\u56e0\u70ba\u5f92\u000d\u000a\u5152\u662f\u70ba\u4e86\u773e\u751f\u800c\u8015\u8018\uff0c\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u25cb\u99a8\u6148\u60b2\uff1a\u53ea\u8981\u5f92\u5152\u771f\u8aa0\u5730\u5531\u5c31\u662f\u9019\u6a23\uff0c\u5531\u5f97\u597d\u4e0d\u000d\u000a\u597d\u5440\uff1f\uff08\u597d\uff09\u8fa6\u4e8b\u4eba\u54e1\u9700\u8981\u9f13\u638c\u55ce\uff1f\u4e5f\u9700\u8981\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u5927\u5bb6\u90fd\u5728\u5b78\u000d\u000a\u7fd2\uff0c\u25cb\u99a8\u5f92\u5152\u9078\u500b\u679c\uff0c\u6709\u5531\u5c31\u6709\u679c\uff0c\u54ea\u4e00\u4f4d\u5f92\u5152\u6700\u9700\u8981\u88ab\u70ba\u5e2b\u9f13\u52f5\uff0c\u000d\u000a\u8acb\u8209\u500b\u624b\u8b93\u70ba\u5e2b\u77e5\u9053\uff1f\uff08\u5764\uff15\u865f\u7acb\u523b\u5c31\u8209\u624b\uff09\u597d\u3002\u000d\u000a\u525b\u624d\u7684\u9f13\u52f5\u9084\u4e0d\u5920\uff0c\u5c0d\u4e0d\u5c0d\uff1f\u77e5\u9053\u70ba\u4ec0\u9ebc\u55ce\uff1f\u56e0\u70ba\u70ba\u5e2b\u7684\u9f13\u52f5\u662f\u000d\u000a\u4e00\u76f4\u90fd\u5728\u7684\uff0c\u4f46\u5e38\u5e38\u4e0d\u5728\u7684\u662f\u4ec0\u9ebc\u5462\uff1f\u662f\u5f92\u5152\u5011\u5c0d\u81ea\u5df1\u7684\u9f13\u52f5\uff0c\u8acb\u5750\u3002\u000d\u000a\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u7576\u6709\u5584\u7de3\u5e6b\u52a9\u5f92\u5152\u5011\u7684\u6642\u5019\uff0c\u518d\u4f86\u5c31\u770b\u5f92\u5152\u80fd\u4e0d\u80fd\u63e1\u7dca\u9019\u500b\u5584\u7de3\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff19\u865f\u6148\u60b2\uff1a\u5f92\u5152\u89ba\u5f97\u54ea\u4e00\u884c\u5b57\u6700\u5408\u5f92\u5152\u7684\u5fc3\u5462\uff1f\uff08\u5f35\u9ede\u50b3","32\u000d\u000a\u5e2b\uff1a\u6700\u6709\u611f\u53d7\u7684\uff09\uff08\u4eba\u751f\u88e1\u969b\u9047\u6709\u8d77\u4f0f\uff09\u5c31\u9019\u4e00\u53e5\u55ce\uff1f\uff08\u5764\uff19\u865f\u9ede\u982d\uff09\u597d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5728\u6247\u9762\u4e0a\u653e\u4e86\u4e00\u9846\u8461\u8404\u793a\u73fe\u7d66\u5764\uff11\uff10\u865f\u770b\uff0c\u4e26\u5c0d\u5979\u6148\u60b2\uff1a\u000d\u000a\u770b\u770b\u5e73\u7684\u6703\u52d5\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u90a3\u659c\u7684\u5462\uff1f\uff08\u6703\uff09\u662f\u5440\uff01\u969b\u9047\u6709\u8d77\u4f0f\u662f\u000d\u000a\u56e0\u70ba\u5728\u52d5\uff0c\u4ec0\u9ebc\u5728\u52d5\u5462\uff1f\u2500\u2500\u5fc3\u5728\u52d5\uff0c\u5982\u679c\u5f92\u5152\u5fc3\u662f\u5e73\u90a3\u9084\u6703\u52d5\u55ce\uff1f\u000d\u000a\uff08\u4e0d\u6703\uff09\u6240\u4ee5\u554a\uff01\u4e0d\u7ba1\u9047\u5230\u4ec0\u9ebc\u56f0\u96e3\uff1f\u74b0\u5883\u7684\u4e5f\u597d\uff0c\u4eba\u4e8b\u4e0a\u7684\u4e5f\u597d\uff0c\u5de5\u000d\u000a\u4f5c\u4e0a\u4efb\u4f55\u90fd\u597d\uff0c\u53ea\u8981\u5f92\u5152\u7684\u5fc3\uff0c\u4e0d\u8981\u9019\u6a23\u6416\u6416\u6643\u6643\uff0c\u9019\u500b\u679c\u662f\u5c6c\u65bc\u4f60\u000d\u000a\u7684\uff0c\u600e\u9ebc\u6703\u6389\u5230\u5730\u4e0a\u53bb\u5462\uff1f\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u4f86\u62ff\u5427\uff01\u000d\u000a\u6240\u4ee5\uff0c\u8981\u300c\u5e73\u5fc3\u300d\uff0c\u5e73\u5fc3\u5c31\u662f\u8d85\u812b\u3002\u000d\u000a\u4eba\u751f\u7684\u969b\u9047\u6709\u591a\u5c11\u8d77\u4f0f\uff0c\uff08\u25ce\u8001\u5e2b\u6307\u8457\u9ede\u50b3\u5e2b\u5011\uff09\u9ede\u50b3\u5f92\u5152\u5011\u6700\u61c2\u4e86\uff0c\u000d\u000a\u4e00\u8def\u4e0a\u985b\u985b\u7c38\u7c38\u767c\u751f\u90a3\u9ebc\u591a\u4e8b\u60c5\uff0c\u8d70\u5230\u73fe\u5728\uff0c\u5916\u5883\u6703\u8d77\u4f0f\uff0c\u4f46\u662f\u5fc3\u53ef\u000d\u000a\u4ee5\u4e0d\u8981\u8d77\u4f0f\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u9673\u9ede\u50b3\u5e2b\u25cb\u82f1\u6148\u60b2\uff1a\u5f92\u5152\u5440\uff01\u8f9b\u4e0d\u8f9b\u82e6\u5440\uff1f\u4fee\u9053\u5c31\u662f\u53d7\u82e6\u4e86\u000d\u000a\u82e6\uff0c\u662f\u4e0d\u662f\uff1f\u6211\u5011\u90fd\u77e5\u9053\u9053\u7406\uff0c\u4f46\u662f\u627f\u53d7\u8d77\u4f86\u6642\u4e0d\u597d\u53d7\uff0c\u70ba\u5e2b\u4e5f\u66c9\u5f97\uff0c","33\u000d\u000a\u4f46\u662f\u53d7\u4e86\u4e00\u9ede\uff0c\u5c31\u4e86\u4e86\u4e00\u9ede\uff0c\u662f\u4e0d\u662f\uff1f\u597d\uff01\u597d\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u9673\u9ede\u50b3\u25cb\u9cf3\u8207\u6797\u9ede\u50b3\u5e2b\u25cb\u798e\u6148\u60b2\uff1a \u8001 \u6148\u60b2\uff0c\u90fd\u662f\u70ba\u5e2b\u7684\u000d\u000a\u597d\u5f92\u5152\uff0c\u90fd\u662f\u70ba\u5e2b\u7684\u5de6\u53f3\u624b\uff0c\u5f92\u5152\u5011\u4e5f\u662f\u4e00\u6a23\u5566\uff01\u000d\u000a\u4eba\u90fd\u662f\u6703\u6709\u82e6\u7684\uff0c\u90fd\u6703\u6709\u89ba\u5f97\u8271\u8f9b\u7684\u4e8b\u60c5\uff0c\u000d\u000a\u4f46\u662f\u8271\u8f9b\u7684\u4e8b\u60c5\uff0c\u53ea\u8981\u589e\u76ca\u5f92\u5152\u6240\u4e0d\u80fd\uff0c\u000d\u000a\u90a3\u9ebc\u9019\u500b\u8271\u8f9b\uff0c\u6703\u6210\u70ba\u4e0b\u4e00\u500b\u56de\u9996\u4e4b\u6642\u300c\u611f\u8b1d\u7684\u8271\u8f9b\u300d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5442\u58c7\u4e3b\u25cb\u82ac\u6148\u60b2\uff1a\u25cb\u82ac\u5f92\u5152\u4fee\u9053\u7684\u5fc3\u662f\u4e0d\u662f\u5f88\u611f\u6069\u5462\uff1f\uff08\u662f\uff09\u000d\u000a\u80fd\u6709\u9019\u500b\u6a5f\u6703\u5c31\u591a\u505a\u591a\u8fa6\uff0c\u70ba\u4ec0\u9ebc\u5462\uff1f\u56e0\u70ba\u61c2\u5f97\u628a\u63e1\u6a5f\u6703\u4fee\u8fa6\u9053\u7684\u4eba\uff0c\u000d\u000a\u5c31\u548c\u70ba\u5e2b\u540c\u5728\uff0c\u8acb\u8d77\uff0c\u8d77\u4f86\u8d77\u4f86\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\u8fa6\u4e8b\u4eba\u54e1\u90fd\u662f\u4e00\u6a23\u554a\uff01\u8981\u600e\u9ebc\u6a23\u5e38\u5e38\u5728\u70ba\u5e2b\u65c1\u908a\uff0c\u5c31\u662f\u548c\u70ba\u5e2b\u505a\u000d\u000a\u4e00\u6a23\u7684\u4e8b\u60c5\uff0c\u4ec0\u9ebc\u4e8b\uff1f\u7121\u975e\u662f\u300c\u4fee\u9053\u4e86\u613f\uff0c\u8fa6\u9053\u4e86\u547d\u300d\u3002\u000d\u000a\u7576\u7136\uff0c\u8def\u4e0d\u6703\u662f\u9806\u5f92\u5152\u5011\u7684\u610f\u7684\uff0c\u5c31\u662f\u56e0\u70ba\u4e0d\u9806\u5f92\u5152\u5011\u7684\u610f\uff0c\u624d","34\u000d\u000a\u80fd\u935b\u934a\u5f92\u5152\u5011\u7684\u5fc3\uff0c\u5982\u679c\u4ec0\u9ebc\u90fd\u4e00\u5e06\u98a8\u9806\uff0c\u90a3\u6703\u4e0d\u6703\u9806\u3001\u9806\u3001\u9806\uff0c\u5c31\u000d\u000a\u9a55\u50b2\u8d77\u4f86\u4e86\uff1f\u90a3\u51e1\u4e8b\u90fd\u662f\u9006\u5883\uff0c\u6703\u4e0d\u6703\u9006\u3001\u9006\u3001\u9006\uff0c\u5c31\u8dcc\u5012\u554a\uff1f\uff08\u4e0d\u6703\uff09\u000d\u000a\u4e0d\u6703\u554a\uff0c\u5f92\u5152\u5011\u90fd\u9577\u5927\u4e86\uff01\u90a3\u6709\u4e9b\u6642\u5019\u70ba\u5e2b\u600e\u9ebc\u770b\u898b\u5f92\u5152\u5e38\u5e38\u8dcc\u5012\u5728\u000d\u000a\u5730\u4e0a\u554a\uff1f\u70ba\u5e2b\u8d95\u5fd9\u53bb\u6276\u3002\u000d\u000a\u9006\u5883\u591a\uff0c\u6703\u8b93\u4eba\u8dcc\u5012\u7684\u6a5f\u6703\u8b8a\u591a\uff0c\u000d\u000a\u540c\u6a23\u7684\uff0c\u4e5f\u589e\u52a0\u4eba\u6210\u9577\u7684\u6a5f\u6703\uff1b\u000d\u000a\u6c92\u6709\u8dcc\u904e\u8de4\u7684\u4eba\uff0c\u4e0d\u6703\u77e5\u9053\u90a3\u908a\u6709\u5751\u8981\u9583\uff0c\u000d\u000a\u6c92\u6709\u5403\u904e\u8667\u7684\u4eba\uff0c\u4e0d\u6703\u77e5\u9053\u5403\u8667\u662f\u591a\u9ebc\u5e78\u798f\u7684\u4e8b\u3002\u000d\u000a\u5403\u5f97\u4e0b\u8667\u55ce\uff1f\u9019\u5403\u8667\u53ef\u4e0d\u50cf\u7528\u4e09\u9910\u4e00\u6a23\u9019\u9ebc\u8c50\u76db\uff0c\u5982\u679c\u5403\u7684\u98ef\u83dc\u000d\u000a\u5230\u4e86\u809a\u5b50\u6c92\u6d88\u5316\u6703\u600e\u9ebc\u6a23\uff1f\uff08\u809a\u5b50\u4e0d\u8212\u670d\uff09\u662f\u5440\uff01\u6709\u5e74\u7d00\u90fd\u77e5\u9053\uff0c\u4e0d\u80fd\u000d\u000a\u4e00\u6b21\u5403\u592a\u591a\u6771\u897f\uff0c\u809a\u5b50\u4e00\u4e0b\u5c31\u8139\u8d77\u4f86\u4e86\uff0c\u4e0d\u8212\u670d\uff0c\u9019\u5c31\u662f\u6c92\u6709\u6d88\u5316\u3002\u000d\u000a\u5982\u679c\u5783\u573e\u4e00\u76f4\u5012\u4e00\u76f4\u5012\uff0c\u4f46\u662f\u90fd\u6c92\u6709\u6e05\u7406\u5b83\u6703\u600e\u9ebc\u6a23\uff1f\u6703\u81ed\uff0c\u9084\u6703\u628a\u000d\u000a\u4e00\u500b\u5730\u65b9\u5360\u6eff\u90fd\u662f\u5783\u573e\uff0c\u90a3\u7576\u4f60\u8d70\u904e\u90a3\u9ebc\u591a\u5783\u573e\u7684\u5730\u65b9\u6642\uff0c\u4f60\u6703\u4e0d\u6703\u000d\u000a\u628a\u9f3b\u5b50\u634f\u4f4f\uff1f\uff08\u6703\uff09\u6703\u55ce\uff1f\u4e0d\u7136\u5c31\u66ab\u6642\u4e0d\u8981\u547c\u5438\uff0c\u56e0\u70ba\u6709\u81ed\u5473\u662f\u4e0d\u662f\uff1f","35\u000d\u000a\uff08\u662f\uff09\u000d\u000a\u5982\u679c\u5f92\u5152\u5011\u7684\u5fc3\uff0c\u5e38\u5e38\u7d2f\u7a4d\u4e00\u4e9b\u300c\u8ca0\u9762\u7684\u601d\u7dd2\u300d\uff0c\u4e45\u4e86\u6703\u600e\u9ebc\u6a23\uff1f\u000d\u000a\u751f\u75c5\uff0c\u9084\u6709\u5462\uff1f\u6703\u8b93\u4f60\u5e38\u5e38\u6c89\u6d78\u5728\u75db\u82e6\u7684\u6ecb\u5473\u88e1\u9762\u3002\u6709\u53ef\u80fd\u767c\u751f\u4e00\u4ef6\u000d\u000a\u597d\u4e8b\uff0c\u4f46\u539f\u672c\u4f60\u7684\u5fc3\u60c5\u5c31\u4e0d\u597d\uff0c\u6240\u4ee5\u9047\u5230\u597d\u4e8b\u9084\u662f\u89ba\u5f97\u9019\u4e0d\u662f\u4ec0\u9ebc\u597d\u000d\u000a\u4e8b\uff1b\u90a3\u5982\u679c\u5f92\u5152\u7684\u5fc3\u60c5\u5f88\u597d\uff0c\u9047\u5230\u4e86\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6703\u4e0d\u6703\u6bd4\u8f03\u5bb9\u6613\u63a5\u53d7\uff1f\u000d\u000a\u2500\u2500\u56e0\u70ba\u539f\u672c\u5c31\u5f88\u958b\u5fc3\u561b\uff0c\u662f\u4e0d\u662f\uff1f\u6240\u4ee5\u5f88\u591a\u4eba\u624d\u8aaa\u5440\uff01\u8ddf\u4eba\u5bb6\u8aaa\u8a71\u000d\u000a\u8981\u770b\u81c9\u8272\uff0c\u5982\u679c\u767c\u73fe\u9019\u500b\u4eba\u5fc3\u60c5\u4e0d\u597d\u7684\u6642\u5019\uff0c\u4e0d\u8981\u53bb\u627e\u4ed6\u8aaa\u8a71\u3002\u7236\u6bcd\u000d\u000a\u5982\u679c\u5728\u5435\u67b6\u7684\u6642\u5019\uff0c\u4f60\u5011\u6562\u53bb\u8aaa\u8a71\u55ce\uff1f\uff08\u4e0d\u6562\uff09\u662f\u5440\uff01\u8eb2\u5f97\u8d8a\u9060\u8d8a\u597d\uff0c\u000d\u000a\u5c0d\u4e0d\u5c0d\uff1f\u90a3\u56de\u904e\u982d\u4f86\u60f3\u4e00\u60f3\uff0c\u5f92\u5152\u81ea\u5df1\u662f\u600e\u9ebc\u6a23\u7684\u4e00\u500b\u4eba\u5462\uff1f\u662f\u80fd\u8b93\u000d\u000a\u4eba\u63a5\u8fd1\u4f60\uff0c\u5c31\u611f\u89ba\u5230\u548c\u7166\u6eab\u6696\uff0c\u611f\u89ba\u5230\u4f5b\u5802\u771f\u597d\u3002\u9084\u662f\u8aaa\u8b93\u4eba\u63a5\u8fd1\u4f60\u000d\u000a\u4e4b\u5f8c\uff0c\u60f3\u8aaa\uff1a\u300c\u4f5b\u5802\u7684\u4eba\u4e0d\u662f\u4e5f\u9019\u6a23\uff0c\u90a3\u6211\u70ba\u4ec0\u9ebc\u8981\u4fee\u9053\u5462\uff1f\u300d\u000d\u000a\u60f3\u60f3\u770b\uff1a\u5f92\u5152\u8981\u505a\u4ec0\u9ebc\u6a23\u7684\u4eba\u5440\uff1f\u662f\u4e0d\u662f\u90fd\u60f3\u505a\u4e00\u500b\u7b11\u5bb9\u53ef\u63ac\u7684\u000d\u000a\u4eba\uff0c\u4f46\u662f\u4e0d\u77e5\u9053\u70ba\u4ec0\u9ebc\u6709\u6642\u5019\u5c31\u7b11\u4e0d\u51fa\u4f86\uff0c\u6703\u4e0d\u6703\uff1f","36\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5764\uff12\u865f\u6148\u60b2\uff1a\u5f92\u5152\u8acb\u8d77\uff0c\u7576\u5f92\u5152\u7b11\u4e0d\u51fa\u4f86\u7684\u6642\u5019\uff0c\u600e\u9ebc\u8fa6\uff1f\u000d\u000a\uff08\u5c31\u5fae\u7b11\uff09\u662f\uff01\u90a3\u9084\u4e0d\u932f\u3002\u7576\u5f92\u5152\u7b11\u4e0d\u51fa\u4f86\u7684\u6642\u5019\uff0c\u5c31\u60f3\u60f3\u70ba\u5e2b\u9001\u7d66\u5f92\u000d\u000a\u5152\u7684\u9019\u500b\u679c\uff0c\u597d\u4e0d\u597d\u554a\uff1f\u8acb\u5750\u3002\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u4e0d\u8981\u628a\u81ea\u5df1\u8b8a\u82e6\u547d\u4eba\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u738b\u5144\u25cb\u7165\u6148\u60b2\uff1a\u25cb\u7165\u5f92\u5152\uff0c\uff08\u8001\u5e2b\u8cdc\u679c\uff09\u9019\u597d\u4e0d\u597d\u5403\u000d\u000a\u5462\uff1f\uff08\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09\u597d\uff0c\u5c31\u591a\u5403\u597d\u55ce\uff1f\u8981\u9577\u667a\u6167\u5566\uff01\u8acb\u8d77\u8acb\u8d77\u3002\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u8981\u9577\u667a\u6167\u5566\uff01\uff09\u000d\u000a\u518d\u628a\u9019\u500b\u6b4c\u5531\u4e00\u904d\uff0c\u597d\u55ce\uff1f\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6587\u66f8\u7d44\u65bd\u5144\u25cb\u6ea2\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u6709\u6c92\u6709\u5c08\u5fc3\u554a\uff1f\uff08\u6709\uff09\u6709\u554a\uff0c\u000d\u000a\u597d\uff01\uff08\u73fe\u5834\u807d\u6253\u7684\u65bd\u5144\u6b64\u6642\u8d95\u7dca\u5f9e\u5ea7\u4f4d\u4e0a\u8d77\u8eab\uff09\u6c92\u95dc\u4fc2\uff0c\u9019\u4fee\u9053\uff0c\u6bcf\u4e00\u6b65\u8def\uff0c\u000d\u000a\u90fd\u8981\u50cf\u73fe\u5728\u4e00\u6a23\u53bb\u8e0f\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09\u56db\u5341\u6b72\u7684\u6642\u5019\uff0c\u9084\u80fd\u9019\u6a23\u55ce\uff1f\uff08\u53ef\u000d\u000a\u4ee5\uff09\u53ef\u4ee5\uff0c\u4e00\u8a00\u70ba\u5b9a\u3002\u597d\uff0c\u8acb\u5750\u3002\u000d\u000a\u25ce\u8001\u5e2b\u540c\u6642\u8ddf\u65bd\u5144\u8eab\u5f8c\u7684\u9673\u5144\u25cb\u7db8\u6148\u60b2\uff1a\u5f92\u5152\u4e5f\u662f\u5462\uff01","37\u000d\u000a\u518d\u628a\u9019\u500b\u6b4c\u5531\u4e00\u904d\u597d\u55ce\uff1f\uff08\u25cb\u99a8\u58c7\u4e3b\u63a5\u8457\u5e36\u5531\uff09\u000d\u000a\u25ce\u5531\u5584\u6b4c\u6642\u8001\u5e2b\u9678\u7e8c\u8ddf\u591a\u4f4d\u524d\u8ce2\u6148\u60b2\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6587\u66f8\u7d44\u8521\u59d0\u25cb\u878d\u6148\u60b2\uff1a\u5f92\u5152\u5440\uff01\u73fe\u5728\u4f86\u4f5b\u5802\uff0c\u4e0d\u662f\u53ea\u6709\u8ddf\u8457\u000d\u000a\u4f86\u4e86\uff0c\u73fe\u5728\u4f86\u4f5b\u5802\uff0c\u81ea\u52d5\u81ea\u767c\u662f\u4e0d\u662f\uff1f\u81ea\u52d5\u81ea\u767c\u7684\u4eba\uff0c\u5c31\u6703\u600e\u9ebc\u6a23\u5462\uff1f\u000d\u000a\u81ea\u5df1\u53bb\u9ad4\u6703\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u591a\u4f4d\u5764\u9053\u8fa6\u4e8b\u4eba\u54e1\u6148\u60b2\uff1a\u8b93\u70ba\u5e2b\u770b\u770b\u5f92\u5152\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u9ece\u59d0\u25cb\u559c\u6148\u60b2\uff1a\u4eba\u751f\u7684\u8f49\u8b8a\u5c31\u662f\u5f9e\u77e5\u9053\u4fee\u9053\u958b\u59cb\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u6797\u59d0\u25cb\u83ef\u3001\u674e\u59d0\u25cb\u7389\u3001\u5433\u59d0\u25cb\u83ef\u7b49\u4e09\u4eba\u6148\u60b2\uff1a\u8fa6\u4e8b\u4eba\u54e1\u000d\u000a\u7684\u5fc3\u8981\u5408\u4f5c\u5728\u4e00\u8d77\uff0c\u5408\u4f5c\u6703\u6709\u66f4\u5927\u7684\u529b\u91cf\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u694a\u58c7\u4e3b\u25cb\u676d\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u4fee\u9053\u8981\u600e\u9ebc\u57f9\u990a\u6210\u5168\u4eba\u7684\u5fc3\uff0c\u7d66\u000d\u000a\u5f92\u5152\u4e00\u500b\u529f\u8ab2\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6797\u58c7\u4e3b\u25cb\u826f\u6148\u60b2\uff1a\u5f92\u5152\uff0c\u4fee\u9053\u7684\u529f\u8ab2\u6709\u6c92\u6709\u505a\u554a\uff1f\uff08\u52aa\u529b\u4e2d\uff09","38\u000d\u000a\u9019\u500b\u52aa\u529b\u5c31\u662f\u4e00\u500b\u529f\u8ab2\uff0c\u56e0\u70ba\u52aa\u529b\u7684\u4eba\uff0c\u624d\u6703\u628a\u63e1\u884c\u529f\u3001\u624d\u6703\u52aa\u529b\u4fee\u000d\u000a\u9053\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8fa6\u4e8b\u4eba\u54e1\u674e\u5144\u25cb\u5fe0\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u770b\u5230\u5f92\u5152\u771f\u597d\u5462\uff01\u4ee5\u5f8c\u8981\u000d\u000a\u5e38\u5e38\u4f86\uff0c\u4e0d\u8981\u8b93\u70ba\u5e2b\u627e\u554a\uff01\u000d\u000a\u5531\u5f97\u771f\u597d\u5462\uff01\u5927\u5bb6\u90fd\u6703\u5531\uff0c\u90a3\u662f\u4e0d\u662f\u5927\u5bb6\u90fd\u8981\u6709\u679c\u5440\uff1f\u7562\u73ed\u4e4b\u5f8c\uff0c\u000d\u000a\u5927\u5bb6\u90fd\u62ff\u500b\u679c\u3002\u8fa6\u4e8b\u4eba\u54e1\u4e5f\u662f\uff0c\u90fd\u62ff\u500b\u679c\u3002\u53ef\u611b\u7684\u5f92\u5152\u5011\u5440\uff01\u5f92\u5152\u5011\u000d\u000a\u4e0d\u80fd\u53ea\u6709\u5728\u70ba\u5e2b\u7684\u5fc3\u4e2d\u53ef\u611b\uff0c\u800c\u662f\u8981\u5728\u4f60\u5011\u8eab\u65c1\u7684\u6bcf\u4e00\u500b\u4eba\u5fc3\u4e2d\uff0c\u90fd\u000d\u000a\u89ba\u5f97\u4f60\u5011\u53ef\u611b\u3001\u90fd\u89ba\u5f97\u4f60\u5011\u5f88\u5584\u826f\u3001\u813e\u6c23\u5f88\u597d\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u000d\u000a\u56de\u5bb6\u7167\u8457\u505a\u5594\uff01\u5bb6\u88e1\u7684\u4eba\u90fd\u559c\u6b61\u4f60\uff0c\u770b\u898b\u4f60\u5c31\u6703\u7b11\u3002\uff09\u8981\u600e\u9ebc\u505a\u5230\u9019\u6a23\u5462\uff1f\u000d\u000a\u7b2c\u4e00\u500b\uff0c\u300c\u8a71\u4f11\u4e0d\u60f3\u4fbf\u8aaa\u300d\uff1b\u7b2c\u4e8c\u500b\uff0c\u300c\u6c23\u4f11\u4e0d\u5fcd\u4fbf\u52d5\u300d\u3002\u000d\u000a\u5728\u8aaa\u8a71\u4e4b\u524d\uff0c\u60f3\u4e00\u60f3\u6703\u4e0d\u6703\u8b93\u5225\u4eba\u96e3\u904e\uff1f\u60f3\u597d\u4e86\u518d\u8aaa\u3002\u9019\u500b\u813e\u6c23\u000d\u000a\u4e0a\u4f86\u7684\u6642\u5019\uff0c\u60f3\u4e00\u60f3\u5f8c\u679c\u662f\u4ec0\u9ebc\uff1a\u4e00\u5b9a\u662f\u5435\u67b6\u3001\u4e00\u5b9a\u662f\u4e0d\u5713\u6eff\u3001\u4e00\u5b9a\u000d\u000a\u662f\u9b27\u5f97\uff08\u4e0d\u8b1b\u8a71\uff09\u3002\u662f\uff0c\u4e0d\u8b1b\u8a71\uff0c\u4f60\u5011\u6bd4\u70ba\u5e2b\u6e05\u695a\u5462\uff01\u6240\u4ee5\uff0c\u9019\u500b\u6c23\u5c31","39\u000d\u000a\u8981\u628a\u5b83\u5fcd\u4e0b\u4f86\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u5927\u5bb6\u807d\u61c2\u55ce\uff1f\uff08\u61c2\uff09\u4e0d\u8981\u4e0d\u60f3\u5c31\u8aaa\u51fa\u4f86\uff0c\u8981\u60f3\u60f3\u9019\u000d\u000a\u500b\u8a71\u53ef\u4e0d\u53ef\u4ee5\u8aaa\u5462\uff1f\u4e0d\u80fd\u8aaa\u5c31\u4e0d\u8981\u8aaa\uff0c\u7ba1\u4f4f\u81ea\u5df1\u7684\u813e\u6c23\uff0c\u8001\u5e2b\u8ddf\u6211\u5011\u8b1b\u7684\u3002\uff09\u000d\u000a\u90a3\u4eca\u5929\u5927\u5bb6\u662f\u540c\u4fee\u9053\u4f34\u4e86\uff0c\u662f\u561b\uff1f\uff08\u662f\uff09\u4e09\u5929\u6cd5\u6703\u904e\u53bb\u4e4b\u5f8c\uff0c\u662f\u4e0d\u000d\u000a\u662f\u5927\u5bb6\u90fd\u53ef\u4ee5\u4e00\u8d77\u4f86\u4f5b\u5802\u5b78\u7fd2\uff0c\u90a3\u4f86\u4f5b\u5802\u8981\u6ce8\u610f\u4ec0\u9ebc\u5462\uff1f\uff08\u4f5b\u898f\u79ae\u7bc0\uff09\u000d\u000a\u4f60\u5011\u662f\u6bcf\u4e00\u5802\u8ab2\u90fd\u5728\u807d\u4f5b\u898f\u79ae\u7bc0\u55ce\uff1f\uff08\u5728\u5834\u7684\u4eba\u807d\u5230\u8001\u5e2b\u5999\u8a9e\u5982\u73e0\u7684\u8aaa\u6cd5\uff0c\u000d\u000a\u5fcd\u4e0d\u4f4f\u7b11\u6210\u4e86\u4e00\u7247\uff09\u662f\u662f\u662f\uff0c\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4f46\u9084\u6709\u5f88\u591a\uff0c\u6108\u5b78\u5c31\u6108\u6703\u505a\u3002\u000d\u000a\u70ba\u5e2b\u5728\u9019\u908a\u8981\u8ddf\u6bcf\u4f4d\u5f92\u5152\u63d0\u9192\u5e7e\u9ede\uff0c\u597d\u4e0d\u597d\u554a\uff1f\u4f86\u5beb\u4e0a\u4e86\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6731\u8b1b\u5e2b\u6148\u60b2\uff1a\u6e96\u5099\u597d\u4e86\u55ce\uff1f\u000d\u000a\u3010\u9053\u89aa\u4e43\u56e0\u9053\u800c\u89aa \u67d4\u8edf\u5584\u5f85\u6df1\u9ad4\u95dc\u61f7\u3011\u000d\u000a\u6df1\u9ad4\u95dc\u61f7\uff0c\u6df1\u5165\u9ad4\u6703\uff0c\u662f\u6bcf\u4e00\u4f4d\u5f92\u5152\u90fd\u8981\u6ce8\u610f\u7684\u3002\u000d\u000a\u3010\u540c\u5fc3\u4e43\u6b64\u5fc3\u76f8\u540c \u5929\u6069\u5bb6\u4eba\u7406\u89e3\u5305\u5bb9\u3011\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6d3b\u4f5b\u8001\u5e2b\u4f86\uff0c\u5927\u5bb6\u8981\u6709\u4e00\u9ede\u7b11\u5bb9\u554a\uff0c\u90fd\u597d\u50cf\u5f88\u82e6\u7684\u6a23\u5b50\u554a\uff01\uff09","40\u000d\u000a\u6709\u6642\u5019\u8eab\u9ad4\u7d2f\u4e5f\u6703\u5f88\u96e3\u7b11\u5f97\u51fa\u4f86\uff0c\u4f46\u662f\uff0c\u4fee\u9053\u4e0d\u80fd\u88ab\u8eab\u9ad4\u969c\u7919\uff0c\u000d\u000a\u8eab\u9ad4\u6108\u7d2f\uff0c\u6108\u4e0d\u8981\u53bb\u611f\u53d7\u5b83\u3002\u5c31\u50cf\u662f\u5f92\u5152\u751f\u6d3b\u88e1\u9047\u5230\u8f9b\u82e6\u7684\u4e8b\u60c5\uff0c\u4e0d\u000d\u000a\u8981\u592a\u53bb\u611f\u53d7\u5b83\uff0c\u75db\u82e6\u5c31\u6703\u6e1b\u5c11\u4e00\u9ede\u3002\u000d\u000a\u25ce\u8001\u5e2b\u6148\u60b2\u8acb\u8cf4\u9ede\u50b3\u5e2b\u7ad9\u5230\u524d\u9762\u8207\u5927\u5bb6\u8b1b\u8a71\uff1a\u25cb\u745e\u5f92\u5152\uff0c\u8ddf\u5927\u5bb6\u8b1b\u8b1b\u8a71\u3002\u000d\u000a\u7cbe\u795e\u4e0d\u597d\u4e86\u5462\uff01\u000d\u000a\uff08\u8cf4\u9ede\u50b3\u5e2b\uff1a\u6211\u5011\u4eca\u5929\u9ad8\u8208\u4e0d\u9ad8\u8208\uff1f\uff08\u9ad8\u8208\uff09\u6211\u5011\u9019\u4e00\u500b\u73ed\u8ddf\u5176\u4ed6\u7684\u73ed\u4e0d\u4e00\u000d\u000a\u6a23\uff0c\u77e5\u9053\u55ce\uff1f\u4e00\u822c\u4e09\u5929\u6cd5\u6703\uff0c\u6211\u5011\u7684\u8001\u5e2b\u4e0d\u4e00\u5b9a\u81e8\u58c7\u5594\uff01\u6709\u6642\u5019\u662f\u5176\u4ed6\u7684\u4ed9\u4f5b\u81e8\u000d\u000a\u58c7\uff0c\u6240\u4ee5\u6211\u5011\u9019\u4e00\u73ed\uff0c\u53ef\u4ee5\u8aaa\u8ddf\u6211\u5011\u8001\u5e2b\u7684\u9019\u500b\u7de3\uff0c\u975e\u5e38\u6df1\u539a\u3002\u5927\u5bb6\u8981\u77e5\u9053\uff0c\u4e86\u000d\u000a\u89e3\u55ce\uff1f\uff08\u4e86\u89e3\uff09\u6211\u5011\u4eca\u5929\u5f97\u4e86\u660e\u5e2b\u6307\u9ede\uff0c\u662f\u56e0\u70ba\u8001\u5e2b\u8eab\u64d4\u4e09\u66f9\u7684\u91cd\u4efb\uff0c\u6211\u5011\u624d\u6709\u000d\u000a\u9019\u500b\u6a5f\u6703\uff0c\u80fd\u5920\u6c42\u9053\u3001\u80fd\u5920\u4fee\u9053\u3001\u80fd\u5920\u8fa6\u9053\uff0c\u6240\u4ee5\u6709\u53e5\u8a71\u8aaa\u300c\u51e1\u592b\u8fa6\u8056\u4e8b\uff0c\u81ea\u53e4\u000d\u000a\u672a\u66fe\u6709\u300d\uff0c\u6240\u4ee5\u6211\u5011\u4eca\u5929\u80fd\u5920\u5728\u9019\u500b\u4e09\u5929\u6cd5\u6703\uff0c\u8ddf\u6211\u5011\u7684\u8001\u5e2b\u7d50\u9019\u500b\u5584\u7de3\uff0c\u771f\u7684\uff0c\u000d\u000a\u5404\u4f4d\u592a\u6709\u798f\u6c23\u4e86\uff01\u8001\u5e2b\u95dc\u5fc3\u6211\u5011\u8981\u4fee\u9053\uff0c\u4fee\u9053\u5c31\u662f\u8981\u600e\u9ebc\u6a23\uff1f\u6539\u8b8a\u81ea\u5df1\u3002\uff09\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5433\u9ede\u50b3\u5e2b\u25cb\u6674\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff01\u77e5\u9053\u4fee\u9053\u4fee\u4ec0\u9ebc\u55ce\uff1f\u4fee\u9053\u53ea\u662f\u000d\u000a\u300c\u6c42\u5176\u653e\u5fc3\u800c\u5df2\u77e3\u300d\uff0c\u5982\u679c\u9019\u9846\u5fc3\u80fd\u653e\uff0c\u90a3\u9ebc\u5fc3\u975c\u751f\u7a7a\uff0c\u9084\u6709\u4ec0\u9ebc\u6703\u963b","41\u000d\u000a\u7919\u5462\uff1f\u4e0d\u6703\uff0c\u6240\u4ee5\u5f92\u5152\u8d70\u5230\u73fe\u5728\uff0c\u5c31\u662f\u9019\u6a23\uff0c\u9019\u500b\u57fa\u790e\u5982\u679c\u5920\u7684\u8a71\uff0c\u000d\u000a\u5e6b\u70ba\u5e2b\u591a\u8fa6\u4e8b\uff0c\u597d\uff01\u000d\u000a\uff08\u8cf4\u9ede\u50b3\u5e2b\uff1a\u6211\u5011\u70ba\u4ec0\u9ebc\u6703\u6709\u6bdb\u75c5\u813e\u6c23\u77e5\u9053\u55ce\uff1f\u525b\u624d\u8001\u5e2b\u6709\u8b1b\uff0c\u6211\u5011\u7684\u813e\u6c23\u000d\u000a\u8aaa\u8981\u767c\u5c31\u8981\u767c\uff0c\u5c0d\u4e0d\u5c0d\uff1f\u4f60\u6c92\u6709\u8fa6\u6cd5\u58d3\u4f4f\u4f60\u7684\u813e\u6c23\u8981\u767c\uff0c\u70ba\u4ec0\u9ebc\uff1f\u4fee\u9053\u5c31\u8b1b\u300c\u6539\u000d\u000a\u6bdb\u75c5\u3001\u53bb\u813e\u6c23\u300d\u7c21\u55ae\u4e0d\u7c21\u55ae\uff1f\uff08\u4e0d\u7c21\u55ae\uff09\uff0c\u4e0d\u7c21\u55ae\uff0c\u8981\u5148\u6539\u4f60\u7684\u5984\u5fc3\uff0c\u6211\u5011\u4eba\u7684\u000d\u000a\u5984\u5fc3\u592a\u591a\u4e86\uff0c\u5984\u5fc3\u5c31\u662f\u80e1\u601d\u4e82\u60f3\uff0c\u6709\u4e86\u5984\u5fc3\u5c31\u6709\u7fd2\u6c23\uff0c\u516d\u842c\u5e74\u4f86\u7684\u7fd2\u6c23\uff0c\u9019\u500b\u7fd2\u000d\u000a\u6c23\u8981\u600e\u9ebc\u6a23\uff0c\u4f60\u7684\u7fd2\u6c23\u6539\u6389\uff0c\u4f60\u7684\u6bdb\u75c5\u3001\u4f60\u7684\u813e\u6c23\u81ea\u7136\u5c31\u6d88\u5931\uff0c\u5927\u5bb6\u4e86\u89e3\u55ce\uff1f\u4f60\u000d\u000a\u5982\u679c\u4e0d\u77e5\u9053\uff0c\u4fee\u9053\u8981\u6539\u6389\u6211\u5011\u7684\u7fd2\u6c23\uff0c\u4f60\u5149\u53ea\u6709\u60f3\u8aaa\u6539\u6bdb\u75c5\u53bb\u813e\u6c23\uff0c\u544a\u8a34\u5404\u4f4d\u5f88\u000d\u000a\u96e3\u554a\uff01\u4f60\u76f8\u4fe1\u4e0d\u76f8\u4fe1\uff0c\u4f60\u73fe\u5728\u7fd2\u6c23\u4e00\u767c\uff0c\u77e5\u9053\u81ea\u5df1\u7684\u7fd2\u6c23\u767c\u8d77\u4f86\u4e0d\u5c0d\uff0c\u4e0d\u80fd\u767c\u813e\u000d\u000a\u6c23\uff0c\u6211\u8981\u6539\u6389\uff0c\u53ef\u662f\u4f60\u4eca\u5929\u8aaa\u8981\u6539\u6389\uff0c\u660e\u5929\u9006\u5883\u4e00\u4f86\uff0c\u4f60\u7684\u813e\u6c23\u6bdb\u75c5\u53c8\u767c\u4e86\uff0c\u7121\u000d\u000a\u660e\u706b\u53c8\u767c\u4e86\uff0c\u76f8\u4fe1\u4e0d\u76f8\u4fe1\uff08\u76f8\u4fe1\uff09\uff0c\u6240\u4ee5\u8981\u6539\u6389\u6211\u5011\u7fd2\u6c23\uff0c\u7fd2\u6c23\u6539\u6389\uff0c\u6bdb\u75c5\u5c31\u6d88\u000d\u000a\u5931\u4e86\u3002\u8001\u5e2b\u6148\u60b2\uff0c\u662f\u4e0d\u662f\uff1f\uff09\u000d\u000a\u662f\u5440\uff0c\u70ba\u5e2b\u4e5f\u5728\u807d\u5f92\u5152\u8b1b\u5462\uff0c\u9019\u5c31\u53eb\u505a\u300c\u6559\u5b78\u76f8\u9577\u300d\uff0c\u662f\u4e0d\u662f\uff1f\u000d\u000a\u5f7c\u6b64\u4e92\u76f8\u5b78\u7fd2\uff0c\u53ea\u8981\u8aaa\u7684\u6709\u9053\u7406\uff0c\u54b1\u5011\u662f\u4e0d\u662f\u5c31\u8981\u5b78\uff1f\uff08\u662f\uff09\u4f46\u662f\u9019\u500b","42\u000d\u000a\u5b78\u7fd2\uff0c\u4e0d\u662f\u518d\u7528\u53e3\u50b3\u9001\u51fa\u53bb\u800c\u5df2\uff0c\u800c\u662f\u5fc3\u88e1\u9762\u771f\u7684\u53bb\u505a\u51fa\u4f86\u662f\u4e0d\u662f\uff1f\u000d\u000a\uff08\u662f\uff09\u000d\u000a\u597d\u5566\uff0c\u9019\u5e7e\u9ede\u5404\u4f4d\u5f92\u5152\u5011\u4e00\u8d77\u5927\u8072\u7684\u5538\uff1a\u000d\u000a\u9053\u89aa\u4e43\u56e0\u9053\u800c\u89aa \u67d4\u8edf\u5584\u5f85\u6df1\u9ad4\u95dc\u61f7\u000d\u000a\u540c\u5fc3\u4e43\u6b64\u5fc3\u76f8\u540c \u5929\u6069\u5bb6\u4eba\u7406\u89e3\u5305\u5bb9\u000d\u000a\u9075\u524d\u4e43\u91cd\u9053\u57fa\u790e \u606d\u656c\u865b\u5df1\u5167\u5916\u4e00\u5982\u000d\u000a\u63d0\u5f8c\u4e43\u5c0a\u5e2b\u6bcb\u5fd8 \u6148\u60b2\u548c\u6696\u8010\u6027\u63d0\u651c\u000d\u000a\u5b78\u4fee\u4e43\u4ee5\u4fee\u70ba\u5b78 \u8a00\u884c\u8b19\u7766\u53cd\u6c42\u8eac\u8eab\u000d\u000a\u53c3\u8fa6\u4e43\u8fa6\u8056\u53c3\u5fb7 \u78e8\u6ecc\u7fd2\u6c23\u5713\u878d\u4e92\u6210\u000d\u000a\u6210\u5c31\u4e43\u84bc\u751f\u5f97\u6551 \u4e09\u671f\u61c9\u904b\u6e21\u5316\u6709\u7de3\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u674e\u8b1b\u5e2b\u25cb\u7f8e\u6148\u60b2\uff1a\u5f92\u5152\u554a\uff0c\u4fee\u9053\u662f\u4e0d\u662f\u8d8a\u4f86\u8d8a\u5feb\u6a02\uff0c\u77e5\u9053\u70ba\u000d\u000a\u4ec0\u9ebc\u5feb\u6a02\u55ce\uff1f\u56e0\u70ba\u8d70\u5c0d\u8def\u554a\uff0c\u90a3\u8981\u5e26\u66f4\u591a\u7684\u4eba\u4f86\u8d70\u9019\u689d\u5c0d\u7684\u8def\uff0c\u624d\u662f\u000d\u000a\u5171\u4eab\uff0c\u7368\u6a02\u4e0d\u5982\u773e\u6a02\u6a02\u3002\uff08\u597d\uff01\u8b1d\u8b1d\u8001\u5e2b\u6148\u60b2\uff09","43\u000d\u000a\u7167\u5f92\u5152\u5011\u9019\u500b\u8072\u97f3\u5538\u8d77\u4f86\u554a\uff01\u9019\u5b57\u5538\u5b8c\u4e86\uff0c\u884c\u70ba\u53ef\u80fd\u8d95\u4e0d\u4e0a\u554a\uff01\u000d\u000a\u54b1\u5011\u662f\u4e0d\u662f\u90fd\u4e00\u8d77\u5171\u8fa6\uff1f\uff08\u662f\uff09\u4e00\u500b\u4f5b\u5802\u662f\u5927\u5bb6\u4e00\u8d77\u5171\u8fa6\u3001\u8b77\u6301\uff0c\u000d\u000a\u5c0d\u4e0d\u5c0d\uff1f\u90a3\u5f92\u5152\u5011\u7684\u5fc3\u6709\u4e92\u76f8\u95dc\u611b\u8457\u5f7c\u6b64\u55ce\uff1f\u000d\u000a\u9075\u524d\u63d0\u5f8c\u3001\u627f\u4e0a\u555f\u4e0b\u662f\u4fee\u9053\u7684\u57fa\u672c\u529f\u592b\u3002\u000d\u000a\u70ba\u5e2b\u70ba\u4ec0\u9ebc\u6703\u9019\u6a23\u8aaa\u5462\uff1f\u56e0\u70ba\u70ba\u5e2b\u5e0c\u671b\u6bcf\u4e00\u500b\u6709\u4f5b\u5802\u7684\u5730\u65b9\u90fd\u80fd\u000d\u000a\u5920\u6709\u8457\u4eba\u7de3\u3001\u6709\u8457\u5584\u7de3\u4f86\u8b77\u6301\uff0c\u4f46\u662f\u96e3\u514d\u4fee\u8fa6\u4e45\u4e86\uff0c\u662f\u4e0d\u662f\u5927\u5bb6\u90fd\u5f88\u000d\u000a\u4e86\u89e3\u5f7c\u6b64\uff0c\u5f7c\u6b64\u7684\u500b\u6027\u3001\u5f7c\u6b64\u7684\u4f5c\u70ba\uff0c\u662f\u4e0d\u662f\u90fd\u4e00\u6e05\u4e8c\u695a\u5462\uff1f\uff08\u662f\uff09\u000d\u000a\u90a3\u9019\u500b\u6642\u5019\uff0c\u6bcf\u4e00\u4f4d\u5f92\u5152\u60f3\u60f3\u81ea\u5df1\uff0c\u6211\u5011\u662f\u5e38\u5e38\u8aaa\u5225\u4eba\u4e0d\u597d\u7684\u4eba\uff0c\u000d\u000a\u9084\u662f\u5e38\u5e38\u53cd\u7701\u81ea\u5df1\u4e0d\u597d\u7684\u4eba\uff0c\u5982\u679c\u8aaa\u5225\u4eba\u4e0d\u597d\u7684\u4eba\u591a\u4e86\uff0c\u90a3\u4f5b\u5802\u9084\u6703\u000d\u000a\u7965\u548c\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u90a3\u5982\u679c\u8aaa\u53cd\u7701\u81ea\u5df1\u4e0d\u597d\u7684\u4eba\u591a\u4e86\uff0c\u90a3\u4f5b\u5802\u6703\u4e0d\u7965\u548c\u000d\u000a\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u662f\u5440\uff01\u6240\u4ee5\u300c\u540c\u5fc3\u662f\u56e0\u70ba\u6b64\u5fc3\u76f8\u540c\u300d\u3002\u000d\u000a\u70ba\u5e2b\u6253\u4e00\u500b\u6bd4\u55bb\uff0c\u5e38\u8b1b\u6709\u300c\u51f9\u300d\u6709\u300c\u51f8\u300d\uff0c\u000d\u000a\u5982\u679c\u7528\u5728\u8056\u4e8b\u4e0a\uff0c\u300c\u51f8\u300d\u5c31\u662f\u4e00\u500b\u4eba\u6703\u8fa6\u8056\u4e8b\u3001\u6703\u8fa6\u4f5b\u4e8b\uff1b","44\u000d\u000a\u90a3\u300c\u51f9\u300d\u662f\u4ec0\u9ebc\uff1f\u2500\u2500\u4e00\u500b\u4eba\u6709\u4fee\u990a\u8b19\u865b\uff0c\u80fd\u4f4e\u5f97\u4e0b\u81ea\u5df1\u3002\u000d\u000a\u90a3\u5982\u679c\u8981\u7d50\u5408\u5713\u6eff\uff0c\u51f9\u51f8\u8981\u76f8\u5408\u4e92\u88dc\uff0c\u000d\u000a\u662f\u4e0d\u662f\u5c31\u8981\u6709\u51f8\u63a5\u8457\u6709\u51f9\uff0c\u5927\u5bb6\u624d\u53ef\u4ee5\u5f7c\u6b64\u7e6b\u5728\u4e00\u8d77\u3002\u000d\u000a\u5982\u679c\u51f9\u51f8\u4e0d\u4e92\u88dc\u7684\u8a71\uff0c\u524d\u9762\u7684\u8def\u662f\u4e0d\u662f\u5c31\u8b8a\u6210\u51f9\u51f8\u4e0d\u5e73\u4e86\uff1f\u000d\u000a\uff08\u662f\uff09\u000d\u000a\u5f92\u5152\u5011\u60f3\u8d70\u9019\u6a23\u7684\u8def\u55ce\uff1f\uff08\u4e0d\u60f3\uff09\u5c31\u662f\u56e0\u70ba\u4e0d\u60f3\uff0c\u6240\u4ee5\u5f92\u5152\u5011\u73fe\u5728\u000d\u000a\u90fd\u5728\u5b78\u7fd2\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u5c0d\uff09\u4e92\u76f8\u5f7c\u6b64\u591a\u5713\u878d\u4e00\u4e9b\uff0c\u5e38\u5e38\u60f3\u554a\uff1a\u81ea\u5df1\u662f\u770b\u000d\u000a\u5230\u5225\u4eba\u7684\u904e\u591a\uff0c\u9084\u662f\u81ea\u5df1\u7684\u904e\u591a\uff1f\u5982\u679c\u90fd\u662f\u770b\u5230\u5225\u4eba\u4e0d\u5c0d\uff0c\u60f3\u8457\u300c\u5225\u000d\u000a\u4eba\u53c8\u600e\u9ebc\u6a23\u3001\u600e\u9ebc\u6a23\u4e86\u3002\u300d\u300c\u4ed6\u4e00\u5b9a\u662f\u600e\u9ebc\u6a23\u3001\u600e\u9ebc\u6a23\u2026\u2026\u300d\u90fd\u628a\u5225\u000d\u000a\u4eba\u505a\u4ec0\u9ebc\u5148\u60f3\u597d\u4e86\uff0c\u5728\u5fc3\u88e1\u653e\u8457\uff0c\u90a3\u4f60\u6709\u8fa6\u6cd5\u7528\u6b63\u78ba\u7684\u5fc3\u614b\u4f86\u770b\u5225\u4eba\u000d\u000a\u55ce\uff1f\uff08\u6c92\u8fa6\u6cd5\uff09\u662f\uff0c\u6240\u4ee5\u4fee\u9053\u8981\u653e\u4e0b\u9019\u500b\u4e0d\u5e73\u4e4b\u5883\u3002\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u4e0d\u53ef\u4ee5\u000d\u000a\u5e36\u8457\u6210\u898b\u770b\u4eba\u5bb6\u3002\uff09\u000d\u000a\u73fe\u5728\u8fa6\u4e8b\u4eba\u54e1\u7ad9\u8457\uff0c\u8ddf\u8457\u70ba\u5e2b\u4e00\u6a23\uff0c\u624b\u63d2\u8457\u8170\u3002\uff08\u5927\u5bb6\u7b11\u4e86\uff09\u597d\u7b11\u000d\u000a\u5427\uff1f\u770b\u770b\u624b\u63d2\u8457\u8170\uff0c\u4e2d\u9593\u662f\u4e0d\u662f\u7a7a\u7684\uff1f\uff08\u662f\uff09","45\u000d\u000a\u552f\u6709\u4f60\u7684\u5fc3\u662f\u7a7a\u7684\uff0c\u4f60\u624d\u80fd\u5920\u5bb9\u5f97\u4e0b\u5225\u4eba\uff1b\u000d\u000a\u552f\u6709\u4f60\u662f\u8b19\u865b\u3001\u865b\u5fc3\uff0c\u662f\u4e00\u500b\u7a7a\u7684\u5fc3\u614b\uff0c\u000d\u000a\u4f60\u624d\u80fd\u5920\u63a5\u7d0d\u5225\u4eba\u7684\u4e0d\u8db3\u3002\u000d\u000a\u5426\u5247\u90fd\u6eff\u4e86\uff0c\u90a3\u9019\u6a23\u80fd\u5920\u63a5\u53d7\u5f97\u4e86\u5225\u4eba\u4e00\u9ede\u9ede\u4e0d\u597d\u55ce\uff1f\uff08\u4e0d\u884c\uff09\u80fd\u000d\u000a\u5920\u63a5\u53d7\u5f97\u4e86\u5225\u4eba\u6bd4\u81ea\u5df1\u597d\u55ce\uff1f\uff08\u4e0d\u884c\uff09\u90fd\u6c92\u8fa6\u6cd5\uff0c\u597d\u6216\u4e0d\u597d\u90fd\u6c92\u8fa6\u6cd5\u63a5\u000d\u000a\u53d7\uff0c\u9019\u6a23\u7684\u4eba\u9084\u80fd\u63a5\u53d7\u4ec0\u9ebc\uff1f\u53ea\u80fd\u5920\u63a5\u53d7\u81ea\u5df1\u826f\u5fc3\u7684\u8b74\u8cac\u3002\uff08\u662f\uff09\u5c31\u662f\u000d\u000a\u5440\uff01\u4e00\u76f4\u60f3\u5225\u4eba\u4e0d\u597d\u7684\u5730\u65b9\uff0c\u91cd\u8907\u5730\u53bb\u60f3\uff0c\u90a3\u91cd\u8907\u7684\u50b7\u5bb3\u81ea\u5df1\uff0c\u597d\u3002\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u6703\u6108\u4f86\u6108\u4e0d\u5feb\u6a02\uff09\u000d\u000a\u25ce\u8001\u5e2b\u8acb\u6240\u6709\u9ede\u50b3\u5e2b\u90fd\u8acb\u5230\u4f5b\u5802\u524d\u9762\uff1a\u25cb\u6674\u5f92\u5152\u5230\u524d\u9762\uff0c\u4f86\uff01\u9ede\u50b3\u5f92\u5152\u000d\u000a\u90fd\u4f86\uff0c\u770b\u770b\u7a7a\u7684\u3001\u63d2\u8170\uff0c\u50cf\u70ba\u5e2b\u9019\u6a23\u52fe\u8457\uff0c\u7a7a\u7684\u5c31\u53ef\u4ee5\u52fe\uff0c\u662f\u4e0d\u662f\uff1f\u000d\u000a\uff08\u662f\uff09\u9019\u4e0d\u5c31\u662f\u51f9\u51f8\u7d50\u5408\u5728\u4e00\u8d77\u55ce\uff1f\uff08\u662f\uff09\u90a3\u7528\u5728\u4e00\u822c\u51e1\u4e8b\u4e0a\uff0c\u5c31\u662f\u6709\u512a\u000d\u000a\u9ede\u3001\u6709\u7f3a\u9ede\uff0c\u5927\u5bb6\u4e92\u76f8\u5305\u5bb9\uff0c\u662f\u4e0d\u662f\u90fd\u52fe\u5728\u4e00\u8d77\u5c31\u4e00\u6a23\u4e86\u5462\uff1f\uff08\u662f\uff09\u90a3\u000d\u000a\u8fa6\u4e8b\u4eba\u54e1\u4f86\u7d66\u73ed\u54e1\u505a\u4e00\u500b\u6700\u597d\u7684\u793a\u7bc4\uff0c\u597d\u4e0d\u597d\uff1f\uff08\u597d\uff09","46\u000d\u000a\u624b\u8981\u5148\u6709\u7a7a\u7684\u5730\u65b9\uff0c\u53e6\u5916\u4e00\u65b9\u7684\u4eba\u4ed6\u7684\u624b\u624d\u80fd\u5920\u8ddf\u4f60\u7e6b\u4e0a\uff0c\u5982\u679c\u000d\u000a\u4f60\u7684\u624b\u90fd\u4e0d\u655e\u958b\uff0c\u90fd\u4e0d\u7559\u4e00\u500b\u7a7a\u9593\u7d66\u5225\u4eba\u62c9\u8457\uff0c\u90a3\u4f60\u8aaa\u4fee\u9053\u600e\u9ebc\u5718\u7d50\uff1f\u000d\u000a\u90a3\u4f60\u8aaa\u9053\u5834\u8981\u600e\u9ebc\u8208\u76db\u5462\uff1f\u52fe\u5f97\u5f88\u597d\u5462\uff01\u70ba\u5e2b\u6aa2\u67e5\u6aa2\u67e5\u3002\u73ed\u54e1\u9019\u6642\u5019\u000d\u000a\u5c31\u662f\u4f60\u5011\u7684\u798f\u5229\u4e86\uff0c\u6aa2\u67e5\u4e00\u4e0b\u770b\u8ab0\u6c92\u6709\u52fe\u597d\u3002\u000d\u000a\u9019\u6642\u5019\u5fc3\u88e1\u9762\u8981\u8ddf\u5916\u8868\u4e00\u6a23\uff0c\u9019\u53eb\u505a\u300c\u5167\u5916\u4e00\u5982\u300d\u3002\u800c\u4e0d\u662f\u53ea\u6709\u000d\u000a\u8868\u9762\u4e0a\u52fe\u8457\uff0c\u5176\u5be6\u5fc3\u88e1\u9762\u5728\u60f3\uff1a\u300c\u600e\u9ebc\u662f\u52fe\u5230\u4ed6\u5440\uff01\u300d\u5fc3\u88e1\u9762\u5728\u60f3\uff1a\u000d\u000a\u300c\u65e9\u77e5\u9053\u525b\u624d\u5c31\u7ad9\u524d\u9762\u4e00\u9ede\u3002\u300d\u4e0d\u80fd\u6709\u9019\u6a23\u7684\u60f3\u6cd5\u3002\u000d\u000a\u5171\u8fa6\u8655\u4e8b\uff0c\u53ef\u8cb4\u7684\u5730\u65b9\u5c31\u5728\u65bc\uff1a\u4f60\u80fd\u5bb9\u5f97\u4e0b\u6211\uff0c\u6211\u4e5f\u5bb9\u5f97\u4e0b\u4f60\u3002\u000d\u000a\u4f60\u6bd4\u6211\u597d\uff0c\u6211\u6210\u5c31\u4f60\uff1b\u4f60\u6bd4\u6211\u4e0d\u597d\uff0c\u6211\u63d0\u651c\u4f60\u3002\u000d\u000a\u90a3\u9084\u6709\u4ec0\u9ebc\u4e0a\u4e0b\u4e4b\u5206\u55ce\uff1f\uff08\u6c92\u6709\uff09\u000d\u000a\u6240\u4ee5\uff0c\u5176\u5be6\u300c\u9075\u524d\u63d0\u5f8c\u300d\u5c31\u662f\u300c\u5c0a\u5e2b\u91cd\u9053\u300d\u7684\u5c55\u73fe\u3002\u4f55\u8b02\u300c\u5c0a\u5e2b\u300d\u000d\u000a\u5462\uff1f\u4f60\u53ef\u4ee5\u628a\u81ea\u5df1\u4f4e\u5fc3\u4e0b\u6c23\u8d77\u4f86\uff0c\u4e00\u500b\u4f4e\u5fc3\u4e0b\u6c23\u7684\u4eba\uff0c\u4ed6\u6703\u6709\u63a5\u53d7\u4e0d\u000d\u000a\u4e86\u7684\u4e8b\u55ce\uff1f\uff08\u6c92\u6709\uff09","47\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u4e7e\uff11\u865f\u6148\u60b2\uff1a\u5982\u679c\u5f92\u5152\u73fe\u5728\u5f88\u8b19\u865b\u3001\u5f88\u67d4\u548c\uff0c\u90a3\u5225\u4eba\u8aaa\u4f60\u600e\u000d\u000a\u9ebc\u6a23\uff0c\u4f60\u6703\u5728\u4e4e\u55ce\uff1f\uff08\u4e0d\u6703\uff09\u000d\u000a\u90a3\u8fa6\u4e8b\u4eba\u54e1\u5011\uff0c\u77e5\u9053\u5c07\u4f86\u8981\u600e\u9ebc\u505a\u4e86\u55ce\uff1f\uff08\u77e5\u9053\uff09\u5982\u679c\u4e0d\u5c0f\u5fc3\u624b\u52fe\u000d\u000a\u4e0d\u8d77\u4f86\u7684\u8a71\uff0c\u6c92\u95dc\u4fc2\uff0c\u9019\u662f\u4eba\u4e4b\u5e38\u60c5\uff0c\u56e0\u70ba\u5927\u5bb6\u90fd\u9084\u5728\u5b78\u4fee\uff0c\u4e0d\u662f\u53ea\u000d\u000a\u6709\u9019\u88e1\uff0c\u4e16\u754c\u5404\u5730\u6bcf\u4e00\u500b\u4f5b\u5802\uff0c\u53ea\u8981\u6709\u4eba\u90fd\u6709\u9019\u500b\u554f\u984c\uff0c\u662f\u5427\uff01\u56e0\u70ba\u000d\u000a\u70ba\u5e2b\u4eca\u5929\u8ddf\u5927\u5bb6\u6709\u7de3\uff0c\u4e0d\u7136\u70ba\u5e2b\u5230\u8655\u90fd\u60f3\u53bb\u5462\uff01\u6240\u4ee5\u5728\u9019\u88e1\u8b1b\uff0c\u9019\u5370\u000d\u000a\u6210\u6587\u5b57\u4e5f\u53ef\u4ee5\u5927\u5bb6\u770b\u4e00\u770b\uff0c\u90fd\u662f\u5927\u5bb6\u7684\u554f\u984c\uff0c\u5982\u679c\u65c1\u908a\u7684\u4eba\u4f60\u4e0d\u60f3\u52fe\u000d\u000a\u4ed6\u4e86\uff0c\u70ba\u4ec0\u9ebc\uff1f\u53ef\u80fd\u662f\u56e0\u70ba\u4ed6\u4e5f\u4e0d\u8b93\u4f60\u52fe\uff0c\u6240\u4ee5\u5728\u5171\u8fa6\u8655\u4e8b\u4e0a\uff0c\u4ed6\u5206\u000d\u000a\u5230\u90a3\u4e00\u7d44\uff0c\u6211\u5c31\u5148\u770b\u4e00\u770b\uff0c\u6709\u4ed6\u7684\u540d\u5b57\u6211\u5c31\u9583\u4e00\u9583\uff0c\u5230\u5225\u7d44\u53bb\uff0c\u53cd\u6b63\u000d\u000a\u9084\u597d\u4f5b\u5802\u4e0d\u662f\u53ea\u6709\u4e00\u500b\u7d44\u554a\uff01\u6709\u5f88\u591a\u7d44\uff0c\u4f46\u662f\u9019\u6a23\u597d\u55ce\uff1f\uff08\u4e0d\u597d\uff09\u000d\u000a\u8981\u60f3\u60f3\u770b\u5440\uff01\u4f60\u5011\u8e0f\u9032\u7b2c\u4e00\u6b65\u9084\u4e0d\u662f\u8e0f\u5728\u540c\u6a23\u7684\u9580\u6abb\u4e0a\uff08\u662f\uff09\uff0c\u6240\u000d\u000a\u4ee5\u4fee\u5230\u6700\u5f8c\uff0c\u300c\u4eba\u4e8b\u5713\u878d\u300d\uff0c\u662f\u4fee\u5f97\u6700\u597d\u7684\u78e8\u7149\u8003\u5377\u3002\u9019\u5f35\u8003\u5377\u5440\uff0c\u000d\u000a\u5f92\u5152\u5011\u65e9\u5c31\u4e00\u4eba\u62ff\u4e00\u5f35\u4e86\uff0c\u90a3\u73fe\u5728\u4f86\u770b\u8a55\u5206\uff0c\u9019\u6a23\u770b\u8a55\u5206\u597d\u55ce\uff1f\u4e0d\u597d\u3001","48\u000d\u000a\u4e0d\u597d\uff0c\u56e0\u70ba\u529f\u904e\u81ea\u5728\u5fc3\uff0c\u70ba\u5e2b\u4e5f\u4e0d\u9700\u8981\u8aaa\u3002\u000d\u000a\u5728\u70ba\u5e2b\u5fc3\u4e2d\uff0c\u5927\u5bb6\u90fd\u662f\u597d\u5f92\u5152\uff0c\u5728\u70ba\u5e2b\u5fc3\u4e2d\u6bcf\u4e00\u4f4d\u5f92\u5152\u90fd\u662f\u53ef\u9020\u000d\u000a\u53ef\u7149\u4e4b\u6750\u3002\u4f46\u662f\u5728\u4f60\u5011\u7684\u5fc3\u4e2d\uff0c\u5c0d\u65b9\u4e5f\u8981\u662f\u53ef\u9020\u4e4b\u6750\uff0c\u70ba\u4ec0\u9ebc\u9019\u6a23\u8aaa\u000d\u000a\u5462\uff1f\u000d\u000a\u770b\u73fe\u5728\u6e21\u4eba\u96e3\u4e0d\u96e3\uff1f\uff08\u96e3\uff09\u5de6\u53f3\u770b\u770b\uff0c\u73ed\u54e1\u6709\u5f88\u591a\u4f4d\u55ce\uff1f\u4e0d\u591a\u4f4d\u3002\u000d\u000a\u9019\u662f\u56e0\u70ba\u4eba\u5f88\u96e3\u6e21\uff0c\u5c0d\u4e0d\u5c0d\uff1f\uff08\u662f\uff09\u90a3\u4eba\u65e2\u7136\u5f88\u96e3\u6e21\uff0c\u8981\u4e0d\u8981\u6bcf\u4e00\u500b\u6e21\u000d\u000a\u4f86\u7684\u4eba\u90fd\u7559\u4f4f\u5462\uff1f\u9996\u5148\u8fa6\u4e8b\u4eba\u54e1\u81ea\u5df1\u5c31\u8981\u7559\u4f4f\u81ea\u5df1\u3002\u90a3\u9ebc\u73ed\u54e1\u770b\u5230\u4f60\u000d\u000a\u5011\u9019\u9ebc\u548c\u7766\uff0c\u9019\u9ebc\u548c\u6c23\uff0c\u6703\u4e0d\u6703\u5e38\u5e38\u60f3\u8981\u56de\u4f86\u76db\u539f\u4f5b\u5802\u5440\uff1f\uff08\u6703\uff09\u662f\u4e0d\u000d\u000a\u662f\u5440\uff1f\uff08\u662f\uff09\u90a3\u73fe\u5728\u8ddf\u65c1\u908a\u7684\u4eba\u8aaa\u2026\u2026\u8aaa\u4ec0\u9ebc\uff1f\u70ba\u5e2b\u6c92\u8aaa\u8a71\u5c31\u6c92\u8a5e\u5566\uff01\u000d\u000a\uff08\u9019\u6642\u5019\u73fe\u5834\u6709\u8fa6\u4e8b\u4eba\u54e1\u8aaa\uff1a\u6709\u4f60\u771f\u597d\uff0c\u5e0c\u671b\u73ed\u54e1\u56de\u4f86\u8b80\u65b0\u6c11\u73ed\uff0c\u6211\u5011\u624d\u6709\u4e86\u613f\u000d\u000a\u7684\u6a5f\u6703\uff0c\u6211\u5011\u90fd\u611b\u4f60\u5011\uff09\u90a3\u9019\u6a23\u660e\u767d\u70ba\u5e2b\u7684\u5fc3\u610f\u4e86\u55ce\uff1f\uff08\u660e\u767d\uff09\u000d\u000a\u9019\u6a23\u52fe\u8457\u4e5f\u4e0d\u932f\u5462\uff01\u662f\u4e0d\u662f\uff01\u624b\u53ef\u4ee5\u62c9\u7b4b\u4e00\u4e0b\uff0c\u597d\uff0c\u5927\u5bb6\u90fd\u662f\u540c\u000d\u000a\u4fee\u9053\u4f34\u3002\u90a3\u70ba\u5e2b\u73fe\u5728\u5c31\u5728\u770b\u56c9\uff01\u73fe\u5728\u52fe\u8457\uff0c\u4e4b\u5f8c\u5171\u8fa6\u8655\u4e8b\u7684\u6642\u5019\uff0c\u770b\u000d\u000a\u5fc3\u6709\u6c92\u6709\u9023\u8457\u5fc3\uff0c\u770b\u5ff5\u982d\u6709\u6c92\u6709\u76f8\u540c\uff0c\u53ea\u6709\u4e0d\u65b7\u78e8\u6ecc\u7fd2\u6c23\uff0c\u5f92\u5152\u5011\u624d","49\u000d\u000a\u80fd\u52fe\u5f97\u8d77\u4f86\u3002\u597d\uff01\u8acb\u653e\u958b\u5427\u3002\u000d\u000a\u25ce\u6b64\u6642\u5927\u5bb6\u628a\u52fe\u8457\u7684\u624b\u7d66\u653e\u958b\u4e86\u3002\u000d\u000a\u8aaa\u653e\u958b\uff0c\u5c31\u771f\u7684\u653e\u958b\u554a\uff1f\u5982\u679c\u6709\u4eba\u53eb\u4f60\u5011\u653e\u958b\uff0c\u53ef\u5343\u842c\u5225\u653e\u958b\uff0c\u000d\u000a\u9019\u662f\u5728\u8003\u5f92\u5152\u5011\u554a\uff01\u000d\u000a\u5927\u5bb6\u4e00\u8d77\u8aaa\uff1a\u300c\u6211\u5011\u4fee\u9053\u8fa6\u9053\uff0c\u5fc3\u8981\u52fe\u5728\u4e00\u8d77\u3002\u300d\u4e0d\u662f\u770b\u8457\u70ba\u000d\u000a\u5e2b\u5462\uff0c\u4f60\u5011\u90fd\u5c0d\u70ba\u5e2b\u771f\u8aa0\u4e00\u5ff5\uff0c\u4f46\u70ba\u5e2b\u5e0c\u671b\u4f60\u5011\u5f7c\u6b64\u5c0d\u5f7c\u6b64\u771f\u8aa0\u4e00\u5ff5\uff0c\u000d\u000a\u5c0d\u4f5b\u7684\u771f\u8aa0\u662f\u7c21\u55ae\u7684\uff0c\u5c0d\u65c1\u908a\u7684\u4eba\u771f\u8aa0\uff0c\u9084\u8981\u4fee\u7149\u3002\u000d\u000a\u6240\u4ee5\uff0c\u770b\u8457\u65c1\u908a\u7684\u4eba\u8aaa\uff1a\u300c\u6211\u5011\u4fee\u9053\u8fa6\u9053\uff0c\u5fc3\u8981\u52fe\u5728\u4e00\u8d77\u3002\u300d\u000d\u000a\u25ce\u6b64\u6642\u5927\u5bb6\u4e92\u76f8\u76f8\u5c0d\uff0c\u8ddf\u8457\u8001\u5e2b\u4e00\u8d77\u8aaa\uff1a\u300c\u6211\u5011\u4fee\u9053\u8fa6\u9053\uff0c\u5fc3\u8981\u52fe\u5728\u4e00\u8d77\u3002\u300d\u000d\u000a\u597d\uff0c\u90a3\u53ef\u4ee5\u8b93\u70ba\u5e2b\u4e0b\u6b21\u4f86\u7684\u6642\u5019\uff0c\u53ef\u4ee5\u770b\u5230\u5f92\u5152\u5011\uff0c\u53ef\u4ee5\u770b\u5230\u6bcf\u000d\u000a\u500b\u5730\u65b9\u7684\u5f92\u5152\u5011\u90fd\u6709\u9032\u6b65\u3002\u000d\u000a\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u8001\u5e2b\u6148\u60b2\u6709\u4e09\u9593\u4f5b\u5802\u8981\u8cdc\u58c7\u540d\uff09\u597d\u3002","50\u000d\u000a\u80fd\u5728\u9019\u500b\u6642\u5019\u958b\u4f5b\u5802\uff0c\u662f\u4e0d\u662f\u5927\u559c\u4e8b\u5440\uff1f\uff08\u662f\uff09\u000d\u000a\u96d6\u7136\u5f88\u591a\u6182\u60b2\u7684\u4e8b\u60c5\u90fd\u5728\u767c\u751f\uff0c\u4f46\u662f\u4e5f\u6709\u597d\u7684\u4e8b\u60c5\u5728\u767c\u751f\u3002\u4e5f\u5e0c\u000d\u000a\u671b\u597d\u7684\u4e8b\u60c5\uff0c\u53ef\u4ee5\u56e0\u70ba\u5f92\u5152\u5011\u7684\u5fc3\u597d\u3001\u5fc3\u5584\u3001\u5fc3\u8aa0\uff0c\u800c\u767c\u751f\u5728\u6bcf\u4e00\u4f4d\u000d\u000a\u5f92\u5152\u8eab\u4e0a\u3002\u000d\u000a\u25ce\u53f0\u5317\u90ed\u6c0f\u4f5b\u5802\u2500\u2500\u627f\u65b0\u4f5b\u5802\u000d\u000a\u627f\u65b0\u3002\u627f\u4e0a\u555f\u4e0b\u7684\u627f\uff0c\u65b0\u820a\u7684\u65b0\u3002\u597d\uff0c\uff08\u4ed9\u4f5b\u8cdc\u7cd6\u679c\uff09\u5e36\u56de\u53bb\u7d66\u58c7\u000d\u000a\u4e3b\uff0c\u9019\u5c31\u662f\u50b3\u627f\uff0c\u50b3\u627f\u4ec0\u9ebc\u5462\uff1f\u50b3\u627f\u9019\u4e00\u9846\u4fee\u9053\u7684\u5fc3\uff0c\u65e5\u65b0\u53c8\u65b0\uff0c\u5e38\u000d\u000a\u5e38\u8b93\u9053\u5728\u5fc3\u88e1\u9762\u860a\u542b\u51fa\u6eab\u6545\u77e5\u65b0\u7684\u6ecb\u5473\u3002\uff08\u662f\uff09\uff08\u5433\u8b1b\u5e2b\u25cb\u6797\u4ee3\u8868\u8b1d\u6069\uff09\u000d\u000a\u597d\uff0c\u518d\u4f86\u3002\u000d\u000a\u25ce\u65b0\u7af9\u5f35\u6c0f\u4f5b\u5802\u2500\u2500\u84ee\u6e05\u4f5b\u5802\u000d\u000a\u84ee\u82b1\u7684\u84ee\uff0c\u6e05\u975c\u7684\u6e05\u3002\u904e\u4f86\u904e\u4f86\uff0c\u4e00\u5305\u4f9b\u7cd6\uff0c\u8981\u80fd\u5982\u84ee\u51fa\u6de4\u4e0d\u67d3\u000d\u000a\u624d\u662f\u771f\u6e05\u975c\u3002\u73fe\u5728\u8ab0\u7684\u5fc3\u662f\u4e7e\u6de8\u7684\u5440\uff1f\u4f5b\u5802\u7684\u5fc3\u662f\u4e7e\u6de8\u7684\uff0c\u6240\u4ee5\u53eb\u84ee\u000d\u000a\u6e05\uff0c\u90a3\u4f5b\u5802\u88e1\u7684\u4eba\u5c31\u8981\u5b78\u7fd2\u9019\u9846\u4e7e\u6de8\u7684\u5fc3\uff0c\u6709\u4e86\u4e7e\u6de8\u7684\u5fc3\u624d\u80fd\u5920\u4e0d\u5e36","51\u000d\u000a\u592a\u591a\u7684\u4e0d\u597d\u7684\u5fc3\u5728\u8eab\u4e0a\u3002\uff08\u662f\uff09\u597d\uff0c\u8acb\u8d77\u8acb\u8d77\u3002\uff08\u6797\u9ede\u50b3\u5e2b\u25cb\u798e\u4ee3\u8868\u8b1d\u6069\uff09\u000d\u000a\u25ce\u67ec\u57d4\u5be8\u78c5\u901a\u516c\u5171\u4f5b\u5802\u2500\u2500\u666e\u9053\u8056\u5802\u000d\u000a\u4e0d\u7c21\u55ae\u4e0d\u7c21\u55ae\u5440\uff0c\u6b77\u76e1\u4e86\u591a\u5c11\u8f9b\u82e6\u624d\u84cb\u4e86\u4e00\u9593\u4f5b\u5802\uff0c\u70ba\u5e2b\u771f\u6b61\u559c\u000d\u000a\u5462\uff01\uff08\u9673\u9ede\u50b3\u5e2b\uff1a\u8001\u5e2b\u6148\u60b2\u6697\u4e2d\u4fdd\u8b77\uff09\u000d\u000a\u666e\u9053\uff0c\u666e\u6e21\u7684\u666e\uff0c\u4fee\u9053\u7684\u9053\u3002\u5e0c\u671b\u4f5b\u5149\u5e38\u5e38\u666e\u7167\uff0c\u56e0\u70ba\u90a3\u500b\u5730\u65b9\u000d\u000a\u6709\u9053\u2500\u2500\u666e\u9053\u8056\u5802\u3002\u90a3\u908a\u7684\u5f92\u5152\u8981\u6709\u679c\uff0c\u521d\u4e00\u5341\u4e94\u7684\u6642\u5019\u737b\u4f9b\uff0c\u5927\u5bb6\u000d\u000a\u5206\u4e00\u5206\uff0c\u597d\u4e0d\u597d\uff1f\u000d\u000a\u90a3\u25cb\u82f1\u5f92\u5152\u70ba\u5e2b\u4e5f\u8981\u7d66\u4f60\u679c\u3002\uff08\u6b64\u6642\uff0c\u5f35\u9ede\u50b3\u5e2b\u62ff\u4e86\u4e00\u689d\u9999\u8549\u5448\u7d66\u8001\u000d\u000a\u5e2b\uff0c\u8001\u5e2b\u6148\u60b2\u53c8\u591a\u8981\u4e00\u500b\uff09\u4f86\uff0c\u518d\u4e00\u500b\uff0c\u6703\u4e0d\u6703\u70ba\u96e3\uff1f\uff08\u5f35\u9ede\u50b3\u5e2b\uff1a\u4e0d\u6703\uff09\u000d\u000a\u4e0d\u6703\u554a\uff01\u8f15\u800c\u6613\u8209\uff0c\u597d\u3002\u000d\u000a\u4f86\uff0c\u4e00\u5929\u4e00\u500b\u5462\uff0c\u4e00\u5929\u4e00\u6839\uff0c\u6c92\u95dc\u4fc2\u4e0d\u7528\u8aaa\uff0c\u8eab\u9ad4\u91cd\u8981\uff0c\u9053\u5fc3\u4e5f\u000d\u000a\u91cd\u8981\uff0c\u56e0\u70ba\u9053\u5fc3\u5805\u56fa\uff0c\u6240\u4ee5\u4e0d\u61fc\u8eab\u9ad4\u4e0a\u7684\u56f0\u96e3\uff0c\u6bcf\u4f4d\u5f92\u5152\u90fd\u662f\uff0c\u5f92\u5152","52\u000d\u000a\u90fd\u662f\u627f\u64d4\u4e86\u8eab\u9ad4\u4e0a\u7684\u8f9b\u82e6\uff0c\u4f46\u662f\u56e0\u70ba\u77e5\u9053\u4fee\u9053\u662f\u5c0d\u7684\uff0c\u6240\u4ee5\u90fd\u8d70\u5230\u4e86\u000d\u000a\u73fe\u5728\u662f\u4e0d\u662f\uff1f\uff08\u662f\uff09\u597d\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5728\u5834\u7684\u5176\u4ed6\u9ede\u50b3\u5e2b\u6148\u60b2\u4e26\u5404\u8cdc\u4e86\u4e00\u9846\u8461\u8404\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u9673\u9ede\u50b3\u5e2b\u25cb\u9cf3\u6148\u60b2\uff1a\u5f92\u5152\u4f86\uff0c\u25cb\u9cf3\u5f92\u5152\uff0c\u9019\u500b\u96d6\u7136\u5c0f\uff0c\u4f46\u300c\u82a5\u000d\u000a\u5b50\u7d0d\u9808\u5f4c\u300d\uff0c\u8acb\u8d77\u8acb\u8d77\uff0c\u5927\u5bb6\u8eab\u9ad4\u90fd\u8981\u9867\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u6797\u9ede\u50b3\u5e2b\u25cb\u798e\u6148\u60b2\uff1a\u25cb\u798e\u5f92\u5152\u4f86\uff0c\u9019\u500b\u96d6\u7136\u5c0f\uff0c\u4f46\u662f\u300c\u5927\u4ee5\u000d\u000a\u5c0f\u6210\u300d\uff0c\u8eab\u9ad4\u4e5f\u8981\u9867\u5440\uff01\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5433\u9ede\u50b3\u5e2b\u25cb\u6674\u6148\u60b2\uff1a\u25cb\u6674\u5f92\u5152\uff0c\u9019\u500b\u662f\u5927\uff0c\u9084\u662f\u5c0f\u554a\uff1f\uff08\u5f88\u000d\u000a\u5927\uff09\u5f88\u5927\uff1f\u597d\uff0c\u90a3\u53ef\u80fd\u5c31\u4e0d\u662f\u9019\u500b\u4e86\uff01\u9019\u662f\u5927\u5728\u4fee\u9053\u7684\u5fc3\u613f\uff0c\u613f\u5927\u679c\u000d\u000a\u5927\uff0c\u4f46\u662f\u8981\u5805\u6301\uff0c\u56e0\u70ba\u5805\u6301\u8d70\u5230\u6700\u5f8c\uff0c\u90a3\u624d\u80fd\u4e0d\u8f9c\u8ca0\u4e0a\u5929\u7684\u5fc3\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u5f35\u9ede\u50b3\u5e2b\u25cb\u826f\u6148\u60b2\uff1a\u25cb\u826f\u5f92\u5152\u60f3\u4e0d\u60f3\u8981\uff1f\u9019\u500b\u4e0d\u5927\u4e5f\u4e0d\u5c0f\uff0c\u000d\u000a\u70ba\u4ec0\u9ebc\u5462\uff1f\u56e0\u70ba\u81ea\u5df1\u5c31\u53ef\u4ee5\u9020\u5316\uff0c\u662f\u4e0d\u662f\uff1f\uff08\u4e0d\u662f\uff09\u4e0d\u662f\uff1f\u90a3\u662f\u4f60\u5011\u7684\u000d\u000a\u524d\u4eba\u7167\u9867\uff0c\u6240\u4ee5\u90fd\u662f\u5728\u540c\u4e00\u500b\u679d\u4e0a\u7684\uff0c\u6bcf\u4e00\u4f4d\u5f92\u5152\u90fd\u4e00\u6a23\u3002","53\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u8cf4\u9ede\u50b3\u5e2b\u25cb\u745e\u6148\u60b2\uff1a\u25cb\u745e\u5f92\u5152\u8981\u4e0d\u8981\uff1f\u9019\u500b\u662f\u300c\u5ee3\u300d\uff0c\u56e0\u70ba\u000d\u000a\u70ba\u5e2b\u53ea\u662f\u85c9\u8457\u9019\u500b\u4f86\u50b3\u905e\u70ba\u5e2b\u7684\u95dc\u61f7\uff0c\u6240\u4ee5\u4e0d\u662f\u770b\u5230\u7684\u9019\u500b\u597d\u4e0d\u597d\u000d\u000a\u5462\uff1f\u7e7c\u7e8c\u6210\u5168\u66f4\u591a\u4eba\u5566\uff0c\u9053\u5834\u90fd\u9700\u8981\u50b3\u627f\uff0c\u9700\u8981\u5ef6\u7e8c\u85aa\u706b\u3002\u000d\u000a\u25ce\u8001\u5e2b\u5c0d\u937e\u9ede\u50b3\u5e2b\u6148\u60b2\uff1a\u25cb\u25cb\u5f92\u5152\u4f86\uff0c\u525b\u624d\u62ff\u4e86\u4e00\u500b\u679c\uff0c\u9084\u8981\u518d\u4e00\u500b\u000d\u000a\u55ce\uff1f\uff08\u8981\uff09\u70ba\u4ec0\u9ebc\u8981\u5462\uff1f\uff08\u662f\u8001\u5e2b\u7d66\u5f92\u5152\uff09\u56e0\u70ba\u525b\u624d\u90a3\u4e00\u500b\u662f\u8fa6\u9053\uff0c\u9019\u4e00\u000d\u000a\u500b\u662f\u4fee\u9053\uff0c\u4fee\u9053\u8fa6\u9053\u5169\u76f8\u5408\u4e00\uff0c\u90a3\u624d\u662f\u70ba\u5e2b\u5e0c\u671b\u6bcf\u4e00\u4f4d\u5f92\u5152\u6240\u8fa6\u51fa\u4f86\u000d\u000a\u7684\u9053\u5834\uff0c\u90fd\u8981\u4fee\u8fa6\u5408\u4e00\uff0c\uff08\u662f\uff09\u8acb\u8d77\u8acb\u8d77\u3002\u000d\u000a\u597d\u5566\uff01\u4e0d\u803d\u8aa4\u5f92\u5152\u5011\u7684\u6642\u9593\u4e86\u3002\u000d\u000a\u25ce\u9673\u9ede\u50b3\u5e2b\u8acb\u793a\u8001\u5e2b\u6148\u60b2\u67ec\u57d4\u5be8\u666e\u9053\u8056\u5802\u662f\u5426\u8cdc\u679c\uff0c\u8001\u5e2b\u6148\u60b2\uff1a\u521d\u4e00\u3001\u5341\u000d\u000a\u4e94\u7684\u6642\u5019\u8acb\u4ed6\u5011\u591a\u8cb7\u4e00\u4e9b\u5427\uff0c\u5c31\u737b\u4e0a\u53bb\uff0c\u737b\u5b8c\u6536\u4e0b\u4f86\u5206\u7d66\u6240\u6709\u7684\u9053\u89aa\uff0c\u000d\u000a\u80fd\u5206\u5230\u7684\u76e1\u91cf\u5206\u5230\uff0c\u597d\u55ce\uff1f\u50b3\u9054\u70ba\u5e2b\u7684\u95dc\u61f7\u3002\u000d\u000a\u4eca\u5929\u548c\u5f92\u5152\u5011\u6703\u9762\uff0c\u4e0d\u662f\u7d50\u675f\u5462\uff0c\u53ea\u662f\u525b\u958b\u59cb\uff0c\u5e0c\u671b\u5f92\u5152\u5011\u7e7c\u7e8c\u000d\u000a\u4f86\u4fee\u8fa6\u3002\u4fee\u81ea\u5df1\uff0c\u770b\u898b\u81ea\u5df1\uff0c\u6e1b\u5c11\u5206\u5225\uff0c\u6536\u7a6b\u6085\u6a02\u3002 \u5148\u5c07","54\u000d\u000a\u7687 \u53e9\u8fad \u9084\u6709\u5eda\u623f\uff0c\u5eda\u623f\u4e5f\u62ff\u500b\u679c\u904e\u53bb\u3002\u000d\u000a\u5f92\u5152\u5011\u73cd\u91cd\u5566\uff0c\u5225\u8b93\u70ba\u5e2b\u5931\u671b\u3002 \u8fd4\u5c4f\u5c71~","55\u000d\u000a\u3010\u8a3b\u91cb\u9644\u9304\u3011\u000d\u000a\u3010\u8a3b \u4e00\u3011\u5b17\u905e\u3002\u5b17\u97f3\u540c\u5584\uff0c\u8f49\u3001\u6539\u8b8a\u3002\u5b17\u905e\uff0c\u6539\u8b8a\u3001\u66f4\u8fed\u3001\u4ea4\u66ff\u3002\u000d\u000a\u3010\u8a3b \u4e8c\u3011\u300c\u8ca1\u7269\u8f15\uff0c\u6028\u4f55\u751f\uff1b\u8a00\u8a9e\u5fcd\uff0c\u5fff\u81ea\u6cef\u300d\u3002\u8a9e\u672c\u300a\u5f1f\u5b50\u898f\u300b\u3002\u7c21\u7fa9\uff1a\u000d\u000a\u4fee\u9053\u4eba\u5b78\u7fd2\u4e0d\u5c07\u9322\u8ca1\u7269\u54c1\u770b\u592a\u91cd\uff0c\u4e0d\u904e\u5206\u53bb\u722d\u596a\u3001\u8ffd\u9010\uff0c\u8207\u4ed6\u4eba\u7684\u000d\u000a\u4ea4\u60e1\u4e5f\u81ea\u7136\u80fd\u6e1b\u9664\uff0c\u4e0d\u6703\u5f15\u8d77\u4ed6\u4eba\u7684\u6028\u6068\uff1b\u8aaa\u8a71\u6642\u82e5\u80fd\u8b39\u614e\u8a00\u8a9e\uff0c\u000d\u000a\u81ea\u7136\u4e0d\u6703\u5f15\u8d77\u4ed6\u4eba\u5167\u5fc3\u7684\u6c23\u61a4\u4e4b\u610f\u3002 \u000d\u000a\u3010\u8a3b \u4e09\u3011\u300c\u4e00\u5e74\u6625\u4e8b\u90fd\u4f86\u5e7e\uff0c\u65e9\u904e\u4e86\uff0c\u4e09\u4e4b\u4e8c\u300d\u3002\u8a9e\u672c\uff0c\u5b8b\uff0e\u6b50\u967d\u4fee\u300a\u9752\u7389\u000d\u000a\u6848\u300b\u3002\u7c21\u7fa9\uff1a\u7d30\u7d30\u7b97\u4f86\uff0c\u4e00\u5e74\u6625\u5149\u5df2\u904e\u4e86\u4e09\u5206\u4e4b\u4e8c\u3002\u5f15\u7533\u70ba\u6642\u9593\u904e\u000d\u000a\u5f97\u5f88\u5feb\u3002\u000d\u000a\u3010\u8a3b \u56db\u3011\u300c\u8a18\u61b6\u7d30\u6545\u975e\u9ad8\u8ce2\u300d\u3002\u8a9e\u672c\u5510\uff0e\u675c\u752b\u300a\u8d64\u9704\u884c\u300b\u300c\u4e08\u592b\u5782\u540d\u52d5\u842c\u000d\u000a\u5e74\uff0c\u8a18\u61b6\u7d30\u6545\u975e\u9ad8\u8ce2\u300d\uff0c\u7c21\u7fa9\uff1a\u5373\u4f7f\u60a8\u5728\u4e16\u9593\u6709\u540d\u52d5\u842c\u53e4\u7684\u8072\u540d\uff0c\u000d\u000a\u4f46\u662f\u5167\u5fc3\u537b\u5e38\u5e38\u8a18\u8457\u4e00\u4e9b\u5c0f\u4e8b\u60c5\u3001\u5c0f\u7d1b\u722d\u3001\u5c0f\u7169\u60f1\u3001\u5c0f\u8a08\u8f03\u3001\u5c0f\u4ec7\u000d\u000a\u6068\uff0c\u4e14\u4e45\u8a18\u4e0d\u5fd8\uff0c\u90a3\u4e5f\u7a31\u4e0d\u4e0a\u662f\u4e00\u4f4d\u6709\u9053\u8ce2\u58eb\u3002","56\u000d\u000a\u3010\u8a3b \u4e94\u3011\u300c\u775a\u7725\u5fc5\u5831\u300d\u3002\u775a\uff0c\u97f3\u540c\u7259\u3002\u7725\uff0c\u97f3\u540c\u81ea\u3002\u8a9e\u672c\u300a\u53f2\u8a18\uff0e\u5377\u4e03\u4e5d\uff0e\u000d\u000a\u8303\u96ce\u8521\u6fa4\u50b3\u300b\u3002\u7c21\u7fa9\uff1a\u6307\u6975\u5c0f\u7684\u6028\u6068\u3001\u4ec7\u6068\u3001\u904e\u7bc0\uff0c\u4e5f\u4e00\u5b9a\u8981\u5831\u000d\u000a\u5fa9\uff0c\u5ea6\u91cf\u72f9\u7a84\u3002\u000d\u000a\u3010\u8a3b \u516d\u3011\u300c\u751f\u6b7b\u3001\u5b58\u4ea1\u3001\u7aae\u9054\u3001\u8ca7\u5bcc\u3001\u90fd\u662f\u4e8b\u4e4b\u8b8a\uff0c\u547d\u4e4b\u884c\u300d\u3002\u8a9e\u672c\uff1a\u300a\u838a\u000d\u000a\u5b50\u4e4b\u5fb7\u5145\u7b26\u300b\u300c\u6b7b\u751f\u3001\u5b58\u4ea1\u3001\u7aae\u9054\u3001\u8ca7\u5bcc\u3001\u8ce2\u8207\u4e0d\u8096\u3001\u8b6d\u8b7d\u3001\u98e2\u000d\u000a\u6e34\u3001\u5bd2\u6691\uff0c\u662f\u4e8b\u4e4b\u8b8a\u3001\u547d\u4e4b\u884c\u4e5f\u3002\u300d\u7c21\u7fa9\uff1a\u51fa\u751f\u3001\u6b7b\u4ea1\uff0c\u5f97\u5230\u3001\u5931\u000d\u000a\u53bb\uff0c\u7aae\u56f0\u6f66\u5012\u3001\u9806\u9042\u4ea8\u9054\uff0c\u8ca7\u7aae\u3001\u5bcc\u8cb4\uff0c\u667a\u6167\u3001\u611a\u920d\uff0c\u8a46\u6bc0\u3001\u7a31\u000d\u000a\u8b7d\uff0c\u98e2\u3001\u6e34\u3001\u5bd2\u3001\u6691\uff0c\u9019\u4e9b\u90fd\u662f\u4e8b\u7269\u4eba\u60c5\u5e38\u614b\u7684\u8b8a\u5316\uff0c\u4ee5\u53ca\u904e\u53bb\u4eca\u000d\u000a\u751f\u56e0\u679c\u7684\u6d41\u8f49\u6f14\u8b8a\uff0c\u7686\u6642\u523b\u66f4\u66ff\u51fa\u73fe\u5728\u6211\u5011\u751f\u547d\u4e2d\uff0c\u5e38\u4e0d\u53d7\u4eba\u7684\u5fc3\u000d\u000a\u610f\u6240\u63a7\u5236\u3002\u6240\u4ee5\u4fee\u9053\u8981\u5b78\u7fd2\u6e1b\u5c11\u8b93\u9019\u4e9b\u64fe\u4e82\u81ea\u5df1\u5e73\u975c\u7684\u5fc3\uff0c\u8a66\u8457\u8abf\u000d\u000a\u6574\u3001\u964d\u4f0f\u3001\u6539\u8b8a\u5fc3\u5ff5\uff0c\u4f7f\u6b64\u5fc3\u5e73\u548c\u8212\u9069\uff0c\u96a8\u9806\u56e0\u7de3\u4e2d\u4e0d\u8b8a\u5176\u611f\u6069\u77e5\u000d\u000a\u8db3\u5e38\u4ed8\u51fa\uff0c\u5247\u53ef\u4f7f\u81ea\u5df1\u6642\u523b\u5fc3\u7121\u55d4\u6068\uff0c\u8207\u842c\u7269\u548c\u8ae7\u5171\u751f\u3002\u82e5\u53ef\u4ee5\u9047\u000d\u000a\u5230\u4efb\u4f55\u72c0\u6cc1\u90fd\u4fdd\u6301\u9019\u6a23\u5fc3\u614b\u7684\u4eba\uff0c\u624d\u662f\u8056\u8ce2\u6240\u8aaa\u7684\u300c\u624d\u5168\u300d\u2500\u2500\u7121\u000d\u000a\u6eef\u7121\u7919\uff0c\u667a\u6167\u5fc3\u6027\u901a\u9054\u3002","57\u000d\u000a\u3010\u8a3b \u4e03\u3011\u300c\u5b78\u5982\u4e0d\u53ca\uff0c\u7336\u6050\u5931\u4e4b\u300d\u3002\u8a9e\u672c\u300a\u8ad6\u8a9e\uff0e\u6cf0\u4f2f\u7b2c\u516b\u300b\uff0c\u610f\u601d\u662f\u70ba\u000d\u000a\u5b78\u3001\u52e4\u7814\u5b78\u554f\u3001\u9053\u7406\uff0c\u60f3\u8981\u9054\u5230\u7684\u76ee\u6a19\u7e3d\u662f\u9054\u4e0d\u5230\uff0c\u9084\u5bb3\u6015\u5fd8\u6389\u5df2\u000d\u000a\u7d93\u6240\u5b78\u5230\u7684\uff0c\u5e38\u4fdd\u6301\u6230\u6230\u5162\u5162\uff0c\u52aa\u529b\u4e0d\u61c8\u7684\u614b\u5ea6\u3002\u000d\u000a\u3010\u8a3b \u516b\u3011\u300c\u9022\u5c71\u958b\u8def\uff0c\u9047\u6c34\u67b6\u6a4b\u3002\u300d\u7c21\u7fa9\uff1a\u78b0\u5230\u5c71\u963b\u64cb\u5728\u524d\uff0c\u6211\u5011\u5c31\u6316\u4e00\u689d\u000d\u000a\u901a\u9053\uff0c\u958b\u51fa\u4e00\u689d\u8def\uff1b\u9047\u5230\u6cb3\u6d41\u7121\u6cd5\u904e\u6cb3\uff0c\u5c31\u67b6\u4e00\u5ea7\u6a4b\u6e21\u6cb3\u3002\u610f\u55bb\uff1a\u000d\u000a\u4fee\u9053\u4eba\u4e0d\u7ba1\u9047\u5230\u4ec0\u9ebc\u6a23\u7684\u56f0\u96e3\u3001\u963b\u7919\u3001\u8003\u9a57\uff0c\u53ea\u8981\u80af\u52aa\u529b\u7528\u5fc3\u53bb\u9762\u000d\u000a\u5c0d\uff0c\u4e26\u4e14\u4e00\u5fc3\u5805\u6301\u5230\u5e95\uff0c\u5c31\u80fd\u5920\u9010\u6f38\u7372\u5f97\u89e3\u51b3\uff1b\u7e31\u4e0d\u80fd\u5982\u610f\u9806\u5229\u89e3\u000d\u000a\u6c7a\uff0c\u4e5f\u4e00\u5b9a\u8981\u6709\u4e00\u9846\u767e\u6298\u4e0d\u6493\u3001\u6108\u632b\u6108\u52c7\u4e4b\u5fc3\u5fd7\u3002"]